INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '740cb29f-0910-4634-b12f-fdf5f4af2ceb', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020101', '0201', '风邪犯耳', 1, '', '耳中胀闷，耳鸣，听力下降，鼻塞流涕，或有咳嗽咯痰、头痛等症。舌苔薄白，脉浮。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7a0394fb-ae4f-4caf-91c3-a220bf23945a', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020102', '0201', '痰浊积聚', 1, '', '耳胀不适，听力不聪，头晕头重，或有咳嗽咯痰，胸脘痞闷。检查见鼓室积液，量多难消。舌苔白腻，脉溺或滑。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6cecdb85-77e2-497b-9b25-620e0c913cdf', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020103', '0201', '气滞血瘀', 1, '', '耳胀、耳中闭气，或有刺痛感，耳鸣不聪。检查见鼓膜混浊、内陷，或增厚、粘连，或有鼓室积液。舌质紫暗或有瘀点，脉涩。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '11612f8d-d6c5-4f06-af2d-7a09f883a98d', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020104', '0201', '脾气虚弱', 1, '', '耳闭时轻时重，面色无华，食少腹胀，或有便溏。检查见鼓膜内陷，或有鼓室积液。舌淡，苔白，脉弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '20811f9b-5eab-4a16-b17b-d04f334d376f', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020105', '0201', '肝肾阴虚', 1, '', '耳闭、听力下降，头晕眼花，腰膝酸软，手足心热。舌红，苔少，脉细数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1b522595-97d3-4844-b520-59fdf46b4589', '04fe2d0e-5d9c-4e9e-ba49-740225d9177d', '020201', '0202', '肝胆火热', 1, '', '急性发作，耳深部痛，头痛，听力下降。发热，面红目赤，小便黄赤。检查见鼓膜充血、穿孔，流脓较多。舌红，苔黄，脉弦数。 。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '750b740b-4251-476f-b8c3-57c21d6f820b', '04fe2d0e-5d9c-4e9e-ba49-740225d9177d', '020202', '0202', '脾虚湿困', 1, '', '耳内流脓，量较多，日久不愈。倦怠乏力，食少，便溏。舌质淡红，苔白腻，脉细无力。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9d3fca70-0c47-4b62-9647-8b672a5f9ef6', '04fe2d0e-5d9c-4e9e-ba49-740225d9177d', '020203', '0202', '肾阴亏虚', 1, '', '耳内流脓，时多时少，混有豆渣样物，带秽臭味。听力检查呈传导性耳聋或混合性耳聋。头晕头痛，腰酸乏力。X线乳突摄片见骨质破坏。舌质红，苔薄，脉细数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fb2c672c-b194-499c-8ba5-da093b6524bc', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020301', '0203', '风邪外犯', 1, '', '突发耳聋，伴鼻塞、流涕，或有头痛、耳胀闷，或有恶寒、发热、身疼。舌苔薄白，脉浮。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '13edf7d4-7033-4b17-b4f6-bcd172d01f07', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020302', '0203', '肝火上炎', 1, '', '情志抑郁或恼怒之后，突发耳聋，伴口苦口干，便秘尿黄，面红，目赤。舌红，苔黄，脉弦数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5dbcaa55-2e8f-4cf7-b5ac-20d4579ed711', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020303', '0203', '肝阳上亢', 1, '', '突聋发于恼怒之后，头晕、头痛、口苦，烦躁易怒。舌苔薄白，脉弦。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '073613c3-dc1f-461e-aa66-bc67daf8fee1', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020304', '0203', '气滞血瘀', 1, '', '耳聋伴耳胀闷感，耳鸣不休，或耳聋因强大声音震击而成。舌质暗红，脉涩。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f1158c53-4937-4593-84a3-9b211fe6f2bb', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020305', '0203', '气血亏虚', 1, '', '素体虚弱，面色无华，突发耳聋；或暴聋数日后头痛、耳胀闷等症消除，而面色无华、头晕眼花、语声无力、四肢倦怠等症仍在。舌淡，苔薄，脉细弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '024be939-16b2-48b3-9a2a-c477b9f8cc0e', 'c5ede233-cf57-4a9b-b16c-861e08ee0ece', '020401', '0204', '肾精亏损', 1, '', '耳聋日久，耳鸣如闻蝉声，头晕眼花，腰膝酸软。舌红，苔少，脉细数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6a2aa490-4924-49fb-aaaa-60d098c4928e', 'c5ede233-cf57-4a9b-b16c-861e08ee0ece', '020402', '0204', '气血亏虚', 1, '', '耳聋耳鸣，时轻时重，劳累后加重，面色无华，食欲不振。舌苔薄白，脉细弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8eae9eec-0ef7-4f10-a916-67f60f3518ee', 'c5ede233-cf57-4a9b-b16c-861e08ee0ece', '020403', '0204', '痰火郁结', 1, '', '耳聋，头重而晕，耳中胀闷，脘腹痞满。舌苔黄腻，脉滑数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '90527357-1f4d-48f5-a951-de64c640bc98', 'c5ede233-cf57-4a9b-b16c-861e08ee0ece', '020404', '0204', '气滞血瘀', 1, '', '耳聋或重或轻，或伴耳鸣，鼓膜增厚，或有粘连、钙质沉着。舌质紫暗，脉涩。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1772a305-a48e-4872-9156-c18f8d48a1ab', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020501', '0205', '肝阳上亢', 1, '', '眩晕因情绪波动而发作或加重。平时性情急躁，胸胁胀闷。舌红，苔黄，脉弦。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c2f81af9-2bbc-44ad-a210-a4cd901d0e30', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020502', '0205', '痰浊中阻', 1, '', '眩晕而胸脘痞闷，泛泛欲吐，咽喉痰多而粘。舌苔白腻，脉滑。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ce8c6932-8165-43ca-89d1-5c3ea797b3ea', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020503', '0205', '脾气虚弱', 1, '', '眩晕因疲劳而发，或发作后头晕乏力，食欲不振，大便溏薄。舌淡，苔白，脉弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'be9152fb-f8b7-4424-9e47-1107c49e544c', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020504', '0205', '肾阴亏虚', 1, '', '眩晕、耳鸣常有发作，腰膝酸软，记忆力差，手足心热。舌红，苔薄黄，脉细数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a190d0f9-85c8-43af-8552-5853b06c9f1f', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020505', '0205', '肾阳亏虚', 1, '', '眩晕、耳鸣，听力下降，精神萎靡，腰痛背冷，面色恍白。舌淡胖，苔白，脉沉细。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c6b2a6bc-34a9-4496-8565-948f7e958e2b', '66522337-9b13-473d-a8c9-1c8ea4996f59', '020601', '0206', '风寒外袭', 1, '', '鼻塞遇寒加重，鼻涕清稀、量多。伴恶寒，发热，头痛，身痛。鼻粘膜充血轻，但肿胀较甚。舌苔薄白，豚浮紧。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '60a854fc-8024-448f-a396-7a36ee19b75f', '66522337-9b13-473d-a8c9-1c8ea4996f59', '020602', '0206', '风热外犯', 1, '', '鼻塞，流涕粘稠，色白或黄，发热，微恶寒，口微渴。鼻粘膜充血。舌质偏红，脉浮数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1895eab4-da46-4c31-89f2-88e1ebd53b70', '2609d530-b1ed-47ae-ac12-3846c9a55e8e', '020701', '0207', '肺虚邪滞', 1, '', '鼻塞多为间歇性，时轻时重，鼻涕白粘、量多，遇寒加重。或伴气短乏力、大便溏薄等症。鼻粘膜肿胀，色淡红。舌苔薄白，脉细弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c7795194-18f2-4556-a8e8-27b4c4f98768', '2609d530-b1ed-47ae-ac12-3846c9a55e8e', '020702', '0207', '气滞血瘀', 1, '', '鼻塞多为持续性，鼻涕粘稠，不易擤出，嗅觉迟钝。伴头昏、耳鸣、记忆力减退等症。鼻粘膜充血，呈暗红或深红色。鼻甲肿大，表面不平滑，如桑椹样，触之较硬，缺乏弹性，对一般滴鼻剂收缩反应较差。舌质紫暗或有瘀点，脉涩。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fea12fd0-08d7-4fc3-ab73-8464caa4bff2', '404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', '020801', '0208', '燥热外犯', 1, '', '鼻干无涕，鼻腔痂皮积聚，时作鼻衄。伴口渴喜饮，小便短黄，大便干燥。鼻腔粘膜干燥充血。舌红，苔黄燥，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4dd1aa50-cfb7-499b-87cb-e05a82305801', '404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', '020802', '0208', '肺阴亏虚', 1, '', '鼻中干燥，痂皮积聚，鼻塞失嗅，口干不欲多饮，或有午后潮热、盗汗、头晕、手足：心热。鼻甲萎缩，粘膜暗红。舌红，苔薄，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b695c489-110b-4165-a3cb-6437e7bda727', '404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', '020803', '0208', '肺脾气虚', 1, '', '鼻燥少涕，头晕，神疲乏力，语声低弱，食少，便溏。鼻粘膜干燥萎缩，色淡红。舌淡，苔白润，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a3edff0b-018f-434e-a6bb-3e7436f85d87', '594eda87-98a4-46f1-8e27-2f1ca1f3b86a', '020901', '0209', '肺虚感寒', 1, '', '常因感受风冷异气发病，恶风寒，面白，气短，咳嗽，咯痰色白。舌苔薄白，脉浮。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a778a1bd-b0e6-4492-9a15-3f3e262c5b4b', '594eda87-98a4-46f1-8e27-2f1ca1f3b86a', '020902', '0209', '脾气虚弱', 1, '', '鼻痒而喷嚏连作，清涕量多，四肢乏力，大便溏薄。鼻粘膜色淡红。舌淡，苔白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3356825e-c9a4-4bd0-84c5-ef7d6b0fb599', '594eda87-98a4-46f1-8e27-2f1ca1f3b86a', '020903', '0209', '肾阳亏虚', 1, '', '鼻痒，鼻塞，喷嚏较多，遇风冷则易发作。畏寒肢冷，小便清长，大便溏薄。鼻粘膜淡白，鼻甲水肿。舌淡，苔白，脉沉细。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '553c31cb-6104-4e3b-af4c-59575dffcd5f', '6c56539b-b855-43d5-8ed9-e6197828f2d0', '021001', '0210', '肺经风热', 1, '', '多见于发病初期，或慢性鼻渊因外感而急性发作。鼻塞，涕多色白或微黄，头痛，咳嗽，咯痰。鼻粘膜充血，鼻甲肿大。舌苔薄白，脉浮数。。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b30df52b-01d2-47b1-958f-99b1fb3754b4', '6c56539b-b855-43d5-8ed9-e6197828f2d0', '021002', '0210', '胆经郁热', 1, '', '多见于急性鼻渊，或慢性鼻渊急性发作。鼻塞、头痛较甚，涕多色黄而浊。身热，口渴，大便干燥。鼻粘膜充血明显，且肿胀，鼻腔内可见较多脓性分泌物。舌红，苔黄腻，脉弦数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6f2f180b-cd28-4b4d-b3c4-7101bb2d732b', '6c56539b-b855-43d5-8ed9-e6197828f2d0', '021003', '0210', '脾胃湿热', 1, '', '多见于急性鼻渊后期。鼻塞，流涕缠绵不愈。伴头昏，食欲不振，大便溏薄。鼻粘膜充血肿胀，鼻腔内有较多黄浊分泌物。舌苔黄腻，脉濡数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7c2adc11-d561-46a4-8f85-90dbc32c9e59', '6c56539b-b855-43d5-8ed9-e6197828f2d0', '021004', '0210', '肺脾气虚', 1, '', '多见于慢性鼻渊。鼻塞，头昏，记忆力减退，鼻涕混浊，时多时少。面色萎黄或白，少气乏力，大便溏薄。鼻腔粘膜不充血，但肿胀，并有粘性或脓性分泌物。舌淡，苔白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '51135a47-1749-438c-b22e-c5ec4c9d5c41', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021101', '0211', '肺经热盛', 1, '', '鼻衄点滴渗出，血色鲜红，伴鼻塞，咳嗽，或有发热。舌质偏红，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0f1bc748-3dbf-49f7-b5fa-336fe31c8e7e', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021102', '0211', '胃火炽盛', 1, '', '鼻中出血量多，血色深红。身热，口渴，便秘。鼻腔粘膜充血。舌红，苔黄，脉洪数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2cd30e08-7cf5-4bd4-8e90-5ea2db197042', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021103', '0211', '肝火上炎', 1, '', '鼻衄起于恼怒之后，头痛头晕，口苦咽干，胸胁苦满。舌红，苔黄，脉弦数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '46f5c0cb-a351-4179-9e5c-676fa8a2fa58', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021104', '0211', '阴虚火旺', 1, '', '鼻衄量少，口干咽燥，头晕眼花，手足心热。舌红，苔少，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8d0cb477-1f24-42c0-b88d-5900f41c1487', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021105', '0211', '脾不统血', 1, '', '鼻衄量少，面白肢冷，大便溏薄，鼻粘膜色淡。舌淡，苔白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5642f1da-3196-4c82-bd67-312fc593a173', 'ac70791e-522b-4363-a46c-a5044568a13e', '021201', '0212', '风寒外袭', 1, '', '咽痛，口不渴，恶寒，不发热或微发热，咽粘膜水肿，不充血或轻度充血。舌质淡红，苔薄白，脉浮紧。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3cb52112-6b88-4334-84d0-66e81c5c4ee4', 'ac70791e-522b-4363-a46c-a5044568a13e', '021202', '0212', '风热外侵', 1, '', '咽痛而口微渴，发热，微恶寒，咽部轻度充血，水肿。舌边尖红，苔薄白，脉浮数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9f48bccd-c582-4cf5-be2f-04720cfec64e', 'ac70791e-522b-4363-a46c-a5044568a13e', '021203', '0212', '肺胃实热', 1, '', '咽痛较剧，口渴多饮，咳嗽，痰粘稠，发热，大便偏干，小便短黄。咽部充血较甚。舌红，苔黄，脉数有力。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9dbeab9f-d2e9-4b4d-8130-cd6eec6bd0ad', '523762f2-424f-4c00-87be-ef8e14d657f7', '021301', '0213', '阴虚肺燥', 1, '', '咽喉干疼、灼热，多言之后症状加重，呛咳无痰，频频求饮，而饮量不多，午后及黄昏时症状明显。咽部充血呈暗红色，粘膜干燥、或有萎缩，或有淋巴滤泡增生。舌红，苔薄，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e04f492b-cc50-4b92-845f-b8201918b11f', '523762f2-424f-4c00-87be-ef8e14d657f7', '021302', '0213', '肺脾气虚', 1, '', '咽喉干燥，但不欲饮，咳嗽，有痰易咯，平时畏寒，易感冒，神倦乏力，语声低微，大便溏薄。咽部充血较轻。舌苔白润，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5e02cb91-9c47-4f97-9469-aad1aaa4a4e6', '523762f2-424f-4c00-87be-ef8e14d657f7', '021303', '0213', '痰热蕴结', 1, '', '咽喉不适，因受凉、疲劳、多言之后症状较重。咳嗽、咯痰粘稠，口渴喜饮。咽粘膜充血呈深红色，肥厚，有黄白色分泌物附着。舌红，苔黄腻，脉滑数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1e3e2877-2ad7-446a-be6c-e5a54ed80bf6', '8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', '021401', '0214', '风热外侵', 1, '', '急乳蛾初起，咽痛，轻度吞咽困难。伴发热、恶寒、咳嗽、咯痰等症。咽粘膜及扁桃体充血，未成脓。舌苔薄白，脉浮数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0908c9c4-94cf-4807-bec3-ee1b5fd7cf83', '8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', '021402', '0214', '胃火炽盛', 1, '', '咽痛较甚，吞咽困难。身热，口渴，大便秘结。咽部及扁桃体充血红肿，上有脓点或小脓肿。舌红，苔黄，脉滑数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd10ec532-38b1-44b8-b03b-ab39af4726b7', '8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', '021403', '0214', '肺肾阴虚', 1, '', '咽部干燥、灼热，微痛不适。干咳少痰，手足心热，精神疲乏，或午后低热，颧赤。扁桃体暗红、肿大，或有少许脓液附于表面。舌红，苔薄，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a3946062-2d42-4cf3-a270-50c94d27e1a3', '8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', '021404', '0214', '脾气虚弱', 1, '', '咽部不适，微痒或干燥，或有异物感，咯痰色白，面色少华，声音低怯，神疲乏力，食少，便溏。扁桃体肿大，充血较轻或不充血，挤压时有少许脓液。舌质淡胖，苔白润，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '85131675-fb94-455e-b835-1f7b6f79fe6d', 'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', '021501', '0215', '胃火炽盛', 1, '', '咽痛，吞咽困难，发热。扁桃体旁充血，隆起，悬雍垂被推向对侧。舌红，苔黄，脉数有力。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9ed9540f-1ccf-43f3-942b-0d1149141568', 'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', '021502', '0215', '阳明腑实', 1, '', '咽痛剧烈，汤水难下，口涎外流，语言含混，发热，口渴，小便短黄，腹胀便秘，咽部充血，扁桃体周围隆起明显，触之软。舌红，苔黄而厚，脉数有力。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '746bbf56-29b1-406d-9d23-e13a1c5a8cde', 'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', '021503', '0215', '阴虚邪恋', 1, '', '脓液排出，发热渐退，咽痛渐减，口渴，尿黄，便结。舌红，苔薄少，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '771a3453-fbd1-4eb1-bc48-d9c9b3ca3ca2', '51c6ef80-f206-46bb-bd37-d53242775eda', '021601', '0216', '风寒袭肺', 1, '', '声音嘶哑，发音低沉，咽喉胀紧，鼻塞、流清涕，咳嗽，咯痰清稀。声带肿胀而不、充血、舌苔薄白，脉浮紧。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '68c4fa94-eca2-45cd-a4f0-a702edd9e537', '51c6ef80-f206-46bb-bd37-d53242775eda', '021602', '0216', '风热犯肺', 1, '', '声音粗糙，嘶哑，咽喉干燥，疼痛，咳嗽，咯痰粘白或微黄，咽喉粘膜充血，肿胀。舌边尖红，苔薄白，脉浮数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1c4e8b98-2978-4023-a22c-469529173bae', '51c6ef80-f206-46bb-bd37-d53242775eda', '021603', '0216', '肺热壅盛', 1, '', '声嘶，咽痛，口渴，咳嗽，咯痰色黄，身热，便秘。咽喉粘膜充血深红、肿胀，有黄白色分泌物粘附于表面。舌红，苔黄，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '37f2d7ea-cfd6-4746-ba17-2021fbde586d', '3b4be459-6d84-42c2-83ae-4303aa5ac1c4', '021701', '0217', '肺肾阴虚', 1, '', '声嘶日久，咽喉干燥、锨热微痛，口干，干咳无痰，或痰少而粘。声带微红。舌红，少苔，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '664ac158-d400-4481-a53a-af052331851a', '3b4be459-6d84-42c2-83ae-4303aa5ac1c4', '021702', '0217', '肺脾气虚', 1, '', '语声低沉，气短懒言，咳嗽咯痰，色白略稀，体倦乏力，纳少便溏。声带肿而不红，声门关闭不密。舌淡，苔白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '773b3a64-9e4d-41db-8ccd-259aa29ac950', '3b4be459-6d84-42c2-83ae-4303aa5ac1c4', '021703', '0217', '气滞血瘀', 1, '', '声音嘶哑，咳嗽痰少，多言后喉中觉痛，痛处不移，胸胁胀闷。声带暗红、增厚，或有声带小结、声带息肉，或室带肥厚、超越。舌质紫暗或有瘀点，脉涩。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '916345db-4d0e-4f86-8b2d-cb7460dd5ce8', '3b4be459-6d84-42c2-83ae-4303aa5ac1c4', '021704', '0217', '痰浊凝聚', 1, '', '声音粗浊，喉中痰多，痰白而粘，声带水肿，或有声带小结、声带息肉，色灰白。舌苔白腻，脉滑。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '46f30086-45f1-41b7-98e3-7dd85c3bb25e', '01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', '021801', '0218', '痰气互结', 1, '', '咽中如有炙脔或其他异物感，咽之不下、吐之不出。时作嗳气、呃逆、恶心、泛泛欲吐，胸脘胀满。舌苔白腻，脉弦滑。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ef1e2b4e-bf0c-475d-997c-6f1cf6e5896d', '01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', '021802', '0218', '肝郁气滞', 1, '', '咽中梗阻感，嗳气频频，或作呃逆，胁下胀闷，嗳气后稍舒。舌苔薄白，脉弦。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '43b7f4f3-607a-425d-85cd-f755e96b9e27', '01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', '021803', '0218', '心脾气虚', 1, '', '咽中异物感，不思饮食，口中无味，面白神疲，少气懒言，或时时悲伤欲哭，夜寐不实，易惊醒或惶恐不安，小便清长，大便溏薄。舌淡，苔白，脉弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6a6f27b8-0e73-4884-b502-feff01329e07', 'b8d2a791-9d7c-4747-a4f1-fc04cb6259b0', '021901', '0219', '胃火炽盛', 1, '', '牙龈作痛、出血，口气热臭，渴喜冷饮，大便干结。牙龈红肿疼痛，溢出脓血。舌红，苔黄，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ce153d3d-585d-401b-bc37-de368a5324dc', 'b8d2a791-9d7c-4747-a4f1-fc04cb6259b0', '021902', '0219', '肾阴亏虚', 1, '', '牙龈萎缩，牙根松动，牙龈粘膜微红肿。或有头晕，耳鸣，腰膝酸软。舌红少津，苔薄，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '912e4bb3-4967-4ade-ad04-81796ee0a8ad', 'b8d2a791-9d7c-4747-a4f1-fc04cb6259b0', '021903', '0219', '气血亏虚', 1, '', '牙龈萎缩，颜色淡白，牙根宣露，牙齿松动，咀嚼无力，牙龈时有渗血。面白或萎黄，倦怠乏力。舌淡，苔白，脉弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7b67c3d7-1e16-4cc0-966e-cdf350fdb41c', '14a55090-ad4c-422c-ba4f-99e111379560', '022001', '0220', '心脾积热', 1, '', '口内疼痛，口渴，口臭，尿短黄，便秘。口疮数量多，周围充血明显。舌红，苔黄，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a1ac7790-12de-4eb2-8714-4e537b3c06da', '14a55090-ad4c-422c-ba4f-99e111379560', '022002', '0220', '阴虚火旺', 1, '', '口内疼痛，口干，手足心热，乏力。口疮1～2个或2～3个，周围轻微充血。舌红，苔少，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '31e32bcd-9f67-4e1c-824a-b4818f248984', '14a55090-ad4c-422c-ba4f-99e111379560', '022003', '0220', '气血亏虚', 1, '', '口不渴，或伴畏寒，便溏。口疮数量不多，周围粘膜不充血。舌淡，苔薄白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fd6baacc-3e22-4df8-851d-dcb59ffc5edc', '91884d9e-aaa2-4434-98e7-21ff5faaf66e', '080101', '0801', '热毒蕴结', 1, '', '常见于气实火盛的患者。轻者疖肿只有一、二个，多者可散发全身，或簇集一处，或此愈彼起。可有发热，口渴，溲赤，便秘。苔黄，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2737eb99-71a9-4460-b4a0-0fd03c222d77', '91884d9e-aaa2-4434-98e7-21ff5faaf66e', '080102', '0801', '暑热浸淫', 1, '', '发于夏秋季节，以儿童及产妇多见。可有发热，口渴，便秘，溲赤等。苔薄腻，脉滑数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3f6174a6-d6be-49ea-9a1c-47619f374c91', '91884d9e-aaa2-4434-98e7-21ff5faaf66e', '080103', '0801', '体虚毒恋', 1, '', '疖肿常此愈彼起，不断发生。常见于体质虚弱或有某些慢性病患者，并有相应的全身证候。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '692f7521-8836-4f4a-b1a5-0aee64b7fd06', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080201', '0802', '风热痰毒', 1, '', '肿块发生在颌颈部，全身恶寒发热，头痛，口干，咽痛。苔薄白或薄黄，脉浮数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f5297a9c-e8bb-45d6-8b57-0357e0e113cc', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080202', '0802', '肝郁痰火', 1, '', '肿块发生在腋下，全身发热，头痛，胸胁牵痛，口苦咽干。舌红，苔黄，脉弦数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '16014b05-c26a-44d9-b9ed-9ccb11f6138a', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080203', '0802', '湿热蕴结', 1, '', '肿块发生在腹股沟部，全身发热，患肢拘急，小便黄热。苔黄腻，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '29b6bf0b-b9f4-4b81-a46c-9b547d5147d6', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080204', '0802', '热胜酿脓', 1, '', '局部皮肤红肿发亮，{热疼痛，肿块变软，有应指感。全身发热，口干。舌红，苔黄，脉滑数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '256284b2-1bf4-466b-859f-e2faabe43bd6', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080205', '0802', '余毒凝滞', 1, '', '全身症状消退，局部红肿热痛亦轻，唯肿块僵硬不消。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '16e6219c-237b-4f33-acaa-ffede4b0e775', 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '080301', '0803', '火毒凝结', 1, '', '局部红肿高突，灼热疼痛，根脚收束，脓液稠黄，能迅速化脓脱腐。全身发热，口渴，尿赤。苔黄，脉数有力。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '275070eb-404f-4b36-b977-e2b6ff284944', 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '080302', '0803', '湿热壅滞', 1, '', '局部症状与火毒凝结证相同。全身壮热，朝轻暮重，胸闷呕恶。苔白腻或黄腻，脉濡数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c9d7b5f4-8b86-4013-9b52-6794ecb9e54f', 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '080303', '0803', '阴虚火炽', 1, '', '肿势平塌，根脚散漫，皮色紫滞，疼痛剧烈，脓腐难化，脓水稀少或带血水。全身发热烦躁，口渴多饮，大便燥结，小便短赤。舌红，苔黄燥，脉细弦数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e8cc4153-c8b7-4a57-9915-4a3f0d7cf73a', 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '080304', '0803', '气虚毒滞', 1, '', '肿势平塌，根脚散漫，皮色灰暗不泽，胀重木痛，腐肉不化，脓液稀少，易成空腔。全身畏寒高热或身热不扬，小便频数，口渴喜热饮，精神萎靡，面色少华。舌质淡红，苔白或微黄，脉数无力。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '16c5b334-84aa-4f35-b5dd-6dd415ea8c72', '46759b5b-96b9-4a30-9ecb-b0f982f0ee5c', '080401', '0804', '热毒凝结', 1, '', '红肿高突，根脚收束，发热头痛。舌红，苔黄，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '29a75640-5f57-4130-b42f-53bf171c6e81', '46759b5b-96b9-4a30-9ecb-b0f982f0ee5c', '080402', '0804', '火毒炽盛', 1, '', '疮形平塌，肿势散漫，皮色紫暗，{热疼痛。有高热，头痛，烦渴，呕恶，搜赤，便秘。舌红，苔黄腻，脉洪数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6dfdc658-2c53-476f-b7f2-1ef21db256b7', 'f21ec997-aa76-4744-8d90-97c362d003af', '080501', '0805', '火毒凝结', 1, '', '局部红肿疼痛，全身有畏寒发热。舌红，苔黄，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2cd5fd43-936d-4230-9655-4ec1dfc52615', 'f21ec997-aa76-4744-8d90-97c362d003af', '080502', '0805', '热胜肉腐', 1, '', '红肿明显，疼痛剧烈，肉腐为脓，溃后脓出肿痛消退；如溃后肿痛不退，脓液不断，可能是筋骨腐蚀。舌红，苔黄，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2d997c33-a43e-4bbf-8479-47c8f7dbcc7e', '81fdb486-7c56-426d-b90a-6ac673829446', '080601', '0806', '火毒入络', 1, '', '患肢红丝较细，全身症状较轻。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '13e207b0-2574-401f-b221-9f41a4ef1bdd', '81fdb486-7c56-426d-b90a-6ac673829446', '080602', '0806', '火毒入营', 1, '', '患肢红丝粗肿明显，迅速向近端蔓延。全身寒战高热，烦躁，头痛，口渴。苔黄腻，脉洪数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3bcff740-e447-4757-9093-aa6e5ed2e4b5', 'c1b52342-59a6-4171-ba7e-2790fa286794', '080701', '0807', '湿火炽盛', 1, '', '初起患肢有沉重和紧束感，以后逐渐出现胀裂样疼痛，创口周围皮肤呈红色、肿胀发亮，按之陷下，迅速蔓延成片。1～2日后肿胀剧烈，可出现水疱，皮肉腐烂，高热持续。舌质红，苔薄白或黄，脉弦数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd27c5f89-b805-49bd-a5ce-e1654ed40d2b', 'c1b52342-59a6-4171-ba7e-2790fa286794', '080702', '0807', '毒入营血', 1, '', '壮热头痛，神昏谵语，气促，烦躁不安，呃逆呕吐。局部胀痛，伤口周围高度水肿发亮，迅速成暗紫色，有血疱，肌肉腐烂，脓液稀薄，混有气泡溢出，恶臭。舌质红绛，苔薄黄，脉洪滑数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fb90e016-7da4-4bdf-b61f-8b6be6a2ce4f', '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '080801', '0808', '热毒蕴结', 1, '', '颐颌之间结块疼痛，张口不利，继则肿痛渐增，检视腮腺管口常现红肿，压迫局部有粘稠的分泌物溢出，身热。苔薄腻，脉浮数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e22d6b2d-8b79-49f8-b924-d65085b24fa6', '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '080802', '0808', '毒盛酿脓', 1, '', '颐颌间结肿疼痛日增，甚至肿势延及面颊和颈项，掀红灼热，张口困难，继之酿脓应指，腮腺管口能挤出脓性分泌物，高热口渴。苔黄腻，脉弦数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '62a61987-4e63-4b9b-9c15-639463886472', '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '080803', '0808', '热毒内陷', 1, '', '颐颌间肿块多平塌散漫，肿势可延及脸面和颈项，{红灼热，疼痛剧烈，汤水难咽，壮热口渴，痰涌气粗，烦躁不安，甚至神昏谵语。舌质红绛，苔少而干，脉细数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3afc7c7e-00d5-4cb2-a0cd-89b87636f90b', '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '080804', '0808', '余毒未清', 1, '', '病程日久，经常反复发作，发作时颐颌部肿痛，腮腺管口能挤出脓性分泌物，每在进食时颐颌部感到胀痛，口内常有恶臭。苔薄黄或腻，脉滑。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '04dbdd4d-2b45-47e9-8d49-8cfcd05726da', 'd1defda5-27db-4535-b374-dd10662aadda', '080901', '0809', '风热毒蕴', 1, '', '发于头面部，恶寒发热，皮肤掀红灼热，肿胀疼痛，甚则发生水疮，眼胞肿胀难睁。舌质红，苔薄黄，脉浮数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f3c5cb19-f8ac-400d-81a8-81adc0b0f50e', 'd1defda5-27db-4535-b374-dd10662aadda', '080902', '0809', '湿热毒蕴', 1, '', '发于下肢。除发热等症状外，局部以红赤肿胀，灼热疼痛为主，亦可发生水疱、紫斑，甚至结毒化脓或皮肤坏死。苔黄腻，脉洪数。反复发作，可形成大脚疯（象皮腿）。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '40d4b86c-22e5-4df2-9356-b0ead056aae1', 'd1defda5-27db-4535-b374-dd10662aadda', '080903', '0809', '胎火蕴毒', 1, '', '发于新生儿。多见于臀部，局部红肿灼热，可呈游走性，并有壮热烦躁。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'db651ace-a495-438f-855c-d67546e808c9', 'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', '081001', '0810', '余毒攻窜', 1, '', '发病前有疔疮、痈、疖等病史。全身伴有壮热，口渴，甚则神昏谵语。苔黄，脉洪数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd83c8974-591b-453a-9819-79318e9fcb5b', 'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', '081002', '0810', '暑湿交阻', 1, '', '多发于夏秋之间。初起恶寒发热，头胀，胸闷呕恶，周身骨节酸痛，胸部可布白pei(陪）。苔白腻，脉滑数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f0e6ecf5-c104-4251-868e-637cc719c0e6', 'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', '081003', '0810', '瘀血凝滞', 1, '', '劳伤筋脉诱发者，多发于四肢内侧。跌打损伤诱发者，多发于伤处。局部漫肿疼痛，皮色微红，或呈青紫，溃后脓液中央有瘀血块。妇女产后恶露停滞而成者，多发于小腹及大腿等处。发病较缓，初起一般无全身症状或全身症状较轻，化脓时出现高热。', '2008/04/10 08:17:14', '', 3 );
