INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '740cb29f-0910-4634-b12f-fdf5f4af2ceb', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020101', '0201', '风邪犯耳', 1, '', '耳中胀闷，耳鸣，听力下降，鼻塞流涕，或有咳嗽咯痰、头痛等症。舌苔薄白，脉浮。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7a0394fb-ae4f-4caf-91c3-a220bf23945a', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020102', '0201', '痰浊积聚', 1, '', '耳胀不适，听力不聪，头晕头重，或有咳嗽咯痰，胸脘痞闷。检查见鼓室积液，量多难消。舌苔白腻，脉溺或滑。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6cecdb85-77e2-497b-9b25-620e0c913cdf', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020103', '0201', '气滞血瘀', 1, '', '耳胀、耳中闭气，或有刺痛感，耳鸣不聪。检查见鼓膜混浊、内陷，或增厚、粘连，或有鼓室积液。舌质紫暗或有瘀点，脉涩。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '11612f8d-d6c5-4f06-af2d-7a09f883a98d', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020104', '0201', '脾气虚弱', 1, '', '耳闭时轻时重，面色无华，食少腹胀，或有便溏。检查见鼓膜内陷，或有鼓室积液。舌淡，苔白，脉弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '20811f9b-5eab-4a16-b17b-d04f334d376f', 'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', '020105', '0201', '肝肾阴虚', 1, '', '耳闭、听力下降，头晕眼花，腰膝酸软，手足心热。舌红，苔少，脉细数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1b522595-97d3-4844-b520-59fdf46b4589', '04fe2d0e-5d9c-4e9e-ba49-740225d9177d', '020201', '0202', '肝胆火热', 1, '', '急性发作，耳深部痛，头痛，听力下降。发热，面红目赤，小便黄赤。检查见鼓膜充血、穿孔，流脓较多。舌红，苔黄，脉弦数。 。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '750b740b-4251-476f-b8c3-57c21d6f820b', '04fe2d0e-5d9c-4e9e-ba49-740225d9177d', '020202', '0202', '脾虚湿困', 1, '', '耳内流脓，量较多，日久不愈。倦怠乏力，食少，便溏。舌质淡红，苔白腻，脉细无力。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9d3fca70-0c47-4b62-9647-8b672a5f9ef6', '04fe2d0e-5d9c-4e9e-ba49-740225d9177d', '020203', '0202', '肾阴亏虚', 1, '', '耳内流脓，时多时少，混有豆渣样物，带秽臭味。听力检查呈传导性耳聋或混合性耳聋。头晕头痛，腰酸乏力。X线乳突摄片见骨质破坏。舌质红，苔薄，脉细数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fb2c672c-b194-499c-8ba5-da093b6524bc', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020301', '0203', '风邪外犯', 1, '', '突发耳聋，伴鼻塞、流涕，或有头痛、耳胀闷，或有恶寒、发热、身疼。舌苔薄白，脉浮。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '13edf7d4-7033-4b17-b4f6-bcd172d01f07', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020302', '0203', '肝火上炎', 1, '', '情志抑郁或恼怒之后，突发耳聋，伴口苦口干，便秘尿黄，面红，目赤。舌红，苔黄，脉弦数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5dbcaa55-2e8f-4cf7-b5ac-20d4579ed711', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020303', '0203', '肝阳上亢', 1, '', '突聋发于恼怒之后，头晕、头痛、口苦，烦躁易怒。舌苔薄白，脉弦。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '073613c3-dc1f-461e-aa66-bc67daf8fee1', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020304', '0203', '气滞血瘀', 1, '', '耳聋伴耳胀闷感，耳鸣不休，或耳聋因强大声音震击而成。舌质暗红，脉涩。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f1158c53-4937-4593-84a3-9b211fe6f2bb', 'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', '020305', '0203', '气血亏虚', 1, '', '素体虚弱，面色无华，突发耳聋；或暴聋数日后头痛、耳胀闷等症消除，而面色无华、头晕眼花、语声无力、四肢倦怠等症仍在。舌淡，苔薄，脉细弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '024be939-16b2-48b3-9a2a-c477b9f8cc0e', 'c5ede233-cf57-4a9b-b16c-861e08ee0ece', '020401', '0204', '肾精亏损', 1, '', '耳聋日久，耳鸣如闻蝉声，头晕眼花，腰膝酸软。舌红，苔少，脉细数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6a2aa490-4924-49fb-aaaa-60d098c4928e', 'c5ede233-cf57-4a9b-b16c-861e08ee0ece', '020402', '0204', '气血亏虚', 1, '', '耳聋耳鸣，时轻时重，劳累后加重，面色无华，食欲不振。舌苔薄白，脉细弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8eae9eec-0ef7-4f10-a916-67f60f3518ee', 'c5ede233-cf57-4a9b-b16c-861e08ee0ece', '020403', '0204', '痰火郁结', 1, '', '耳聋，头重而晕，耳中胀闷，脘腹痞满。舌苔黄腻，脉滑数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '90527357-1f4d-48f5-a951-de64c640bc98', 'c5ede233-cf57-4a9b-b16c-861e08ee0ece', '020404', '0204', '气滞血瘀', 1, '', '耳聋或重或轻，或伴耳鸣，鼓膜增厚，或有粘连、钙质沉着。舌质紫暗，脉涩。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1772a305-a48e-4872-9156-c18f8d48a1ab', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020501', '0205', '肝阳上亢', 1, '', '眩晕因情绪波动而发作或加重。平时性情急躁，胸胁胀闷。舌红，苔黄，脉弦。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c2f81af9-2bbc-44ad-a210-a4cd901d0e30', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020502', '0205', '痰浊中阻', 1, '', '眩晕而胸脘痞闷，泛泛欲吐，咽喉痰多而粘。舌苔白腻，脉滑。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ce8c6932-8165-43ca-89d1-5c3ea797b3ea', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020503', '0205', '脾气虚弱', 1, '', '眩晕因疲劳而发，或发作后头晕乏力，食欲不振，大便溏薄。舌淡，苔白，脉弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'be9152fb-f8b7-4424-9e47-1107c49e544c', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020504', '0205', '肾阴亏虚', 1, '', '眩晕、耳鸣常有发作，腰膝酸软，记忆力差，手足心热。舌红，苔薄黄，脉细数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a190d0f9-85c8-43af-8552-5853b06c9f1f', '4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', '020505', '0205', '肾阳亏虚', 1, '', '眩晕、耳鸣，听力下降，精神萎靡，腰痛背冷，面色恍白。舌淡胖，苔白，脉沉细。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c6b2a6bc-34a9-4496-8565-948f7e958e2b', '66522337-9b13-473d-a8c9-1c8ea4996f59', '020601', '0206', '风寒外袭', 1, '', '鼻塞遇寒加重，鼻涕清稀、量多。伴恶寒，发热，头痛，身痛。鼻粘膜充血轻，但肿胀较甚。舌苔薄白，豚浮紧。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '60a854fc-8024-448f-a396-7a36ee19b75f', '66522337-9b13-473d-a8c9-1c8ea4996f59', '020602', '0206', '风热外犯', 1, '', '鼻塞，流涕粘稠，色白或黄，发热，微恶寒，口微渴。鼻粘膜充血。舌质偏红，脉浮数。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1895eab4-da46-4c31-89f2-88e1ebd53b70', '2609d530-b1ed-47ae-ac12-3846c9a55e8e', '020701', '0207', '肺虚邪滞', 1, '', '鼻塞多为间歇性，时轻时重，鼻涕白粘、量多，遇寒加重。或伴气短乏力、大便溏薄等症。鼻粘膜肿胀，色淡红。舌苔薄白，脉细弱。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c7795194-18f2-4556-a8e8-27b4c4f98768', '2609d530-b1ed-47ae-ac12-3846c9a55e8e', '020702', '0207', '气滞血瘀', 1, '', '鼻塞多为持续性，鼻涕粘稠，不易擤出，嗅觉迟钝。伴头昏、耳鸣、记忆力减退等症。鼻粘膜充血，呈暗红或深红色。鼻甲肿大，表面不平滑，如桑椹样，触之较硬，缺乏弹性，对一般滴鼻剂收缩反应较差。舌质紫暗或有瘀点，脉涩。', '2008/04/09 03:52:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fea12fd0-08d7-4fc3-ab73-8464caa4bff2', '404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', '020801', '0208', '燥热外犯', 1, '', '鼻干无涕，鼻腔痂皮积聚，时作鼻衄。伴口渴喜饮，小便短黄，大便干燥。鼻腔粘膜干燥充血。舌红，苔黄燥，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4dd1aa50-cfb7-499b-87cb-e05a82305801', '404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', '020802', '0208', '肺阴亏虚', 1, '', '鼻中干燥，痂皮积聚，鼻塞失嗅，口干不欲多饮，或有午后潮热、盗汗、头晕、手足：心热。鼻甲萎缩，粘膜暗红。舌红，苔薄，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b695c489-110b-4165-a3cb-6437e7bda727', '404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', '020803', '0208', '肺脾气虚', 1, '', '鼻燥少涕，头晕，神疲乏力，语声低弱，食少，便溏。鼻粘膜干燥萎缩，色淡红。舌淡，苔白润，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a3edff0b-018f-434e-a6bb-3e7436f85d87', '594eda87-98a4-46f1-8e27-2f1ca1f3b86a', '020901', '0209', '肺虚感寒', 1, '', '常因感受风冷异气发病，恶风寒，面白，气短，咳嗽，咯痰色白。舌苔薄白，脉浮。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a778a1bd-b0e6-4492-9a15-3f3e262c5b4b', '594eda87-98a4-46f1-8e27-2f1ca1f3b86a', '020902', '0209', '脾气虚弱', 1, '', '鼻痒而喷嚏连作，清涕量多，四肢乏力，大便溏薄。鼻粘膜色淡红。舌淡，苔白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3356825e-c9a4-4bd0-84c5-ef7d6b0fb599', '594eda87-98a4-46f1-8e27-2f1ca1f3b86a', '020903', '0209', '肾阳亏虚', 1, '', '鼻痒，鼻塞，喷嚏较多，遇风冷则易发作。畏寒肢冷，小便清长，大便溏薄。鼻粘膜淡白，鼻甲水肿。舌淡，苔白，脉沉细。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '553c31cb-6104-4e3b-af4c-59575dffcd5f', '6c56539b-b855-43d5-8ed9-e6197828f2d0', '021001', '0210', '肺经风热', 1, '', '多见于发病初期，或慢性鼻渊因外感而急性发作。鼻塞，涕多色白或微黄，头痛，咳嗽，咯痰。鼻粘膜充血，鼻甲肿大。舌苔薄白，脉浮数。。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b30df52b-01d2-47b1-958f-99b1fb3754b4', '6c56539b-b855-43d5-8ed9-e6197828f2d0', '021002', '0210', '胆经郁热', 1, '', '多见于急性鼻渊，或慢性鼻渊急性发作。鼻塞、头痛较甚，涕多色黄而浊。身热，口渴，大便干燥。鼻粘膜充血明显，且肿胀，鼻腔内可见较多脓性分泌物。舌红，苔黄腻，脉弦数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6f2f180b-cd28-4b4d-b3c4-7101bb2d732b', '6c56539b-b855-43d5-8ed9-e6197828f2d0', '021003', '0210', '脾胃湿热', 1, '', '多见于急性鼻渊后期。鼻塞，流涕缠绵不愈。伴头昏，食欲不振，大便溏薄。鼻粘膜充血肿胀，鼻腔内有较多黄浊分泌物。舌苔黄腻，脉濡数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7c2adc11-d561-46a4-8f85-90dbc32c9e59', '6c56539b-b855-43d5-8ed9-e6197828f2d0', '021004', '0210', '肺脾气虚', 1, '', '多见于慢性鼻渊。鼻塞，头昏，记忆力减退，鼻涕混浊，时多时少。面色萎黄或白，少气乏力，大便溏薄。鼻腔粘膜不充血，但肿胀，并有粘性或脓性分泌物。舌淡，苔白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '51135a47-1749-438c-b22e-c5ec4c9d5c41', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021101', '0211', '肺经热盛', 1, '', '鼻衄点滴渗出，血色鲜红，伴鼻塞，咳嗽，或有发热。舌质偏红，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0f1bc748-3dbf-49f7-b5fa-336fe31c8e7e', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021102', '0211', '胃火炽盛', 1, '', '鼻中出血量多，血色深红。身热，口渴，便秘。鼻腔粘膜充血。舌红，苔黄，脉洪数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2cd30e08-7cf5-4bd4-8e90-5ea2db197042', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021103', '0211', '肝火上炎', 1, '', '鼻衄起于恼怒之后，头痛头晕，口苦咽干，胸胁苦满。舌红，苔黄，脉弦数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '46f5c0cb-a351-4179-9e5c-676fa8a2fa58', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021104', '0211', '阴虚火旺', 1, '', '鼻衄量少，口干咽燥，头晕眼花，手足心热。舌红，苔少，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8d0cb477-1f24-42c0-b88d-5900f41c1487', 'e79686d3-6a61-4142-9544-1ab5a7b35d51', '021105', '0211', '脾不统血', 1, '', '鼻衄量少，面白肢冷，大便溏薄，鼻粘膜色淡。舌淡，苔白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5642f1da-3196-4c82-bd67-312fc593a173', 'ac70791e-522b-4363-a46c-a5044568a13e', '021201', '0212', '风寒外袭', 1, '', '咽痛，口不渴，恶寒，不发热或微发热，咽粘膜水肿，不充血或轻度充血。舌质淡红，苔薄白，脉浮紧。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3cb52112-6b88-4334-84d0-66e81c5c4ee4', 'ac70791e-522b-4363-a46c-a5044568a13e', '021202', '0212', '风热外侵', 1, '', '咽痛而口微渴，发热，微恶寒，咽部轻度充血，水肿。舌边尖红，苔薄白，脉浮数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9f48bccd-c582-4cf5-be2f-04720cfec64e', 'ac70791e-522b-4363-a46c-a5044568a13e', '021203', '0212', '肺胃实热', 1, '', '咽痛较剧，口渴多饮，咳嗽，痰粘稠，发热，大便偏干，小便短黄。咽部充血较甚。舌红，苔黄，脉数有力。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9dbeab9f-d2e9-4b4d-8130-cd6eec6bd0ad', '523762f2-424f-4c00-87be-ef8e14d657f7', '021301', '0213', '阴虚肺燥', 1, '', '咽喉干疼、灼热，多言之后症状加重，呛咳无痰，频频求饮，而饮量不多，午后及黄昏时症状明显。咽部充血呈暗红色，粘膜干燥、或有萎缩，或有淋巴滤泡增生。舌红，苔薄，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e04f492b-cc50-4b92-845f-b8201918b11f', '523762f2-424f-4c00-87be-ef8e14d657f7', '021302', '0213', '肺脾气虚', 1, '', '咽喉干燥，但不欲饮，咳嗽，有痰易咯，平时畏寒，易感冒，神倦乏力，语声低微，大便溏薄。咽部充血较轻。舌苔白润，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5e02cb91-9c47-4f97-9469-aad1aaa4a4e6', '523762f2-424f-4c00-87be-ef8e14d657f7', '021303', '0213', '痰热蕴结', 1, '', '咽喉不适，因受凉、疲劳、多言之后症状较重。咳嗽、咯痰粘稠，口渴喜饮。咽粘膜充血呈深红色，肥厚，有黄白色分泌物附着。舌红，苔黄腻，脉滑数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1e3e2877-2ad7-446a-be6c-e5a54ed80bf6', '8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', '021401', '0214', '风热外侵', 1, '', '急乳蛾初起，咽痛，轻度吞咽困难。伴发热、恶寒、咳嗽、咯痰等症。咽粘膜及扁桃体充血，未成脓。舌苔薄白，脉浮数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0908c9c4-94cf-4807-bec3-ee1b5fd7cf83', '8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', '021402', '0214', '胃火炽盛', 1, '', '咽痛较甚，吞咽困难。身热，口渴，大便秘结。咽部及扁桃体充血红肿，上有脓点或小脓肿。舌红，苔黄，脉滑数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd10ec532-38b1-44b8-b03b-ab39af4726b7', '8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', '021403', '0214', '肺肾阴虚', 1, '', '咽部干燥、灼热，微痛不适。干咳少痰，手足心热，精神疲乏，或午后低热，颧赤。扁桃体暗红、肿大，或有少许脓液附于表面。舌红，苔薄，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a3946062-2d42-4cf3-a270-50c94d27e1a3', '8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', '021404', '0214', '脾气虚弱', 1, '', '咽部不适，微痒或干燥，或有异物感，咯痰色白，面色少华，声音低怯，神疲乏力，食少，便溏。扁桃体肿大，充血较轻或不充血，挤压时有少许脓液。舌质淡胖，苔白润，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '85131675-fb94-455e-b835-1f7b6f79fe6d', 'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', '021501', '0215', '胃火炽盛', 1, '', '咽痛，吞咽困难，发热。扁桃体旁充血，隆起，悬雍垂被推向对侧。舌红，苔黄，脉数有力。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9ed9540f-1ccf-43f3-942b-0d1149141568', 'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', '021502', '0215', '阳明腑实', 1, '', '咽痛剧烈，汤水难下，口涎外流，语言含混，发热，口渴，小便短黄，腹胀便秘，咽部充血，扁桃体周围隆起明显，触之软。舌红，苔黄而厚，脉数有力。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '746bbf56-29b1-406d-9d23-e13a1c5a8cde', 'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', '021503', '0215', '阴虚邪恋', 1, '', '脓液排出，发热渐退，咽痛渐减，口渴，尿黄，便结。舌红，苔薄少，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '771a3453-fbd1-4eb1-bc48-d9c9b3ca3ca2', '51c6ef80-f206-46bb-bd37-d53242775eda', '021601', '0216', '风寒袭肺', 1, '', '声音嘶哑，发音低沉，咽喉胀紧，鼻塞、流清涕，咳嗽，咯痰清稀。声带肿胀而不、充血、舌苔薄白，脉浮紧。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '68c4fa94-eca2-45cd-a4f0-a702edd9e537', '51c6ef80-f206-46bb-bd37-d53242775eda', '021602', '0216', '风热犯肺', 1, '', '声音粗糙，嘶哑，咽喉干燥，疼痛，咳嗽，咯痰粘白或微黄，咽喉粘膜充血，肿胀。舌边尖红，苔薄白，脉浮数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1c4e8b98-2978-4023-a22c-469529173bae', '51c6ef80-f206-46bb-bd37-d53242775eda', '021603', '0216', '肺热壅盛', 1, '', '声嘶，咽痛，口渴，咳嗽，咯痰色黄，身热，便秘。咽喉粘膜充血深红、肿胀，有黄白色分泌物粘附于表面。舌红，苔黄，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '37f2d7ea-cfd6-4746-ba17-2021fbde586d', '3b4be459-6d84-42c2-83ae-4303aa5ac1c4', '021701', '0217', '肺肾阴虚', 1, '', '声嘶日久，咽喉干燥、锨热微痛，口干，干咳无痰，或痰少而粘。声带微红。舌红，少苔，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '664ac158-d400-4481-a53a-af052331851a', '3b4be459-6d84-42c2-83ae-4303aa5ac1c4', '021702', '0217', '肺脾气虚', 1, '', '语声低沉，气短懒言，咳嗽咯痰，色白略稀，体倦乏力，纳少便溏。声带肿而不红，声门关闭不密。舌淡，苔白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '773b3a64-9e4d-41db-8ccd-259aa29ac950', '3b4be459-6d84-42c2-83ae-4303aa5ac1c4', '021703', '0217', '气滞血瘀', 1, '', '声音嘶哑，咳嗽痰少，多言后喉中觉痛，痛处不移，胸胁胀闷。声带暗红、增厚，或有声带小结、声带息肉，或室带肥厚、超越。舌质紫暗或有瘀点，脉涩。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '916345db-4d0e-4f86-8b2d-cb7460dd5ce8', '3b4be459-6d84-42c2-83ae-4303aa5ac1c4', '021704', '0217', '痰浊凝聚', 1, '', '声音粗浊，喉中痰多，痰白而粘，声带水肿，或有声带小结、声带息肉，色灰白。舌苔白腻，脉滑。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '46f30086-45f1-41b7-98e3-7dd85c3bb25e', '01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', '021801', '0218', '痰气互结', 1, '', '咽中如有炙脔或其他异物感，咽之不下、吐之不出。时作嗳气、呃逆、恶心、泛泛欲吐，胸脘胀满。舌苔白腻，脉弦滑。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ef1e2b4e-bf0c-475d-997c-6f1cf6e5896d', '01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', '021802', '0218', '肝郁气滞', 1, '', '咽中梗阻感，嗳气频频，或作呃逆，胁下胀闷，嗳气后稍舒。舌苔薄白，脉弦。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '43b7f4f3-607a-425d-85cd-f755e96b9e27', '01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', '021803', '0218', '心脾气虚', 1, '', '咽中异物感，不思饮食，口中无味，面白神疲，少气懒言，或时时悲伤欲哭，夜寐不实，易惊醒或惶恐不安，小便清长，大便溏薄。舌淡，苔白，脉弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6a6f27b8-0e73-4884-b502-feff01329e07', 'b8d2a791-9d7c-4747-a4f1-fc04cb6259b0', '021901', '0219', '胃火炽盛', 1, '', '牙龈作痛、出血，口气热臭，渴喜冷饮，大便干结。牙龈红肿疼痛，溢出脓血。舌红，苔黄，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ce153d3d-585d-401b-bc37-de368a5324dc', 'b8d2a791-9d7c-4747-a4f1-fc04cb6259b0', '021902', '0219', '肾阴亏虚', 1, '', '牙龈萎缩，牙根松动，牙龈粘膜微红肿。或有头晕，耳鸣，腰膝酸软。舌红少津，苔薄，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '912e4bb3-4967-4ade-ad04-81796ee0a8ad', 'b8d2a791-9d7c-4747-a4f1-fc04cb6259b0', '021903', '0219', '气血亏虚', 1, '', '牙龈萎缩，颜色淡白，牙根宣露，牙齿松动，咀嚼无力，牙龈时有渗血。面白或萎黄，倦怠乏力。舌淡，苔白，脉弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7b67c3d7-1e16-4cc0-966e-cdf350fdb41c', '14a55090-ad4c-422c-ba4f-99e111379560', '022001', '0220', '心脾积热', 1, '', '口内疼痛，口渴，口臭，尿短黄，便秘。口疮数量多，周围充血明显。舌红，苔黄，脉数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a1ac7790-12de-4eb2-8714-4e537b3c06da', '14a55090-ad4c-422c-ba4f-99e111379560', '022002', '0220', '阴虚火旺', 1, '', '口内疼痛，口干，手足心热，乏力。口疮1～2个或2～3个，周围轻微充血。舌红，苔少，脉细数。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '31e32bcd-9f67-4e1c-824a-b4818f248984', '14a55090-ad4c-422c-ba4f-99e111379560', '022003', '0220', '气血亏虚', 1, '', '口不渴，或伴畏寒，便溏。口疮数量不多，周围粘膜不充血。舌淡，苔薄白，脉细弱。', '2008/04/09 03:52:18', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fd6baacc-3e22-4df8-851d-dcb59ffc5edc', '91884d9e-aaa2-4434-98e7-21ff5faaf66e', '080101', '0801', '热毒蕴结', 1, '', '常见于气实火盛的患者。轻者疖肿只有一、二个，多者可散发全身，或簇集一处，或此愈彼起。可有发热，口渴，溲赤，便秘。苔黄，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2737eb99-71a9-4460-b4a0-0fd03c222d77', '91884d9e-aaa2-4434-98e7-21ff5faaf66e', '080102', '0801', '暑热浸淫', 1, '', '发于夏秋季节，以儿童及产妇多见。可有发热，口渴，便秘，溲赤等。苔薄腻，脉滑数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3f6174a6-d6be-49ea-9a1c-47619f374c91', '91884d9e-aaa2-4434-98e7-21ff5faaf66e', '080103', '0801', '体虚毒恋', 1, '', '疖肿常此愈彼起，不断发生。常见于体质虚弱或有某些慢性病患者，并有相应的全身证候。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '692f7521-8836-4f4a-b1a5-0aee64b7fd06', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080201', '0802', '风热痰毒', 1, '', '肿块发生在颌颈部，全身恶寒发热，头痛，口干，咽痛。苔薄白或薄黄，脉浮数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f5297a9c-e8bb-45d6-8b57-0357e0e113cc', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080202', '0802', '肝郁痰火', 1, '', '肿块发生在腋下，全身发热，头痛，胸胁牵痛，口苦咽干。舌红，苔黄，脉弦数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '16014b05-c26a-44d9-b9ed-9ccb11f6138a', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080203', '0802', '湿热蕴结', 1, '', '肿块发生在腹股沟部，全身发热，患肢拘急，小便黄热。苔黄腻，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '29b6bf0b-b9f4-4b81-a46c-9b547d5147d6', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080204', '0802', '热胜酿脓', 1, '', '局部皮肤红肿发亮，{热疼痛，肿块变软，有应指感。全身发热，口干。舌红，苔黄，脉滑数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '256284b2-1bf4-466b-859f-e2faabe43bd6', '47002e12-49d8-4984-9697-bf81a3d4e3de', '080205', '0802', '余毒凝滞', 1, '', '全身症状消退，局部红肿热痛亦轻，唯肿块僵硬不消。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '16e6219c-237b-4f33-acaa-ffede4b0e775', 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '080301', '0803', '火毒凝结', 1, '', '局部红肿高突，灼热疼痛，根脚收束，脓液稠黄，能迅速化脓脱腐。全身发热，口渴，尿赤。苔黄，脉数有力。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '275070eb-404f-4b36-b977-e2b6ff284944', 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '080302', '0803', '湿热壅滞', 1, '', '局部症状与火毒凝结证相同。全身壮热，朝轻暮重，胸闷呕恶。苔白腻或黄腻，脉濡数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c9d7b5f4-8b86-4013-9b52-6794ecb9e54f', 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '080303', '0803', '阴虚火炽', 1, '', '肿势平塌，根脚散漫，皮色紫滞，疼痛剧烈，脓腐难化，脓水稀少或带血水。全身发热烦躁，口渴多饮，大便燥结，小便短赤。舌红，苔黄燥，脉细弦数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e8cc4153-c8b7-4a57-9915-4a3f0d7cf73a', 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '080304', '0803', '气虚毒滞', 1, '', '肿势平塌，根脚散漫，皮色灰暗不泽，胀重木痛，腐肉不化，脓液稀少，易成空腔。全身畏寒高热或身热不扬，小便频数，口渴喜热饮，精神萎靡，面色少华。舌质淡红，苔白或微黄，脉数无力。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '16c5b334-84aa-4f35-b5dd-6dd415ea8c72', '46759b5b-96b9-4a30-9ecb-b0f982f0ee5c', '080401', '0804', '热毒凝结', 1, '', '红肿高突，根脚收束，发热头痛。舌红，苔黄，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '29a75640-5f57-4130-b42f-53bf171c6e81', '46759b5b-96b9-4a30-9ecb-b0f982f0ee5c', '080402', '0804', '火毒炽盛', 1, '', '疮形平塌，肿势散漫，皮色紫暗，{热疼痛。有高热，头痛，烦渴，呕恶，搜赤，便秘。舌红，苔黄腻，脉洪数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6dfdc658-2c53-476f-b7f2-1ef21db256b7', 'f21ec997-aa76-4744-8d90-97c362d003af', '080501', '0805', '火毒凝结', 1, '', '局部红肿疼痛，全身有畏寒发热。舌红，苔黄，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2cd5fd43-936d-4230-9655-4ec1dfc52615', 'f21ec997-aa76-4744-8d90-97c362d003af', '080502', '0805', '热胜肉腐', 1, '', '红肿明显，疼痛剧烈，肉腐为脓，溃后脓出肿痛消退；如溃后肿痛不退，脓液不断，可能是筋骨腐蚀。舌红，苔黄，脉数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2d997c33-a43e-4bbf-8479-47c8f7dbcc7e', '81fdb486-7c56-426d-b90a-6ac673829446', '080601', '0806', '火毒入络', 1, '', '患肢红丝较细，全身症状较轻。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '13e207b0-2574-401f-b221-9f41a4ef1bdd', '81fdb486-7c56-426d-b90a-6ac673829446', '080602', '0806', '火毒入营', 1, '', '患肢红丝粗肿明显，迅速向近端蔓延。全身寒战高热，烦躁，头痛，口渴。苔黄腻，脉洪数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3bcff740-e447-4757-9093-aa6e5ed2e4b5', 'c1b52342-59a6-4171-ba7e-2790fa286794', '080701', '0807', '湿火炽盛', 1, '', '初起患肢有沉重和紧束感，以后逐渐出现胀裂样疼痛，创口周围皮肤呈红色、肿胀发亮，按之陷下，迅速蔓延成片。1～2日后肿胀剧烈，可出现水疱，皮肉腐烂，高热持续。舌质红，苔薄白或黄，脉弦数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd27c5f89-b805-49bd-a5ce-e1654ed40d2b', 'c1b52342-59a6-4171-ba7e-2790fa286794', '080702', '0807', '毒入营血', 1, '', '壮热头痛，神昏谵语，气促，烦躁不安，呃逆呕吐。局部胀痛，伤口周围高度水肿发亮，迅速成暗紫色，有血疱，肌肉腐烂，脓液稀薄，混有气泡溢出，恶臭。舌质红绛，苔薄黄，脉洪滑数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fb90e016-7da4-4bdf-b61f-8b6be6a2ce4f', '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '080801', '0808', '热毒蕴结', 1, '', '颐颌之间结块疼痛，张口不利，继则肿痛渐增，检视腮腺管口常现红肿，压迫局部有粘稠的分泌物溢出，身热。苔薄腻，脉浮数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e22d6b2d-8b79-49f8-b924-d65085b24fa6', '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '080802', '0808', '毒盛酿脓', 1, '', '颐颌间结肿疼痛日增，甚至肿势延及面颊和颈项，掀红灼热，张口困难，继之酿脓应指，腮腺管口能挤出脓性分泌物，高热口渴。苔黄腻，脉弦数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '62a61987-4e63-4b9b-9c15-639463886472', '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '080803', '0808', '热毒内陷', 1, '', '颐颌间肿块多平塌散漫，肿势可延及脸面和颈项，{红灼热，疼痛剧烈，汤水难咽，壮热口渴，痰涌气粗，烦躁不安，甚至神昏谵语。舌质红绛，苔少而干，脉细数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3afc7c7e-00d5-4cb2-a0cd-89b87636f90b', '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '080804', '0808', '余毒未清', 1, '', '病程日久，经常反复发作，发作时颐颌部肿痛，腮腺管口能挤出脓性分泌物，每在进食时颐颌部感到胀痛，口内常有恶臭。苔薄黄或腻，脉滑。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '04dbdd4d-2b45-47e9-8d49-8cfcd05726da', 'd1defda5-27db-4535-b374-dd10662aadda', '080901', '0809', '风热毒蕴', 1, '', '发于头面部，恶寒发热，皮肤掀红灼热，肿胀疼痛，甚则发生水疮，眼胞肿胀难睁。舌质红，苔薄黄，脉浮数。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f3c5cb19-f8ac-400d-81a8-81adc0b0f50e', 'd1defda5-27db-4535-b374-dd10662aadda', '080902', '0809', '湿热毒蕴', 1, '', '发于下肢。除发热等症状外，局部以红赤肿胀，灼热疼痛为主，亦可发生水疱、紫斑，甚至结毒化脓或皮肤坏死。苔黄腻，脉洪数。反复发作，可形成大脚疯（象皮腿）。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '40d4b86c-22e5-4df2-9356-b0ead056aae1', 'd1defda5-27db-4535-b374-dd10662aadda', '080903', '0809', '胎火蕴毒', 1, '', '发于新生儿。多见于臀部，局部红肿灼热，可呈游走性，并有壮热烦躁。', '2008/04/10 08:17:13', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'db651ace-a495-438f-855c-d67546e808c9', 'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', '081001', '0810', '余毒攻窜', 1, '', '发病前有疔疮、痈、疖等病史。全身伴有壮热，口渴，甚则神昏谵语。苔黄，脉洪数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd83c8974-591b-453a-9819-79318e9fcb5b', 'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', '081002', '0810', '暑湿交阻', 1, '', '多发于夏秋之间。初起恶寒发热，头胀，胸闷呕恶，周身骨节酸痛，胸部可布白pei(陪）。苔白腻，脉滑数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f0e6ecf5-c104-4251-868e-637cc719c0e6', 'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', '081003', '0810', '瘀血凝滞', 1, '', '劳伤筋脉诱发者，多发于四肢内侧。跌打损伤诱发者，多发于伤处。局部漫肿疼痛，皮色微红，或呈青紫，溃后脓液中央有瘀血块。妇女产后恶露停滞而成者，多发于小腹及大腿等处。发病较缓，初起一般无全身症状或全身症状较轻，化脓时出现高热。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0e67efd2-0d3c-45f8-a8c0-647ae96a23fc', '4f952459-5dc6-48bc-9b12-dec0d53c5295', '081101', '0811', '风热痰凝', 1, '', '局部结块疼痛明显，伴恶寒发热，头痛，口渴，咽干。苔薄黄，脉浮数或滑数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '59cb16c5-f1ea-437b-bd4e-85301fc986f2', '4f952459-5dc6-48bc-9b12-dec0d53c5295', '081102', '0811', '气滞痰凝', 1, '', '肿块坚实，轻度作胀，重按才感疼痛，其痛常反射至后枕部，或有喉间梗塞感，痰多，一般无全身症状。苔薄腻，脉弦滑。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9db89fe6-a526-4f08-b212-1e0f10a95a93', '130c5f08-714f-4cb5-96cf-f52fdb21daa9', '081201', '0812', '气滞痰凝', 1, '', '颈部肿块，不红，不热，不痛，随吞咽上下移动，可有呼吸不畅或吞咽不利。一般无明显全身症状。苔薄腻，脉弦滑。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0bfc6422-76bf-40f6-87fa-b9d0f529514e', '130c5f08-714f-4cb5-96cf-f52fdb21daa9', '081202', '0812', '气阴两虚', 1, '', '局部症状同上。性情急躁，易怒，怕热，易汗，口苦，心悸，失眠，多梦，手颤，善食，消瘦，月经不调。舌红，苔薄，脉弦。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c2e9dd14-34a9-4a91-9e82-68dc9a7f25a0', '3f80a5bc-36cd-43d7-a1dc-7780dbec4fd1', '081301', '0813', '气滞痰凝', 1, '', '多见于瘰疬初期，肿块坚实，无明显全身症状。苔薄腻，脉弦滑。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8f7e3840-bdef-4489-a206-d8abe15e9c6f', '3f80a5bc-36cd-43d7-a1dc-7780dbec4fd1', '081302', '0813', '阴虚火旺', 1, '', '核块逐渐增大，与皮肤粘连，皮色转暗红。午后潮热，夜间盗汗。舌红，少苔，脉细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ebf76a8f-3783-42d6-bd3d-7ca7a6aee965', '3f80a5bc-36cd-43d7-a1dc-7780dbec4fd1', '081303', '0813', '气血两虚', 1, '', '疮口脓出清稀，夹有败絮样物，形体消瘦，精神倦怠，面色无华。舌淡质嫩，苔薄，脉细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a1ad8226-c939-4edb-b8b9-eb7e31c0929c', 'ffdd213d-59bf-4677-9b95-9fc12354b47b', '081401', '0814', '气滞热壅', 1, '', '乳汁淤积结块，皮色不变或微红，肿胀疼痛。伴有恶寒发热，头痛，周身酸楚，口渴，便秘。苔黄，脉数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b1d5cebf-ba68-401c-afdb-aa13f5828172', 'ffdd213d-59bf-4677-9b95-9fc12354b47b', '081402', '0814', '热毒炽盛', 1, '', '壮热，乳房肿痛，皮肤掀红灼热，肿块变软，有应指感。或切开排脓后引流不畅，红肿热痛不消，有“传囊”现象。舌质红，苔黄腻，脉洪数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd676a101-1b99-4daa-9ae8-8daf8b876315', 'ffdd213d-59bf-4677-9b95-9fc12354b47b', '081403', '0814', '正虚毒恋', 1, '', '溃脓后乳房肿痛虽轻，但疮口脓水不断，脓汁清稀，愈合缓慢或形成乳漏。全身乏力，面色少华，或低热不退，饮食减少。舌质淡，苔薄，脉弱无力。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '59af24d1-b9d9-429b-acf8-c966ba0fca13', '2f7c7b5f-7e7d-46fb-a677-a1d8787901fb', '081501', '0815', '气滞痰凝', 1, '', '多见于初起阶段。肿块不红、不热、不痛，伴心情不畅，胸闷胁胀。苔薄腻，脉弦滑。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd056f2c3-9d4a-4d25-8fba-b0f866654961', '2f7c7b5f-7e7d-46fb-a677-a1d8787901fb', '081502', '0815', '阴虚痰热', 1, '', '多见于化脓或溃后阶段，皮色暗红，肿块变软，溃后脓出稀薄，有败絮状物。伴潮热盗汗，神疲乏力，颧红，形瘦，食欲减退。舌质红，苔黄，脉细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0e2c817b-d199-4239-b697-e6efd9e34bbe', '2fcc4ba9-58f7-46f3-a976-95763a11c476', '081601', '0816', '肝气郁结', 1, '', '性情急躁，遇事易怒，乳房肿块胀痛，触痛明显，胸胁牵痛。舌质红，苔白，脉弦。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7aae383f-fc73-46c3-bd64-cfa967ad2c7e', '2fcc4ba9-58f7-46f3-a976-95763a11c476', '081602', '0816', '肾气亏虚', 1, '', '多见于中老年。轻者多无全身症状。重者，偏于肾阳虚，面色淡白，腰腿酸软，容易倦怠，舌质淡，苔白，脉沉弱；偏于肾阴虚，头目眩晕，五心烦热，眠少梦多，舌红，苔少，脉弦细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3ed34bb8-eef7-4b15-9974-cdf595e80f99', 'df820837-1a76-4fa2-b83c-75ad42d9f24b', '010201', '0102', '血寒凝滞', 1, '', '月经周期延后，量少，色黯有血块，小腹冷痛，得热减轻，畏寒肢冷。苔白，脉沉紧。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '10224a6a-288a-40f1-8227-64bbbf971fbb', 'df820837-1a76-4fa2-b83c-75ad42d9f24b', '010202', '0102', '肝血亏虚', 1, '', '月经周期延后，量少，色淡无块，小腹隐涌，头晕眼花，心悸少寐，面色苍白或萎黄。舌质淡红，脉细弱。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4b89d4a3-6926-49a2-89ec-33edaf64d6c1', 'df820837-1a76-4fa2-b83c-75ad42d9f24b', '010203', '0102', '肝气郁滞', 1, '', '月经周期延后，量少，色黯红或有小血块，小腹胀痛或胸腹、两胁、乳房胀痛。舌苔正常，脉弦。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3e656687-fc18-420d-9016-39949b9f7678', 'fc65c84a-0d1e-4f50-945f-3c967f306498', '010301', '0103', '肝气郁滞', 1, '', '月经周期不定，经量或多或少，色紫红有块，经行不畅，胸胁、乳房以及小腹胀痛，脘闷不舒，时叹息。苔薄白或薄黄，脉弦。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1dd2c262-b451-4edb-935f-c560df38e0a9', 'fc65c84a-0d1e-4f50-945f-3c967f306498', '010302', '0103', '肾气不足', 1, '', '月经周期不定，量少，色淡黯，质稀，神疲乏力，腰骶酸痛，头晕耳鸣。舌淡苔少，脉细尺弱。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '48be94cc-6639-404d-a0a2-c577ae17d0f3', '7f5d1b84-f2c9-4ae7-97f4-69f9f1a8a58f', '010401', '0104', '气不摄血', 1, '', '经来量多，色淡红，质清稀。或面色苍白，气短懒言，肢软无力，或小腹空坠。舌淡，脉细。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '02bcde44-47c1-4a0f-9fdf-4ce90cf84ad7', '7f5d1b84-f2c9-4ae7-97f4-69f9f1a8a58f', '010402', '0104', '血热内扰', 1, '', '经来量多，色鲜红或深红，质稠粘，或有小血块。常伴心烦口渴，尿黄便秘。舌质红，苔黄，脉滑数。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c07bc77c-f314-41cc-a144-bdacd09b53fc', '7f5d1b84-f2c9-4ae7-97f4-69f9f1a8a58f', '010403', '0104', '瘀滞胞宫', 1, '', '经来量多，或多时不净，色紫黑，有血块或伴小腹疼痛拒按。舌质紫暗或有瘀点,脉细涩。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '495b1e2f-7148-4803-a934-ec9713bc9adc', '6e99def8-813f-4fc1-bce7-6afb2f9b4884', '010501', '0105', '肝血亏虚', 1, '', '月经量少或点滴即净，色淡无块，或伴头晕眼花，心悸怔忡，面色萎黄,小腹空坠，舌质淡红，脉细。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'daa38ed3-6a27-48e6-b0d0-9174493296fa', '6e99def8-813f-4fc1-bce7-6afb2f9b4884', '010502', '0105', '肾阳亏虚', 1, '', '月经量少，色淡红或黯红，质稀，腰脊酸软，头晕耳鸣，或小腹冷，夜尿多。舌质淡，脉弱或沉迟。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7fa8c358-cd54-43e3-aab1-2f9ed8439e2c', '6e99def8-813f-4fc1-bce7-6afb2f9b4884', '010503', '0105', '瘀滞胞宫', 1, '', '月经量少，色紫黑，有血块，小腹胀痛，拒按，血块排出后胀痛减轻。舌正常或紫黯，或有瘀点，脉细弦涩。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9d81feae-8f55-435a-886f-4a1dacdc1777', '6e99def8-813f-4fc1-bce7-6afb2f9b4884', '010504', '0105', '痰湿阻滞', 1, '', '月经量少，色淡红，质粘腻，形体肥胖，胸闷呕恶，或带多粘稠。苔白腻，脉滑。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5d5ffbdd-050c-4333-b72e-82d9cd89bdac', 'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', '010601', '0106', '瘀滞胞宫', 1, '', '经行淋漓8－9日或10余日始净，量少，色黯有块，小腹疼痛拒按。舌质紫黯或有瘀点，脉弦涩。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3d7e2552-c6a1-4323-8bab-17e3766cc89f', 'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', '010602', '0106', '阴虚血热', 1, '', '月经持续8－10天，量少色红质稠，咽干口燥，或有颧红，潮热，或见手心灼热。舌质红少津，苔少或无苔，脉细数。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0e1b8032-c29d-4b50-b63f-2719bd08c281', 'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', '010603', '0106', '气不摄血', 1, '', '月经持续8－l0天始净，量少，色淡，质清稀。伴神疲乏力，或头晕眼花，心悸少寐，或纳少便溏。舌质淡，苔薄白，脉弱或虚细。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '68ca8f5d-5bca-402d-a369-b0975f3a2a6d', 'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', '010604', '0106', '湿热下注', 1, '', '经行8～10天始净，量少，色黯如酱，混杂粘液，气味秽臭，腰腹胀痛。平素带多色黄、有臭味。舌正常或偏红，苔黄腻，脉濡数。', '2008/04/09 02:50:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '06263374-b11e-4984-a540-e1c919584a0e', 'be06429d-94ad-41c8-83a8-45e1571bcf93', '010701', '0107', '气血瘀滞', 1, '', '经前或经期小腹胀痛拒按，或伴乳胁胀痛。经行量少不畅，色紫黑有块，块下痛减。舌质紫黯或有瘀点，脉沉弦或涩。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f5c5f4bd-6270-4914-a199-948d222245c0', 'be06429d-94ad-41c8-83a8-45e1571bcf93', '010702', '0107', '寒湿凝滞', 1, '', '经行小腹冷痛，得热则舒，经量少，色紫黯有块。伴形寒肢冷，小便清长。苔白，脉细或沉紧。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '67e04691-ba6b-4c94-8b33-0d15168d70f0', 'be06429d-94ad-41c8-83a8-45e1571bcf93', '010703', '0107', '肝郁湿热', 1, '', '经前或经期小腹疼痛，或痛及腰骶，或感腹内灼热。经行量多质稠，色鲜或紫，有小血块。时伴乳胁胀痛，大便干结，小便短赤，平素带下黄稠。舌质红，苔黄腻，脉弦数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fbb60c3f-7674-493d-a90b-bceca7ef4c7c', 'be06429d-94ad-41c8-83a8-45e1571bcf93', '010704', '0107', '气血亏虚', 1, '', '经期或经后小腹隐痛喜按，经行量少质稀。形寒肢疲，头晕目花，心悸气短.舌质淡，苔薄，脉细弦。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd35a7c8a-94fa-4bdb-b0d5-ff8b5163189e', 'be06429d-94ad-41c8-83a8-45e1571bcf93', '010705', '0107', '肝肾亏损', 1, '', '经期或经后小腹绵绵作痛，经行量少，色红无块。腰膝酸软，头晕耳鸣。舌淡红，苔薄，脉细弦。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '972eb5ef-74e2-4b6a-9511-2cf0bc9d57ca', 'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', '010801', '0108', '肾气不足', 1, '', '年逾18周岁，月经未至或来潮后复闭。素体虚弱，头晕耳鸣，第二性征不足，腰腿酸软，腹无胀痛，小便频数。舌淡红，脉沉细。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fd9a6748-b1d2-4356-8092-128891101455', 'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', '010802', '0108', '气血亏虚', 1, '', '月经周期后延，经量偏少，继而闭经。面色不荣，头晕目眩，心悸气短，神疲乏力。舌淡边有齿印，苔薄，脉细无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f3a4db26-301e-4b3e-9bd2-480c0176388a', 'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', '010803', '0108', '痰湿阻滞', 1, '', '月经停闭，形体肥胖，神疲嗜睡，头晕目眩，胸闷泛恶多痰，带下量多。苔白腻，脉濡或滑。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '54fa28da-19be-4de6-95a7-f7a27829e18d', 'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', '010804', '0108', '阴虚内热', 1, '', '月经先多后少，渐致闭经。五心烦热，颧红升火，潮热盗汗，口干舌燥。舌质红或有裂纹，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3c691396-b8eb-43c3-9848-9e71f1f66a5e', 'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', '010805', '0108', '血寒凝滞', 1, '', '经闭不行，小腹冷痛，得热痛减，四肢欠温，大便不实。苔白，脉沉紧。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7d7692ed-c7d9-43a4-ac02-5f51dd41048a', 'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', '010806', '0108', '血瘀气滞', 1, '', '月经闭止，胸胁胀满，小腹胀痛，精神抑郁。舌质紫黯，边有瘀点，苔薄，脉沉涩或沉弦。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c423d247-f753-490f-a7c3-9ce182fdef38', '764def47-5f2b-4be8-bbb8-df84901861e2', '010901', '0109', '血热内扰', 1, '', '经血量多，或淋漓不净，色深红或紫红，质粘稠，夹有少量血块。面赤头晕，烦躁易怒，口干喜饮，便秘尿赤。舌质红，苔黄，脉弦数或滑数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '52e0d567-8547-4074-8a1c-2485bcc4fc98', '764def47-5f2b-4be8-bbb8-df84901861e2', '010902', '0109', '气不摄血', 1, '', '经血量多，或淋漓不净，色淡质稀。神疲懒言，面色萎黄，动则气促，头晕心悸，纳呆便溏。舌质淡胖或边有齿印，舌苔薄润，脉芤或细无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '816bf6fb-1948-4d29-9d44-28f5db6b0c96', '764def47-5f2b-4be8-bbb8-df84901861e2', '010903', '0109', '肾阳亏虚', 1, '', '经血量多，或淋漓不净，色淡质稀。精神不振，面色晦暗，肢冷畏寒，腰膝酸软，小便清长。舌质淡，苔薄润，脉沉细无力，尺部尤弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4e110aef-79d4-439c-8b19-015d4eedd52f', '764def47-5f2b-4be8-bbb8-df84901861e2', '010904', '0109', '肾阴亏虚', 1, '', '经血时多时少，色鲜红。头晕耳鸣，五心烦热，夜寐不安。舌质红或有裂纹，苔少或无苔，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '84ec306e-692b-4eb6-9ce5-826740814c33', '764def47-5f2b-4be8-bbb8-df84901861e2', '010905', '0109', '瘀滞胞宫', 1, '', '经漏淋漓不绝，或骤然暴下，色暗或黑，夹有瘀块，小腹疼痛，块下痛减。舌质紫暗或边有瘀斑，脉沉涩或弦紧。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '87b0a196-3834-4971-b020-d79ef326b066', 'c857d734-2de2-434a-97db-7b92a7161419', '011001', '0110', '肝气郁滞', 1, '', '经前或行经时，乳房胀硬作痛，或乳头胀痛作痒，郁怒不欢。苔薄白，脉弦。痛', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ab8489cd-2291-4465-971b-e070a2f0fcae', 'c857d734-2de2-434a-97db-7b92a7161419', '011002', '0110', '肝郁肾虚', 1, '', '经前或经行时乳胀，触之无块，经后消失。腰酸耳鸣，五心烦热。舌红少苔，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '10159807-bc5d-45d8-b226-71c94c548b89', 'b8edee1f-1278-43d4-8bd0-555fcddd4c90', '011101', '0111', '阴虚内热', 1, '', '经行前后见有发热，午后潮热，烦躁少寐，口于便艰。舌红而于，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bbefff27-74f6-4231-9051-34e0e9a919fb', 'b8edee1f-1278-43d4-8bd0-555fcddd4c90', '011102', '0111', '气血亏虚', 1, '', '经期或经后发热，经行量多色淡，神疲畏寒，气短懒言，面目虚浮。舌淡嫩，苔白润，脉虚或细略数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '867d5d5b-37a5-4096-9e75-f1fc3da55c9d', 'b8edee1f-1278-43d4-8bd0-555fcddd4c90', '011103', '0111', '营卫不和', 1, '', '经行发热，经血量少不畅，小腹作胀，恶风自汗。舌质淡红，苔薄白，脉浮缓。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5fc03d7c-6241-478e-8875-acdb5e081bce', 'b8edee1f-1278-43d4-8bd0-555fcddd4c90', '011104', '0111', '瘀热内郁', 1, '', '经前或经期发热，经色紫黯，间有血块，腹痛。舌黯或边尖有瘀点，脉沉弦数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ffda6580-5368-4323-a22b-376e6a33f22a', '0f0e36fd-6418-401b-bddb-469f1650c519', '011201', '0112', '气血瘀滞', 1, '', '经前或经期头痛剧烈，痛如锥刺。经血量少，行而不畅，色紫有块，或伴小腹疼痛，拒按。舌暗边有瘀点，脉细涩或弦涩。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a9006bd5-d543-4df3-8b03-0f4c88313b1b', '0f0e36fd-6418-401b-bddb-469f1650c519', '011202', '0112', '肝血亏虚', 1, '', '经期', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '646a2490-16f1-4c5c-9282-6f34fe3abbc2', '0f0e36fd-6418-401b-bddb-469f1650c519', '011203', '0112', '肝火旺盛', 1, '', '经前头痛目胀，甚或巅顶掣痛。烦躁易怒，口苦咽干，经行量多色鲜。舌质红，苔薄白，脉弦带数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '42c57d87-d704-416b-b6a7-51c2519d11be', 'a880fa7f-14ba-4f8e-8af2-6d21efa350d9', '011301', '0113', '气血亏虚', 1, '', '经期或行经前后，肢体酸痛麻木，神疲乏力，经行量少色淡。舌质淡，苔薄，脉细。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0d2863d6-a7d9-4a34-be85-0f8a0ed751b1', 'a880fa7f-14ba-4f8e-8af2-6d21efa350d9', '011302', '0113', '寒湿凝滞', 1, '', '经期或行经前后，关节疼痛酸楚重着，腰膝尤甚，得热则舒。月经后期，量少不畅，色暗有块。苔薄白，脉沉紧。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'af0c040c-a8ab-464a-9ebb-d8f88eadec40', '10434b3e-4787-48ad-8882-e43036a625d6', '011401', '0114', '肝郁脾虚', 1, '', '经行期间大便溏泻。神疲肢重，面色不华，纳差口淡，或伴腹胀隐痛。舌质淡，边有齿印，苔薄，脉濡。若兼肝旺者，则有小腹胀痛，胸胁痞闷等，脉兼弦象。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3998a702-1779-46ef-b404-e26ef4e27407', '10434b3e-4787-48ad-8882-e43036a625d6', '011402', '0114', '肾阳亏虚', 1, '', '经行期间大便泄泻或完谷不化，面色晦暗，腰腿酸软，形寒肢冷，小便清长。舌质淡，苔白滑，脉沉细。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '304a96da-b932-468c-9317-8fb5e139118b', 'ec09bbef-a2fa-46cb-9088-f30e9edbba3d', '011501', '0115', '肝郁化火', 1, '', '经期或行经前，发生吐血、衄血，量较多色鲜红。月经先期，量少色红质稠，或月经不行，面赤烦热，口苦咽干，乳胁胀痛，烦躁易怒。舌红苔黄，脉弦数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '13066707-d96c-43a8-85db-57c5a6fef748', 'ec09bbef-a2fa-46cb-9088-f30e9edbba3d', '011502', '0115', '阴虚肺燥', 1, '', '行经前后或月经期间，出现鼻衄或咳血。经行量少色红，甚或不行。咽干鼻燥，咳呛气逆，音哑不扬，潮热颧红，腰膝酸软。舌红少津，苦薄，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2fed8fa2-a70b-40eb-8b5d-1bb429d7ff55', 'ec09bbef-a2fa-46cb-9088-f30e9edbba3d', '011503', '0115', '胃热炽盛', 1, '', '经行前后或经期发生吐血，色紫红或兼夹食物。经行先期，量少色鲜，甚则闭而不行，唇红口干，牙龈肿痛，口臭便秘。舌红，苔黄燥，脉滑数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '51ceb114-c38d-425a-b77e-43b7ab400711', '5bf25f9f-dc81-48d4-891b-247de309b0a9', '011601', '0116', '阴虚火旺', 1, '', '经期见有口舌糜烂，口燥咽干，五心烦热，形体消瘦，头晕腰酸，夜寐不安、尿少色黄。舌尖红，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7f2259e3-879a-484c-b1ef-e80967b03ac8', '5bf25f9f-dc81-48d4-891b-247de309b0a9', '011602', '0116', '胃热炽盛', 1, '', '经行见有口舌糜烂，口臭，口干喜饮，胸闷纳呆，尿黄，便结。舌苔黄厚，脉滑数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cb0b1e96-c782-4787-989c-d6e2d7588299', 'f7eb6ea6-5ee8-4f89-bd1c-45d4c44964b9', '011701', '0117', '肝肾阴虚', 1, '', '经行先期，量多色红或淋漓不绝。烘热汗出，五心烦热，口干便艰，腰膝酸软，头晕耳鸣。舌红少苔，脉细数。兼肝旺者多见烦躁易怒；心火旺者可见心悸失眠。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a78ed6bd-3c58-4fee-bcb9-0fcbbba833c3', 'f7eb6ea6-5ee8-4f89-bd1c-45d4c44964b9', '011702', '0117', '肾阳亏虚', 1, '', '月经后愆或闭阻不行，行则量多，色淡质稀或淋漓不止。神萎肢冷，面色晦暗，头目晕眩，腰酸尿频。舌淡，苔薄，脉沉细无力。兼脾阳虚者可见纳少便溏，面浮肢肿；兼心脾两虚者，可见心悸善忘，少寐多梦。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8c734fb7-7ce6-435b-9fe2-5ac29a033279', '76c17650-3820-4cda-864a-368b4fea7005', '011801', '0118', '脾虚湿困', 1, '', '分泌物色白或淡黄，量多如涕，无臭，绵绵不断。恶心纳少，腰酸神倦。舌淡胖，苔白腻，脉缓弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '92725e16-f253-486a-b597-8df21000a68e', '76c17650-3820-4cda-864a-368b4fea7005', '011802', '0118', '肾阴亏虚', 1, '', '分泌物色黄或兼赤，质粘无臭。阴户灼热，五心烦热，腰酸耳鸣，头晕心悸。舌红，苔少，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0c99bff9-c00b-43d8-ae6c-249b5a529208', '76c17650-3820-4cda-864a-368b4fea7005', '011803', '0118', '肾阳亏虚', 1, '', '分泌物量多，清稀如水，或透明如鸡子清，绵绵不绝，腰酸腹冷，小便频数清长，夜间尤甚。舌质淡，苔薄白，脉沉迟。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '595faae9-8e1a-4563-a6fc-3473dcaca6db', '76c17650-3820-4cda-864a-368b4fea7005', '011804', '0118', '湿热下注', 1, '', '分泌物量多，色黄或兼绿，质粘稠，或如豆渣，或似泡沫，气秽或臭，阴户灼热瘙痒，小便短赤，或伴有腹部掣痛。舌质红，苔黄腻，脉儒数。兼肝胆湿热者，出现乳胁胀痛，头痛口苦，烦躁易怒，大便干结。舌红，苔黄，脉弦数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c885c8a2-4b0e-43aa-8e6f-b28ee726a786', '5562ed3a-fe6d-4937-b099-ea86c696b318', '011901', '0119', '肝胃不和', 1, '', '妊娠初期呕吐酸水或苦水，恶闻油腥。胸满胁痛，心烦口苦，暖气叹息，头胀而晕。舌淡红，苔微黄，脉滑。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ca991a20-659d-45f9-8cdd-419a9123ef2a', '5562ed3a-fe6d-4937-b099-ea86c696b318', '011902', '0119', '脾胃虚弱', 1, '', '妊娠初起，呕吐不食，或吐清水，头晕体倦，脘痞腹胀。舌淡，苔自，脉缓滑。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9beb1176-28d7-4b1c-bd73-d748d9ceedee', '5562ed3a-fe6d-4937-b099-ea86c696b318', '011903', '0119', '痰湿阻滞', 1, '', '妊娠早期，呕吐痰涎，口淡而腻，不思饮食，胸腹满闷。舌淡，苔白腻，脉滑。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '54ee64af-eb10-4efa-9305-855428b2118c', '5562ed3a-fe6d-4937-b099-ea86c696b318', '011904', '0119', '气阴两虚', 1, '', '妊娠剧吐，甚至吐苦黄水或兼血水，频频发作。持续日久，以致精神萎靡，嗜睡消瘦，双目无神，眼眶下陷，肌肤干皱失泽，低热口于，尿少便艰。舌红少津，苔薄黄或光剥，脉细滑数无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7effa4e9-5fc6-4217-9851-4e749528cf82', 'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', '012001', '0120', '肾气不足', 1, '', '妊娠期，阴道漏红，量少色淡。腰酸腹坠，或伴头晕耳鸣，小便频数，或有流产史。舌淡，苔白，脉沉滑尺弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ea0c9cdd-aeb3-45b7-ad06-f0059d41c2f6', 'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', '012002', '0120', '气血亏虚', 1, '', '妊娠期，阴道漏红，量少，色淡质薄。腰酸腹坠，神疲肢软，心悸气短，面色少华。舌质淡，苔薄白，脉细滑。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5d21abff-cb3a-4afd-b47e-2a17ab0ce8ed', 'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', '012003', '0120', '血热内扰', 1, '', '妊娠期，阴道漏红，色鲜。或腹痛下坠，心烦不安，手心灼热，口干咽燥，大使秘结。舌红，苔黄而干，脉弦滑或滑数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3226e963-9d5e-48ec-9572-c4a75a238602', 'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', '012004', '0120', '外伤损络', 1, '', '妊娠外伤后腰腹胀坠作痛，阴道漏红，色紫红，或有小血块。舌淡红，脉细滑无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '86ea35ca-72cd-4f2d-871e-a0939c7576ca', 'ad320557-e18e-4922-ae91-fc4d0b2665c6', '012101', '0121', '脾肾两虚', 1, '', '头晕耳鸣，腰酸膝软，神疲肢倦，气短懒言，纳少便溏，夜尿频多，眼眶黯黑或面有黯斑。舌质淡，苔薄，脉沉弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'daea2a8d-5df8-4268-b1ca-67a2c5a1d424', 'ad320557-e18e-4922-ae91-fc4d0b2665c6', '012102', '0121', '气血亏虚', 1, '', '面色苍自或萎黄，心悸气短，头晕肢软，神疲乏力。舌淡，苔薄，脉沉细弱无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5f378c9f-0c98-4e0c-9990-f34189e8a658', 'ad320557-e18e-4922-ae91-fc4d0b2665c6', '012103', '0121', '阴虚内热', 1, '', '两颧潮红，口干咽燥，手足心热，烦躁不宁，或形体消瘦。舌红，少苔，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2b78e7be-5ffa-44fb-ab4b-8cf437dbe9bb', '8ec1fcde-fa82-4594-82c3-d9e8adb733a5', '012201', '0122', '脾虚湿困', 1, '', '妊娠数月，面目四肢浮肿或遍及全身，肤色淡黄，皮薄而光亮，胸闷气短，懒于言语，口淡无味，食欲不振，大便溏薄。舌质淡胖，脉缓滑无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '33746089-54b6-4440-8755-1b9429d3a543', '8ec1fcde-fa82-4594-82c3-d9e8adb733a5', '012202', '0122', '肾阳亏虚', 1, '', '孕后数月或晚期，面目浮肿，下肢尤甚，按之没指，心悸气短，肢冷畏寒，小便短少。舌质淡嫩或边尖有齿痕，苔白润，脉沉细无力或沉迟而弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ffc748d9-43c5-4425-8fe3-d6c667d64a12', '8ec1fcde-fa82-4594-82c3-d9e8adb733a5', '012203', '0122', '肝气郁滞', 1, '', '妊娠4～5月后，先由脚肿，渐及于腿，皮色不变，随按随起，头晕胀痛，胸闷胁胀，食少。苔薄腻，脉弦滑。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9f2b625e-bfa0-42f5-b741-42897e8bd289', '718bb78d-ab3f-45f7-9982-3a844170e5fd', '012301', '0123', '膀胱湿热', 1, '', '小便频数涩痛，淋沥不爽，尿少色黄而赤，小腹拘急。舌尖红，苔黄而干，脉数有力或弦滑。湿重者，尿色黄浊，头身困重，胸闷纳呆，苔黄腻，脉滑数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'df4a079e-df86-4070-8246-7f02ccb0477e', '718bb78d-ab3f-45f7-9982-3a844170e5fd', '012302', '0123', '阴虚内热', 1, '', '小便频数短黄，欲出未尽，腰腹酸坠，五心烦热，心悸颧赤。舌红，少苔，脉细滑数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '739be0ef-09b9-4cbb-ad82-a8b205702688', '5d99527d-cc0b-44ec-9426-2eb8b43193fb', '012401', '0124', '脾虚气陷', 1, '', '妊娠期间尿急频作点滴量少，甚或不通，小腹拘急，胀坠难忍，烦躁不安。面色淡白，神疲肢软。舌淡，苔薄白，脉细无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9834e676-68a0-4aa4-9470-adc0bbb4b55b', '5d99527d-cc0b-44ec-9426-2eb8b43193fb', '012402', '0124', '肾阳亏虚', 1, '', '妊娠期间尿频尿涩，点滴不畅或闭而不通，小腹拘急，胀坠疼痛，烦躁不安。腰膝酸软，形寒畏冷。舌质淡嫩，苔薄润，脉沉细无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b57feb62-6ea0-40f6-a8ea-52466063f20d', '5db29490-3003-4348-90b6-3a6593ea6e58', '012501', '0125', '气不摄血', 1, '', '产后下血如崩，色红或淡红，质稀。面色苍白，腰膝酸软，头晕目眩，精神疲乏，心悸气短，甚或出现冷汗淋漓，四肢厥冷，神志昏迷等症。舌淡，苔薄，脉微或虚大。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '00aa270f-06eb-4ccf-9c6f-13a4a89b359f', '5db29490-3003-4348-90b6-3a6593ea6e58', '012502', '0125', '气血瘀滞', 1, '', '产后出血不止，量多如崩，色紫黯，夹有血块，小腹胀痛。舌质紫或有瘀斑，脉弦或涩。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b739d34d-fc10-4557-8f56-bb8269017a9a', '5db29490-3003-4348-90b6-3a6593ea6e58', '012503', '0125', '血热内扰', 1, '', '产后下血量多，血色鲜红或紫，势急如崩。头胀眩晕，胸胁胀痛，烦躁易怒。舌质偏红，苔黄，脉弦数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cd97c087-124e-46e2-965f-27a716cf0ba4', '7b362e26-78c1-40be-88b1-d11d6e723b3a', '012601', '0126', '脾虚气陷', 1, '', '产后恶露过期不止，量多或淋漓不断，色淡红，质稀薄，无臭味。小腹空坠，神倦懒言，面色淡白。舌淡，脉缓弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b25c417a-ecfa-405c-9e38-7330b40eceb2', '7b362e26-78c1-40be-88b1-d11d6e723b3a', '012602', '0126', '血热内扰', 1, '', '产后恶露过期不止，量较多，色深红，质惆粘，有臭秽气。面色潮红，口燥咽干。舌质红，脉虚细而数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2bfb4503-e1da-43d3-8352-699b71a237ed', '7b362e26-78c1-40be-88b1-d11d6e723b3a', '012603', '0126', '气血瘀滞', 1, '', '产后恶露淋漓涩滞不爽，量少，色紫黯有块，小腹疼痛拒按。舌紫黯或边有紫点，脉弦涩或沉而有力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2655fdfd-dce5-460d-9b20-0748ff6473c7', 'fdb562c7-9f9c-4c62-a5be-57f5afaca38f', '012701', '0127', '血虚津亏', 1, '', '产后大便干燥，数日不解，或解时艰涩难下，但腹无胀痛，饮食如常。面色萎黄，皮肤不润。舌淡，苔薄，脉虚涩。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '747fa79c-ae7d-4f61-94de-1b69c9503adc', 'fdb562c7-9f9c-4c62-a5be-57f5afaca38f', '012702', '0127', '阴虚内热', 1, '', '产后大便燥结，数日不解，伴口干咽燥，手心灼热，胸满腹胀。舌红少津，苔薄黄，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7c661881-4830-444d-a20c-060cd93a15de', 'fdb562c7-9f9c-4c62-a5be-57f5afaca38f', '012703', '0127', '脾虚气陷', 1, '', '产后虽有便意，临厕努责乏力，气短汗出，倦怠无力，便出并不干硬，或头晕目眩。舌淡，苔薄，脉大而虚。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '33e6d8f2-a3ac-4a48-92ba-5f71a998f02b', 'cfad8981-d0c3-4189-b920-ddc9e4650047', '012801', '0128', '热毒炽盛', 1, '', '恶寒，高热，恶露量多或少，色紫黯如败酱，有秽臭，小腹疼痛拒按，烦躁口渴，尿少色黄，大便燥结。舌红，苔黄，脉数有力。若邪入营血，则高热不退，斑疹隐隐，舌红绛，苔黄燥，脉细弦而数。若热传心包，则神昏谵语，甚至昏厥，面色苍白，四肢厥逆，脉微细而数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '091d5d6e-a524-4df9-9b85-b2709c0871f7', 'cfad8981-d0c3-4189-b920-ddc9e4650047', '012802', '0128', '气血瘀滞', 1, '', '寒热时作，恶露不下或甚少，气秽臭，色紫黯有块，小腹疼痛拒按。舌紫黯有瘀点，脉细涩。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '340b98b4-74ce-42ae-b1bd-a9abaa47bd65', '834deeb6-c861-4e0b-a56f-466dc1c697c6', '012901', '0129', '肺卫气虚', 1, '', '产后汗出较多，不能自止，动则益甚。时或恶风，面色恍白，气短懒言，语声低怯，倦怠乏力。舌质淡，苔薄，脉虚弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '21e81a51-957b-46d9-8e77-81f46fd209a8', '834deeb6-c861-4e0b-a56f-466dc1c697c6', '012902', '0129', '阴虚内热', 1, '', '产后睡中汗出，甚则湿透衣衫，醒来即止。面色潮红，头晕耳鸣，口燥咽干，渴不思饮，或五心烦热，腰膝酸软。舌红嫩或绛，少苔或无苔，脉细数无力。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '73d4a342-74d6-4531-be8f-9fae50a220d0', '23082329-5388-4482-b911-56bc771ba054', '013001', '0130', '气血亏虚', 1, '', '产后乳少，甚或全无，乳汁清稀，乳房柔软，无胀感。伴面色少华，神疲食少。舌淡，少苔，脉虚细。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8a243f71-7b02-47f1-ba5a-939ebf4c5f63', '23082329-5388-4482-b911-56bc771ba054', '013002', '0130', '肝气郁滞', 1, '', '产后乳汁甚少或全无，乳汁稠，而乳房胀硬而痛。情志抑郁不乐，胸胁胀痛，食欲减退，或有微热。舌质暗红或尖边红，苔薄黄，脉弦细或弦数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b4ac538b-6ee8-498b-9918-27a880a388a0', 'ecbce90b-d6a1-406a-9a45-be51fd4022e0', '013101', '0131', '气血亏虚', 1, '', '产后乳汁自出，量少质清稀，乳房柔软无胀满。面色少华，神疲气短，或动则心悸。舌淡苔薄，脉细弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '96d34905-afc0-46f7-8be4-b83b778773ef', 'ecbce90b-d6a1-406a-9a45-be51fd4022e0', '013102', '0131', '肝郁化火', 1, '', '产后乳汁自出，质较稠，乳房胀痛。精神仰郁或急躁易怒，甚或心烦少寐，口苦咽干，便秘尿黄。舌质红，苔薄黄，脉弦数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '94230653-f048-4bc0-becd-b988551c5388', '1ed54efd-c53d-4514-aa8f-7ed7fb06a27b', '013201', '0132', '脾虚气陷', 1, '', '子宫脱垂，劳则加剧，卧则消失，小腹坠胀，面色少华，四肢乏力，少语懒言，带下色白，量多质稀。舌淡，苔薄，脉细弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '22bf89f7-7ad7-4f1e-94e5-e06f0dc54da4', '1ed54efd-c53d-4514-aa8f-7ed7fb06a27b', '013202', '0132', '肾阳亏虚', 1, '', '子宫脱垂，腰酸腿软，小腹下垂，头晕耳鸣，小便频数，夜间尤甚。舌淡红，脉沉弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0114ac2d-8658-4a9e-9273-145b6fdc931c', '1ed54efd-c53d-4514-aa8f-7ed7fb06a27b', '013203', '0132', '湿热下注', 1, '', '子宫脱出日久，表面溃烂，黄水淋漓或小便灼热，或口干口苦。舌质红，苔黄或黄腻，脉滑数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7e00ae29-b74d-420c-b4ca-4a2c49a2e6ff', '90b9fc26-8181-4b63-ba42-891943413edb', '013301', '0133', '肾阳亏虚', 1, '', '婚后不孕，经行量少色淡，头晕耳鸣，腰酸形寒，小腹冷感，带下清稀，性欲淡漠，有时便溏。舌淡胖，苔白，脉沉细尺弱。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'df5135c2-0458-455d-b7bb-8992e6489518', '90b9fc26-8181-4b63-ba42-891943413edb', '013302', '0133', '肾阴亏虚', 1, '', '婚后不孕，经行先期，量少色红，五心烦热，咽干口渴，头晕心悸，腰酸腿软。舌红少苔，脉细数。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8b165ffd-f280-4a59-9305-e77903f581e2', '90b9fc26-8181-4b63-ba42-891943413edb', '013303', '0133', '痰湿内阻', 1, '', '婚后不孕，月经后期，量少色淡，形体肥胖，胸闷口腻，带多粘腻。苔白腻，脉弦滑。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b0ae3192-8e4b-4e43-8f12-59dbb3683049', '90b9fc26-8181-4b63-ba42-891943413edb', '013304', '0133', '肝气郁滞', 1, '', '婚后不孕，月经不调，量或多或少，色紫红有血块，情志失畅，经前胸闷急躁，乳房作胀，行经少腹疼痛。苔薄黄，脉弦。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd8c27c03-0bcf-4aba-8518-75e5e87dbe81', '90b9fc26-8181-4b63-ba42-891943413edb', '013305', '0133', '瘀滞胞宫', 1, '', '婚后不孕，经行后期量少，色紫有块，小腹疼痛，临经尤甚。舌边或有紫斑，苔薄黄，脉弦或涩。', '2008/04/09 02:50:56', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c1db6d86-94fb-457c-86f0-078505283ae9', '3c13642f-29cd-4825-a54a-e792876e0f63', '060102', '0601', '横断型骨折', 1, '', '多见成年人，骨折端可具有典型的重叠，近端向上后方移位，远端向下前方移位。。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '71593d67-5beb-4195-ba6e-163cb90e8b32', '3c13642f-29cd-4825-a54a-e792876e0f63', '060103', '0601', '粉碎型骨折', 1, '', '多为直接暴力引起，常于中1／3处有小骨片呈垂直移位。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd406d75a-563d-404a-911e-bb134834820a', 'b4635fd4-5677-4e1a-a2c3-84f7fe66540f', '060201', '0602', '肩胛体骨折', 1, '', '肩胛部软组织肿胀明显，皮下瘀斑，压痛，活动受限，充分外展受限。无移位型：肩活动疼痛，肩部、腋窝有压痛。移位型：肩外型变平，肩峰突出，锁骨下窝处饱满，呈固定畸形，从外向内顶压肱骨头可使疼痛加重。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e18387ac-25d8-4f09-8abe-199070171774', 'b4635fd4-5677-4e1a-a2c3-84f7fe66540f', '060202', '0602', '肩胛冈骨折', 1, '', '局部肿胀，压痛，或可触及折端异常活动。无移位型：肩活动疼痛，肩部、腋窝有压痛。移位型：肩外型变平，肩峰突出，锁骨下窝处饱满，呈固定畸形，从外向内顶压肱骨头可使疼痛加重。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '485ff945-2d59-4859-a8a9-8c2f52de3856', 'b4635fd4-5677-4e1a-a2c3-84f7fe66540f', '060203', '0602', '肩胛颈骨折', 1, '', '多由间接外力所致。无移位型：肩活动疼痛，肩部、腋窝有压痛。移位型：肩外型变平，肩峰突出，锁骨下窝处饱满，呈固定畸形，从外向内顶压肱骨头可使疼痛加重。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '46943837-24d0-4275-a14a-49af3b455350', 'b4635fd4-5677-4e1a-a2c3-84f7fe66540f', '060204', '0602', '肩胛盂骨折', 1, '', '肩部肿胀，活动受限。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '43e33448-6100-4b19-ac0e-88bd52912d7c', 'b4635fd4-5677-4e1a-a2c3-84f7fe66540f', '060205', '0602', '肩峰骨折', 1, '', '肿胀，肩峰端压痛，外展上臂时疼痛加剧。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '377d5c7b-81ea-4ba9-99b1-819281f61648', 'b4635fd4-5677-4e1a-a2c3-84f7fe66540f', '060206', '0602', '喙突骨折', 1, '', '抗阻力内收肩及屈肘时疼痛加剧。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e67224ec-2bcc-410c-93f3-87c7f8b58237', 'b73dce57-ab60-4eb7-b456-8894c318d5f9', '060301', '0603', '裂缝骨折', 1, '', '大结节处轻到中度肿胀，压痛。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd8a0bc04-8e7d-48c5-a97e-095a9b08caa6', 'b73dce57-ab60-4eb7-b456-8894c318d5f9', '060302', '0603', '无移位粉碎性骨折', 1, '', '大结节处肿胀，压痛，可扪及骨擦音。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '80cd2e2c-7b11-409b-8545-34452627e209', 'b73dce57-ab60-4eb7-b456-8894c318d5f9', '060303', '0603', '撕脱性骨折', 1, '', '大结节压痛，肿胀，肩前外方常可触及骨折块，肩外展活动受限。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '62b94585-9c38-4d3c-bafc-c0bac29daba3', '5af95408-93ad-41f4-aea9-a294ee166765', '060401', '0604', '裂缝骨折', 1, '', '骨裂多为骨膜下，骨膜增粗，肩部轻度肿胀，压痛。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f21530ea-d870-4224-abbc-620122484149', '5af95408-93ad-41f4-aea9-a294ee166765', '060402', '0604', '嵌插骨折', 1, '', '骨折端相互嵌插，伤肩肿胀，压痛，纵轴叩击痛，无明显骨擦音。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ddf4db60-e32a-4772-87f3-b26b711c4d45', '1831225c-6b16-4417-af94-9a0efdabf978', '030101', '0301', '风伤肠络', 1, '', '大便带血、滴血或喷射状出血，血色鲜红，或有肛门瘙痒。舌红，苔薄白或薄黄，脉浮数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd5cc7e62-951b-40dd-ae00-aca63001de58', '1831225c-6b16-4417-af94-9a0efdabf978', '030102', '0301', '湿热下注', 1, '', '便血色鲜，量较多，肛内肿物外脱，可自行回缩，肛门灼热。舌红，苔黄腻，脉滑数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7f1b88c5-b720-4eda-97b0-13545a1f416a', '1831225c-6b16-4417-af94-9a0efdabf978', '030103', '0301', '气滞血瘀', 1, '', '肛内肿物脱出，甚或嵌顿，肛管紧缩，坠胀疼痛。甚则肛缘有血栓，水肿，触痛明显。舌质暗红，苔白或黄，脉弦细涩。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8741e181-575c-42ae-8df3-efd20494870a', '1831225c-6b16-4417-af94-9a0efdabf978', '030104', '0301', '脾虚气陷', 1, '', '肛门坠胀，肛内肿物外脱，需手法复位。便血色鲜或淡，可出现贫血，面色少华，头昏神疲，少气懒言，纳少便溏。舌淡胖，边有齿痕，舌苔薄白，脉弱。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '33bc5599-5f45-46d3-b169-92e0a9a782ed', '0ab0b5df-7d6a-4ccd-9ac1-f2b69806bd2b', '030201', '0302', '气滞血瘀', 1, '', '肛缘肿物突起，排便时可增大，有异物感，可有胀痛或坠痛，局部可触及硬性结节。舌紫，苔淡黄，脉弦涩。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ae1c753f-2d32-4158-96d2-bc4c1e850fd6', '0ab0b5df-7d6a-4ccd-9ac1-f2b69806bd2b', '030202', '0302', '湿热下注', 1, '', '肛缘肿物隆起，灼热疼痛或有滋水，便干或溏。舌红，苔黄腻，脉滑数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '477ba152-c699-44d9-be72-2138e9ef3892', '0ab0b5df-7d6a-4ccd-9ac1-f2b69806bd2b', '030203', '0302', '脾虚气陷', 1, '', '肛缘肿物隆起，肛门坠胀，似有便意，神疲乏力，纳少便溏。舌淡胖，苔薄白，脉细无力。多见于经产妇、老弱体虚者。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4c6e5cfc-f32d-47b3-a2a6-656cc5f63aec', 'be8f7535-c637-41ef-9f26-2dcf569e3d33', '030301', '0303', '气滞血瘀', 1, '', '', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a17e21c6-4dc1-42d1-8a06-02a13542bd95', 'be8f7535-c637-41ef-9f26-2dcf569e3d33', '030302', '0303', '湿热下注', 1, '', '', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '37617453-7daa-43db-8d6f-0030578ecb08', 'be8f7535-c637-41ef-9f26-2dcf569e3d33', '030303', '0303', '脾虚气陷', 1, '', '', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd2717f88-ecfc-4138-bd1d-08258727ee4f', '7f06b32b-f70b-4134-9318-b788ac9ebd4b', '030401', '0304', '血热肠燥', 1, '', '大便而三日一行，质干硬，便时滴血或手纸染血，肛门疼痛，腹部胀满，溲黄。裂口色红。舌质偏红，苔黄燥，脉弦数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7028e804-8f94-4c6e-aac2-20beaa0a4ae1', '7f06b32b-f70b-4134-9318-b788ac9ebd4b', '030402', '0304', '阴虚津亏', 1, '', '大便干燥数日一行，便时疼痛点滴下血， 口干咽燥，五心烦热。裂口深红。舌红，少苔或无苔，脉细数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e0a9129b-c7b8-449b-8804-25ae950484d8', '7f06b32b-f70b-4134-9318-b788ac9ebd4b', '030403', '0304', '气滞血瘀', 1, '', '肛门刺痛，便时便后尤甚。肛门紧缩，裂口色紫暗。占质紫暗，脉弦或涩。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2d32c047-7ff4-4a6a-af8e-e52739449894', '096dc779-dd53-4916-8fa4-cf3a94b8cc62', '030501', '0305', '火毒蕴结', 1, '', '肛门周围突然肿痛，持续加剧，伴有恶寒、发热、便秘、溲赤。肛周红肿，触痛明显，质硬，表面灼热。舌红，苔薄黄，脉数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1de10193-32f1-49d2-a48b-2fb3ab9bbbfb', '096dc779-dd53-4916-8fa4-cf3a94b8cc62', '030502', '0305', '热毒炽盛', 1, '', '肛门肿痛剧烈，可持续数日，痛如鸡啄，夜寐不安，伴有恶寒发热，口干便秘，小便困难。肛周红肿，按之有波动感或穿刺有脓。舌红，苔黄，脉弦滑。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '41bbf078-ca86-4c29-84c4-a34977981fc6', '096dc779-dd53-4916-8fa4-cf3a94b8cc62', '030503', '0305', '阴虚毒恋', 1, '', '肛门肿痛、灼热，表皮色红，溃后难敛，伴有午后潮热，心烦口干，夜间盗汗。舌红，少苔，脉细数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd8afddea-2190-448d-8585-d2ba6cf1bee1', '95617344-0f74-4eb5-ae09-d8e8fbb9522c', '030601', '0306', '湿热下注', 1, '', '肛周经常流脓液，脓质稠厚，肛门胀痛，局部灼热。肛周有溃口，按之有索状物通向肛内。舌红，苔黄，脉弦或滑。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'be416859-45aa-49ea-9276-f470e2d96b0c', '95617344-0f74-4eb5-ae09-d8e8fbb9522c', '030602', '0306', '正虚邪恋', 1, '', '肛周流脓液，质地稀薄，肛门隐隐作痛，外口皮色暗淡，漏口时溃时愈，肛周有溃口，按之较硬，或有脓液从溃口流出，且多有索状物通向肛内，可伴有神疲乏力。舌淡，苔薄，脉濡。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f8b10dfe-0b77-405b-b01c-64203271cc52', '95617344-0f74-4eb5-ae09-d8e8fbb9522c', '030603', '0306', '阴液亏虚', 1, '', '肛周有溃口，颜色淡红，按之有索状物通向肛内，可伴有潮热盗汗，心烦口干。舌红，少苔，脉细数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '46b1cdb9-95ea-4445-92fb-5344eaca645a', 'fcc703eb-1a63-40e4-9854-6b9ee8f88bf9', '030701', '0307', '脾虚气陷', 1, '', '便时肛内肿物脱出，轻重不一，色淡红，伴有肛门坠胀，大便带血，神疲乏力，食欲不振，甚则有头昏耳鸣，腰膝酸软。舌淡，苔薄白，脉弱。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '05f586ec-408a-454b-8aca-b57f24fcab02', 'fcc703eb-1a63-40e4-9854-6b9ee8f88bf9', '030702', '0307', '湿热下注', 1, '', '肛内肿物脱出，色紫暗或深红，甚则表面部分溃破，糜烂，肛门坠痛，肛内指检有灼热感。舌红，苔黄腻，脉弦数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ee64cddc-aade-4968-ac68-b7260aef560c', 'f45ae0b0-a86c-4b11-92b2-fd0d323a4b6d', '030801', '0308', '湿热下注', 1, '', '肛周潮湿、潮红、有灼热感。肥大的肛乳头充血、水肿。舌红，苔黄，脉滑数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4d08dcbc-c043-48c8-baa5-d453e0ee2c73', 'f45ae0b0-a86c-4b11-92b2-fd0d323a4b6d', '030802', '0308', '气滞血瘀', 1, '', '排便后肛门部肿物脱出，表面色紫暗，伴有肛门坠胀。舌紫暗，苔薄，脉涩。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9237319c-f50f-4382-aa24-f5a09420de24', '16f0ef9c-e5c5-41a1-94c1-8276ba925cf0', '030901', '0309', '肾气亏虚', 1, '', '肛门坠胀，腰骶酸软，乏力，毛发不荣，肿物溃后流液清稀，溃口凹陷不愈。舌淡，苔薄，脉细弱。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '02be2c46-5a07-4723-a470-67a039595b34', '16f0ef9c-e5c5-41a1-94c1-8276ba925cf0', '030902', '0309', '湿热下注', 1, '', '肛门胀痛，局部红肿，按之可有波动感，伴有恶寒发热。舌红，苔黄腻，脉数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4163e067-0831-4767-94fe-534e943a1767', '1fb14ffb-bce9-42e0-a58f-a003aac8df53', '031001', '0310', '寒湿凝聚', 1, '', '肿块生长缓慢，光滑活动，无压痛，伴口淡，畏寒。舌淡红，苔薄白，脉沉紧。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '701ddec7-5e24-4bdc-b6ff-2b9975718849', '1fb14ffb-bce9-42e0-a58f-a003aac8df53', '031002', '0310', '湿热蕴结', 1, '', '恶寒发热，局部红肿、疼痛拒按。舌质红，苔黄，脉数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5ce633d4-e66e-41c5-b101-499df36fbb76', '1fb14ffb-bce9-42e0-a58f-a003aac8df53', '031003', '0310', '气阴两虚', 1, '', '肿块溃破，经久不愈，流液清稀，伴有精神萎靡，形体消瘦。舌质嫩红，苔薄，脉细无力。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fe5e9daf-e856-4574-b4a6-e3090f46d1f2', '4a84d33f-527d-4f04-b02f-9c5ee3eafd03', '031101', '0311', '风伤肠络', 1, '', '便血鲜红，滴血、带血。息肉表面充血明显，脱出或不脱出肛外。舌红，苔白或薄黄，脉浮数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0428e22b-daa1-408c-b7b0-546881589e58', '4a84d33f-527d-4f04-b02f-9c5ee3eafd03', '031102', '0311', '气滞血瘀', 1, '', '肿物脱出肛外，不能回纳，疼痛甚，表面紫暗。舌紫，脉涩。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '93260c61-f8d4-4158-90d2-100cefa7ef3d', '4a84d33f-527d-4f04-b02f-9c5ee3eafd03', '031103', '0311', '脾气亏虚', 1, '', '肿物易于脱出肛外，表面增生粗糙，或有少量出血， 肛门松驰。舌淡，苔薄，脉弱。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '763fcf99-6403-4acd-b411-9cbb2fbed58e', '75201b21-dd7c-4727-a6b6-789aa63086ad', '031201', '0312', '气滞血瘀', 1, '', '肛门坠胀，疼痛，排便时加重，排便不畅，伴有腹胀、肠鸣。舌紫暗或有瘀斑，苔黄或白，脉弦。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fa754000-cd30-4798-be71-76b7c4f9e0d7', '75201b21-dd7c-4727-a6b6-789aa63086ad', '031202', '0312', '热结肠燥', 1, '', '大便秘结，干硬难解，口干。舌红，苔黄少津，脉数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a22fb2dc-4adb-4cd0-a316-4317cd46450a', '75201b21-dd7c-4727-a6b6-789aa63086ad', '031203', '0312', '肠道湿热', 1, '', '排便不畅，便溏次多，有粘液或脓血，低热，肛门潮湿。舌红，苔黄厚腻，脉滑数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '82d56e11-348d-4a31-aeab-f3b097da8325', '939be377-c1d1-45fa-980c-af038e2dbc5f', '031301', '0313', '脾虚不固', 1, '', '大便不能完全控制，伴有神疲乏力，纳谷欠佳，或有泄泻、脱肛。肛门指检肛管松弛。舌淡，苔薄，脉弱。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a915f58f-3423-4e01-b407-d5f4cd55885e', '939be377-c1d1-45fa-980c-af038e2dbc5f', '031302', '0313', '肾虚不固', 1, '', '大便不能控制，病程较长，伴有头昏乏力，腰酸耳鸣，肛门指检肛管松弛。舌淡，脉沉细无力。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f128d449-76ad-4443-a894-26a885efd2e3', 'b2cf1c63-313d-4fe2-aed0-16e5747a38e3', '031401', '0314', '脾气亏虚', 1, '', '神疲乏力，纳食欠佳，排便困难，伴肛门坠胀及便意不尽感，大便日行数次，质稀软，但解时困难。舌淡，苔薄，脉弱。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2655a061-40c2-4d7f-b117-f303e6279e90', 'b2cf1c63-313d-4fe2-aed0-16e5747a38e3', '031402', '0314', '气阴两虚', 1, '', '头昏乏力，口干欲饮，排便困难，排便时间延长，便意未尽感，大便数日一行，质较硬。舌淡红，苔薄，脉弱。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd01dca99-8fe0-4065-be6f-70f3b967e5ce', 'abb6ff75-7a70-46a9-ad1f-baf3ac64143d', '031501', '0315', '气滞血瘀', 1, '', '排便困难，排便时间延长，便时肛门疼痛，大便2一3日一行，质干。舌暗淡或夹有瘀斑，脉涩。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dca7181f-088d-4c03-bcc7-b9f54fdc5abe', 'abb6ff75-7a70-46a9-ad1f-baf3ac64143d', '031502', '0315', '湿热下注', 1, '', '排便困难，排便时间延长，大便3～4日一行，质干，便意不尽感，肛门坠胀。舌红，苔黄厚腻，脉滑数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '47a7aa37-3022-415f-981d-f70f83c17621', 'abb6ff75-7a70-46a9-ad1f-baf3ac64143d', '031503', '0315', '阴虚火旺', 1, '', '排便困难，排便时间延长，大便3～4日一行，质干结，口干欲饮，可有盗汗。舌红，苔少，脉细数、', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a6c47f4b-ca6e-4c9d-8aca-13b056bd3015', 'e4982182-71b4-4fd5-8b9a-fbf02d947f0d', '031601', '0316', '湿热下注', 1, '', '以急性、亚急性湿疡较为多见。起病较急，皮损为潮红、肿胀、糜烂、滋水浸淫成片，结痂。伴有瘙痒或大便秘结，小便短黄，苔黄腻，脉滑数等症状者，为热重于湿。若起病较缓慢，皮损以丘疹、疱疹为主，滋水较多，伴有倦怠无力，纳呆，大便溏，苔白腻，脉滑等症状者，为湿重于热。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c7ff96a2-5e6e-45a9-bee8-594f5c8e95b2', 'e4982182-71b4-4fd5-8b9a-fbf02d947f0d', '031602', '0316', '血虚风燥', 1, '', '以慢性湿疡为多见，反复发作，病程较长。皮损肥厚，呈苔藓样变，色索沉着，结痂脱屑等，或伴有头昏乏力，腰酸腿软。舌淡红，苔薄白，脉细无力。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8cfd5857-2f71-45a8-a459-c2c8a5116a84', '22c16f97-f663-46e4-b52f-21713644876f', '031701', '0317', '湿热蕴结', 1, '', '肛门坠胀，便次增多，大便带血，色泽暗红，或挟粘液，或有里急后重。舌红，苔黄腻，脉滑数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '79978806-2c77-4f45-b344-fa6dbf1e3a0f', '22c16f97-f663-46e4-b52f-21713644876f', '031702', '0317', '气阴两虚', 1, '', '面色无华，消瘦乏力，便溏，或排便困难，便中带血，色泽紫暗，肛门坠胀，或伴心烦口干，夜间盗汗。舌红或绛，少苔，脉细弱或细数。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5e0d3e61-9bcc-4e52-b2cc-a3767f789f78', '22c16f97-f663-46e4-b52f-21713644876f', '031703', '0317', '气滞血瘀', 1, '', '肛周肿物隆起，触之坚硬如石，坠痛不休。或大便带血，色紫暗，里急后重，排便困难。舌质紫暗，脉涩。', '2008/04/09 04:09:55', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3036196b-14a3-4690-b5d4-2e2a963aa615', '0204b76e-0e41-4375-b449-efece3765811', '040101', '0401', '暑湿热蕴', 1, '', '脓疱密集，色黄周围有红晕，糜烂面鲜红，多有口干，便干，小便黄。舌红，苔黄腻，脉濡滑数。', '2008/04/09 04:59:42', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '44ade049-8ad5-4196-a366-ccc3fc0e520c', '0204b76e-0e41-4375-b449-efece3765811', '040102', '0401', '脾虚湿蕴', 1, '', '脓疱稀疏，色灰白或淡黄，糜烂面淡红。多有面黄，纳少，大便溏薄。舌淡，苔薄微腻，脉濡细。', '2008/04/09 04:59:42', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2bd3f649-541f-4ad3-a88a-236c1e45910b', 'e763afd8-7fb2-4275-ab07-ae811ac6aef8', '040201', '0402', '湿热瘀阻', 1, '', '颜面可见黄豆大小褐红色结节，四周浸润融合成片，或有溃疡。伴有纳差、烦躁易怒。舌淡红或边尖红，苔自腻或黄腻，脉弦滑。', '2008/04/09 04:59:42', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0aea334e-6f8f-469a-b586-8032ac5b3450', 'e763afd8-7fb2-4275-ab07-ae811ac6aef8', '040202', '0402', '气血亏虚', 1, '', '颜面有暗红或淡红色的斑片，结节浸润，脓水稀薄，老疤痕凹陷，新溃疡色暗，伴神疲乏力，低热盗汗，纳差。舌质淡，苔薄白，脉细缓。', '2008/04/09 04:59:42', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e68d2c00-26e9-469c-ade2-515ba65df668', '7ff56278-7880-4610-ba61-d6245b863efa', '040301', '0403', '湿热瘀阻', 1, '', '结节暗红，压痛明显，多有低热，小腿酸痛，纳差便干。舌红，苔薄白，脉细或数。', '2008/04/09 04:59:42', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '86cde87a-72d4-4a7f-88be-40d7e186a91d', '7ff56278-7880-4610-ba61-d6245b863efa', '040302', '0403', '气血亏虚', 1, '', '结节溃烂，脓水稀薄，久不收口，伴有神倦乏力，纳少，便溏。舌淡，苔薄，脉沉细弱。', '2008/04/09 04:59:42', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '19c37a7e-6881-4b88-bfe3-6fdbe8810e99', '3b8160c9-f835-400f-b718-67160197e4e6', '040401', '0404', '风湿毒聚', 1, '', '黄痂污秽，毛发枯黄易落，瘙痒无休，走窜漫延。舌红，苔薄，脉浮或滑。', '2008/04/09 04:59:42', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4906cf2f-f894-4737-86d5-027591de14fe', '3b8160c9-f835-400f-b718-67160197e4e6', '040402', '0404', '湿热毒聚', 1, '', '黄痂粘着，头皮潮红，按之疼痛，糜烂溢脓。伴寒热头痛，口渴咽干。舌红，苔黄或腻，脉滑数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7cefca57-5264-468f-a1a2-f5663504497b', '1a12d225-9f82-4bf5-a7c2-28a7a120f953', '040501', '0405', '血虚风燥', 1, '', '皮损呈灰白色斑片，瘙痒，毛发干枯，易于折断，面色晦黄。舌淡红，苔薄白，脉濡细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '998310e3-209f-47dc-a17c-c9e383626df3', '1a12d225-9f82-4bf5-a7c2-28a7a120f953', '040502', '0405', '湿热毒聚', 1, '', '皮损呈红斑肿胀，丘疹脓疱，结黄色痂，多有发热，身疼。舌红，苔薄黄，脉滑数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5d490588-3c13-4f98-8109-ba0ebc7069af', '72435478-e2f2-432c-b315-96f9427ea871', '040601', '0406', '湿热毒聚', 1, '', '皮损呈花环红斑，伴有脓疱，轻微疼痛，糜烂结痂，或有低热不适。舌红、苔薄、脉数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e8fe5a1d-acf5-46ac-ae1f-1134a9af3ca3', '8cc7202b-4528-4f00-a915-8509b662022b', '040701', '0407', '风湿蕴肤', 1, '', '手掌或指间水疱如晶，涸干脱屑，境界明显，渐次扩大。或指间潮红，湿烂。舌红，苔白或腻，脉滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f3aae2e7-cb38-4e64-b223-c60117622beb', '8cc7202b-4528-4f00-a915-8509b662022b', '040702', '0407', '血虚风燥', 1, '', '手掌皮肤肥厚粗糙、干燥、龟裂。或水疱不显，干涸落屑。舌淡红，苔薄，脉细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8dc231af-6ee5-40c0-b369-d5503cbadd7c', '79a8c923-fe03-4815-87b5-fa202cce7477', '040801', '0408', '湿热下注', 1, '', '密集水疱，糜烂流水，浸淫成片，瘙痒疼痛或有发热。舌苔薄黄，脉滑数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1ff68d0e-d1ab-4e47-90f0-aead2bd1e2c8', '79a8c923-fe03-4815-87b5-fa202cce7477', '040802', '0408', '血虚风燥', 1, '', '皮肤增厚，粗糙于裂，瘙痒不流水。舌红，苔薄，脉细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '90143139-0f67-4a4e-9e03-45af70552cc1', '498f67ed-1f95-482f-83f8-7dd3fed78993', '040901', '0409', '血燥失养', 1, '', '甲板色泽不荣，增厚或翘起，或蛀蚀呈蜂窝状。舌淡，少苔，脉细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3552326e-5412-4e5b-b80e-f9b7fe4b83d8', '498f67ed-1f95-482f-83f8-7dd3fed78993', '040902', '0409', '湿热蕴结', 1, '', '甲板色红，甲沟红肿，，或有脓疱，瘙痒刺痛。舌红，苔薄腻，脉滑数.', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2adbf667-10e0-4fdd-8347-40f9577de15d', '19f2c756-1d11-44d1-bf88-0ed2e178e756', '041001', '0410', '风热蕴肌', 1, '', '皮肤水疱少，丘疱疹多，壁厚波少，抓破干痂，瘙痒不已，久则皮肤干糙肥厚，舌红，苔薄，脉浮或滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '158d1852-e589-4f2a-a165-025661bed7d6', '19f2c756-1d11-44d1-bf88-0ed2e178e756', '041002', '0410', '湿热毒聚', 1, '', '皮肤水疱多，丘疱疹泛发，壁薄液多，破流脂水，浸淫湿烂。或脓疱叠起，或起红丝、眷核肿痛。舌红，苔黄腻，脉数滑。 。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fcd96fd3-8ac6-4284-b4dc-c5d1b1483714', '19f2c756-1d11-44d1-bf88-0ed2e178e756', '041003', '0410', '虫毒结聚', 1, '', '阴茎、阴囊发生孤立的褐红色硬节，时时作痒，久难消散。舌淡，苔白，脉滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '830c44cc-8e35-4a78-a2a5-8c9b7bb6c6a6', 'a0109f7c-f5c5-4f43-b1c1-4c3b1abc8ef1', '041101', '0411', '肝经郁热', 1, '', '皮损鲜红，疱壁紧张，灼热刺痛，口苦咽干，烦燥易怒，大便干或小便黄。舌质红，舌苔簿黄或黄厚，脉弦滑数。 “', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c3450a9d-4dd7-4202-aeb5-7383030a1697', 'a0109f7c-f5c5-4f43-b1c1-4c3b1abc8ef1', '041102', '0411', '脾虚湿蕴', 1, '', '颜色较淡，疱壁松弛，口不渴，食少腹胀，大便时溏，舌质淡，舌苔白或白腻，脉沉缓或滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2bd652af-7519-4c56-84a6-e0681e9eb8f5', 'a0109f7c-f5c5-4f43-b1c1-4c3b1abc8ef1', '041103', '0411', '气滞血瘀', 1, '', '皮疹消退后局部疼痛不止。舌质暗，苔白，脉弦细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '22b02072-7691-4621-b26a-3f767a45fa14', '30fe8813-318d-440b-b497-2c66861d8e21', '041201', '0412', '肺胃热盛', 1, '', '群集小疱，灼热刺痒。轻度周身不适，心烦郁闷，大便干，小便黄。舌质红，苔黄，脉弦数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '41abae10-675a-4ddb-b6a9-579fdb272a2a', '30fe8813-318d-440b-b497-2c66861d8e21', '041202', '0412', '阴虚内热', 1, '', '间歇发作，口干唇燥，午后微热。舌红，苔薄，脉细数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0e6debac-7852-4b5e-8f05-42807fd644c3', 'f8f86121-e8e3-418f-9810-1b4d698fcfb3', '041301', '0413', '热毒蕴结', 1, '', '皮疹淡红，数目较多，伴口干不欲饮，身热，大便不畅，尿黄。舌质红，苔白或腻，脉滑数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cb3a9e3a-88b2-440d-8c35-276975bb24f0', 'f8f86121-e8e3-418f-9810-1b4d698fcfb3', '041302', '0413', '热蕴络瘀', 1, '', '病程较长，皮疹黄褐或暗红、可有烦热。舌暗红，苔薄白，脉沉缓。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '01c6bdab-8c69-4809-816f-e8b75bb84a33', '566f6d3b-21c8-47da-b194-ebc4fb7b7921', '041401', '0414', '风热血燥', 1, '', '结节如豆，坚硬粗糙，色黄或红。舌红，苔薄，脉弦数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd020cba8-34e7-4d40-8d19-c23c8fd87e4e', '566f6d3b-21c8-47da-b194-ebc4fb7b7921', '041402', '0414', '湿热血瘀', 1, '', '结节疏松，色灰或褐。舌暗红，苔薄白，脉细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '342ca183-7ce4-4fea-b55e-ac7601b6cf11', '727a9939-d242-4406-a12a-5a7d686c4e18', '041501', '0415', '风热蕴肤', 1, '', '丘疹光亮，微痒，抓破疼痛，四周稍红。舌红，苔薄，脉细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c6e0cb8d-9a52-448a-8eaa-40c9f3f322fe', '727a9939-d242-4406-a12a-5a7d686c4e18', '041502', '0415', '湿热蕴结', 1, '', '丘疹瘙抓流汁，或有抓痕，破后可挤出粉状白色小体。舌红，苔薄腻，脉濡。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fe4cf876-b398-4227-81bf-b1c5e3a84773', '82630884-dfae-422c-af1c-8cd4419071b7', '041601', '0416', '痰湿凝结', 1, '', '表面呈圆锥形硬结，灰黄色或蜡黄色，压之疼痛。舌苔薄白，脉滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fc8a09ec-db3f-465f-9eb1-3a14249ed6c3', '82630884-dfae-422c-af1c-8cd4419071b7', '041602', '0416', '湿热毒聚', 1, '', '结块四周稍红，略肿压痛。舌红，苔薄，脉微数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ee8ee80b-86cc-410f-861c-bad1cb4b3b4c', 'a85fb0dd-d808-480a-b9ed-bae0f84832e6', '041701', '0417', '痰瘀互结', 1, '', '皮厚结块圆短如茧，呈蜡黄色，或有压痛。舌红，苔薄，脉滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7e93e838-743d-4aba-b091-45f7cf96ddb0', 'a85fb0dd-d808-480a-b9ed-bae0f84832e6', '041702', '0417', '痰结毒滞', 1, '', '结块肿胀，四周紫红，压痛明显。舌红，苔薄，脉微数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '27c31762-33d6-4bd3-8485-08e5cb53d382', '3e6f90a6-be93-4726-b98b-d6a39c709c67', '041801', '0418', '湿热浸淫', 1, '', '发病急，皮损潮红灼热，瘙痒无休，渗液流汁。伴身热、心烦口渴，大便干，尿短赤。舌质红，苔薄白或黄，脉滑或数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'db353aed-5e1c-48a7-8368-0182be194908', '3e6f90a6-be93-4726-b98b-d6a39c709c67', '041802', '0418', '脾虚湿蕴', 1, '', '发病较缓，皮损潮红，瘙痒，抓后糜烂渗出，可见鳞屑。伴有纳少，神疲，腹胀便溏。舌质淡胖，苔白或腻，脉弦缓。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a8a16ac9-de64-4cb0-9982-585b160dd439', '3e6f90a6-be93-4726-b98b-d6a39c709c67', '041803', '0418', '血虚风燥', 1, '', '病久，皮损色暗或色素沉着，剧痒，或皮损粗糙肥厚。伴口干不欲饮，纳差腹胀。舌淡，苔自，脉细弦。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f23a76dc-bc3d-49ee-bacc-2b69a009e111', 'ad6dcbf2-d476-4b8c-8c8f-c00bde373aee', '041901', '0419', '血虚风燥', 1, '', '皮肤干燥肥厚，瘙痒抓痕血痂。食后腹胀，便秘或溏。舌质淡胖，苔白，脉滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '91477aab-0a89-4311-827e-c7bb5ee41dc1', 'ad6dcbf2-d476-4b8c-8c8f-c00bde373aee', '041902', '0419', '风湿蕴肤', 1, '', '皮肤潮红，瘙痒剧烈，抓之可糜烂渗出。伴神倦，便溏。舌淡，苔薄腻，脉弦滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b1358d96-8b21-4952-a408-e9e73abb785d', 'a32700f2-b85c-49d0-b986-9d356fc642c3', '042001', '0420', '风热犯表', 1, '', '风团鲜红，灼热剧痒。伴有发烧、恶寒、咽喉肿痛，遇热则皮疹加重。舌苔薄白或薄黄，脉浮数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b0c397d2-531d-47e3-883e-5ae19a0f8e6c', 'a32700f2-b85c-49d0-b986-9d356fc642c3', '042002', '0420', '风寒束表', 1, '', '皮疹色白，遇风寒加重，得暖则减，口不渴。舌质淡，舌苔白，脉浮紧。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0aadb2f3-7993-4aac-ae91-9fc0f2c2caa6', 'ca50967f-9424-4e08-bda6-47338520d3b0', '042101', '0421', '风热犯表', 1, '', '风团样菱形红斑，中心有小丘疹或水疱。舌尖红，苔薄白，脉浮数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ae4495d6-6636-463c-b9a9-7b59645b9c0d', 'ca50967f-9424-4e08-bda6-47338520d3b0', '042102', '0421', '胃肠湿热', 1, '', '风团红斑，糜烂结痂。伴脘腹痞胀，大便秘结。舌质稍红，苔白腻，脉弦滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '270f554f-dbf1-4811-b4f0-996b9ce2d055', '57ba211e-598e-4b2d-99f9-cf2c63271bdc', '042201', '0422', '湿毒蕴肤', 1, '', '皮损处呈红斑、水疱，甚则糜烂渗液，表皮剥脱。伴剧痒，烦躁，口干，大便燥结，小便黄赤，或有发热。舌质红，苔薄白或黄，脉滑或数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a96d89c1-88a8-4a33-afab-a391f266ac6a', '57ba211e-598e-4b2d-99f9-cf2c63271bdc', '042202', '0422', '热毒入营', 1, '', '皮疹鲜红或紫红，甚则紫斑、血疱，高热神志不清，口唇焦燥，口渴不欲饮，大便干，小便短赤。舌质绛，苔少或镜面舌，脉洪数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8ba51a18-4dc3-47cc-b114-bc770e2d77a1', '57ba211e-598e-4b2d-99f9-cf2c63271bdc', '042203', '0422', '气阴两虚', 1, '', '皮疹消退，伴低热，口渴，乏力，气短，大便干，尿黄。舌红，少苔，脉细数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8f636609-aae2-4d4c-8a40-9004a894bbb0', '6688fcdb-4463-4c15-b8f4-069d2cfdea35', '042301', '0423', '热毒炽盛', 1, '', '发病急骤，水疱迅速扩展或增多，糜烂面鲜红。身热口渴，便干溲赤。舌质红绛，苔少或黄，脉弦滑或数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0d39bade-9f26-4324-ab27-18b81f8e4f24', '6688fcdb-4463-4c15-b8f4-069d2cfdea35', '042302', '0423', '心火炽盛', 1, '', '口腔糜烂或疮面色红，心烦口渴，小便短赤。舌质红，苔黄，脉数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0f91cfa1-d87e-43e4-87e7-262ab84a004f', '6688fcdb-4463-4c15-b8f4-069d2cfdea35', '042303', '0423', '湿热蕴结', 1, '', '糜烂面大或湿烂成片，口渴不欲饮或恶心呕吐。舌质红，苔黄腻，脉滑数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3dd12435-cba9-4134-85f2-da911042f671', '6688fcdb-4463-4c15-b8f4-069d2cfdea35', '042304', '0423', '脾虚湿蕴', 1, '', '结痂，较厚而不易脱落，或疱壁紧张，潮红不著。倦怠乏力，腹胀便溏。舌淡胖，苔白腻，脉沉缓。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '96c7998c-75a0-4db8-9ddb-f48c3e3c9e7c', '6688fcdb-4463-4c15-b8f4-069d2cfdea35', '042305', '0423', '气阴两伤', 1, '', '病程日久，已无水疱出现。倦怠无力，气短懒言，或五心烦热。舌质淡红，苔少或苔剥，脉沉细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '26703595-716a-47aa-a6fe-6599405e7d70', 'a18c45c7-6a82-4cb6-97d4-e57c29234351', '042401', '0424', '脾虚湿蕴', 1, '', '水疱簇集，呈丘疱疹、风团样瘙痒剧烈。腹胀纳呆，便溏，四肢沉重。舌质淡，苔白，脉弦滑。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '12610b6f-d0a4-4591-ab85-b60bbc8f1c2f', 'a18c45c7-6a82-4cb6-97d4-e57c29234351', '042402', '0424', '气阴两伤', 1, '', '病程日久，无新疱发生。烦躁不安，倦怠懒言，周身无力，或五心烦热。舌质淡红少苔，脉沉细。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '30af0bae-ba36-4c8b-bf55-6a9a9b459612', '7459fbd6-4a60-4da1-bd51-f532edb3ab40', '042501', '0425', '热入营血', 1, '', '群集性脓疱，基底潮红。伴高热，畏寒，小便短赤，大便干燥。舌质红绛，苔黄或腻，脉弦滑或数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd457a757-4f75-41aa-9919-3294034ef190', '7459fbd6-4a60-4da1-bd51-f532edb3ab40', '042502', '0425', '气阴两伤', 1, '', '病程日久，已无新脓疱发生。皮肤淡红，脱屑，气短乏力，或五心烦热。舌质淡红，少苔，脉细数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4e128b01-b470-4ccd-ae70-83afd100d810', 'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', '042601', '0426', '热毒炽盛', 1, '', '相当于系统性红蝴蝶疮急性活动期。面部蝶形红斑鲜艳，皮肤紫斑。伴有高热，烦躁口渴，神昏谵语，抽搐，关节肌肉疼痛，大便干结，小便短赤。舌质红绛，苔黄腻，脉洪数或细数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd66d6c84-52dc-439c-9ca0-2652449a31bf', 'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', '042602', '0426', '气阴两伤', 1, '', '斑疹暗红。伴有不规则发热或持续低热，手足心热，心烦无力，自汗盗汗，面浮红，关节痛，足跟痛，月经量少或闭经。舌红，苔薄，脉细数。', '2008/04/09 04:59:43', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c1fc8ea2-6912-495d-83ae-7d75fa2e03d9', 'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', '042603', '0426', '脾肾阳虚', 1, '', '面色无华，眼睑、下肢浮肿，胸胁胀满，腰膝酸软，面热肢冷，口干不渴，尿少或尿闭。舌质淡胖，苔少，脉沉细。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '73e48bf1-475a-43f7-b5d9-7f106d1f7cf3', 'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', '042604', '0426', '脾虚肝旺', 1, '', '皮肤紫斑。胸胁胀满，腹胀纳呆，头昏头痛，耳鸣失眠，月经不调或闭经。舌紫暗或有瘀斑，脉细弦。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bad33678-491d-4337-bb60-c737fd57be10', 'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', '042605', '0426', '气滞血瘀', 1, '', '多见于盘状局限型及亚急性皮肤型红蝴蝶疮。红斑暗滞，角栓形成及皮肤萎缩。伴倦怠乏力。舌暗红，苔白或光面舌，脉沉细。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '821b4695-cc7e-4b8c-bcde-7af73ef795f8', 'ddef2ce8-a9dd-4f36-aadf-37d00cdc9d46', '042701', '0427', '寒湿阻滞', 1, '', '多见于局限性皮痹。摸之坚硬，蜡样光泽，手捏不起，渐有萎缩。舌质淡或暗，苔薄白，脉沉缓或迟。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '923a1023-5c2b-45bc-a098-3592d4680670', 'ddef2ce8-a9dd-4f36-aadf-37d00cdc9d46', '042702', '0427', '脾肾阳虚', 1, '', '多见于系统性皮痹。初起皮损处水肿，逐渐变硬萎缩。自觉乏力，畏寒肢冷，关节痛甚至活动受限，腹胀纳呆，大便溏泻，月经不调或停经。舌淡胖嫩或边有齿痕，脉沉伏。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '85237a1a-9c8a-4642-bfc0-3f91b5b7c56d', 'ef9fb9d7-50f7-450e-9d78-e28e30b03d96', '042801', '0428', '湿热毒结', 1, '', '多见于急性发作期。下肢红斑结节，症见高热，心烦汗出，口舌生疮，灼热疼痛，关节酸痛，胸胁闷胀，口苦咽干，妇女带下黄稠。舌红，苔黄，脉弦滑数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '30444e90-83ef-40bd-975e-cdd558ad6f02', 'ef9fb9d7-50f7-450e-9d78-e28e30b03d96', '042802', '0428', '肝肾阴虚', 1, '', '，口、眼、外阴部溃疡时轻时重，反复发作，低热缠绵，手足心热，头昏目眩，口干咽燥，遗精盗汗，月经不调，腰膝酸软。舌红少津或裂纹舌、光面舌，脉细数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '35eae6fc-e872-4dce-af2b-fe3ad8cd1614', 'ef9fb9d7-50f7-450e-9d78-e28e30b03d96', '042803', '0428', '脾肾阳虚', 1, '', '反复出现口腔、外阴溃疡，遇冷加重。病程长，全身乏力，少气懒言，手足不温，食欲不振，畏寒肢冷，下肢浮肿，大便溏稀，遗精阳萎，月经不调。舌淡，苔白，脉沉细。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fa29efa0-1af1-4a72-8267-46d7f1ca3fc5', 'e111efd8-506c-4d38-8d8f-9c0d1f49409b', '042901', '0429', '湿热蕴结', 1, '', '发病急，皮损鲜红，中心水疱明显。发热，咽痛，口干，关节痛，便干，尿黄。舌质红，苔白或微黄，脉弦滑或微数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '979d8a37-6205-407f-bcee-2ed995aceeb4', 'e111efd8-506c-4d38-8d8f-9c0d1f49409b', '042902', '0429', '寒湿阻络', 1, '', '皮疹暗红，遇寒加重。下肢沉重，关节痛，小便清长。舌质淡，苔白，脉沉细或缓。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fff8988d-78da-4fa0-bcdf-82a3014722e5', '1b49b96f-a69c-4477-b4b9-48ac0576b0e5', '043001', '0430', '湿热瘀阻', 1, '', '发病急骤，皮下结节，略高出皮面，灼热红肿。伴头痛，咽痛，关节痛，体温增高，口渴，大便干，小便黄。舌质微红，苔白或腻，脉滑微数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3fd64c55-1a31-4749-8439-da6a362397a5', '1b49b96f-a69c-4477-b4b9-48ac0576b0e5', '043002', '0430', '寒湿入络', 1, '', '皮损暗红，反复缠绵不愈。伴有关节痛，遇寒加重，肢冷，口不渴，大便不干。舌质淡，苔白或白腻，脉沉缓或迟。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a13be79e-e199-4353-b6df-9a467ede7480', '6ce15cfc-1f03-4ab5-88b8-486ef03f3867', '043101', '0431', '风热蕴肤', 1, '', '发病急骤，皮损呈圆形或椭圆形淡红斑片，中心有细微皱纹，表面少量细糠状鳞屑，伴心烦口渴，大便干，尿微黄。舌质红，苔白或薄黄，脉浮微数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cb9767fa-4597-4220-8090-a571aff37da3', '6ce15cfc-1f03-4ab5-88b8-486ef03f3867', '043102', '0431', '风热血燥', 1, '', '斑片鲜红或紫红，鳞屑较多，瘙痒较剧，伴有抓痕血痂。舌红，苔少，脉弦数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b61774b7-4f2d-49fd-a073-46a4171ae352', 'fbcc7440-3df6-46e8-8543-172c364fdd6e', '043201', '0432', '风热血燥', 1, '', '皮损鲜红，皮疹不断出现，红斑增多，刮去鳞屑可见发亮薄膜，点状出血，有同形反应。伴心烦口渴，大便干，尿黄。舌质红，舌苔黄或腻，脉弦滑或数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a43b2ca2-d9a3-4802-90b4-0cd55e72b98c', 'fbcc7440-3df6-46e8-8543-172c364fdd6e', '043202', '0432', '血虚风燥', 1, '', '皮损色淡，部分消退，鳞屑较多。伴口干，便干。舌质淡红，苔薄白，脉细缓。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5286bf1c-f472-48ab-87b7-bd15c31f5ff9', 'fbcc7440-3df6-46e8-8543-172c364fdd6e', '043203', '0432', '瘀滞肌肤', 1, '', '皮损肥厚浸润，颜色暗红，经久不退。舌质紫暗或见瘀斑、瘀点，脉涩或细缓。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7fb8c460-3e9c-466b-ac9b-577c1a05a671', 'e7e4cbf2-07e9-4992-ad53-bfecaf99ae63', '043301', '0433', '肝郁化火', 1, '', '皮损色红，心烦易怒，失眠多梦，眩晕，心悸，门苦咽干。舌边尖红，脉弦数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8d271cc8-f589-4b09-a0d4-832f73a98b62', 'e7e4cbf2-07e9-4992-ad53-bfecaf99ae63', '043302', '0433', '风湿蕴肤', 1, '', '皮损呈淡褐色片状，粗糙肥厚，剧痒时作，夜间尤甚。苔薄或白腻，脉濡而缓。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ccc22f7d-930d-4c19-81cb-b34e7400df9b', 'e7e4cbf2-07e9-4992-ad53-bfecaf99ae63', '043303', '0433', '血虚风燥', 1, '', '皮损灰白，抓如枯木，肥厚粗糙似牛皮，心悸怔忡，失眠健忘，女子月经不凋。舌质淡，脉沉细。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0e0dd20b-bc14-470e-b035-35e0f1cbca35', '9be8370a-bc77-4305-a99e-a8e9e1c488e5', '043401', '0434', '湿热蕴结', 1, '', '皮疹呈半球形隆起，色红或灰褐，散在孤立，触之坚实，剧痒时作。舌质红，苔薄白、脉滑。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd9615aad-0ea3-4d3e-8188-c49e4f25a215', '9be8370a-bc77-4305-a99e-a8e9e1c488e5', '043402', '0434', '血瘀风燥', 1, '', '皮疹呈结节，色紫红或紫褐，皮肤肥厚，干燥，阵作瘙痒。舌紫暗，苔薄，脉涩。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7bc181e7-3fa8-48dc-b66a-1fab5831db51', '6e4e72f1-8318-456c-95a0-2a350071a4dd', '043501', '0435', '风热阻络', 1, '', '皮疹紫红，表面光滑，中心凹陷，常呈多角形，阵发剧痒，口干欲饮。舌质淡红，苔薄白，脉弦滑。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c605b2f7-8c00-4510-82a5-b025ff76c637', '6e4e72f1-8318-456c-95a0-2a350071a4dd', '043502', '0435', '风湿蕴肤', 1, '', '皮疹呈疣状突起，融合成片，或呈条状，色紫红，剧痒难忍，夜寐欠安，口干不欲饮。舌质暗红，舌苔薄白或微腻，脉缓。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0b2d5685-2628-4037-85a6-dd5dc1fe3715', '6e4e72f1-8318-456c-95a0-2a350071a4dd', '043503', '0435', '虚火上炎', 1, '', '粘膜发疹呈乳白色，或糜烂，有网状条纹。常有头昏，多梦，记忆力差。舌边尖红，苔薄白，脉沉细数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '28a0a305-26f2-4664-83c4-8a1c71b4139a', '6b0ffbd1-4bc5-4b60-acc4-ecf20d859849', '043601', '0436', '气滞血瘀', 1, '', '皮肤白斑，或有气郁不舒及心烦不安。舌淡或有瘀斑，苔薄白，脉缓。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '50dfaac8-d9db-4790-8dfc-77ef1c90c5a2', '6b0ffbd1-4bc5-4b60-acc4-ecf20d859849', '043602', '0436', '肝肾阴虚', 1, '', '白斑，伴倦怠乏力，腰膝酸软，或五心烦热。舌质红，苔少，脉沉细。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4c0cacbd-66ec-48d4-8fd2-3472d5078246', 'ba030faf-6167-491e-9a1b-9cc1d0251b99', '043701', '0437', '气滞血瘀', 1, '', '颜面出现黄褐色斑片，腰膝酸软，或急躁易怒，胸胁胀痛。舌质暗，苔薄白，脉沉细。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '31c238ff-171c-4841-9372-b04f31e78dc1', 'ba030faf-6167-491e-9a1b-9cc1d0251b99', '043702', '0437', '肝肾阴虚', 1, '', '黄斑褐黑，伴腰膝酸软，怠倦无力，身体羸瘦。舌红，苔少，脉沉细。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c7dfc5b2-d0f6-4bf9-966c-36bd354af43a', '1a23a39f-2533-4b70-b25d-834237a8224f', '043801', '0438', '气滞血瘀', 1, '', '皮损坚硬，色淡红或正常皮色，或有痒痛。舌质多暗淡，苔白腻，脉沉缓。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a4bd70e4-26bd-4822-984c-c576108c1e62', '1a23a39f-2533-4b70-b25d-834237a8224f', '043802', '0438', '瘀血阻络', 1, '', '结块高凸，色暗滞，多发生在关节处，肿胀疼痛，影响活动。舌有瘀点，苔薄，脉细。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1398a799-da62-4d86-8539-06b2b50376cd', '8798cc4d-8c99-4383-92ef-c93ed78f3444', '043901', '0439', '肺胃热盛', 1, '', '红斑多发于鼻尖或两翼，压之退色。常嗜酒，便秘，饮食不节，口干口渴。舌质红，苔薄黄，脉弦滑。多见于红斑期。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9c3d777b-91e2-4385-b77e-728a8e122c53', '8798cc4d-8c99-4383-92ef-c93ed78f3444', '043902', '0439', '热毒蕴肤', 1, '', '在红斑上出现痤疮祥丘疹、脓疱。毛细血管扩张明显，局部灼热，口干，便秘。片质红绛，苔黄。多见于丘疹期。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'eafc63fd-3ca1-45e0-93c9-8d38b6647fb4', '8798cc4d-8c99-4383-92ef-c93ed78f3444', '043903', '0439', '气滞血瘀', 1, '', '鼻部组织增生，呈结节状，毛孔扩大。舌质略红，脉沉缓。多见于鼻赘期。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '957cd937-fedf-4145-912f-22d2eb9c596b', '6633b88d-5692-4dd3-aa5c-4ce1e8ae1acc', '044001', '0440', '肺经风热', 1, '', '丘疹色红，或有痒痛。舌红，苔薄黄，脉浮数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b1a91a7b-c23a-4bd9-8310-87c67123cca7', '6633b88d-5692-4dd3-aa5c-4ce1e8ae1acc', '044002', '0440', '湿热蕴结', 1, '', '皮疹红肿疼痛，或有脓疱，口臭，便秘，尿黄。舌红，苔黄腻，脉滑数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a16dcf44-2aa1-48df-a3d8-599321961871', '6633b88d-5692-4dd3-aa5c-4ce1e8ae1acc', '044003', '0440', '痰湿凝结', 1, '', '皮疹结成囊肿，或有纳呆，便溏。舌淡胖，苔薄，脉滑。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '47219dc4-f265-4852-9c34-3bf435db2014', '9e1b6da9-39f5-4d21-b00f-96bdc330b7bb', '044101', '0441', '肺胃热盛', 1, '', '急性发病。皮损色红，并有渗出、糜烂、结痂，痒剧。伴心烦口渴，大便秘结。舌质红，苔黄，脉滑数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '63be9fdf-7c4f-4064-bc6d-0721705c3206', '9e1b6da9-39f5-4d21-b00f-96bdc330b7bb', '044102', '0441', '脾虚湿困', 1, '', '发病较缓。皮损淡红或黄，有灰白色鳞屑，伴有便溏。舌质淡红，苔白腻，脉滑。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cfcfa556-1480-4800-a12b-a12137d2be4e', '9e1b6da9-39f5-4d21-b00f-96bdc330b7bb', '044103', '0441', '血虚风燥', 1, '', '皮肤干燥。有糠秕状鳞屑，瘙痒，头发干燥无光，常伴有脱发。舌质红，苔薄白，脉弦。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'aed31aac-ee3c-4fc6-858b-a4f9281537a4', '432f866d-a069-4bf2-bb0e-78efecf1c511', '044201', '0442', '湿热毒蕴（急性淋病）', 1, '', '尿道口红肿，有尿急、尿频、尿痛、淋沥不止，尿液混浊如脂，尿道口溢脓。严重者尿道粘膜水肿，附近淋巴结红肿疼痛。女性宫颈充血、触痛，并有脓性分泌物，可有前庭大腺红肿热痛等。可有发热等全身症状。舌质红，苔黄腻，脉滑数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4740059f-73c0-4e68-a63b-510550f1e79c', '432f866d-a069-4bf2-bb0e-78efecf1c511', '044202', '0442', '正虚毒恋（慢性淋病）', 1, '', '小便不畅，短涩，淋沥不禁，腰酸腿软，五心烦热。酒后或疲劳易发，食少纳差，女性带下多。舌质淡或有齿痕，苔白腻，脉沉细弱。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9178592d-94fa-4fc2-8966-d02e9712af02', '432f866d-a069-4bf2-bb0e-78efecf1c511', '044203', '0442', '毒邪流窜（伴有合并症者）', 1, '', '前列腺肿痛，拒按，小便溢浊或点滴淋沥，腰酸下坠感。女性有下腹部隐痛，压痛，外阴瘙痒，白带多，或有低热等，全身不适感。舌质红，苔薄黄，脉滑数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '251d969d-fdca-49cd-855a-980c13394dfb', '432f866d-a069-4bf2-bb0e-78efecf1c511', '044204', '0442', '热毒入络（淋病性败血症）', 1, '', '小便灼热刺痛，尿液赤涩，下腹痛，头痛高热，或寒热往来，神情淡漠，面目浮肿，四肢关节痛，心悸烦闷。舌质红绛，苔黄燥，脉滑数。', '2008/04/09 04:59:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'eff1bb17-3764-4deb-93f3-ae6796d812e0', '08ecbb6e-3f5c-4dcb-a5fc-0ad49b845281', '050101', '0501', '风寒束表', 1, '', '发热轻，恶寒重，无汗，鼻塞流涕，喷嚏咳嗽。年长儿可诉肢体疼痛，头痛。舌苔薄白，脉浮紧。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2215fffc-8d55-4964-8f59-700f54f08ad6', '08ecbb6e-3f5c-4dcb-a5fc-0ad49b845281', '050102', '0501', '风热犯表', 1, '', '发热重，恶寒轻，有汗或无汗，头痛，鼻塞流稠涕，咳嗽，咽红。或目赤流泪，烦热口渴。舌质红少津，苔薄黄，脉浮数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '92f6b201-9487-420b-84a5-9f92716086a6', '08ecbb6e-3f5c-4dcb-a5fc-0ad49b845281', '050103', '0501', '暑湿袭表', 1, '', '高热不退，或身热不扬，汗出不畅，头痛，倦怠，泛恶，鼻塞流涕，咳嗽。舌尖红，苔白腻，脉数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c9d47c8b-7a78-4c84-af5f-9d102d0dccaf', '444a26aa-31fe-435a-b7a7-f109a57952fe', '050201', '0502', '风寒袭肺', 1, '', '咳嗽，痰稀色白，鼻塞流清涕。或伴恶寒，无汗，咽部不红。苔薄白，脉浮紧。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f769626c-0dab-41a0-aa05-06b5f7b29ec9', '444a26aa-31fe-435a-b7a7-f109a57952fe', '050202', '0502', '风热犯肺', 1, '', '咳嗽，痰黄而稠，鼻塞，流浊涕，发热恶风，咽红而肿。舌尖红，苔薄白或微黄，脉浮数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c5b18113-2c84-4e08-8a6c-1aae4f4d94cf', '444a26aa-31fe-435a-b7a7-f109a57952fe', '050203', '0502', '痰热壅肺', 1, '', '咳嗽，痰黄白粘稠，咯吐不爽，咳时面赤唇红，或伴发热口渴，咽喉痛。舌质红，苔黄腻，脉滑数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e6675b89-b835-4e98-80ec-24a0e77c3d38', '444a26aa-31fe-435a-b7a7-f109a57952fe', '050204', '0502', '痰湿蕴肺', 1, '', '咳嗽，痰多色白如泡沫，咳时喉有痰声，或呼吸气粗，多下发热。苔白腻，脉滑。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '33c1bb21-87da-48d8-98e6-74be10f53269', '444a26aa-31fe-435a-b7a7-f109a57952fe', '050205', '0502', '肺气亏虚', 1, '', '咳声无力，痰白清稀，面色淡白，体弱多汗，易于感冒。舌淡，脉无力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c2776433-ddc4-4b06-87a6-fc15dc3fe755', '444a26aa-31fe-435a-b7a7-f109a57952fe', '050206', '0502', '肺阴亏虚', 1, '', '干咳无痰，或痰少而粘，不易咯出，口渴咽干，咳声嘶哑，手足心热。舌红苔少，脉细数。', '2008/04/10 12:04:44', '', 3 );
