INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '087078fa-58b7-482d-87d8-73bc7bf8915e', 'f640ec4f-9c56-42b7-830f-df869ff382eb', '050401', '0504', '风寒袭肺', 1, '', '恶寒发热，无汗不渴，咳嗽气急，痰稀色白。舌质淡红，苔薄白，脉浮紧。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '07a9f500-d481-4d0a-a5eb-914b43bf2cbf', 'f640ec4f-9c56-42b7-830f-df869ff382eb', '050402', '0504', '风热犯肺', 1, '', '发热恶风，微有汗出，口渴欲饮，咳嗽，痰稠色黄，呼吸急促，咽红。舌尖红，苔薄黄，脉浮数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '36cf1ef0-cfd4-47d8-b040-c8cb36376310', 'f640ec4f-9c56-42b7-830f-df869ff382eb', '050403', '0504', '痰热壅肺', 1, '', '壮热烦躁，喉间痰呜，痰稠色黄，气促喘憋，鼻翼煽动，或口唇青紫。舌质红，苔黄腻，脉滑数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f4cecfee-2ca8-4adc-82ad-12159c3d620c', 'f640ec4f-9c56-42b7-830f-df869ff382eb', '050404', '0504', '阴虚肺热', 1, '', '病程延长，低热出汗，面色潮红，干咳无痰。舌质红而干，苔光剥，脉细数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a8664158-a26d-40b9-8f1d-7859fce14fb6', 'f640ec4f-9c56-42b7-830f-df869ff382eb', '050405', '0504', '肺脾气虚', 1, '', '病程延长，低热起伏，气短多汗，咳嗽无力，纳差，便溏，面色淡白，神疲乏力，四肢欠温。舌质偏淡，苔薄白，脉细无力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '937cd0a7-49f2-4180-8bb5-57eef29aad08', 'fb8b15c6-1cfe-43c9-9eeb-dc384a7b2854', '050501', '0505', '心脾积热', 1, '', '口舌满布白屑，周围红赤，面赤唇红，口于喜饮，大便干，小便黄。舌质红，苔黄厚腻，脉数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9fd350aa-52d9-4e33-af42-6e90d39b052c', 'fb8b15c6-1cfe-43c9-9eeb-dc384a7b2854', '050502', '0505', '虚火上浮', 1, '', '口舌散布白屑，周围淡红，形体怯弱，面白颧红，口干不渴。舌质嫩红，脉细数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '063330b0-d676-41de-8c39-202ce17f2b53', '3c13642f-29cd-4825-a54a-e792876e0f63', '060101', '0601', '青枝型骨折', 1, '', '多见于幼儿，骨折处形成向上弯曲的弓形。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '96cb8378-d1db-4ab8-bdd9-7d7440fc9159', '5af95408-93ad-41f4-aea9-a294ee166765', '060403', '0604', '外展型骨折', 1, '', '伤肩疼痛，压痛，中度肿胀，内侧有瘀斑，断端外侧嵌插内侧分离，向前内侧突起成角畸形，或远端完全向内侧移位，可有骨擦音，肩关节功能受限。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9b2761d8-6310-468a-95e4-a65721a99b89', '5af95408-93ad-41f4-aea9-a294ee166765', '060404', '0604', '内收型骨折', 1, '', '伤肩肿胀，疼痛，压痛，纵轴叩击痛，可触及骨擦音，断端外侧分离内侧嵌插，向外侧突起成角，肩关节功能受限。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2db467fd-77ed-45d2-bd70-4112b0200124', '5af95408-93ad-41f4-aea9-a294ee166765', '060405', '0604', '骨折合并关节脱位', 1, '', '方肩畸形，肩峰下空虚，晓突下或腋窝部常可触及脱位的肱骨，头，上计无固定的外展畸形。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b1d75ba4-c9b5-4530-a2e1-feff9f3d3403', '8e01dd06-b1cc-42fb-9a04-8ab3838b0a2c', '060501', '0605', '上1／3骨折', 1, '', '多由直接暴力所致。伤肢明显压痛，叩击痛，功能障碍，伤肢可有短缩畸形，骨折近端向前、内移位，远端多向上移位。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1e096da4-c524-44eb-9193-0ec053f59319', '8e01dd06-b1cc-42fb-9a04-8ab3838b0a2c', '060502', '0605', '中1／3骨折', 1, '', '多由直接暴力所致。伤肢压痛，叩击痛，功能受限。近端多向外前移位，远端向上移位，中下1／3骨折常可损伤桡神经。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '44e86609-7a82-449c-9127-ff66cec31cd4', '8e01dd06-b1cc-42fb-9a04-8ab3838b0a2c', '060503', '0605', '下1／3骨折', 1, '', '多由间接暴力所致。伤肢下段明显肿胀、压痛，可有成角、缩短及内旋畸形，骨折线常呈斜形或螺旋形。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '67e5a795-8f0e-4076-a7dd-0d508146e086', 'f9d1e242-9d28-44a1-9285-b348952872e0', '060601', '0606', '伸直型骨折', 1, '', '伤肢肘部肿胀或靴样畸形，髁上处压痛敏锐，时屈伸功能障碍，骨折远端向后上移位，折线多从前下方斜向后上方。可合并血管、神经损伤。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f5a66a2d-4e25-4e6c-8a91-3d9626e9c249', 'f9d1e242-9d28-44a1-9285-b348952872e0', '060602', '0606', '屈曲型骨折', 1, '', '伤肢肿胀，髁上处压痛，功能受限，骨折远端向前上方移位，折线从后下方斜向前上方。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '78de46fa-3034-46dc-a534-0c804343dacc', 'e17c7384-354b-493a-bf9b-635419936b26', '060701', '0607', '无移位型', 1, '', '骨折无移位或轻度移位。肘部肿胀，压痛，肘后三角关系无改变。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '43eed758-e1d7-4f2c-a6b2-62d9f52e088d', 'e17c7384-354b-493a-bf9b-635419936b26', '060702', '0607', '伸直内翻型Ⅰ°', 1, '', '髁间折线偏向内侧并向内上方延续，内上髁及其上方的骨质完整，折块向肘后方移位。肘部肿胀明显，横径增宽，肘后三角关系改变，尺骨鹰嘴后突。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cd94b8c3-199a-4aae-9650-597e52b82990', 'e17c7384-354b-493a-bf9b-635419936b26', '060703', '0607', '伸直内翻型Ⅱ°', 1, '', '折线走向同Ⅰ°，内上髅上方有一蝶形三角骨折片，但并未分离。肘部肿胀明显，横径增宽，肘后三角关系改变，尺骨鹰嘴后突。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e970999d-497f-4717-94de-bb2c162a4e7e', 'e17c7384-354b-493a-bf9b-635419936b26', '060704', '0607', '伸直内翻型Ⅲ°', 1, '', '折线走向同Ⅰ°，内侧之三角形骨折片可完全分离。肘部肿胀明显，横径增宽，肘后三角关系改变，尺骨鹰嘴后突。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c6b9bf48-01a1-42e0-909a-f38dfdb446f2', 'e17c7384-354b-493a-bf9b-635419936b26', '060705', '0607', '屈曲内翻型Ⅰ°', 1, '', '折线呈"T"形或类似伸直内翻型Ⅰ°，但折块移向肘前方。肘部肿胀明显，横径增宽，肘后三角关系改变，可扪及骨擦感。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '75b3b293-4e31-464b-abd2-dfd0f041059f', 'e17c7384-354b-493a-bf9b-635419936b26', '060706', '0607', '屈曲内翻型Ⅱ°', 1, '', '与伸直内翻Ⅱ°相似，但骨折三角形折片的形状不如伸直型典型。肘部肿胀明显，横径增宽，肘后三角关系改变，可扪及骨擦感。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2826d91d-c91b-4c04-a1e6-cc189da495b4', 'adabeb3b-f9c7-4832-8531-d9abad7d659c', '060801', '0608', 'Ⅰ型', 1, '', '无移位骨折，肘外侧轻度肿胀，压痛，肪桡关节关系良好。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3ef540ea-e25a-46e7-b66a-ded31a7a5704', 'adabeb3b-f9c7-4832-8531-d9abad7d659c', '060802', '0608', 'Ⅱ型', 1, '', '折块轻度外移，肢桡关节解剖关系良好，肘外侧肿胀，可扪及外髁轻度隆突。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5b6818d5-c1f9-43a1-b236-2a4af77057ec', 'adabeb3b-f9c7-4832-8531-d9abad7d659c', '060803', '0608', 'Ⅲ型', 1, '', '折块倾斜移位，肢桡关节倾斜，肘外侧可扪及外踝隆突，弹性固定，或可扪及骨擦音。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c0916df8-d1ae-4102-8ac7-6366acc53d55', 'adabeb3b-f9c7-4832-8531-d9abad7d659c', '060804', '0608', 'Ⅳ型前移翻转型', 1, '', '折块向前翻转，于肘前外侧可扪及折块。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e9bc4ad3-3eee-474d-9f82-553943afcef1', 'adabeb3b-f9c7-4832-8531-d9abad7d659c', '060805', '0608', 'Ⅳ型后移翻转型', 1, '', '折块向后翻转，于肘后外侧可扪及折块。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '31356ff0-89df-4dd7-a837-b6681ed3c829', '3f1655cb-fff4-4682-b196-bcde646d4abb', '060901', '0609', 'Ⅰ°', 1, '', '裂缝骨折或骨折轻度移位，骨膜尚未完全断离，肘内侧牵拉性疼痛，轻度肿胀。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c2f37260-5043-4f86-878e-a433c1449187', '3f1655cb-fff4-4682-b196-bcde646d4abb', '060902', '0609', 'Ⅱ°', 1, '', '骨折块有分离和轻度旋转，肘内侧压痛，可扪及活动的骨折块，关节活动轻度障碍，肱骨髁间距增宽。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4b3ff4cf-83cb-4083-8d99-9d8b55dc4a9d', '3f1655cb-fff4-4682-b196-bcde646d4abb', '060903', '0609', 'Ⅲ°', 1, '', '内上髁撕脱和完全旋转，折块进入关节腔内，肘内侧肿胀，有皮下瘀斑及压痛，可损伤尺神经。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fa4d4fe1-88cd-4938-8a80-4fdcbf56a1d4', '3f1655cb-fff4-4682-b196-bcde646d4abb', '060904', '0609', 'Ⅳ°', 1, '', '骨折块旋转，肘关节肿胀严重，甚至出现张力性水泡，疼痛，关节明显畸形，有弹性固定，肘后三点关系异常，可损伤尺神经。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b510d8eb-6da4-4a82-901b-0d87f5fea995', '15724e8a-141e-404c-b503-aae52065532d', '061001', '0610', '完全骨折', 1, '', '骨折发生在肱骨小头基底部，骨折线在冠状面上，骨折块本身包含全部肱骨小头及滑车外1／3或外侧1／2.或只限于肱骨小头本身，肘外侧肿胀，肘前可扪及折块。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b0d84e59-b86f-4022-9c8d-129915899493', '15724e8a-141e-404c-b503-aae52065532d', '061002', '0610', '部分骨折', 1, '', '骨折块仅包含肱骨小头关节软骨及其下方少量骨质，肘外侧可扪及较小折块。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3aa73dbc-c357-4d80-ae58-53edf849d9dc', '15724e8a-141e-404c-b503-aae52065532d', '061003', '0610', '肱骨小头关节软骨挫伤', 1, '', '肘外侧肿胀，活动功能受限。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '459bd4fd-018b-401c-82f2-e1b3fe5beb48', 'cdacee1c-f8ea-4a52-b1a7-bf8a79034c8e', '061101', '0611', '无移位型', 1, '', '骨折端分离小于2mm，肘后肿胀，主动伸肘功能障碍不明显。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'af588c59-a50f-400e-a1c9-ba69c996ba0e', 'cdacee1c-f8ea-4a52-b1a7-bf8a79034c8e', '061102', '0611', '移位型Ⅰ°', 1, '', '撕脱骨折。折块小，仅为三头肌腱止点部肿胀，伸时障碍。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2e5d232a-b49d-43fc-ae26-77f5ec47f6ff', 'cdacee1c-f8ea-4a52-b1a7-bf8a79034c8e', '061103', '0611', '移位型Ⅱ°', 1, '', '横骨折或斜骨折。可剂及折块较大，分离移位，肿胀。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'af49f679-908f-4fb7-ad54-a5c2324800dc', 'cdacee1c-f8ea-4a52-b1a7-bf8a79034c8e', '061104', '0611', '移位型Ⅲ°', 1, '', '粉碎骨折。肿胀明显，可扪及明显骨擦音。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7368aa56-a40f-4091-9eef-1b62365aed84', 'cdacee1c-f8ea-4a52-b1a7-bf8a79034c8e', '061105', '0611', '移位型Ⅳ°', 1, '', '骨折合并肘关节脱位。上臂缩短，时三角关系改变，屈时90°畸形，时屈伸受限。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3d9ec598-24f2-4bb1-bc34-6b860a0bac30', '006c660e-7aa6-4a9d-94af-2c71919ea1bb', '061201', '0612', '裂纹骨折', 1, '', '骨折无移位或移位小于1mm。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'faf4146e-8fb4-4452-99a1-f55ec74c6db1', '006c660e-7aa6-4a9d-94af-2c71919ea1bb', '061202', '0612', '劈裂骨折', 1, '', '桡骨头纵行骨折，骨折块移位大于1mm。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd528ee11-9530-4847-a5a8-d64937bc82a9', '006c660e-7aa6-4a9d-94af-2c71919ea1bb', '061203', '0612', '嵌插性骨折', 1, '', '骨折端嵌插，或呈"歪戴帽"样。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ee062849-c87b-4efb-a08b-651d8022729d', '006c660e-7aa6-4a9d-94af-2c71919ea1bb', '061204', '0612', '粉碎性骨折', 1, '', '桡骨头粉碎。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bef51682-668e-4d96-a485-eeb51cf45bd5', 'a412cad7-963c-4f89-9155-fa0daa67323a', '061301', '0613', '伸直型', 1, '', '尺骨任何水平骨折，向掌侧成角，合并桡骨头前脱位，于肘关节前外侧可扪及桡骨头圆滑之关节面。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bde58e95-c560-4211-b7ec-39484fcf48bd', 'a412cad7-963c-4f89-9155-fa0daa67323a', '061302', '0613', '屈曲型', 1, '', '尺骨干骨折，向背侧成角，合并桡骨头后脱位，于肘关节后方或后外侧可扪及桡骨头圆滑之关节面。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8a7459f5-f914-42d2-abc3-3ece3bdd3719', 'a412cad7-963c-4f89-9155-fa0daa67323a', '061303', '0613', '内收型', 1, '', '尺骨近侧干能端骨折，合并桡骨头的外侧或前侧脱位，仅可见''HL袁，症见尺骨干骺部有明显压痛，于肘关节外侧可扪及桡骨头隆突。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e8501d6d-ba4e-4399-9c18-135a975b24cc', 'a412cad7-963c-4f89-9155-fa0daa67323a', '061304', '0613', '特殊型', 1, '', '桡骨头前脱位，桡骨头近1／3骨折，尺骨任何水平骨折，于肘前方可扪及桡骨头圆滑之关节面，前臂双骨干力消失。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9c9aad68-0aa4-4104-9c71-48ca7221cf28', 'e4cad3f3-0e33-4990-ad34-2ba7cac26b28', '061401', '0614', '青枝骨折', 1, '', '伤肢轻度肿胀，骨膜增粗或成角。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '31e05795-e2d9-431d-83cc-1e53564001c9', 'e4cad3f3-0e33-4990-ad34-2ba7cac26b28', '061402', '0614', '无移位骨折，伤肢轻度肿胀，压痛，活动功能障碍，畸形不明显。', 1, '', '', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c58ee453-83ae-46da-92e5-09fb3329dfa7', 'e4cad3f3-0e33-4990-ad34-2ba7cac26b28', '061403', '0614', '有移位骨折直接暴力所致者', 1, '', '伤肢在同一水平处压痛，肿胀，有假关节活动，功能障碍。骨折线多在同一水平面上，呈粉碎或横断形。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4e3256e1-e1cf-4c24-9ba8-24dc0819e2df', 'e4cad3f3-0e33-4990-ad34-2ba7cac26b28', '061404', '0614', '有移位骨折间接暴力所致者', 1, '', '伤肢肿胀，桡骨上段尺骨下段压痛甚，旋转功能障碍，桡骨骨折线多在上，尺骨骨折线多在下。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ae0c1a0f-6cf3-4b1a-92a4-49495985dfa4', 'e4cad3f3-0e33-4990-ad34-2ba7cac26b28', '061405', '0614', '有移位骨折扭转暴力所致者', 1, '', '伤肢肿胀，桡骨下段尺骨上段压痛甚，旋转功能障碍，尺骨骨折线在上，桡骨骨折线在下。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f821cc8f-236d-46ed-b308-27a52d75b842', '7f5ca4ad-11d2-49fd-a038-f91f5adf778a', '061501', '0615', 'Ⅰ型', 1, '', '桡骨远端青枝骨折并尺骨下端骨骺分离，儿童骨折多为本型。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8307640e-9df4-4c8c-86dc-b85f683329b1', '7f5ca4ad-11d2-49fd-a038-f91f5adf778a', '061502', '0615', 'Ⅱ型', 1, '', '桡骨干下1／3横断，螺旋或斜形骨折，骨折端移位较大，下尺桡关节明显脱位。症见桡下1／3压痛，畸形，可扪及骨擦音，尺骨头隆突，稳定性差，腕横径增宽。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8d84da44-8089-4d8b-98f2-9ee043104fa4', '7f5ca4ad-11d2-49fd-a038-f91f5adf778a', '061503', '0615', 'Ⅲ型', 1, '', '桡骨下1／3骨折，下尺桡关节脱位，尺骨干骨折或不完全性骨折，桡骨下1／3可扪及骨擦音，或尺骨成角畸形。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6feab5e6-3dca-487b-9748-4549f22e16d4', '35887e55-ee2d-4844-85f7-eaa254d7aae0', '061601', '0616', '无移位型', 1, '', '骨折无移位，或可为轻度嵌入骨折，腕关节轻度肿胀，无明显畸形，折端有环形压痛，纵轴冲击痛，前臂旋转功能障碍。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '42573efe-463d-488a-8dc9-acadec816023', '35887e55-ee2d-4844-85f7-eaa254d7aae0', '061602', '0616', '伸直型。远端向背侧移位，前臂下端呈"餐叉样"畸形，腕背侧可扪及骨折远端骨突。', 1, '', '', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b1e0c892-6efc-42cf-b267-c6bdc13e900d', '35887e55-ee2d-4844-85f7-eaa254d7aae0', '061603', '0616', '屈曲型', 1, '', '远折端向掌侧移位，可并尺桡下关节脱位，腕关节掌侧可扪及骨折远端骨突，畸形与伸直型相反。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f84f6c59-d027-41a7-9ce8-f95f4643433d', '35887e55-ee2d-4844-85f7-eaa254d7aae0', '061604', '0616', '半脱位型', 1, '', '桡骨远端背侧缘骨折，合并腕关节半脱位，腕关节肿胀，畸形呈半脱位状，腕横径增宽。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '38f0d1ec-a00f-4a43-98c1-103eff505e7f', '8eee9047-c549-4206-aa27-ed7ad042e8af', '061701', '0617', 'Ⅰ型', 1, '', '结节部骨折。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9f0af8a3-d6d1-4127-9ba1-16e5eac76e5e', '8eee9047-c549-4206-aa27-ed7ad042e8af', '061702', '0617', 'Ⅱ型', 1, '', '远端骨折。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '82c4665c-90c5-4b87-baff-2226c8beb338', '8eee9047-c549-4206-aa27-ed7ad042e8af', '061703', '0617', 'Ⅲ型', 1, '', '腰部骨折。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '53796021-31dd-4a28-9b41-8e796adb32be', '8eee9047-c549-4206-aa27-ed7ad042e8af', '061704', '0617', 'Ⅳ型', 1, '', '近端骨折。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '35f6dd0d-3f98-400d-b51c-38ce868561b3', 'd3ea8072-6dda-44e0-82cf-9bf0fb137eef', '061801', '0618', '掌骨头骨折', 1, '', '指掌关节处瘀肿，疼痛，指掌关节活动功能受限。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7418c22c-5c35-48a9-9456-a065af85fa44', 'd3ea8072-6dda-44e0-82cf-9bf0fb137eef', '061802', '0618', '掌骨颈骨折', 1, '', '指掌关节畸形，掌骨头向掌侧屈曲，掌指关节过伸。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7d340030-006e-46d2-8073-910e896422d2', 'd3ea8072-6dda-44e0-82cf-9bf0fb137eef', '061803', '0618', '掌骨干骨折', 1, '', '掌骨中段处瘀肿，压痛，纵轴叩击痛，骨折多向背侧成角及侧方移位，掌骨头可有凹陷。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9947fc35-96e6-44d1-89ef-1bdb5a6b2928', 'd3ea8072-6dda-44e0-82cf-9bf0fb137eef', '061804', '0618', '掌骨基底部骨折', 1, '', '手掌腕处瘀肿，压痛，腕关节活动功能障碍。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'df492e54-b2b0-4d8b-aa67-ea691a96cd9d', '547bf026-3139-4696-abe0-59fa55097f2a', '061901', '0619', '指骨头骨折', 1, '', '局部肿胀，疼痛，指间关节侧向活动可增大。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'eedb532b-90fe-444a-991d-cfcd7887ae75', '547bf026-3139-4696-abe0-59fa55097f2a', '061902', '0619', '指骨颈骨折', 1, '', '骨折向掌侧成角，远端可向背侧旋转，伤指背侧可扪及圆滑之指骨头。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9b190a59-c083-4e68-9db1-16725bac3105', '547bf026-3139-4696-abe0-59fa55097f2a', '061903', '0619', '指骨干骨折', 1, '', '骨折多向掌侧成角，局部压痛，纵轴冲击痛，有异常活动。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4d889a65-4371-417b-8616-09cb4ea9286f', '547bf026-3139-4696-abe0-59fa55097f2a', '061904', '0619', '末节指骨基底部骨折', 1, '', '骨折处肿胀，压痛，指屈伸功能受限，可呈锤状指。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e5d0d7ea-d216-4641-8ed0-355c68fcbfad', '301dcdb3-cd13-43a0-b06d-f196d55408be', '062001', '0620', '外展型', 1, '', '伤肢呈外旋畸形，骨折线与股骨干纵轴的垂直线所成的夹角小于30°。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '48567a40-1f35-4fd2-9a2c-0d772257ea95', '301dcdb3-cd13-43a0-b06d-f196d55408be', '062002', '0620', '内收型', 1, '', '伤肢呈内收外旋、短缩畸形，骨折线与股骨干纵轴的垂直线所成的夹角大于30°。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '887771fe-b8f7-4dbf-aeb3-6ba1d47a462e', 'cbbf4a23-043b-4f53-b722-9326538261d1', '062101', '0621', '顺粗隆间型', 1, '', '伤肢有短缩，内收，外旋畸形，骨折线自大粗隆顶点开始，斜向内下方行走，达小粗隆部。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9cb1a9f7-cd32-4461-9e37-65c21b5c7b95', 'cbbf4a23-043b-4f53-b722-9326538261d1', '062102', '0621', '反粗隆间型', 1, '', '伤肢有短缩，外展，外旋略形，骨折线自大粗隆下方斜向内上方，达小粗隆的上方。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '44d7525f-b0b8-46f5-8fb1-81785e5091a0', 'cbbf4a23-043b-4f53-b722-9326538261d1', '062103', '0621', '粗隆下型', 1, '', '骨折线经过大小粗隆的下方。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e0c7f53d-3108-48d6-b44b-41d882095ef5', '44263bb5-79f6-436d-83a4-23763335a5ec', '062201', '0622', '上1／3骨折', 1, '', '骨折近端屈曲、外展、外旋移位，骨折远段向后、向上、向内移位。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f470f122-0dd2-421e-a351-cca17cc14914', '44263bb5-79f6-436d-83a4-23763335a5ec', '062202', '0622', '中1／3骨折', 1, '', '除两骨折端重叠外，骨折远端多有向外成角和向内后移位。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '450a59e9-6494-497d-8b92-751e826bf865', '44263bb5-79f6-436d-83a4-23763335a5ec', '062203', '0622', '下1／3骨折', 1, '', '骨折远端多向后移位。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cc646b9a-7f96-4ee2-a0f0-f372061c322a', '06169f66-9269-4c14-91fb-a34254109a12', '062301', '0623', '屈曲型', 1, '', '骨折远端向后移位，呈横断或斜形，骨折线由后上斜向前下方。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '077f8934-a8d9-4fc5-b723-24c39d0f75ef', '06169f66-9269-4c14-91fb-a34254109a12', '062302', '0623', '伸直型', 1, '', '骨折远端向前移位，骨折线从前上斜向后下。', '2008/04/10 03:30:16', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6f1588f7-b8c9-437c-9cae-4c7055edb56b', '8b1a821a-eca7-4219-8625-999292ec53ba', '062401', '0624', '无移位型', 1, '', '骨折端无移位，可有纵型、横型、斜型、边缘、星状及粉碎等多种形态的骨折线出现。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd4bc552c-907e-45c2-bd32-df52b2007108', '8b1a821a-eca7-4219-8625-999292ec53ba', '062402', '0624', '移位型', 1, '', '以骸骨的中1／3或下1／3为多见，骨折端分离，骨折远端可向前下方翻转。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c44e2538-5ded-4f3d-9b2b-29f084d86574', '58d99bd6-b4db-4868-9009-6ad661003256', '062501', '0625', '外髁劈裂', 1, '', '肿痛以外侧为主，可呈外翻畸形，骨折片多向外侧移位，常伴有内侧的韧带损伤。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '49b87465-6c11-4151-a8ae-f7bb1cd32050', '58d99bd6-b4db-4868-9009-6ad661003256', '062502', '0625', '外髁塌陷', 1, '', '疼痛，肿胀以外侧为主，骨折片下陷，常合并腓骨小头骨折。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e140aaa1-be6d-4240-bb6f-2acd3c684976', '58d99bd6-b4db-4868-9009-6ad661003256', '062503', '0625', '内髁塌陷', 1, '', '肿胀以内侧为主，可呈内翻畸形，骨折下陷。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8e1efd69-5765-48dd-a148-a8edcbd8f317', '58d99bd6-b4db-4868-9009-6ad661003256', '062504', '0625', '内外髁劈裂', 1, '', '膝部肿胀较严重，骨折线通过髁间部。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1e2c6a49-482c-43eb-87f0-7ba859f7a222', '0c065ebb-0667-458f-9e7b-d8a598a60f08', '062601', '0626', '胫腓骨上段骨折', 1, '', '小腿上段肿胀，压痛明显，骨折线在胫腓骨上1／3处。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e79ba5cd-644d-404d-8a58-eed67818d07e', '0c065ebb-0667-458f-9e7b-d8a598a60f08', '062602', '0626', '胫腓骨中段骨折', 1, '', '小腿中段肿胀，压痛明显，骨折线在胫腓骨中１／3处。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0ab3c89d-eb1b-4683-a055-ebae776169b7', '0c065ebb-0667-458f-9e7b-d8a598a60f08', '062603', '0626', '胫腓骨下段骨折', 1, '', '小腿下段肿胀，压痛明显，骨折线在胫腓骨下1／3处。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '139d74dd-37d1-491c-bc7a-8f5105d98c2c', 'b24c90a9-ef7a-489b-ae6d-103a08837088', '062701', '0627', '外旋骨折', 1, '', '暴力使足部极度外旋所致。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ee54ccd4-90d0-49bb-b9fe-0c85d6484e36', 'b24c90a9-ef7a-489b-ae6d-103a08837088', '062702', '0627', '外翻骨折', 1, '', '暴力使足部极度外翻所致。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '541c0f96-483b-441d-b698-16aa2a97da37', 'b24c90a9-ef7a-489b-ae6d-103a08837088', '062703', '0627', '内翻骨折', 1, '', '暴力使足部极度内翻所致。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bf83c85f-30a2-477b-80db-6a481e290d74', 'b24c90a9-ef7a-489b-ae6d-103a08837088', '062704', '0627', '纵向挤压骨折', 1, '', '由高处跌落，足部着地所致。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '67726fb3-7de4-4e43-9dbc-02e731b89b56', 'b24c90a9-ef7a-489b-ae6d-103a08837088', '062705', '0627', '侧方挤压骨折', 1, '', '直接暴力打击或挤压踝部，造成双踝粉碎性骨折，骨折移位不大，常合并有皮肤穿伤。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '83b90bbd-c7f8-428d-99fd-ff245c3d5c0d', '951835c7-073d-4849-95d2-ef4eafaa019c', '062801', '0628', '无移位的骨折', 1, '', '踝部肿胀，疼痛，距骨有敏锐压痛，踝关节关系尚属正常。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7cdc4c5d-7d48-4fa4-afe6-4b04ca63304f', '951835c7-073d-4849-95d2-ef4eafaa019c', '062802', '0628', '有移位的骨折', 1, '', '踝部肿胀明显，局部皮肤红肿，有瘀斑，可扪及骨折块，异常隆突，压痛敏锐。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '968c223b-3b0e-4296-b5e7-f07eba811cad', '4a710776-f24f-4296-8f28-70cde39adf08', '062901', '0629', '舟骨结节骨折', 1, '', '为胫后肌牵拉所致。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '05850f30-a739-4461-8719-7917329d2410', '4a710776-f24f-4296-8f28-70cde39adf08', '062902', '0629', '舟骨背侧缘撕脱样骨折', 1, '', '多为扭伤，距舟关节囊撕脱所致，骨折块位于舟骨的近端背侧。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c5943a7a-16a9-4a9b-985b-ed714a8ca38b', '4a710776-f24f-4296-8f28-70cde39adf08', '062903', '0629', '舟骨横行骨折', 1, '', '直接暴力和扭转挤压的暴力所致。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '27b7cb91-03cc-4c1c-be0f-14a826c9d3b6', '7420acb0-66c9-4e05-8748-96cea77a2d3f', '063001', '0630', '不波及跟距关节面的骨折', 1, '', '结节部纵行骨折或呈"鸭嘴样"骨折，骨折片可向后上方移位，甚至有翻转移位，但不触及跟距关节面。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e0b540d9-9424-43e1-bbeb-2814cdaad590', '7420acb0-66c9-4e05-8748-96cea77a2d3f', '063002', '0630', '关节面轻度受累的骨折', 1, '', '跟骨体部粉碎性压缩性骨折，结节关节角减少，但骨折线未直接进入关节面。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bac632f3-008d-4c3d-82b4-41b3138de240', '7420acb0-66c9-4e05-8748-96cea77a2d3f', '063003', '0630', '关节面严重受累的骨折', 1, '', '严重移位的粉碎性骨折，碎骨片挤压关节面，结节关节角减少，消失或成为负角，跟骨横径变宽，甚至将距骨挤压至跟骨之中，形成摇椅状。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '858bef82-2657-475f-af06-4ada1fe01f94', 'e22f9352-fda2-41ef-a308-c32cabe2f0cb', '063101', '0631', '跖骨干骨折', 1, '', '多为开放性，多发性骨折，常并发跖跗关节脱位，足背瘀肿明显。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bcfcb6fa-fc0b-43ec-ba5c-8a71ad5fc9aa', 'e22f9352-fda2-41ef-a308-c32cabe2f0cb', '063102', '0631', '第五跖骨基底部骨折', 1, '', '常无明显移位，足背外侧肿胀为主，局部压痛敏锐。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e6151f7a-323a-406d-846b-ec307cbd4285', 'e22f9352-fda2-41ef-a308-c32cabe2f0cb', '063103', '0631', '跖骨颈疲劳骨折', 1, '', '第二、三跖骨颈部骨折，折段不完全断离，同时可见新骨产生。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'de5cfc65-b765-48b1-aac8-4ef8f2a52098', '165af04a-cd31-4a90-81e8-9bbf1920f171', '063201', '0632', '无明显移位骨折', 1, '', '常见于末节趾骨骨折。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f65ea295-4bb0-4dbb-93c4-c9157cefcfd9', '165af04a-cd31-4a90-81e8-9bbf1920f171', '063202', '0632', '有移位骨折', 1, '', '常见于趾骨干或第一、二节近段骨折。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2eaa65f9-259b-4d11-b41e-1b26de32816b', 'da7a4977-1a95-4d81-b723-c84ffe45224c', '063301', '0633', '无移位骨折', 1, '', '骨折无移位。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '174cc67d-27ca-41c0-9cf5-8b451fd26b9c', 'da7a4977-1a95-4d81-b723-c84ffe45224c', '063302', '0633', '有移位骨折', 1, '', '骨折移位可触及畸形及骨擦音或骨折端随呼吸移动。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '90e274b2-914a-4c0f-b390-02ad648df5a6', 'dc5059a8-6022-4496-b96d-43d0d76a2430', '063401', '0634', '单发性肋骨骨折', 1, '', '骨折发生于一根肋骨。肿痛，局部压痛，胸廓挤压征阳性。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cf2e6f74-b9ec-4226-8029-b371ac191f08', 'dc5059a8-6022-4496-b96d-43d0d76a2430', '063402', '0634', '多发肋骨骨折', 1, '', '骨折发生于多根或多处肋骨。除肿痛，胸廓挤压征阳性外，常可出现反常呼吸、呼吸困难等，严重者可并发气、血胸。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '66fb56b7-2ecb-4f39-8c90-f3c480f2c159', 'a525f4d5-2333-4f09-a618-66dc25a23f30', '063501', '0635', '环椎骨折', 1, '', '颈及后枕部疼痛，颈犟，不能作旋转及屈伸活动，常伴有脊髓损伤。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f92a90a5-57b2-4b57-8dfd-0d29cfdb3f47', 'a525f4d5-2333-4f09-a618-66dc25a23f30', '063502', '0635', '环椎脱位合并齿状突骨折', 1, '', '多伴有高位截瘫，甚至突然死亡。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a95a2dda-b1a7-4c06-8d7b-62f8aa8ae2b1', 'a525f4d5-2333-4f09-a618-66dc25a23f30', '063503', '0635', '齿状突骨折', 1, '', '伤后颈部疼痛，颈前凸消失，活动受限，可伴有脊髓损伤症状。开口位X线摄片检查显示骨折线。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '349cda26-0580-4065-b71d-96e63d95c3e7', 'a525f4d5-2333-4f09-a618-66dc25a23f30', '063504', '0635', '环椎和齿状突骨折、脱位', 1, '', '局部压痛，颈僵直，前脱位者无脊髓神经损伤，后脱位者可伴有脊髓损伤症状。X线摄片检查可确诊。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a09cd954-041f-493c-a481-ccb554f4bc3f', '6585372a-5eea-48a1-90ce-f4536dd95816', '063601', '0636', '颈椎椎体压缩性骨折', 1, '', '多为外力过度垂直压缩及屈曲损伤。颈椎棘突向后凸出，头屈而不能伸，用两手捧腮，头的各方向活动均受限，椎体压缩呈楔形或粉碎，碎片常可穿进椎管压迫脊髓而引起截瘫症状。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c9e38c7d-50ff-4e39-8c66-d9c8646a9884', '6585372a-5eea-48a1-90ce-f4536dd95816', '063602', '0636', '颈椎关节脱位', 1, '', '颈椎上、下关节突呈交锁，多伴有脊髓损伤，合并截瘫，X线正侧位及斜位摄片可明确移位方向。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4951b4a8-1e20-4c0e-89fc-419af5d014c8', '6585372a-5eea-48a1-90ce-f4536dd95816', '063603', '0636', '颈椎骨折、脱位', 1, '', '常可引起脊髓损伤，合并截瘫，X线摄片检查可明确骨折部位、移位方向，但有些伤员可因自动复位而在X线片上显示不出来、', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9772e94c-b8f0-476e-a7fb-98b1bbbec708', 'feb734d2-f021-4039-83d6-0458abc82053', '063701', '0637', '椎体压缩性骨折', 1, '', '局部肿痛，压痛，叩击痛，屈曲型骨折可见后突畸形，腰部活动障碍。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8c17fd46-e3d8-4fae-90e8-f21f02d37e99', 'feb734d2-f021-4039-83d6-0458abc82053', '063702', '0637', '附件骨折', 1, '', '局部肿痛，压痛，活动障碍。X光斜位片可明确骨折部位及移位情况。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '978b3e9f-e96a-48ba-a1df-5765d78baf16', 'feb734d2-f021-4039-83d6-0458abc82053', '063703', '0637', '胸腰椎骨折、脱位', 1, '', '局部肿痛，隆突，压痛明显，有纵向叩击痛，常伴有脊髓损伤。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd6d1840e-430e-4254-ab8c-6b9ae82cb6f9', '46779818-a5b1-46c1-bd31-a681055442c0', '063801', '0638', '稳定型骨盆骨折', 1, '', '骨盆环结构基本保持完整。如髂前上棘骨折、髂前下棘骨折、坐骨结节骨折、一侧耻骨上支或下支或坐骨上支或下支单独骨折、髂骨翼骨折、骶骨骨折等。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fa8db91d-0d1e-4c63-bd84-3ceb7d693bf0', '46779818-a5b1-46c1-bd31-a681055442c0', '063802', '0638', '不稳定型骨盆骨折', 1, '', '这类骨折均由强大暴力引起，骨折移位和伴有关节错位，而致骨盆环的完整性遭到破坏，常损伤盆腔内脏器或血管、神经及并发休克。常见有以下几种：一侧耻骨上下支或坐骨上下支骨折；髂骨骨折伴耻骨联合分离；耻骨或坐骨上下支骨折伴骶髂关节错位；耻骨联合分离并骶髂关节错位及骨盆多处骨折。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '596f0c13-b4f8-4685-b22d-293f722e69fd', '1763dd65-2fc6-44d9-9e4f-6b3bc9b54ae8', '063901', '0639', '单纯尾椎骨折', 1, '', '骶尾部肿胀、疼痛、坐位及行走困难。X线检查可见尾椎骨折线。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '00c12691-5e86-4e63-8211-f264d56dea5f', '1763dd65-2fc6-44d9-9e4f-6b3bc9b54ae8', '063902', '0639', '尾椎骨折合并脱位', 1, '', '骶尾部肿胀、疼痛，坐位及行走困难，肛门指检可触及脱位之骨块，X线侧位摄片可显示尾骨脱位。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0332d811-886c-4e28-866a-0912a6ae149e', 'ec50d43c-5680-46ae-ac93-6b2de4362ad3', '064001', '0640', '由内而外的开放骨折', 1, '', '局部肿痛，出血，或可见骨折端外露，伤口较小，软组织损伤不太严重，骨折多呈斜形或横断。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b6af7e49-8066-4f8e-89fe-760b55356af5', 'ec50d43c-5680-46ae-ac93-6b2de4362ad3', '064002', '0640', '自外而内的开放骨折', 1, '', '局部肿痛，出血，骨外露。伤口较大，软组织损伤严重，骨折多呈粉碎性。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b7fd64fa-9ea1-48e9-82d0-afef1ba67ff6', 'ec50d43c-5680-46ae-ac93-6b2de4362ad3', '064003', '0640', '潜在的开放骨折', 1, '', '肿胀，疼痛，扪及隆突畸形，折端压迫皮肤，数天后皮肤坏死溃破，骨外露。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ac0be5f2-9e61-41ed-98e0-1ff6685814b3', '40b59446-e887-4c4d-b464-2eb9b24dc367', '064101', '0641', '畸形愈合', 1, '', '局部肿痛明显，活动功能障碍，外观畸形，可扪及骨性隆突。X线片见骨折对位对线差，有大量骨痂生长。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '856864b7-6b92-40f6-84a8-4d06fc8631fb', '40b59446-e887-4c4d-b464-2eb9b24dc367', '064102', '0641', '迟缓愈合', 1, '', '局部轻度肿胀，压痛，X线片见骨痂少，出现迟，不能连接，骨折端吸收明显，间隙增宽，边缘模糊。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'df311c05-7990-42fd-8f6e-0c9799588de3', '40b59446-e887-4c4d-b464-2eb9b24dc367', '064103', '0641', '骨不连', 1, '', '局部肿痛不明显，可扪及骨折端的异常活动。X线片示：骨端硬化，髓腔封闭或骨端萎缩，骨质疏松，骨折间隙增宽，断端间互成为杵臼状假关节。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '39057875-f6c5-4e63-aa0b-df60105d8c42', '058a5c45-d8ca-47bc-98c6-640ae5aa2426', '064201', '0642', '单侧颞颌关节脱位', 1, '', '口半开合，口角歪斜，下颌骨由健侧向患侧歪斜，一侧耳前方可摸到凹陷，并在颧弓下可摸到髁状突。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '73210f1c-19e2-4b6e-a10b-a44209c1af9b', '058a5c45-d8ca-47bc-98c6-640ae5aa2426', '064202', '0642', '双侧颖颌关节脱位', 1, '', '颊部向前下突出，面颊变成扁平状，咬肌痉挛呈块状，语言不清，流涎，吞咽困难，在双侧耳前方可摸到凹陷，并在颧弓下可摸到髁状突。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bdc939a1-2ccb-492a-8e85-f8885d7b0bd1', '5784dc8f-c9ac-465e-b459-eb8bb1b8bf32', '064301', '0643', '胸锁关节前脱位', 1, '', '锁骨内侧端向前隆起，上肢被动活动时，可摸到脱出的锁骨内侧端有异常活动。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0b0cbe75-67ce-48ce-9bfb-2ac0a4cd4989', '5784dc8f-c9ac-465e-b459-eb8bb1b8bf32', '064302', '0643', '胸锁关节后脱位', 1, '', '锁骨内侧向内移位、局部凹陷，可摸到胸骨边缘的锁骨切迹。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '606dc278-7e87-4b37-b549-2684d3247ce2', 'b7809bb1-45e8-4ddd-81a7-e8c390145195', '064401', '0644', '肩锁关节半脱位', 1, '', '向上隆起畸形不明显，可摸到锁骨外端高于肩峰。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'eecaaf59-ca31-4921-a839-2c9db0b1ba44', 'b7809bb1-45e8-4ddd-81a7-e8c390145195', '064402', '0644', '肩锁关节全脱位', 1, '', '局部肿胀较严重，锁骨外端隆起明显，可形成梯状畸形，上肢向下牵拉时，锁骨外端隆起更明显。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0007494b-804b-462e-b19c-13124dc65b3a', 'a1d500ba-25de-45ca-8d7b-5e76c8daafa8', '064501', '0645', '前脱位盂下型', 1, '', '患侧上臂长于健侧（肩峰至肱骨外上髁），腋下可摸到肱骨头。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '44087f70-55f5-41a9-b774-e88bc66442ef', 'a1d500ba-25de-45ca-8d7b-5e76c8daafa8', '064502', '0645', '前脱位喙突下型', 1, '', '喙突下隆起明显，可摸到肱骨头。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '714b5a5d-b761-4f01-b704-107651f3b0c3', 'a1d500ba-25de-45ca-8d7b-5e76c8daafa8', '064503', '0645', '前脱位锁骨下型', 1, '', '锁骨下方隆起明显，可摸到肱骨头。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd9a2d242-c115-41cd-a16d-7db6080de4ec', 'a1d500ba-25de-45ca-8d7b-5e76c8daafa8', '064504', '0645', '后脱位', 1, '', '肩前方变平，肩向前突出，喙突及肩峰较健侧向前显露，上臂处于中立或内旋位，上臂内收，肩峰、盂下或肩胛冈下可摸到肱骨头。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f948a73d-3700-4a04-8dac-d416c298a271', 'a1d500ba-25de-45ca-8d7b-5e76c8daafa8', '064505', '0645', '肩关节脱位合并骨折', 1, '', '常见合并肱骨大结节骨折。局部肿胀较严重，或有瘀斑，肱骨大结节压痛。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7ed47bad-41f5-4800-8837-9a9b4d6c730a', 'b62f9a7d-bbd8-4d1a-9210-6786498ff7b1', '064601', '0646', '后脱位', 1, '', '肘关节肿胀，疼痛，弹性固定于135°左右，肘前后径增宽，肘窝前方饱满，可摸到肱骨滑车部，肘后鹰嘴异常后突，上方凹陷、空虚，肘后三点关系改变。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a4f197df-6633-4071-ab80-7e191cc34c33', 'b62f9a7d-bbd8-4d1a-9210-6786498ff7b1', '064602', '0646', '前脱位', 1, '', '肘后部空虚，前臂较健侧长，肘前方可触及尺骨鹰嘴，肘后三点关系改变。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7298d815-812b-4016-abc4-58e329c56408', 'b62f9a7d-bbd8-4d1a-9210-6786498ff7b1', '064603', '0646', '外脱位', 1, '', '肘内外径变宽，前臂外移，可触及鹰嘴位于外髁外侧，桡骨小头突出。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ec2ceb38-85a7-4a6a-b3d9-129094db5c5e', 'b62f9a7d-bbd8-4d1a-9210-6786498ff7b1', '064604', '0646', '内脱位', 1, '', '肘内外径增宽，在肘关节内侧可触及尺骨鹰嘴及桡骨小头。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1d682a80-834b-4268-a8f4-f40d5a730d9d', 'b62f9a7d-bbd8-4d1a-9210-6786498ff7b1', '064605', '0646', '脱位合并骨折', 1, '', '常合并肱骨内上髁骨折，肘部肿胀严重，肱骨内上髁压痛敏锐，可扪及骨擦音，或肱骨内上髁部平坦。', '2008/04/10 03:30:17', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6040b23e-8e12-4e72-9669-aa114a627975', '4df24a9c-9a2a-4157-ae47-1f483416816b', '081701', '0817', '肝郁痰凝', 1, '', '情志抑郁，或性情急躁，胸闷胁胀，或伴经前期乳房作胀或少腹作胀。乳房部肿块皮色不变。苔薄，脉弦。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '43bc0ed9-64b0-458c-bc15-9e90d0497fa4', '4df24a9c-9a2a-4157-ae47-1f483416816b', '081702', '0817', '冲任失调', 1, '', '经事紊乱，经前期乳房胀痛。或婚后从未生育，或多次流产史。舌质淡，苔薄，脉弦细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '35845935-a8a0-432d-aea1-0ad34edfe109', '4df24a9c-9a2a-4157-ae47-1f483416816b', '081703', '0817', '正虚毒炽', 1, '', '肿块扩大，溃后愈坚，渗流血水，不痛或剧痛。精神萎靡，面色晦暗或苍白，饮食少进，心悸失眠。舌紫或有瘀斑，苔黄，脉弱无力。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2e11e444-a468-4cd9-8344-04f7a40dc8dc', 'bd1985d7-eaab-41bb-945e-40041c0b5e09', '081801', '0818', '肝郁痰凝', 1, '', '多见于青壮年妇女。乳房肿块随喜怒消长，伴有胸闷胁胀，善郁易怒，失眠多梦，心烦口苦。舌苔薄黄，脉弦滑。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4b14ae29-1b68-4772-8403-c68bf38ec87b', 'bd1985d7-eaab-41bb-945e-40041c0b5e09', '081802', '0818', '冲任失调', 1, '', '多见于中年妇女。乳房肿块月经前加重，经后缓减。伴有腰酸乏力，神疲倦怠，月经先后失调，量少色淡，或经闭。舌淡，苔白，脉沉细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8881fc8b-754f-496b-8f41-4bde0b3812cc', 'f3e5f2d9-31be-408c-ad93-151f022f679f', '081901', '0819', '热毒蕴结', 1, '', '乳晕旁结块红肿疼痛，伴有发热，头痛。苔黄腻，脉滑数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e765b16a-a278-4084-8d5f-99a1a577073f', 'f3e5f2d9-31be-408c-ad93-151f022f679f', '081902', '0819', '余毒未清', 1, '', '溃后久不收口，脓水淋漓，形成乳漏，时愈时发，局部可有僵硬肿块。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '480241bf-5317-426b-8ead-56200965edbc', '06da98e8-b46f-416b-bf1c-d67b2b30d5eb', '082001', '0820', '肝郁火旺', 1, '', '乳孔溢液，颜色鲜红或暗红。可伴有烦躁易怒，胸闷胁痛，失眠多梦。舌红，苔薄黄，脉弦。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'efa0e7dc-12ec-4c04-922d-a4f8c8d4b418', '06da98e8-b46f-416b-bf1c-d67b2b30d5eb', '082002', '0820', '脾虚血亏', 1, '', '乳孔溢液，颜色淡红或黄色稀水。伴有面黄倦怠，食少，虚烦不眠。舌淡，苔白，脉细弱。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8f0c93f8-f70e-427a-b625-cd3e22e1316e', '74cb80ea-1bff-4bcc-aeba-841110d6a14d', '082101', '0821', '肝气郁结', 1, '', '一般肿块较小，发展缓慢，不红不热，不痛，推之可移。可有乳房不适，胸闷叹息。苔薄白，脉弦。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c9a69a8f-a5a6-4c40-a945-b1d32a46118b', '74cb80ea-1bff-4bcc-aeba-841110d6a14d', '082102', '0821', '血瘀痰凝', 1, '', '一般肿块较大，坚实木硬，重坠不适。胸胁牵痛，烦闷急躁，或有月经不调，痛经等症。舌暗红，苔薄腻，脉弦细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0525ca26-66fd-4bdc-a35d-9d7f574b34c9', 'e29108bf-0c6c-4a67-b5f3-37424cb70411', '082201', '0822', '气滞血瘀', 1, '', '不发热或发热，腹胀，恶心呕吐。苔白腻，脉弦紧。气滞为主者，腹痛绕脐，尚未固定，腹壁柔软；血瘀为主者，痛点固定在右下腹，拒按，有轻度反跳痛。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '09782427-a603-4440-806a-a71325b9931c', 'e29108bf-0c6c-4a67-b5f3-37424cb70411', '082202', '0822', '瘀滞化热', 1, '', '右下腹痛加剧，有明显跳痛及肌紧张，发热口干，便秘溲赤。舌质红，苔黄或黄腻，脉弦滑数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9c5b6740-727f-4bbc-a15d-864e6fb11b78', 'e29108bf-0c6c-4a67-b5f3-37424cb70411', '082203', '0822', '热毒炽盛', 1, '', '腹痛剧烈，可遍及全腹，有弥漫性压痛，反跳痛及肌紧张，或有界限不清之包块。高热。舌质红绛而干，苔黄厚干燥或黄厚腻，脉弦滑数，或洪大而数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6fc4d0b5-7af1-4cb9-9111-a26a984ec035', '98d1c7df-baef-4b8c-aef2-57a457e13b1e', '082301', '0823', '肾气亏虚', 1, '', '见于婴幼儿，站立、哭叫时肿块增大，平卧时则肿物缩小。肿物过大时，阴囊光亮如水晶。舌苔薄白，脉细滑。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fd8ff038-5410-4071-8f58-c0f585f0abd1', '98d1c7df-baef-4b8c-aef2-57a457e13b1e', '082302', '0823', '湿热下注', 1, '', '阴囊潮湿而热，或有睾丸肿痛，小便赤热。舌红，苔腻，脉数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6fa33626-fda3-4536-adef-a96a5ff1dc54', '98d1c7df-baef-4b8c-aef2-57a457e13b1e', '082303', '0823', '肾虚寒湿', 1, '', '多见于病程长久，阴囊寒冷，皮肤增厚，坠胀不适。可有面色少华，神疲乏力，腰酸腿软，便溏，小便清长。苔白，脉沉细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9a251cc5-cf79-4c51-889c-494330b3f789', '98d1c7df-baef-4b8c-aef2-57a457e13b1e', '082304', '0823', '瘀血阻络', 1, '', '有睾丸损伤或睾丸有肿瘤病史。能触到肿块伴疼痛，多不能透光。舌质紫暗，苔薄，脉细涩。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '48833477-dc34-4310-9bcf-ae915a873bf7', '33c518c4-703f-4052-8ebf-bdb9ae3e6d80', '08240101', '082401', '湿热下注', 1, '', '多见于成人。睾丸或附睾肿大疼痛，阴囊皮肤红肿，皱纹消失，{热疼痛，少腹抽痛，脓肿形成时，按之应指。伴有恶寒发热。舌苔黄腻，脉滑数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a527faa1-9b1d-4441-a65c-39240817e569', '33c518c4-703f-4052-8ebf-bdb9ae3e6d80', '08240102', '082401', '瘟毒下注', 1, '', '多见于儿童。多因患痄腮并发（又称卵子瘟）。睾丸肿大疼痛，有恶寒发热，一般不会化脓。苔黄，脉数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b3cec6b4-d0c7-4c49-82ea-febba1366951', '33c518c4-703f-4052-8ebf-bdb9ae3e6d81', '08240201', '082402', '气滞痰凝', 1, '', '附睾结节，子系粗肿，触痛轻微，牵引少腹不适，一般无全身症状。苔薄腻，脉滑。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '74a09bf6-36ce-4ba0-8261-97b21a1077ea', '33c518c4-703f-4052-8ebf-bdb9ae3e6d81', '08240202', '082402', '阳虚寒凝', 1, '', '附睾结节，子系粗肿，无触痛感，阴囊寒冷。可有腰酸，阳痿，遗精。舌质淡或有齿痕，脉沉或细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a578424a-3326-40d2-ae83-1e7f5612e940', '03fb439b-d2a9-44c7-9e3a-8c19fe3b7aab', '082501', '0825', '湿痰凝结', 1, '', '除肾子酸胀隐痛，附睾硬结，子系呈条索状肿硬外，一般无明显全身症状。苔薄，脉滑。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7b345247-f606-40d5-975e-c4ab4c40c664', '03fb439b-d2a9-44c7-9e3a-8c19fe3b7aab', '082502', '0825', '阴虚内热', 1, '', '肾子与阴囊皮肤粘连，色转暗红，酿脓时有轻微波动。伴有午后潮热，盗汗，倦怠，颧红。舌质红，苔薄黄，脉细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dde3131a-396c-40ea-a18d-65a653ad147b', '03fb439b-d2a9-44c7-9e3a-8c19fe3b7aab', '082503', '0825', '阳虚痰凝', 1, '', '肾子酸胀隐痛，附睾硬结不消，或溃后疮口久不愈合。伴有肢冷畏寒，面色淡白，腰酸肢软，肾子阴冷。舌边有齿痕，苔薄白，脉弱无力。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '07fe6f67-e5ea-4eae-a429-a96f37f19fad', '32abd504-6f65-4530-89d7-af814f529ded', '082601', '0826', '肺热失宣', 1, '', '小便不畅或点滴不通。兼见咽干，口燥，胸闷，呼吸不利，咳嗽咯痰。舌质红，苔薄黄，脉滑数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b333e147-fa73-40dc-ad07-cf7206131b96', '32abd504-6f65-4530-89d7-af814f529ded', '082602', '0826', '湿热下注', 1, '', '尿少黄赤，尿频涩痛，点滴不畅，甚至尿闭，小腹胀满。口渴不欲饮，发热或大便秘结。舌质红，苔黄腻，脉数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd30c0d80-2806-4561-90c0-2884e28fdcf8', 'd431a08e-5ecd-4971-8ebb-c4002de099e9', '052102', '0521', '内闭外脱证', 1, '', '突然出现面色苍白，四肢厥冷，或汗出不温，皮肤可见花纹，口唇紫绀。严重者口吐咖啡样液，呼吸浅促或节律不匀，两目无神，脉来细数无力，血压明显下降等危象。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2dc7e9d0-69c1-4b77-99ec-f6ea98f3c5a0', '8e739ce6-1ad6-4048-88d3-de54e87ee801', '052201', '0522', '血虚风燥', 1, '', '疹点呈粟粒大小，表面起白屑，形如癣疥，皮肤殷红而干燥。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '73d52b52-26aa-4438-9077-54fbbbc15589', '8e739ce6-1ad6-4048-88d3-de54e87ee801', '052202', '0522', '湿热浸淫', 1, '', '疹点有水液渗出，或呈脓性分泌物，皮肤有粟状隆起，重者可融合成片，延及全身，有腥气，皮肤色红。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '88c4bacd-6eba-45c8-b328-d978f13cc7ac', '779483ea-0a4a-421b-86a6-06acb215c235', '052301', '0523', '乳食内积', 1, '', '面黄少华，烦躁多啼，夜卧不安，食欲不振，腹部胀满，大便溏泄酸臭或便秘，小便短黄或如米泔，伴有低热。舌红，苔腻，脉滑数，指纹紫滞。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fba7ec29-3ea2-442e-9d0b-aec93b88abc2', '779483ea-0a4a-421b-86a6-06acb215c235', '052302', '0523', '脾虚夹积', 1, '', '面色萎黄，形体较瘦，困倦无力，夜寐不安，不思乳食，腹满喜伏卧，大便稀糊。唇舌淡红，苔白腻，脉细而滑，指纹淡滞。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '27b15713-62cc-4113-b2dd-f0b0f928aaa7', 'b4caa70c-e42f-4de2-9fad-572051696b01', '052401', '0524', '气阴不足', 1, '', '见于发病初期。面色苍白，神情烦躁，夜寐不安，发稀枕秃，纳呆盗汗，肌肉松软，囟门迟闭。舌苔薄白，脉细软。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3cfb14ed-ce64-482a-8971-c442af93b584', 'b4caa70c-e42f-4de2-9fad-572051696b01', '052402', '0524', '脾肾亏虚', 1, '', '见于发病极期。面白虚浮，多汗肢软，神情呆钝，语言迟发，齿生过缓，立迟行迟，头颅方大，肋骨串珠，甚至鸡胸、龟背，下肢弯曲等。舌质淡白，少苔，脉细无力。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2a56bcac-a9e9-4fa3-ba49-9b84a9fca962', '02a9f1e8-6008-48d8-8d71-bc82ff28260b', '052501', '0525', '脾胃虚弱', 1, '', '面黄少华或淡白，食欲不振，神倦之力，或有腹泻便溏。唇舌色淡，苔薄，脉弱。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3a37fe8b-74d0-4748-915a-15ebeea657c8', '02a9f1e8-6008-48d8-8d71-bc82ff28260b', '052502', '0525', '心脾两虚', 1, '', '面色萎黄或淡白，发焦易脱，倦怠无力，食少纳呆，心悸气短，头晕，口唇粘膜苍白，爪甲色淡。舌质淡胖，苔薄，脉虚细。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd137c50b-2f36-46ae-b33d-779ac130212d', '02a9f1e8-6008-48d8-8d71-bc82ff28260b', '052503', '0525', '肝肾阴虚', 1, '', '两颧嫩红，目眩耳鸣，腰腿酸软，潮热盗汗，口干舌燥，指甲枯脆，肌肤不泽。舌红，少苔，脉细数。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f011f938-41c0-4370-8f04-ff92fa5144ba', '02a9f1e8-6008-48d8-8d71-bc82ff28260b', '052504', '0525', '脾肾阳虚', 1, '', '面色、口唇淡白，畏寒肢冷，食少便溏，消瘦或浮肿，自汗神疲。舌质淡胖，脉沉细。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0150c5ec-193f-4ff1-baea-833d8eaee8a7', '8e275bc4-8e1f-480c-b814-ef957f35cf50', '052601', '0526', '肾虚肝亢', 1, '', '智慧落后于同年龄儿童，动作笨拙，性格暴躁，幼稚任性，不听管教，难以静坐。舌红而干，脉细数。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '90e5c822-bff4-4b56-a9ac-fd2f6847b878', '8e275bc4-8e1f-480c-b814-ef957f35cf50', '052602', '0526', '脾虚肝旺', 1, '', '心神不宁，多动不安，思想不集中，意志不坚，语言冒失，兴趣多变，做事有头无尾，形体消瘦，纳食呆顿，面色淡黄无华。舌苔薄白，舌淡红，脉弱或细弦。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ed7f8cf7-3812-47ae-8160-fd765b1fdc0b', 'af10bb4e-95a0-4cbe-a7fd-1b98d222791e', '052701', '0527', '肾气不足', 1, '', '头颅明显增大，囟门宽裂，颅缝开解，面色淡白，神情呆钝，目无神采，眼珠下垂，呈"落日状"，头大颈细，前倾不立，食欲不振，大便稀溏。舌淡，苔少，脉弱，指纹淡青。严重者还可见斜视、呕吐、惊厥。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '43fa2560-05e9-47ab-a51d-3b3e4af283a5', 'af10bb4e-95a0-4cbe-a7fd-1b98d222791e', '052702', '0527', '肾虚肝亢', 1, '', '颅缝裂开，前囟宽大，眼球下垂，白多黑少，目无神采，心烦不安，手足心热，筋惕肉跳，时或惊叫。口干舌红，脉沉细数，指纹紫红。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '72a48c15-0f0f-4db0-906d-2ca92acc99d8', 'af10bb4e-95a0-4cbe-a7fd-1b98d222791e', '052703', '0527', '脾虚水泛', 1, '', '面色淡白，精神倦怠，囟门宽大，颅缝开解，肢体消瘦，食欲不振，大便稀溏。舌淡，苔薄白或白腻，脉细弱，指纹淡红。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '644ae4d5-c133-4249-b590-fe2441b20ea7', 'af10bb4e-95a0-4cbe-a7fd-1b98d222791e', '052704', '0527', '热毒雍滞', 1, '', '头颅日见增大，囟门高胀，颅缝合而复开，两目下垂。发热气促，烦躁哭闹，面赤唇红，或见两目斜视，四肢痉挛，小溲短赤，大便秘结。舌红，苔黄，脉多弦数，指纹紫滞。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c63854e5-4936-41ea-ad0f-9eb496e855a5', 'a639fbd7-10da-4494-b69b-97e4040b0c73', '052801', '0528', '脾阳亏虚', 1, '', '哭声低微，睡喜弯曲，腹部喜温喜按，四肢欠温，食少便溏，小溲清长，面色青白。唇舌色淡，苔薄白，指纹淡红。"', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f938d38d-e507-48d7-b3e2-e2786e3d35f1', 'a639fbd7-10da-4494-b69b-97e4040b0c73', '052802', '0528', '心经积热', 1, '', '哭声较响，见灯火则啼哭更剧。哭时面赤唇红，烦躁不安，身暖多汗，大便秘结，小溲短赤。舌尖红，苔黄，指纹红紫。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e4ffd110-8346-4a30-b867-dba7024e047e', 'a639fbd7-10da-4494-b69b-97e4040b0c73', '052803', '0528', '惊恐伤神', 1, '', '夜寐突然惊寤而啼哭，哭声尖锐，如见异物状，紧偎母怀，面色青灰。舌苔正常，指纹青紫。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '203b1581-eabe-4d4a-8008-b00b017d61a5', '077c39c0-7551-4976-b477-7ca39aa1b379', '052901', '0529', '发热期', 1, '', '突发高热，持续3～4天，精神如常。舌红，苔薄黄，脉浮数，指纹青紫。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0edf918b-59d3-46fb-9eb9-8e45a2c65b60', '077c39c0-7551-4976-b477-7ca39aa1b379', '052902', '0529', '出疹期', 1, '', '热骤退，全身出现玫瑰色皮疹，以躯干明显，无痒感，1～2天后消退。舌红，苔薄黄或黄腻，脉浮数，指纹紫。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3486a044-25bc-4b67-bc54-248f04f12b61', '377ee666-d3e3-4596-9c87-161f888f7bbf', '053001', '0530', '风寒袭肺', 1, '', '哭声低弱，口吐白沫，喉有痰鸣，面白肢冷，口周微紫，鼻翼轻微煽动，点头呼吸，体温正常或稍低。舌质淡红，舌苔白，指纹在风关或气关色泽淡红。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dce6a6a3-72dd-4226-a09b-f00969488857', '377ee666-d3e3-4596-9c87-161f888f7bbf', '053002', '0530', '风热犯肺', 1, '', '发热咳嗽，气急痰鸣，吮乳困难，口唇微紫，点头呼吸。舌苔黄，舌质红，指纹紫红。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1720d026-8f13-4c1e-9a04-886f07bdded4', '377ee666-d3e3-4596-9c87-161f888f7bbf', '053003', '0530', '肺热血瘀', 1, '', '精神萎软，拒食咳嗽，呼吸浅快。肢端及口唇发紫，面色灰黯，体温不稳定，甚至出现黄疸或瘀点。舌质微紫，苔黄，指纹紫滞。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5ab739da-5428-4994-a3c7-b6379aa49a38', '5a7d6446-12da-4562-b450-133afb6c1819', '053101', '0531', '虫积肠道', 1, '', '脐腹阵发疼痛，时作时止，夜卧不安，寐中磨牙、流涎，鼻痒，有时大使排出蛔虫。苔薄白或呈花白，脉细弦。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cb577f30-9e94-4650-8b20-c2aa509f2db7', '5a7d6446-12da-4562-b450-133afb6c1819', '053102', '0531', '脾胃虚弱', 1, '', '病久，面黄肌瘦，巩膜、面颊、指甲虫斑累累，神疲，食欲减退，时寐时醒。舌淡，苔白，脉细弱。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c7543e77-413c-4227-91a6-044133ab956f', 'dc2aab39-00f9-4699-85df-8d02553b8b3a', '053201', '0532', '虫扰魄门', 1, '', '肛门发痒，夜间为甚，夜眠不安，甚则惊叫。舌苔薄白，脉细。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '36e5bfb3-7bbd-42c0-91db-2d22783b6f0e', 'dc2aab39-00f9-4699-85df-8d02553b8b3a', '053202', '0532', '脾胃虚弱', 1, '', '蛲虫反复感染，食欲减退，腹胀，神倦，形体消瘦。舌淡，苔薄，脉弱。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'de37f515-2112-4f9a-9dd2-5445d1ebb852', '736b5522-df34-468e-b66b-13d535369e37', '053301', '0533', '虫积肠道', 1, '', '阵发腹痛，肠鸣，腹胀，大便或溏或秘，寝寐不安。舌苔薄腻或黄，脉细弦。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fce0390a-b8de-4bc3-aad4-dbbc8b5f0753', '736b5522-df34-468e-b66b-13d535369e37', '053302', '0533', '脾胃虚弱', 1, '', '虫积日久，神倦乏力，食少便溏，自汗，面色萎黄，形体消瘦。舌淡，苔白，脉弱。', '2008/04/10 12:04:45', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd5e9f4c0-72b3-453e-bf65-3542e6a9a4b6', '88b89644-866f-4f3b-b7bb-fbb65f6ed925', '070101', '0701', '风寒束表', 1, '', '恶寒、发热、无汗、头痛身疼，鼻寒流清涕，喷嚏。舌苔薄白，脉浮紧或浮缓。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8f2fb4c2-6926-422c-a927-18a3aeee3380', '88b89644-866f-4f3b-b7bb-fbb65f6ed925', '070102', '0701', '风热犯表', 1, '', '发热、恶风、头胀痛，鼻塞流黄涕，咽痛咽红，咳嗽。舌边尖红，苔白或微黄，脉浮数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c33faa47-a1e3-48e7-a1f3-1b2934cd0454', '88b89644-866f-4f3b-b7bb-fbb65f6ed925', '070103', '0701', '暑湿袭表', 1, '', '见于夏季，头昏胀重，鼻塞流涕，恶寒发热，或热势不扬，无汗或少汗，胸闷泛恶。舌苔黄腻，脉濡数。临床尚有体虚感冒，以及挟湿、挟滞等兼证。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8f55955d-812b-4c63-a05b-461130177538', '61bdfcd4-438d-4b49-9b17-ef338f395d67', '070201', '0702', '风寒袭肺', 1, '', '咳嗽声重，咯痰稀薄色白，恶寒，或有发热，无汗。舌苔薄白，脉浮紧。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2b08ea4f-eb7e-46eb-a155-9d3ff8368281', '61bdfcd4-438d-4b49-9b17-ef338f395d67', '070202', '0702', '风热犯肺', 1, '', '咳嗽气粗，咯痰粘白或黄，咽痛或咳声嘶哑，或有发热，微恶风寒，口微渴。舌尖红，苔薄白或黄，脉浮数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c8529912-0d04-4cbc-adee-45ea0f54d664', '61bdfcd4-438d-4b49-9b17-ef338f395d67', '070203', '0702', '燥邪伤肺', 1, '', '干咳少痰，咯痰不爽，鼻咽干燥，口干。舌尖红，苔薄黄少津，脉细数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '02f93033-eb85-4215-9707-d9217e6da6f1', '61bdfcd4-438d-4b49-9b17-ef338f395d67', '070204', '0702', '痰热壅肺', 1, '', '咳嗽气粗，痰多稠黄，烦热口干。舌质红，苔黄腻，脉滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dfa03d9b-b367-4e34-9af4-e0e0254160ae', '61bdfcd4-438d-4b49-9b17-ef338f395d67', '070205', '0702', '肝火犯肺', 1, '', '咳呛气逆阵作，咳时胸胁引痛，甚则咯血，舌红，苔薄黄少津，脉弦数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e5befc09-1f4e-42bb-851c-3abcfce653ad', '61bdfcd4-438d-4b49-9b17-ef338f395d67', '070206', '0702', '痰湿蕴肺', 1, '', '咳声重浊，痰多色白，晨起为甚，胸闷脘痞，纳少。舌苔白腻，脉滑。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f2ac7933-27a2-4914-837b-7002581a7d5e', '61bdfcd4-438d-4b49-9b17-ef338f395d67', '070207', '0702', '肺阴亏虚', 1, '', '咳久痰少，咯吐不爽，痰粘或夹血丝，咽干口燥，手足心热。舌红，少苔，脉细数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9e9e3869-276a-4eea-9c6c-c94c48ace7bf', '61bdfcd4-438d-4b49-9b17-ef338f395d67', '070208', '0702', '肺气亏虚', 1, '', '病久咳声低微，咳而伴喘，咯痰清稀色白，食少，气短胸闷，神倦乏力，自汗畏寒。舌淡嫩，苔白，脉弱。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b2523d26-0457-4b07-aa57-4ee2dfa94ed1', '305694af-7bb8-45de-aef9-0cabd48a6525', '070301', '0703', '风热犯肺', 1, '', '身热无汗或少汗，微恶风寒，咳嗽痰少，头痛，口微渴。舌边尖红，苔薄白，脉浮数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '195b82c6-c395-49cb-bba3-15b95fc14c12', '305694af-7bb8-45de-aef9-0cabd48a6525', '070302', '0703', '痰热壅肺', 1, '', '身热烦渴，汗出，咳嗽气粗，或痰黄带血，胸闷胸痛，口渴。舌红苔黄，脉洪数或滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '800dcd03-83de-4adb-aeb5-93627bdd8a69', '305694af-7bb8-45de-aef9-0cabd48a6525', '070303', '0703', '肺胃热盛', 1, '', '身热，午后为甚，心烦懊依，口渴多饮，咳嗽痰黄，腹满便秘。舌红，苔黄或灰黑而燥，脉滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '41022923-bfb4-43da-851e-da2708e1c310', '305694af-7bb8-45de-aef9-0cabd48a6525', '070304', '0703', '热闭心包', 1, '', '壮热，烦躁不安，口渴不欲饮，甚则神昏谵语、痉厥或四肢厥冷。舌绛少津，苔黄，脉弦数或沉数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '45c8aba4-1282-4cea-b746-55ad4aad41e2', '305694af-7bb8-45de-aef9-0cabd48a6525', '070305', '0703', '气阴两虚', 1, '', '身热渐退，干咳痰少而粘，自汗神倦，纳少口干。舌红少苔，脉细或细数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '283d0d40-2772-44e1-9410-ce174c07d514', '305694af-7bb8-45de-aef9-0cabd48a6525', '070306', '0703', '邪陷正脱', 1, '', '呼吸短促，鼻翼煽动，面色苍白，大汗淋漓，甚则汗出如油，四肢厥冷，紫绀，烦躁不安，身热骤降。或起病无身热，面色淡白，神志逐渐模糊。舌质淡紫，脉细数无力，或脉微欲绝。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e80782f2-e2a0-4515-956b-9f0a842d40cd', 'fd2be8fe-8064-4c6a-9784-b965e581366c', '070401', '0704', '初期', 1, '', '恶寒发热，咳吐白色粘痰，胸痛，咳时加重。舌苔薄黄或薄白，脉浮滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'aad35222-1d9b-4fa4-89a2-e5a4cca382a4', 'fd2be8fe-8064-4c6a-9784-b965e581366c', '070402', '0704', '成痈期', 1, '', '高热寒战，继则壮热不寒，汗出烦渴，咳呛气急，咯痰黄浊，胸满痛，转侧不利。舌质红，苔黄腻，脉滑数有力。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0f3e2e62-062e-44d7-8c05-f565d36a6de3', 'fd2be8fe-8064-4c6a-9784-b965e581366c', '070403', '0704', '溃脓期', 1, '', '咳吐脓血，状如米粥，量多腥臭，胸满，心烦懊依。舌质红绛，苔黄腻，脉滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6688f8c3-ded3-462f-9fc2-098eb3b92500', 'fd2be8fe-8064-4c6a-9784-b965e581366c', '070404', '0704', '恢复期', 1, '', '热退咳减，脓血痰减少，胸胁隐痛，气短神疲，自汗盗汗，低热。舌质红或淡红，苔薄，脉细或细数无力。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ffebd489-4819-4ffe-ac96-53ed5812f47e', 'e0266cae-83ae-4168-9572-fbd03f9eaa62', '070501', '0705', '肺阴亏虚', 1, '', '干咳，痰少粘白，或带血丝，口干咽燥。舌质红，苔薄，脉细带数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ae6351ce-bab9-4ddc-afca-dc9c2769e6d2', 'e0266cae-83ae-4168-9572-fbd03f9eaa62', '070502', '0705', '阴虚火旺', 1, '', '咳呛气急，咯血，痰少粘白或黄，口干咽燥，午后颧红，潮热，骨蒸，盗汗。舌红或绛，苔薄黄或剥，脉弦细数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '203fa5b7-665f-4030-a3a0-a364852bee01', 'e0266cae-83ae-4168-9572-fbd03f9eaa62', '070503', '0705', '气阴两虚', 1, '', '咳嗽气短，咯痰清稀，偶有咯血，神疲乏力，自汗盗汗，或食少腹胀，便溏。舌质红嫩，苔薄，脉弱而数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ed042197-128b-4828-87da-58fe14688f7d', 'e0266cae-83ae-4168-9572-fbd03f9eaa62', '070504', '0705', '阴阳两虚', 1, '', '咳逆喘息，痰呈泡沫状或夹血，形寒自汗，声嘶音哑，形体消瘦。或伴有浮肿、腹泻等症。舌质淡而少津，苔光剥，脉微数或虚大无力。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd67d40c5-aa32-41c4-87d8-53f0458fce9b', 'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', '070601', '0706', '肝火犯肺', 1, '', '咳呛气逆，咯（咳）血鲜红，胁痛善怒，面赤口苦。舌红苔黄，脉弦数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0824ab9e-0eed-4d1d-870c-801e91347ace', '54c9d129-2b5c-49f3-ac22-60febd35911c', '050601', '0506', '伤食泻', 1, '', '大便酸臭，或如败卵，腹部胀满，口臭纳呆，泻前腹痛哭闹，多伴恶心呕吐。舌苔厚腻，脉滑有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b9f07cae-0d3e-434d-b108-caa4a99ace2c', '54c9d129-2b5c-49f3-ac22-60febd35911c', '050602', '0506', '风寒泻', 1, '', '大便色淡，带有泡沫，无明显臭气，腹痛肠鸣。或伴鼻塞，流涕，身热。舌苔白腻，脉滑有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bd55d18f-570c-4126-bbeb-6c7a38f83f18', '54c9d129-2b5c-49f3-ac22-60febd35911c', '050603', '0506', '湿热泻', 1, '', '泻如水样，每日数次或数十次，色褐而臭，可有粘液，肛门灼热，小便短赤，发热口渴。舌质红，苔黄腻，脉数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5030dd54-3dac-48f8-9f98-58d568cbccff', '54c9d129-2b5c-49f3-ac22-60febd35911c', '050604', '0506', '寒湿泻', 1, '', '大便每日数次或十数次，色较淡，可伴有少量粘液，无臭气，精神不振，不渴或渴不欲饮，腹满。舌苔白腻，脉濡。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c014209e-02fc-4f14-a151-acaf6df67e94', '54c9d129-2b5c-49f3-ac22-60febd35911c', '050605', '0506', '脾虚泻', 1, '', '久泻不止，或反复发作，大便稀薄，或呈水样，带有奶瓣或不消化食物残渣，神疲纳呆，面色少华。舌质偏淡，苔薄腻，脉弱无力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0469e7d5-cb0c-468d-99ef-833a38fa4573', '54c9d129-2b5c-49f3-ac22-60febd35911c', '050606', '0506', '脾肾阳虚泻', 1, '', '大便稀溏，完谷不化，形体消瘦，或面目虚浮，四肢欠温。舌淡苔白，脉细无力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '00adfcfe-16ff-4c5a-bdab-897856fa53f1', 'f30decc7-48ed-499f-9b75-9c249186b214', '050701', '0507', '脾胃不和', 1, '', '厌食或拒食，面色少华，精神尚可，大便偏干，苔、脉无特殊改变。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e3d33632-76ea-43ef-9bed-6fc30389e582', 'f30decc7-48ed-499f-9b75-9c249186b214', '050702', '0507', '脾胃气虚', 1, '', '厌食或拒食，面色萎黄，精神稍差，肌肉松软。或形体消瘦，大便多不成形，或夹不消化食物。舌质淡，苔薄白，脉无力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9d174831-1977-43a3-b3e3-20b17afb6381', 'f30decc7-48ed-499f-9b75-9c249186b214', '050703', '0507', '脾胃阴虚', 1, '', '厌食或拒食，面色萎黄，形瘦，口干食少饮多，甚则每食必饮，烦热不安，便干溲赤。舌质红，苔净或花剥，脉细无力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6074a9e9-aa7e-4fd5-a3ab-05d2de4dec54', 'f30decc7-48ed-499f-9b75-9c249186b214', '050704', '0507', '肝旺脾虚', 1, '', '厌食或拒食，性躁易怒，好动多啼，咬齿磨牙，便溏溲少。舌光苔净，脉细弦。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0176eb06-8c20-48a4-9560-41807b7f80a3', '446ee709-7376-45c1-83ef-3815c5943a8e', '050801', '0508', '疳气', 1, '', '形体略见消瘦，面色稍萎黄，食欲不振，或食多便多，大便干稀不调，精神不振，好发脾气。舌苔腻，脉细滑。多见于本病之初期。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3dddc096-672e-413b-9488-7804e207ba71', '446ee709-7376-45c1-83ef-3815c5943a8e', '050802', '0508', '疳积', 1, '', '形体消瘦明显，脘腹胀大，甚则青筋暴露，面色萎黄，毛发稀疏易落，烦躁。或见揉眉挖鼻，吮指磨牙，食欲减退。或善食易饥、大便下虫。或嗜食生米、泥土等异物。舌质偏淡，苔淡黄而腻，脉濡细而滑。多见于本病之中期。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '913d0696-4c9b-4067-aa45-eaeaefd6c895', '446ee709-7376-45c1-83ef-3815c5943a8e', '050803', '0508', '干疳', 1, '', '极度消瘦，皮包骨头，呈老人貌，皮肤干枯有皱纹，精神萎靡，啼哭无力，无泪。或可见肢体浮肿。或见紫癜、鼻衄、齿衄等。舌淡或光红少津，脉弱。多见于本病之晚期。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4f5c2aeb-6698-4e70-a1c1-1595d30e20fd', '6b70500c-8b09-4a9d-8cca-2f8a20439501', '050901', '0509', '风水相搏', 1, '', '眼睑浮肿，继则全身浮肿，肢节酸痛，尿少。或伴发热咳嗽，舌苔薄白，脉浮。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c076c934-931f-42f1-a84b-cac6438b88eb', '6b70500c-8b09-4a9d-8cca-2f8a20439501', '050902', '0509', '湿热内蕴', 1, '', '肢体浮肿，小便短赤，或肉眼血尿，发热神烦。或有皮肤疮毒。舌苔黄腻，脉略数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ab8faf0b-ab6a-45ed-9201-1fc3077a0874', '6b70500c-8b09-4a9d-8cca-2f8a20439501', '050903', '0509', '脾虚湿困', 1, '', '肢体浮肿，面色萎黄，疲乏无力，胸闷腹胀，纳少便溏，小便短少，舌质偏淡，苔白滑，脉濡。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '15a38075-8c23-4e40-a1c5-2d04c02156cb', '6b70500c-8b09-4a9d-8cca-2f8a20439501', '050904', '0509', '脾肾阳虚', 1, '', '全身高度浮肿，或有胸水、腹水，面色淡白或灰暗，神疲畏寒，四肢欠温，脘腹胀满。甚至咳逆上气，不能平卧。舌淡胖，苔白，脉沉细。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '901ca108-8eb9-4449-b697-92da002ef581', '6b70500c-8b09-4a9d-8cca-2f8a20439501', '050905', '0509', '肺脾气虚', 1, '', '面肢浮肿、淡白无华，神疲乏力，动则自汗。易于感冒，感冒后常咳嗽不沐，伴有痰声，纳谷不馨，大便偏溏，溲清。舌淡，苔薄白，脉细弱。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1574c3b5-93e5-41a1-948e-8080c2f80e35', '4726022d-0426-48b0-9796-860cf7917405', '05100101', '051001', '疹前期（初热期）', 1, '', '从开始发热到出疹3天左右。热势渐升，咳嗽，流涕，目赤畏光，泪水汪汪.汪。舌苔薄白或薄黄，脉浮数。疹点均匀，色泽红，疹子透齐后即顺序消退，同时热退咳减，无合并症发生。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '44101f68-976c-49c5-bd2d-9bbee5764579', '4726022d-0426-48b0-9796-860cf7917405', '05100102', '051001', '出疹期（见形期）', 1, '', '皮疹从见点到透齐3天左右。发热不退，咳嗽加剧，疹点先见于耳后、发际，渐及头面，胸背、腹部、四肢，最后手掌足底见疹，即为出齐。疹色呈暗红色的斑丘疹。舌质红，苔黄，脉洪数。疹点均匀，色泽红，疹子透齐后即顺序消退，同时热退咳减，无合并症发生。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2fc32e37-b93c-424e-8368-bcb8f0d6f65f', '4726022d-0426-48b0-9796-860cf7917405', '05100103', '051001', '疹回期（收没期）', 1, '', '从疹点透齐至收没3天左右。发热渐退，咳嗽减轻，疹点依次渐回。疹退处皮肤呈糠状脱屑，留有色素沉着。舌质红，少津，苔少，脉细弱或细数。疹点均匀，色泽红，疹子透齐后即顺序消退，同时热退咳减，无合并症发生。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9b8cc01a-3713-4b1f-b01b-0871ef1f2c3e', '4726022d-0426-48b0-9796-860cf7917406', '05100201', '051002', '热毒闭肺', 1, '', '高热烦躁，咳嗽气促，鼻翼煽动，喉间痰鸣，疹点紫暗或隐没，甚则面色青灰，口唇紫绀。舌红，苔薄黄或黄腻而干，脉数有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c178ff42-35ba-4928-b630-2c545f5f7f48', '4726022d-0426-48b0-9796-860cf7917406', '05100202', '051002', '热毒攻喉', 1, '', '身热不退，咽喉肿痛，声音嘶哑，或咳声重浊，状如犬吠，喉间痰鸣，甚则呼吸困难，面色发紫，烦躁不安。舌质红，苔黄腻，脉数有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8f1eb3e3-cb79-4fb5-83fd-2e4974bdfb9d', '4726022d-0426-48b0-9796-860cf7917406', '05100203', '051002', '毒陷心肝', 1, '', '高热，烦躁，谵语，皮肤疹点密集成片，色紫红，或见鼻衄，甚则神昏抽搐。舌绛起刺，苔黄糙，脉数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5ec1b685-d43c-4eea-af6d-842912d8e29c', '8811e45f-75c8-43d8-b6ea-6872063672dc', '051101', '0511', '邪郁肺卫', 1, '', '发热恶风，喷嚏流涕，咳嗽，疹色浅红，先起于头面，继及于身躯各部分，分布均匀，稀疏细小，2～3日后消退，有痒感。舌苔薄黄，脉浮数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ce8b06d8-888e-40ae-a6c8-487a0cfc82fe', '8811e45f-75c8-43d8-b6ea-6872063672dc', '051102', '0511', '邪热炽盛', 1, '', '高热口渴，心烦不宁，疹色鲜红或紫暗，小便短赤，大便秘结。舌质红，苔黄厚或黄糙，脉数有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '888a8601-3311-4254-80b9-f607e07530e9', 'e4bfe5da-a85d-4fa2-8b87-f7e7ddf9c9d2', '051201', '0512', '邪侵肺卫', 1, '', '畏寒发热，咽红肿痛，皮疹隐隐。舌尖红，苔薄白，脉浮数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '60efed0c-3f8c-41b6-8c73-1bc1726a08be', 'e4bfe5da-a85d-4fa2-8b87-f7e7ddf9c9d2', '051202', '0512', '邪人气营', 1, '', '高热，烦躁不安，口渴欲饮，咽部红肿疼痛，甚则溃烂，皮疹成片，猩红若丹。若热毒内陷，出现高热昏迷，烦躁谵语，或有抽风。皮疹呈紫红色，或伴有瘀点。舌绛起刺，苔剥，脉数有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '20d371b0-2dd9-4e36-9885-7c07a379bf0f', 'e4bfe5da-a85d-4fa2-8b87-f7e7ddf9c9d2', '051203', '0512', '疹后阴伤', 1, '', '身热渐退，皮疹渐消，继则脱屑退皮，咽部赤烂疼痛渐减，午后或有低热。舌红有刺，脉细数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b2de8397-0e0a-43ea-bf4d-2491d507be9a', 'cadd2374-0df7-4a70-856d-2ae065ebc1f3', '051301', '0513', '风热犯表', 1, '', '发热轻微，鼻塞流涕，偶有喷嚏及咳嗽，疹色淡红而润，疱浆清亮，点粒稀疏，躯干为多，二便如常。舌苔薄白，脉浮数或略数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '54331d8f-1ec2-4691-9adf-5df019f6656b', 'cadd2374-0df7-4a70-856d-2ae065ebc1f3', '051302', '0513', '热毒炽盛', 1, '', '壮热烦渴，面赤唇红，便秘溲赤，痘大而密，疹色红赤或紫暗，疱浆较混，根脚较硬，口、眼等处亦见疱疹或溃疡。舌苔黄糙而于，脉数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '954a4966-9555-473a-986f-5ef598f6921f', '4726022d-0426-48b0-9796-860cf7917402', '051401', '0514', '温毒袭表', 1, '', '发热轻，一侧或两侧耳下腮部肿大，压之疼痛有弹性感。舌尖红，苔薄白，脉浮数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '28ef7465-dca0-4e4e-b3cd-cb1b4facf071', '4726022d-0426-48b0-9796-860cf7917402', '051402', '0514', '热毒蕴结', 1, '', '壮热，头痛，烦躁，腮部漫肿，疼痛拒按。舌红苔黄，脉数有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '99bdc558-dd23-4e06-b734-8051868157d5', '4726022d-0426-48b0-9796-860cf7917402', '051403', '0514', '毒陷心肝', 1, '', '腮部肿胀，高热不退，嗜睡，颈强，呕吐，甚则昏迷，抽风。舌质红绛，苔黄糙，脉洪数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b400a936-9d28-4f70-875a-48d857b108b6', '4726022d-0426-48b0-9796-860cf7917402', '051404', '0514', '邪窜肝经', 1, '', '腮部肿胀，发热，男性睾丸肿痛，女性少腹痛。舌质偏红，苔黄，脉弦数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7460cfcb-efb8-4451-842a-76e3a0120dd0', '8a5810e2-7932-4fe2-8237-b7c43dd76b7f', '051501', '0515', '初咳期', 1, '', '微热，喷嚏，咳嗽逐渐加重，昼轻夜重。偏于风寒者，伴恶寒，痰稀色白，舌苔薄白，脉浮紧；偏于风热者，伴咽红，痰稠不易咯出，舌苔薄黄，脉浮数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1c9c7760-7754-4176-84b7-f6579615a77c', '8a5810e2-7932-4fe2-8237-b7c43dd76b7f', '051502', '0515', '痉咳期', 1, '', '咳嗽阵作，侯轻夜重，咳时面红耳赤，涕泪交流，咳后回吼，甚至吐出乳食痰液后，痉咳方可暂停。剧咳时可见痰中带血丝，甚则鼻衄或结膜下出血，可见舌系带溃疡。舌苔黄，脉数有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '17b763ba-337b-44d0-94ae-9eaa540e0030', '8a5810e2-7932-4fe2-8237-b7c43dd76b7f', '051503', '0515', '恢复期', 1, '', '', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '53830c3e-041f-4ebf-9a01-a8b4e599bfed', '8a5810e2-7932-4fe2-8237-b7c43dd76b7f', '051504', '0515', '脾气亏虚', 1, '', '形体虚弱，咳声低微，痰多稀白，纳呆便溏，神疲乏力。舌质偏淡，苔薄白，脉沉有力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fceaa46e-2afb-4d63-b257-61561af37af8', '8a5810e2-7932-4fe2-8237-b7c43dd76b7f', '051505', '0515', '肺阴亏虚', 1, '', '形体虚弱，干咳少痰，两颧发红，手足心热，夜寐盗汗。舌质偏红，少苔，脉细数无力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f022b913-4178-4f3b-b00c-4ea593789587', '9cb534e1-babe-480a-8c95-737687c91de2', '051601', '0516', '卫气同病', 1, '', '突然发热，常伴有呕吐。嗜睡，较大儿童可自诉头痛，怕风，神志多清醒。高热时可有惊厥或烦躁不宁。夹湿者，舌苔白腻；偏热者，舌红，苔黄，脉浮数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c47e6e17-8f2d-4f9c-8cca-e1e75da973b3', '9cb534e1-babe-480a-8c95-737687c91de2', '051602', '0516', '气营两燔', 1, '', '持续高热，神志不清或时清时昧，颈项强直，四肢抽搐，甚则喉间痰声漉漉，大便秘结。舌质红绛，苔多黄糙或灰暗，脉数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f57a76b7-7fc7-4762-a269-e82d4db1822d', '9cb534e1-babe-480a-8c95-737687c91de2', '051603', '0516', '热入营血', 1, '', '发热不退，抽搐频繁，牙关紧闭，两目上视，面色灰暗，可见呕血，甚或突然呼吸停止，肢厥脉绝，卒然闭脱。舌质紫暗而干，或舌卷僵硬，脉细数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '408be97e-bfaa-449e-bedf-f45f0a732c9b', '9cb534e1-babe-480a-8c95-737687c91de2', '051604', '0516', '余热未清', 1, '', '低热或不规则发热，口干喜饮，两颧潮红，舌光红少苔，或舌红起刺者，为病后津液耗伤，属阴虚发热；如汗出不温，面色淡白，舌质淡嫩，苔薄白者，为营虚卫弱，阳气不足。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '977288ec-b701-4ad4-a4a7-bc225eae0cda', '9cb534e1-babe-480a-8c95-737687c91de2', '051605', '0516', '痰蒙清窍', 1, '', '面色少华，意识不清或痴呆，失语，喉有痰声，吞咽困难，或狂躁不宁。舌质红，苔黄者，为痰火内扰；深度昏迷，舌质不红，苔厚腻者，为痰浊内蒙。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a88a8ec5-4cb1-42da-934e-3427649494dd', '9cb534e1-babe-480a-8c95-737687c91de2', '051606', '0516', '肝风内动', 1, '', '其肢体呈强直性瘫痪或角弓反张者，为风窜络脉；若震颤抖动，不自主动作者，为阴虚风动。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1061ad87-7ab6-4589-bd38-2f81f44672bb', 'ca31a2aa-fc6e-47de-b6c3-9fff05ab59cc', '051701', '0517', '暑伤肺胃', 1, '', '发热，口渴，多尿，无汗或少汗，烦躁不安，口唇干红。舌质红，苔薄白或薄黄，脉数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '604817e7-7751-4032-af56-c22b721cfca7', 'ca31a2aa-fc6e-47de-b6c3-9fff05ab59cc', '051702', '0517', '上盛下虚', 1, '', '发热，口渴多饮，多尿次频，汗闭，精神萎靡，虚烦不安，面色淡白，下肢欠温，胃纳减退，或大便稀薄。舌质淡，苔簿，脉细数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0f3a84c3-cef6-48ab-8a5b-000158ef9650', 'c00cdad0-0123-4831-9049-e55f82b4a2ea', '051801', '0518', '湿困脾胃', 1, '', '胸闷泛恶，倦怠乏力，面色萎黄，食欲不振，身热不扬，大便不调，小便色黄。舌质淡红，苔白腻或微黄腻，脉濡数。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c1c9d8a5-ab35-4ce3-87e0-352c5340922f', 'c00cdad0-0123-4831-9049-e55f82b4a2ea', '051802', '0518', '脾胃气虚', 1, '', '精神萎靡，倦怠乏力，口淡无味，饮食少思，大便溏薄。舌质偏淡，苔薄白，脉濡缓。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '41a7ade8-7c7b-4e6c-a736-721c31440a92', '2bcfb60a-48b6-40e7-9bc8-ea09b38c39a8', '051901', '0519', '肾气不足', 1, '', '睡中遗尿，尿量多，尿色清，熟睡，不易叫醒，面色淡白，精神不振，形寒肢冷。舌质淡，苔白，脉沉迟无力。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ce0fff66-02bc-4684-9814-902bfb73322a', '2bcfb60a-48b6-40e7-9bc8-ea09b38c39a8', '051902', '0519', '脾肺气虚', 1, '', '睡中遗尿，尿频而量多，面色无华，神疲乏力，食欲不振，大便溏薄。舌偏淡，脉缓细。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4e89e3c3-51ca-43af-ba81-426127897570', '2bcfb60a-48b6-40e7-9bc8-ea09b38c39a8', '051903', '0519', '肝经湿热', 1, '', '睡中遗尿，尿频量少，性情急躁，手足心热，唇红而干。舌质红，苔黄，脉弦滑。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b2b5d168-996e-455a-83f0-8397deba4be8', '186120d2-8b08-4f10-9309-2af18ef0650a', '052001', '0520', '湿热内蕴', 1, '', '面目皮肤发黄，颜色鲜明，状如橘色，烦躁啼哭，小便黄赤，大便秘结或灰白。舌红，苔黄厚腻，指纹滞。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ef6bbaa7-2619-407b-9a14-2afe6363649c', '186120d2-8b08-4f10-9309-2af18ef0650a', '052002', '0520', '脾虚湿困', 1, '', '面目皮肤发黄，颜色晦暗，精神倦怠，不欲吮乳，时时啼哭，腹胀便溏，或大便灰白，小便黄少。唇舌偏淡，苔白滑，指纹淡。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cfbab0b5-58f4-4e10-9e50-2543c0761dbc', '186120d2-8b08-4f10-9309-2af18ef0650a', '052003', '0520', '气血瘀滞', 1, '', '面目皮肤发黄，颜色晦滞，日益加重，腹部胀满，青筋暴露，肝脾肿大质硬，小便短黄，大便秘结或灰白，唇色暗红，或衄血。舌见瘀点，指纹紫。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e4713bc9-1837-46f8-a556-5be3287e1eed', 'd431a08e-5ecd-4971-8ebb-c4002de099e9', '052101', '0521', '疫毒内闭证', 1, '', '突然高热，恶心呕吐，烦躁不宁，甚则反复惊厥，神志欠清，或痢下脓血，舌质红，苔黄或灰糙，脉数有力。。', '2008/04/10 12:04:44', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a898f98f-b792-47d1-a0a5-eee8128e3634', 'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', '070602', '0706', '阴虚火旺', 1, '', '反复咯血，血色鲜红，干咳咽燥。舌红苔黄少津，脉细数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3d6da817-d5df-4d00-b510-85af7b050a3f', 'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', '070603', '0706', '痰热壅肺', 1, '', '咯（咳）血量多，血色鲜红或夹有黄痰，或脓痰腥臭，心烦口渴。舌红苔黄腻，脉滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '62eba1bc-a9f0-47d3-a9f3-70f5a4930592', 'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', '070604', '0706', '气虚血瘀', 1, '', '反复咯（咳）血，血色淡红或夹紫黯血块，气短胸闷，易汗。舌淡或有紫色瘀斑，苔薄白，脉细涩。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '74319a35-33d0-4072-ae87-aa4d95af1bab', '79f76a66-ee21-4ae9-b0e2-f612c16bc1ff', '070701', '0707', '邪郁少阳', 1, '', '寒热往来，或恶寒发热，胸胁疼痛，咳嗽痰少。舌苔薄白或黄，脉弦数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2bd31a9c-105d-40f0-8c2c-72e52b9a3c6a', '79f76a66-ee21-4ae9-b0e2-f612c16bc1ff', '070702', '0707', '饮停胸胁', 1, '', '咳唾时胸胁引痛，转侧不利，偏卧于病侧则痛缓，肋间胀满，呼吸息促。舌苔薄白，脉象沉弦。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '653793e5-1a04-49a0-828b-9a0128a88fac', '79f76a66-ee21-4ae9-b0e2-f612c16bc1ff', '070703', '0707', '肺络不畅', 1, '', '胸胁疼痛，呼吸不畅，或有闷咳，迁延不已。百苔薄，脉弦细。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd0ab2b3f-2f33-464e-9a09-61192d2225e1', '497f4299-c6fc-48ea-9420-ee4f5d47b841', '070801', '0708', '风寒束肺', 1, '', '喘急胸闷，咳嗽痰多清稀，伴有恶寒发热，头痛等症。舌苔薄白，脉浮紧。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '44c8df81-6fdc-4668-9e68-3591bc6ba5b2', '497f4299-c6fc-48ea-9420-ee4f5d47b841', '070802', '0708', '风热犯肺', 1, '', '喘促气粗，咳嗽痰黄而稠粘，心胸烦闷，口干而渴，可有发热恶风。舌边红，苔薄黄，脉浮数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3931be0f-6ea4-41b3-a6fc-33f9a8482400', '497f4299-c6fc-48ea-9420-ee4f5d47b841', '070803', '0708', '痰湿蕴肺', 1, '', '喘咳胸闷，痰多易咯，痰粘或咯吐不爽，胸中窒闷，口腻，脘痞腹胀。舌质淡，舌苔白腻，脉弦滑。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7f8f96f8-6dba-46f5-9f41-07d7c4a8dd38', '497f4299-c6fc-48ea-9420-ee4f5d47b841', '070804', '0708', '水气凌心', 1, '', '气喘息涌，痰多呈泡沫状，胸满不能平卧，肢体浮肿，心悸怔忡，尿少肢冷，舌苔白滑，脉弦细数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cc9e758a-01ca-4d18-900d-ee5fef86b6a6', '497f4299-c6fc-48ea-9420-ee4f5d47b841', '070805', '0708', '肺脾两虚', 1, '', '喘息短促无力，语声低微，自汗心悸，面色 白，神疲乏力，食少便塘，舌淡苔少，脉弱。或口干咽燥，舌红，脉细。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2c0a3216-6b09-4b7b-8d4d-28fbb38b6fbf', '497f4299-c6fc-48ea-9420-ee4f5d47b841', '070806', '0708', '肺肾两虚', 1, '', '喘促日久，心悸怔忡，动则喘咳，气不接续，胸闷如窒，不能平卧，痰多而粘，或心烦不寐，唇甲紫绀。舌质紫或舌红苔少，脉微疾或结、代。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2ea72801-d259-439e-92b6-eb48b68002d6', '8219b66f-4567-4647-a700-704b45439f29', '07090101', '070901', '冷哮', 1, '', '喉中哮鸣有声，胸膈满闷，咳痰稀白，面色晦滞。或有恶寒、发热、身痛。舌质淡，苔白滑，脉浮紧。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b4e54049-8c7d-4bf6-9da7-b939ae6bdb29', '8219b66f-4567-4647-a700-704b45439f29', '07090102', '070901', '热哮', 1, '', '喉中哮鸣如吼，气粗息涌，胸膈烦闷，呛咳阵作，痰黄粘稠，面红，伴有发热、心烦口渴。舌质红，苔黄腻，脉滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3aa8b973-8875-4d9a-823f-7b35504851e3', '8219b66f-4567-4647-a700-704b45439f29', '07090103', '070901', '虚哮', 1, '', '反复发作，甚者持续喘哮，咯痰无力，声低气短，动则尤甚，唇爪甲紫绀。舌质紫暗，脉弱。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '75488fb1-fbbb-4e1f-bc5e-6857524e442f', '8219b66f-4567-4647-a700-704b45439f30', '07090201', '070902', '肺气亏虚', 1, '', '平素自汗，怕风，常易感冒，每因气候变化而诱发。发病前喷嚏频作，鼻塞流清涕。舌苔薄白，脉濡。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0ab07a65-358a-4685-8caf-b8d6beb5035f', '8219b66f-4567-4647-a700-704b45439f30', '07090202', '070902', '脾气亏虚', 1, '', '平素痰多，倦怠无力，食少便溏，每因饮食失当而引发。舌苔薄白，脉细缓。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0f9eec6c-f86b-485e-8ede-e436527063ba', '8219b66f-4567-4647-a700-704b45439f30', '07090203', '070902', '肾气亏虚', 1, '', '平素气息短促，动则为甚。腰酸腿软，脑转耳鸣，不耐劳累，下肢欠温，小便清长。舌淡，脉沉细。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '133ccfcd-9e63-4e3b-873d-d181cf204e4d', 'dc721271-afd0-4be7-b9c1-08ff2a847d95', '071001', '0710', '肝胃气滞', 1, '', '胃脘痞胀疼痛或攻窜胁背，嗳气频作。苔薄白，脉弦。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8a929fe4-371e-4832-b307-52a60f6efd17', 'dc721271-afd0-4be7-b9c1-08ff2a847d95', '071002', '0710', '寒邪犯胃', 1, '', '胃脘冷痛暴作，呕吐清水痰涎，畏寒喜暖，口不渴。苔白，脉弦紧。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '58da2700-6622-461d-ab2a-9a8c1d9aeb3f', 'dc721271-afd0-4be7-b9c1-08ff2a847d95', '071003', '0710', '胃热炽盛', 1, '', '胃痛急迫或痞满胀痛，嘈杂吐酸，心烦，口苦或粘。舌质红，苔黄或腻，脉数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd2eae03a-702e-4623-9bdf-1f9c6098bb84', 'dc721271-afd0-4be7-b9c1-08ff2a847d95', '071004', '0710', '食滞胃肠', 1, '', '胃脘胀痛，嗳腐吞酸或呕吐不消化食物，吐后痛缓。苔厚腻，脉滑或实。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5ae7c7ef-9d2e-499d-a4d7-2c5879009871', 'dc721271-afd0-4be7-b9c1-08ff2a847d95', '071005', '0710', '瘀阻胃络', 1, '', '胃痛较剧，痛如针刺或刀割，痛有定处，拒按，或大便色黑。舌质紫暗，脉涩。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '77ee7b62-9369-482b-9fba-5821ed6bd9ca', 'dc721271-afd0-4be7-b9c1-08ff2a847d95', '071006', '0710', '胃阴亏虚', 1, '', '胃痛隐作，灼热不适，嘈杂似饥，食少口干，大便干燥。舌红少津，脉细数。。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9a235d9e-de95-4af2-8a09-708cf30d7dc0', 'dc721271-afd0-4be7-b9c1-08ff2a847d95', '071007', '0710', '脾胃虚寒', 1, '', '胃痛绵绵，空腹为甚，得食则缓，喜热喜按，泛吐清水，神倦乏力，手足不温，大便多溏。舌质淡，脉沉细。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e042c631-afa1-4905-8f72-5debc6d63212', '9b6fb7f8-5285-46cf-a39b-fb4aa9f76ca5', '071101', '0711', '痰气阻膈', 1, '', '吞咽梗噎，胸膈痞满，泛吐痰涎，病情可随情绪变化而增减。苔薄腻，脉弦滑。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '62972ce3-36a9-49f0-9b3e-47400aae4758', '9b6fb7f8-5285-46cf-a39b-fb4aa9f76ca5', '071102', '0711', '瘀血阻膈', 1, '', '饮食难下，食入即吐，吐出物如赤豆汁，胸膈疼痛，肌肤枯燥，形体消瘦。舌质红有紫点、紫斑，脉细涩。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ddf096aa-4620-478e-9235-0f28e240e56e', '9b6fb7f8-5285-46cf-a39b-fb4aa9f76ca5', '071103', '0711', '津亏热结', 1, '', '食入格拒不下，入而复出，形体消瘦，口干咽燥，大便干结，五心烦热。舌质光红少津，脉细弦数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bd70af65-f1e6-4c93-ab34-9683423475dd', '9b6fb7f8-5285-46cf-a39b-fb4aa9f76ca5', '071104', '0711', '气虚阳微', 1, '', '水饮不下，泛吐多量粘液白沫，形瘦神衰，畏寒肢冷，面浮足肿。舌质淡紫，苔白滑，脉弱。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3b469a50-1389-4688-aaf6-7ff4e9f17a14', 'd162865b-92be-4d8d-a0a0-e1fb08b926a3', '071201', '0712', '寒邪犯胃', 1, '', '呕吐食物残渣，量多如喷，胸脘满闷，可伴有恶寒发热，头身疼痛。苔白腻，脉浮滑。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e4464693-2bf4-4836-a9cc-63aa0bd91acd', 'd162865b-92be-4d8d-a0a0-e1fb08b926a3', '071202', '0712', '食滞胃肠', 1, '', '呕吐酸腐食物，吐出为快，大便秘结或秽臭不爽，嗳气厌食，脘痞腹胀。苔厚腻或垢，脉滑或沉实。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd8a824df-e669-484a-b996-1b181b0d8da2', 'd162865b-92be-4d8d-a0a0-e1fb08b926a3', '071203', '0712', '痰饮停胃', 1, '', '呕吐清水痰涎，脘闷痞满，口干不欲饮，饮水则吐，或头眩心悸。苔白滑或腻，脉弦滑。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '89521c78-11c7-4789-ad9a-5a3c83069062', 'd162865b-92be-4d8d-a0a0-e1fb08b926a3', '071204', '0712', '肝气犯胃', 1, '', '呕吐泛酸，口苦嗳气，脘胁烦闷不适，嘈杂。舌边红，苔薄腻或微黄，脉弦。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dcb3b3fe-3d35-4ae3-8871-5c0ee3894b12', 'd162865b-92be-4d8d-a0a0-e1fb08b926a3', '071205', '0712', '脾胃虚寒', 1, '', '呕吐反复，迁延日久，劳累过度或饮食不慎即发。神疲倦怠，胃脘隐痛，喜暖喜按，畏寒肢冷，面色huang(恍)白。舌质淡或胖，苔薄白，脉弱。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'adce866a-395b-43f6-abd8-550286af5093', 'd162865b-92be-4d8d-a0a0-e1fb08b926a3', '071206', '0712', '胃阴亏虚', 1, '', '时时干呕，呕吐少量食物粘液，反复发作。胃脘嘈杂，饥不欲食，口燥咽干，大便干结。舌红少津，脉细数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'da582cf5-15b2-413c-850c-fe933728477a', '5835b53c-98b1-41d3-a851-5485ca48c0b3', '071301', '0713', '胃热炽盛', 1, '', '吐血量多，色红或紫暗，常夹有食物残渣，脘腹胀闷甚则疼痛，口臭便秘，或大便色黑。舌质红，苔黄，脉滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4b6c785f-f49e-4293-b179-47fd5445783c', '5835b53c-98b1-41d3-a851-5485ca48c0b3', '071302', '0713', '肝火犯胃', 1, '', '吐血色鲜红或紫暗，呕哕频作，嘈杂泛酸，胃脘痞胀灼热，心烦易怒，胁痛口苦。舌质红，苔黄，脉弦数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a4c59ead-c8e8-4f2c-938b-d1e601565dde', '5835b53c-98b1-41d3-a851-5485ca48c0b3', '071303', '0713', '瘀阻胃络', 1, '', '吐血紫暗，胃脘疼痛，固定不移，痛如针刺或刀割，口干不欲饮。舌质紫或有瘀斑，苔薄，脉涩。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5a590d1e-e89b-400a-9dab-150c45076f56', '5835b53c-98b1-41d3-a851-5485ca48c0b3', '071304', '0713', '脾不统血', 1, '', '吐血反复不止，时轻时重，血色暗淡，胃脘隐痛，喜按，神疲畏寒，心悸气短，自汗，便溏色黑，面色苍白。舌质淡，苔白，脉弱。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '82701207-2643-443f-8a83-e693c87d2efc', '5835b53c-98b1-41d3-a851-5485ca48c0b3', '071305', '0713', '肝胃阴虚', 1, '', '吐血员多色红，脘胁隐痛，嘈杂吐酸，烦热颧红，盗汗，咽干口燥。舌红无苔，脉细弦数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '153f95a3-f481-4b78-8651-770917861a96', 'd9daa152-8514-4d79-ab96-d36a45ec6ab5', '071401', '0714', '胃肠积热', 1, '', '便干挟血，色鲜紫或暗红，口苦口干，嘈杂烦渴，脘腹痞满胀痛。舌红，苔黄燥，脉洪数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b68182b8-f34b-48d7-8f7d-ab5be0122c34', 'd9daa152-8514-4d79-ab96-d36a45ec6ab5', '071402', '0714', '湿热蕴结', 1, '', '大便下血，色暗红或紫黑如赤豆汁，或下血污浊腥臭，便解不畅，脘腹胀痛；舌红，苔黄腻，脉滑数。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a5be087a-6ec5-44f8-8ee5-8979e7998466', 'd9daa152-8514-4d79-ab96-d36a45ec6ab5', '071403', '0714', '肠风伤络', 1, '', '便下鲜血，血下如溅，大便干结或为便泄。舌红苔黄，脉弦。', '2008/04/10 03:43:35', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '28bae659-7fd5-44da-90ba-523e99d86de7', 'd9daa152-8514-4d79-ab96-d36a45ec6ab5', '071404', '0714', '脾胃虚寒', 1, '', '病程日久，便血紫暗或色黑如柏油样，脘腹隐痛，喜按喜暖，畏寒肢冷，食少便溏。舌淡，苔白，脉细弱。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c755e994-bf52-4cfd-9c7e-abaf18dbda28', 'fdacd65f-4e13-4eff-8a49-3d49415fe8ce', '071501', '0715', '湿热蕴结', 1, '', '腹痛，里急后重，大便赤白脓血，每日数次到数十次，肛门灼热，可伴发热。舌红，苔黄腻，脉滑数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '051a6493-7ab1-43f8-9170-d5b2cd6a0bb9', 'fdacd65f-4e13-4eff-8a49-3d49415fe8ce', '071502', '0715', '寒湿困脾', 1, '', '腹痛，大便赤白粘冻，伴有头身困重，脘痞纳少，口粘不渴。舌苔白腻，脉濡缓。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c2b52e04-d61b-4171-9f7f-66ff9fa90415', 'fdacd65f-4e13-4eff-8a49-3d49415fe8ce', '071503', '0715', '脾阳亏虚', 1, '', '病久迁延不已，大便呈白粘冻状，排便不畅，腹部冷痛时作，畏寒肢冷。舌淡，苔白滑，脉弱。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '73578a6f-4963-4491-b2f4-66bbb2812e69', 'fdacd65f-4e13-4eff-8a49-3d49415fe8ce', '071504', '0715', '热毒炽盛', 1, '', '发病骤急，腹痛剧烈，大便呈鲜紫脓血，气味腐臭。或恶心呕吐，噤口不食。或腹泻前即见高热，腹满胀痛，烦躁不安，面色苍白，四肢发冷，甚至昏迷。舌质红绛，苔黄燥，脉滑数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ad744e61-d8a1-4fdd-8caa-017538abdc4a', 'fdacd65f-4e13-4eff-8a49-3d49415fe8ce', '071505', '0715', '正虚邪恋', 1, '', '腹泻时发时止，发时大便赤白粘冻或果酱样，腹痛后重；不发时疲劳乏力，食少，腹胀或隐痛。舌质淡，苔薄白，脉细。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3d0007ec-b88a-4d97-ac98-241b25bd0f84', '6ea8328b-0849-4957-81bf-16b9ea0b6b54', '071601', '0716', '寒湿困脾', 1, '', '大便清稀或如水样，腹痛肠鸣，畏寒食少。苔白滑、脉濡缓。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '86fc8cd1-acc7-4534-83c6-da31c2dc8748', '6ea8328b-0849-4957-81bf-16b9ea0b6b54', '071602', '0716', '肠道湿热', 1, '', '腹痛即泻，泻下急迫，粪色黄褐秽臭，肛门灼热，可伴有发热。舌红，苔黄腻，脉濡数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'aae32c72-8ddf-420d-b2b9-4dfc6f29ad13', '6ea8328b-0849-4957-81bf-16b9ea0b6b54', '071603', '0716', '食滞胃肠', 1, '', '腹满胀痛，大便臭如败卵，泻后痛减，纳呆，嗳腐吞酸。舌苔垢或厚腻，脉滑。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '901b0543-db58-419f-b1ad-4fe5aa4492eb', '6ea8328b-0849-4957-81bf-16b9ea0b6b54', '071604', '0716', '肝气郁滞', 1, '', '腹痛肠鸣泄泻，每因情志不畅而发，泻后痛缓。舌质红，苔薄白，脉弦。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '03e23263-f04a-4a59-ab2a-cd03089ade4b', '6ea8328b-0849-4957-81bf-16b9ea0b6b54', '071605', '0716', '脾气亏虚', 1, '', '大便溏薄，夹有不消化食物，稍进油腻则便次增多，伴有神疲乏力。舌质淡，苔薄白，脉细。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2ccb7e57-01dc-4d30-bdc1-478651bba5e3', '6ea8328b-0849-4957-81bf-16b9ea0b6b54', '071606', '0716', '肾阳亏虚', 1, '', '晨起泄泻，大便夹有不消化食物，脐腹冷痛，喜暖，形寒肢冷，舌淡胖，苔白，脉沉细。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd1e22246-8974-4f7d-8fe4-9602b32f80fd', '83886669-4da0-48aa-9dde-c1169e25be3c', '071701', '0717', '肝胆湿热', 1, '', '身目俱黄，黄色鲜明。发热口渴，心中懊，口干而苫，恶心欲吐，腹满胁痛，大便秘结或呈灰白色，小便短黄。舌红、苔黄腻，脉弦数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '78e370e2-d5fb-49fd-afb0-1a6f70fc6dc7', '83886669-4da0-48aa-9dde-c1169e25be3c', '071702', '0717', '湿困脾胃', 1, '', '身目俱黄，黄色晦滞，头重身困，胸脘痞满，恶心纳少，腹胀，大便溏垢。苔腻微黄，脉弦滑或濡缓。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cad0b7eb-0a47-4b99-8589-22e33163f105', '32abd504-6f65-4530-89d7-af814f529ded', '082603', '0826', '中气下陷', 1, '', '小腹坠胀，小便欲解不爽，尿失禁或夜间遗尿。精神倦怠，少气懒言。舌质淡，苔薄白，脉濡细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ad51d34d-25e2-43b9-8116-a6060cbee9cf', '32abd504-6f65-4530-89d7-af814f529ded', '082604', '0826', '肾阴亏虚', 1, '', '小便频数不爽，淋漓不尽。伴有头晕目眩，腰酸膝软，失眠多梦，咽干。舌红，苔黄，脉细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '53a18b8d-6923-46d2-874c-46322ae21a6d', '32abd504-6f65-4530-89d7-af814f529ded', '082605', '0826', '肾阳虚损', 1, '', '排尿无力，失禁或遗尿，点滴不尽。面色huang(恍)白，神倦畏寒，腰膝酸软无力，手足不温。舌质淡。苔白，脉沉细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cfc96909-0621-4750-acd9-3c8905a20ae1', '32abd504-6f65-4530-89d7-af814f529ded', '082606', '0826', '气滞血瘀', 1, '', '小便努责方出或点滴全无，会阴、小腹胀痛，偶有血尿或血精。舌质紫暗或有瘀斑，苔白或黄，脉沉弦或细涩。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '068271e0-263e-4e06-8ce1-35d88e87b669', '7934a3b3-43ff-4be4-9b63-b80f5789c3ae', '082701', '0827', '气滞血瘀', 1, '', '少腹、会阴、睾丸坠胀不适，或有血尿，血精。舌质紫或瘀点，苔白或黄，脉沉涩。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '18f45ed7-1662-413b-9de8-6aec42cff99d', '7934a3b3-43ff-4be4-9b63-b80f5789c3ae', '082702', '0827', '湿热蕴结', 1, '', '尿频、尿急、尿痛，有灼热感，排尿或大便时尿道有白浊溢出。会阴、腰骶、睾丸有坠胀疼痛。苔黄腻，脉滑数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '39bdd077-77f5-4ccd-9f18-141d07839ec3', '7934a3b3-43ff-4be4-9b63-b80f5789c3ae', '082703', '0827', '阴虚火旺', 1, '', '腰膝酸软，头昏眼花，失眠，多梦，遗精或血精，阳事易兴，排尿或大便时尿道有白浊滴出。舌红，少苔，脉细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '31243187-8f25-457d-ac1d-e2b366b8b251', '7934a3b3-43ff-4be4-9b63-b80f5789c3ae', '082704', '0827', '肾阳虚损', 1, '', '头昏神疲，腰酸，膝冷，阳萎早泄，甚至稍劳后即尿道有白浊溢出。舌质淡胖，苔白，脉沉细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'de3ccd5a-8039-4bb5-9fab-ff1180598e57', '1bc5a4af-6342-431f-8b7d-00876f54693d', '082801', '0828', '湿热下注', 1, '', '疮面色暗，或上附脓苔，脓水浸淫，秽臭难闻，四周漫肿灼热。伴有湿疹，痛痒时作，甚有恶寒发热。舌苔黄腻，脉数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3f2ff9cf-b6e3-4c6f-aa51-9b7306ee411e', '1bc5a4af-6342-431f-8b7d-00876f54693d', '082802', '0828', '脾虚湿盛', 1, '', '病程日久，疮面色暗，黄水浸淫，患肢浮肿，纳食腹胀，便溏，面色萎黄。舌淡，苔白腻，脉沉无力。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '13b5205f-0d87-426a-a052-3ed0b5bde45a', '1bc5a4af-6342-431f-8b7d-00876f54693d', '082803', '0828', '气虚血瘀', 1, '', '溃烂经年，腐肉已脱，起白色厚边，疮面肉色苍白，四周肤色黯黑，板滞木硬。舌质淡紫，苔白腻，脉细涩。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6a97990c-e643-4cab-aae4-6bc5999520dc', '5ff2f19d-720d-4331-8870-8a76088b9c1c', '082901', '0829', '气滞血瘀', 1, '', '局部皮肤出现褐色红斑，继而紫暗红肿，或有破损。苔脉随原发疾病而异。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c3c35665-c68a-4677-9e6d-ea6ec4e1985b', '5ff2f19d-720d-4331-8870-8a76088b9c1c', '082902', '0829', '蕴毒腐溃', 1, '', '褥疮溃烂，腐肉及脓水较多，或有恶臭，重者溃烂可深及筋骨，四周漫肿。伴有发热或低热，口苦且干，形神萎靡，不思饮食等。舌红，苔少，脉细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '527c80e0-aded-4a51-911a-feafa1781313', '5ff2f19d-720d-4331-8870-8a76088b9c1c', '082903', '0829', '气血两虚', 1, '', '疮面腐肉难脱，或腐肉虽脱，新肌色淡，愈合缓慢。伴有面色huang(恍)白，神疲乏力，纳差食少等。舌质淡，苔少，脉沉细无力。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0b2a38b7-1239-4f6f-9c8b-20aa0b0b9574', '33c518c4-703f-4052-8ebf-bdb9ae3e6d79', '083001', '0830', '湿热瘀阻', 1, '', '除寒战高热等症状外，患肢疼痛彻骨，不能活动。继则局部胖肿，皮色不变，灼热，有明显的骨压痛和患肢叩击痛。苔黄，脉数（', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dd86b593-dab5-449d-ac41-97babffbd4ec', '33c518c4-703f-4052-8ebf-bdb9ae3e6d79', '083002', '0830', '热毒炽盛', 1, '', '起病约1～2周后，高热持续不退。患肢胖肿，疼痛剧烈，皮肤掀红，灼热，内已酿脓。苔黄腻，脉洪数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dfb9e7f0-8d49-43e3-a605-a1cabc1d5290', '33c518c4-703f-4052-8ebf-bdb9ae3e6d79', '083003', '0830', '脓毒蚀骨', 1, '', '溃后急性症状逐渐缓解，脓水淋漓，久则形成窦道。患肢肌肉萎缩，可摸到粗大之骨骼，以探针检查，常可触到粗糙死骨。可伴乏力，神疲，头昏，心悸，低热等症状。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8e18f0b4-6737-4869-ad6f-2aa4cd0668ea', '4c9d8db8-1197-4c6c-9587-531ba0aed57e', '083101', '0831', '阳虚痰凝', 1, '', '初起外形既不红热，又不肿胀，仅感病变关节隐隐酸痛。继则关节活动障碍，动则痛甚，全身情况无明显变化。舌淡，苔薄，脉濡细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '33d08c97-8caa-46ba-8391-73edf2f12730', '4c9d8db8-1197-4c6c-9587-531ba0aed57e', '083102', '0831', '阴虚内热', 1, '', '数月后，在原发和继发部位渐渐漫肿，皮色微红，形成脓肿。伴有午后潮热，颧红，夜间盗汗，口燥咽干，食欲减退，或咳嗽痰血。舌红，少苔，脉细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2ceb83f9-cc34-4184-929f-ad3ef4424b7c', '4c9d8db8-1197-4c6c-9587-531ba0aed57e', '083103', '0831', '肝肾亏虚', 1, '', '溃脓后疮口排出稀薄脓液，或夹有败絮样物，形成窦道。如病在四肢关节，患肢肌肉萎缩，畸形。病在颈、胸、腰椎者，则强直不遂，甚或下肢瘫痪不用，二便潴留或失禁。形体消瘦，面色huang(恍)白，畏寒，心悸，失眠，自汗盗汗。舌质淡红，苔白，脉细数或虚数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4a0322f8-e21c-4613-b7ca-4db1892af4cc', 'c947406e-6e96-44b9-bc7b-3205d076ca8c', '083201', '0832', '寒湿阻络', 1, '', '患趾（指）喜暖怕冷，肤色苍白冰凉，麻木疼痛，遇冷痛剧。步履不利，多走则疼痛加剧，小腿酸胀，稍歇则痛缓（间歇性破行）。舌苔白腻，脉沉细，趺阳脉减弱或消失。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '91c76645-698c-4434-804d-67b75d402fa2', 'c947406e-6e96-44b9-bc7b-3205d076ca8c', '083202', '0832', '血脉瘀阻', 1, '', '患趾（指）酸胀疼痛加重，步履沉重乏力，活动艰难。患趾（指）肤色由苍白转为暗红，下垂时更甚，抬高则见苍白。小腿可有游走性红斑、结节或硬索，疼痛持续加重，彻夜不能入寐。舌质暗红或有瘀斑，苔白，脉弦或涩。趺阳脉消失。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a81d2521-ba63-40c3-87d2-071980093dc5', 'c947406e-6e96-44b9-bc7b-3205d076ca8c', '083203', '0832', '热毒伤阴', 1, '', '皮肤干燥，毫毛脱落，趾（指）甲增厚变形，肌肉萎缩，趾（指）多呈干性坏疽。舌红，苔黄，脉弦细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '533217f8-5a73-4c06-acee-7783b7e5a090', 'c947406e-6e96-44b9-bc7b-3205d076ca8c', '083204', '0832', '湿热毒盛', 1, '', '患肢剧痛，日轻夜重，喜凉怕热。局部皮色紫暗，肿胀，渐变紫黑，浸润蔓延，溃破腐烂，气秽，创面肉色不鲜，甚则五趾相传，波及足背，或伴有发热等症。舌红。苔黄腻，脉弦数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd9f9a5b6-b65c-45ad-b4dc-d03467718115', 'c947406e-6e96-44b9-bc7b-3205d076ca8c', '083205', '0832', '气血两虚', 1, '', '面容憔悴，萎黄消瘦，神情倦怠。坏死组织脱落后疮面久不愈合，肉芽暗红或淡红而不鲜。舌质淡胖，脉细无力。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0ff90015-5cce-4260-962d-0b86f20ab7ba', 'c2d12a7f-7d98-4512-93b4-10b0e7a2f503', '083301', '0833', '血热瘀结，相当于急性期，病变筋脉红肿热痛，上下游走，肢体活动不利，可有身热。舌红，苔黄，脉数。', 1, '', '', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9e396a54-d585-409b-96c9-25935a492430', 'c2d12a7f-7d98-4512-93b4-10b0e7a2f503', '083302', '0833', '瘀阻脉络', 1, '', '相当于慢性期，病变筋脉肿如硬索，粘连不移，牵扯不适，或呈多个硬性结节，皮色褐黑，胫踝水肿。轻者舌脉无变化，重者舌质暗红或有瘀斑，脉多沉涩。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2d394b94-757d-4d0e-913c-7d0b92c9bb6e', '09071624-e613-4732-ad5a-c7c75ba046a9', '083401', '0834', '气滞血瘀', 1, '', '髂股静脉病变时，整个下肢肿胀疼痛，皮肤苍白或紫络，扪之烘热，腿胯部疼痛固定不移，全身发热。舌暗或有瘀斑，脉数。小腿深静脉病变时，腓肠肌胀痛，触痛，胫踝肿胀，行走困难，全身可有低热。苔白或腻，脉数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '46c9bddb-7c86-4738-a065-cb074cfa3a7a', '09071624-e613-4732-ad5a-c7c75ba046a9', '083402', '0834', '气虚血瘀', 1, '', '患肢肿胀久不消退，按之不硬而无明显凹陷，沉重麻木，皮肤发凉，颜色苍白，青筋显露，倦怠乏力。舌淡而有齿痕、瘀斑，苔薄白，脉沉而涩。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '65b57524-a3cb-489f-89f7-5a8aab34d027', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083501', '0835', '火盛伤阴', 1, '', '高热，烦躁，口干便秘，尿少而赤。舌质红而干，苔黄腻或黄糙，或舌光无苔，脉洪大弦数或弦细而数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cafc5290-824b-476b-8a86-db5d011260c9', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083502', '0835', '火毒炽盛', 1, '', '创面腐烂，分泌物增多，局部水肿，寒战高热，汗多，口渴，小便短赤。舌质红，苔黄，脉洪数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'acb409fb-11a0-45fc-88db-bf0c2b4be54b', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083503', '0835', '火毒传心', 1, '', '兼见烦躁不宁，神昏，谵语，小便刺痛不爽。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1ea2b399-ebec-4997-bc79-df7e0f3b236a', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083504', '0835', '火毒传肺', 1, '', '兼见气粗喘息，咳嗽多痰，甚者咯血。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ba9aa7fa-3787-4583-9e54-a49fad3efe06', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083505', '0835', '火毒传肝', 1, '', '兼见痉挛抽搐或黄疸。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'df5ccb06-6894-44b3-b5e9-0e65d44b1989', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083506', '0835', '火毒传脾', 1, '', '兼见腹胀便秘或便溏而粘臭，恶心呕吐，不思饮食。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c6a6a6d0-7ae4-4caf-8144-5cef7ee90b62', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083507', '0835', '火毒传肾', 1, '', '兼见血尿，尿少，尿闭，浮肿。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ff145ed4-f8ad-4235-9008-26d852fc9d53', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083508', '0835', '阴损及阳', 1, '', '具有火盛伤阴见症外，兼见精神萎靡，气促，体温反低，肢冷或振颤。舌质淡嫩，脉虚大无力，重按无根，或微细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '26de419e-8a6d-43d5-baca-950f5eb7ceee', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083509', '0835', '气血两虚', 1, '', '后期热毒渐退，而有神疲乏力，食欲不振，创面肉芽色淡不红，新肉难长。舌质淡嫩，苔薄白，脉虚数或迟缓。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '74e41b99-af98-48b0-ba47-0ba33fdc189a', '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '083510', '0835', '阴伤胃败', 1, '', '后期热毒渐退，口舌生糜，嗳气呕逆，口干少津，食欲日减，或兼有腹胀便泄。舌光如镜，质暗红而干，脉细数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '148bff37-885e-4d1c-8d26-7479e7427e15', 'f18b6b67-2a19-493f-ab62-98c5a9e7b510', '083601', '0836', '阴盛阳衰', 1, '', '四肢厥逆，恶寒倦卧，极度疲乏，昏昏欲睡，呼吸微弱。苔白，脉沉微细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0daa43ff-2c9a-4756-bbc6-da761522bd10', 'f18b6b67-2a19-493f-ab62-98c5a9e7b510', '083602', '0836', '血虚寒凝', 1, '', '形寒肢冷，局部疼痛喜暖。舌淡而暗，苔白，脉沉细。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8cb43a9d-b200-4caa-abb3-632d530c1a2f', 'f18b6b67-2a19-493f-ab62-98c5a9e7b510', '083603', '0836', '气血两虚', 1, '', '头晕目眩，少气懒言，四肢倦怠，面色苍白或萎黄，疮口不敛。舌质淡，苔白，脉细弱或虚大无力。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'de30e72c-b20a-41c6-9073-9bf2100226b6', 'f18b6b67-2a19-493f-ab62-98c5a9e7b510', '083604', '0836', '瘀滞化热', 1, '', '发热口干，患处暗红微肿，疼痛喜冷。或患处红肿灼热，溃烂腐臭，脓水淋漓，筋骨暴露。舌暗红，苔黄，脉数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '11f2b601-0ca3-4c52-9231-6881600ada6d', '7ceaa2cd-e61b-4199-a1d7-c901cebeb3ce', '083701', '0837', '风毒（神经毒）', 1, '', '一般局部不红，不肿，不出血，疼痛轻微，感觉麻木，眼睑下垂，复视，表情肌麻痹，张口困难，言语不清，口角流涎，呼吸急促。脉沉伏迟弱。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0ff9d71f-1494-4709-a529-08f2ad6921dd', '7ceaa2cd-e61b-4199-a1d7-c901cebeb3ce', '083702', '0837', '火毒（血循毒）', 1, '', '伤口疼痛剧烈，出血，皮肤有血疮瘀斑，伤肢水肿明显。内脏、五官出血，发热，少尿无尿，心悸头晕。脉象细数或结代。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '81526254-6c34-4556-b054-10d2300a209a', '7ceaa2cd-e61b-4199-a1d7-c901cebeb3ce', '083703', '0837', '风火毒（混合证）', 1, '', '同时具有以上两种症状。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4782b635-36e7-4fd5-9ab1-27e7f62faad4', '25c64bd9-9ef8-4554-8334-a76a8196f2c0', '083801', '0838', '风毒在表', 1, '', '轻度吞咽困难和牙关紧闭，周身拘急，抽搐较轻，痉挛期短，间歇期较长。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '19ed5a00-8ff4-44be-b455-958ea28d7880', '25c64bd9-9ef8-4554-8334-a76a8196f2c0', '083802', '0838', '风毒入里', 1, '', '角弓反张，频繁而间歇期短的全身肌肉痉挛，高热，面色青紫，呼吸急促，痰涎壅盛，胸腹满闷，腹壁板硬，时时汗出，大便秘结，小便不通。舌质红绛，苔黄糙，脉弦数。', '2008/04/10 08:17:14', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e57bd174-9647-487c-981c-9523e9bfbbe7', '83886669-4da0-48aa-9dde-c1169e25be3c', '071703', '0717', '热毒炽盛', 1, '', '发病急骤，黄疸迅速加深，色黄如金。伴有高热烦渴，神昏谵语，或见衄血、便血、肌肤瘀斑。舌质红绛，苔黄而燥，脉弦滑数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bb766686-e6d7-4615-b655-ecdc7ac37c9f', '83886669-4da0-48aa-9dde-c1169e25be3c', '071704', '0717', '寒凝阳衰', 1, '', '病程较长，身目俱黄，黄色晦暗。纳少脘闷，或腹胀便溏，神疲畏寒，口淡不渴。舌淡，苔白腻，脉濡缓或沉迟。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9a1f8af7-7712-476c-b0fd-d8ce95ab6e38', 'bc25108c-8066-417d-bfac-30872064b1ab', '071801', '0718', '气滞湿阻', 1, '', '腹胀按之不坚，胁下胀痛，饮食减少，食后作胀，嗳气不适，小便短少。舌苔白腻，脉弦。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '240684d3-e95f-4e16-a605-16a117f4c31e', 'bc25108c-8066-417d-bfac-30872064b1ab', '071802', '0718', '寒湿困脾', 1, '', '腹大胀满，按之如囊裹水，颜面微浮，下肢浮肿，脘腹痞胀，精神困倦，怯寒懒动，食少便溏，尿少。舌苔白滑或白腻，脉缓。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b8f476b9-06f1-4ea5-a43f-3fc92a328ea2', 'bc25108c-8066-417d-bfac-30872064b1ab', '071803', '0718', '湿热蕴结', 1, '', '腹大坚满，脘腹撑急，烦热口苦，渴不欲饮，小便短黄，大便秘结或溏垢，两目、皮肤发黄。舌边尖红，苔黄腻或灰黑，脉弦滑或数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6e9cd3e5-a60a-4eaf-b544-1e588c0f305c', 'bc25108c-8066-417d-bfac-30872064b1ab', '071804', '0718', '肝脾血瘀', 1, '', '腹大坚满，脉络怒张，胁肋刺痛，面色黯黑，面颈胸臂有血痣，呈丝纹状，手掌赤痕，唇色紫褐，口渴不欲饮，大便色黑。舌质紫红或有瘀斑，脉细涩。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '14c53a98-9505-491e-84d6-e2cd2dc1dd6d', 'bc25108c-8066-417d-bfac-30872064b1ab', '071805', '0718', '脾肾阳虚', 1, '', '腹大胀满，早轻暮重，面色苍黄，脘闷纳呆，神倦怯寒，肢冷或下肢浮肿，食少便溏，小便短少不利。舌质淡紫，脉沉弦无力。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cd215883-bf99-4c73-a0ea-b450c8610ce5', 'bc25108c-8066-417d-bfac-30872064b1ab', '071806', '0718', '肝肾阴虚', 1, '', '腹大胀急，或见青筋暴露，面色晦暗，唇紫口燥，心烦失眠，牙龈出血，鼻衄时作，小便短少。舌质红绛少津，脉弦细数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b22aef5d-7650-4580-bd60-aec2a573d1be', 'c8b12adb-edd9-418e-a943-b52fa6095876', '071901', '0719', '肠道实热', 1, '', '大便干结，腹部胀满，按之作痛，口干或口臭。舌苔黄燥，脉滑实。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ba81c465-0b93-4d6d-8d2b-eb084504fc5c', 'c8b12adb-edd9-418e-a943-b52fa6095876', '071902', '0719', '肠道气滞', 1, '', '大便不畅，欲解不得，甚则少腹作胀，嗳气频作。苔白，脉细弦。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '10e5885d-2383-4b20-8bd4-14f88d0a692c', 'c8b12adb-edd9-418e-a943-b52fa6095876', '071903', '0719', '脾虚气弱', 1, '', '大便干结如栗，临厕无力努挣，挣则汗出气短，面色huang(恍)白，神疲气怯。舌淡，苔薄白，脉弱。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1facc635-b70c-4bea-9c54-97dcfec278d9', 'c8b12adb-edd9-418e-a943-b52fa6095876', '071904', '0719', '脾肾阳虚', 1, '', '大便秘结，面色萎黄无华，时作眩晕，心悸，甚则少腹冷痛，小便清长，畏寒肢冷。舌质淡，苔白润.脉沉迟。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '47d69e12-5ab9-4c13-9e91-1fe4ab93f793', 'c8b12adb-edd9-418e-a943-b52fa6095876', '071905', '0719', '阴虚肠燥', 1, '', '大便干结，状如羊屎，口干少津，神疲纳差。舌红，苔少，脉细小数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3ac8d315-090d-4808-a0c9-20212b6f701e', '7868745d-8e39-464d-ab60-caa4f0df62b2', '072001', '0720', '风热伤络', 1, '', '起病较急，皮肤紫斑色较鲜红，呈腰部以下对称性分布，略高出皮肤，或有痒感。伴有发热，腹痛，关节酸痛等症。舌尖红，苔薄黄，脉浮数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fc27206f-3a97-41e1-8e77-e9513dfa41ce', '7868745d-8e39-464d-ab60-caa4f0df62b2', '072002', '0720', '血热伤络', 1, '', '皮肤骤见青紫或鲜紫色点状或斑块，此起彼伏。身热烦渴，面红升火，可伴有齿衄、鼻衄，甚或便血、尿血、月经过多。舌质红绛，苔黄燥，脉数有力。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e26684bf-83a3-45f5-aef9-b90d9569ecb9', '7868745d-8e39-464d-ab60-caa4f0df62b2', '072003', '0720', '阴虚火旺', 1, '', '皮肤有青紫点或斑块，时发时止。手足烦热，颧红咽干，或午后潮红，盗汗，月经过多，伴有齿衄、鼻衄。舌红，少苔，脉细数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '242efca0-4eff-469c-9b0b-6ca96f59eb75', '7868745d-8e39-464d-ab60-caa4f0df62b2', '072004', '0720', '气不摄血', 1, '', '病程较长，皮肤紫斑反复发作，色暗呈乌青斑块。面色huang(恍)白，神倦乏力，头晕目眩，心悸少寐。舌淡，苔薄白，脉细弱。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3247d832-eb21-4f56-8d9b-2352a7016b70', '54db04ee-9f22-4375-bd65-9e62353edaa3', '072101', '0721', '湿郁卫气', 1, '', '身热不扬，形寒少汗，头身困重，脘痞腹胀，纳呆，不欲饮水，小便短少。苔白腻，脉缓。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8029c3b5-6667-4f85-baea-8c9336658dce', '54db04ee-9f22-4375-bd65-9e62353edaa3', '072102', '0721', '气分湿热', 1, '', '身热起伏，午后热甚，持续不退，汗出不解，面色晦垢，口渴不欲饮，脘腹痞满，便溏或秘。或胸腹出现红疹、白pei(陪)，或皮肤巩膜发黄。舌质红，苔黄腻，脉滑数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd00aac57-56e3-4781-b938-1255355e5993', '54db04ee-9f22-4375-bd65-9e62353edaa3', '072103', '0721', '热入营血', 1, '', '身热夜间为甚，烦躁不安，或神识昏蒙，循衣摸床。身发斑疹，甚则大便下血，或伴腹痛。舌绛少苔而干，脉细数。若卒然腹痛便血，可见身热骤退，面色苍白，汗出肢冷等危象。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '24609d46-ad36-45bf-aca0-eb77e1d9a863', 'b4810674-76ab-4ead-8567-e7bf9ad76ddc', '072201', '0722', '中焦湿热', 1, '', '突发吐泻频作， 吐出物热臭酸腐，泻下黄水或带有粘液泡沫，口臭，心烦口渴。舌苔黄腻，脉滑数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a6c8f842-9923-4af6-a829-7bc9b6065dd9', 'b4810674-76ab-4ead-8567-e7bf9ad76ddc', '072202', '0722', '寒湿困脾', 1, '', '胸脘痞满，吐泻交作， 呕吐泻下物为稀水或米泔水，口不渴或喜热饮，腹部冷痛，形寒喜温，汗出肢冷。舌苔白腻，脉沉细。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b9aad536-1e73-4215-9895-3ebcf0bce339', 'b4810674-76ab-4ead-8567-e7bf9ad76ddc', '072203', '0722', '亡阴证', 1, '', '吐泻频急，目眶凹陷，指尖螺瘪，神萎倦缩，口渴引饮，心烦声嘶，舌卷囊缩，气急，尿闭。舌质干红萎缩，脉微细数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ee44686e-d572-497b-998f-e543836fa2c2', 'b4810674-76ab-4ead-8567-e7bf9ad76ddc', '072204', '0722', '亡阳证', 1, '', '吐泻频繁，四肢厥冷，汗出如油，呼吸微弱，声微气怯，血压下降。舌淡紫，脉微欲绝。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6e4474c7-7feb-4eaf-afd4-d8f79b95cc9e', '47543a10-4b00-4745-953e-5ed307906d68', '072301', '0723', '阳暑', 1, '', '头昏头痛，心烦胸闷，口渴多饮，全身疲软，汗多，发热，面红。舌红，苔黄，脉浮数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bd0c068a-9ff4-4821-adbd-94114f4db1ab', '47543a10-4b00-4745-953e-5ed307906d68', '072302', '0723', '阴暑', 1, '', '精神衰惫，肢体困倦，头昏嗜睡，胸闷不畅，多汗肢冷，微有畏寒，恶心欲吐，渴不欲饮。舌淡，苔薄腻，脉濡细。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c11717e1-afda-4478-832f-00f001ba19c4', '47543a10-4b00-4745-953e-5ed307906d68', '072303', '0723', '暑厥', 1, '', '昏倒不省人事，手足痉挛，高热无汗，体若燔炭，烦躁不安，胸闷气促，或小便失禁。舌红，苔燥无津，脉细促。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7ff42878-751b-48b9-b2dd-df5df240a809', '47543a10-4b00-4745-953e-5ed307906d68', '072304', '0723', '暑风', 1, '', '高热神昏，手足抽搐，角弓反张，牙关紧闭，皮肤干燥，唇甲青紫。舌红绛，脉细弦紧或脉伏欲绝。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f538ec76-a834-4e70-a111-b08ee41d0d36', '4815e524-7678-4b6a-be28-f7cd71c066bd', '072401', '0724', '邪郁少阳', 1, '', '寒战壮热，汗出热退，休作有时。伴有头痛面赤，恶心呕吐，口苦。舌苔薄白或黄腻，脉弦或弦数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '57262944-4718-4e35-bba6-d9269869394b', '4815e524-7678-4b6a-be28-f7cd71c066bd', '072402', '0724', '暑热内郁', 1, '', '热多寒少，或但热不寒。汗出不畅，头痛，骨节酸楚，口渴引饮。舌质红，苔黄，脉弦数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e404f321-ed94-4ac1-bf3b-de2f20026848', '4815e524-7678-4b6a-be28-f7cd71c066bd', '072403', '0724', '暑湿内蕴', 1, '', '寒多热少，或但寒不热。头痛身楚，口不渴，胸胁满闷，神倦乏力。舌苔白滑或白腻，脉弦紧。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '23a3c7cb-f493-4234-bf64-80dfa47a1cd4', '4815e524-7678-4b6a-be28-f7cd71c066bd', '072404', '0724', '疫毒侵袭', 1, '', '发病急，病情重，热型不一。若热甚寒微，或壮热不寒，头痛面赤，烦渴饮冷，甚则神昏谚语，痉厥，舌红绛苔黑垢，脉洪数者为热瘴。若寒甚热微，或寒战无热，渴不欲饮，或呕吐泄泻，或神昏不语，舌苔白腻，脉弦者为冷瘴。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1e3ae12a-c83e-4315-ad56-4d4ec1309caf', '4815e524-7678-4b6a-be28-f7cd71c066bd', '072405', '0724', '正虚邪恋', 1, '', '遇劳即发，反复发作，寒热不清。胁下痞块，神倦乏力，面黄肌瘦，懒言气短，自汗心悸。舌淡、苔少，脉细弱。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4de6c2f2-f370-4260-b345-4ef13dc193a1', '8219b66f-4567-4647-a700-704b45439f31', '07250101', '072501', '蛊毒侵肺', 1, '', '发热恶寒，汗少，头痛，干咳少痰，或痰中夹血，或皮肤红疹瘙痒。苔薄白，脉浮数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '843eac11-f422-4bc1-a0c3-5c518b7b1239', '8219b66f-4567-4647-a700-704b45439f31', '07250102', '072501', '湿热内蕴', 1, '', '高热微寒，持续不退，口渴神呆，嗜睡，腹痛，泄泻或下痢赤白。甚则可现黄疸、腹水。舌质偏红，苔白腻或黄腻，脉濡数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '35a2f29f-257a-45cf-abcb-54e2c3b4e864', '8219b66f-4567-4647-a700-704b45439f31', '07250103', '072501', '热毒陷营', 1, '', '高热口渴，烦躁谵妄或神昏，或头痛呕吐，抽搐，状如癫痛。舌红绛，苔黄，脉细滑数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '345b3498-9f89-432b-af7d-0cf44947cb19', '8219b66f-4567-4647-a700-704b45439f32', '07250201', '072502', '气滞湿阻', 1, '', '胸闷，脘腹作胀，食后尤甚，嗳气则舒，胁痛或有腹痛，大便溏薄或带脓血，排便不爽。苔腻，脉濡缓。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '231efda4-12e4-45fc-a504-347cd180ad0a', '8219b66f-4567-4647-a700-704b45439f32', '07250202', '072502', '肝郁脾虚', 1, '', '胁肋胀痛，胸闷腹胀，食欲减退，大便不实或溏，精神不振。舌苔薄白，脉细弦。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fdfc5201-b036-4f23-8621-ae5327952753', '8219b66f-4567-4647-a700-704b45439f32', '07250203', '072502', '气滞血瘀', 1, '', '胁肋疼痛或刺痛，脘腹闷胀，纳少嗳气，面黑唇紫。舌质暗红，苔薄腻，脉弦数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '845814ed-dae0-42f8-a717-c155076c2b8b', '8219b66f-4567-4647-a700-704b45439f33', '07250301', '072503', '肝肾阴虚', 1, '', '腹胀胁痛，口干尿少，心烦失眠，牙龈出血，面色晦滞，形体消瘦。舌质红或绛，少津，脉弦细数。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '55d08744-ccbd-4961-b46a-b15ab9343d21', '8219b66f-4567-4647-a700-704b45439f33', '07250302', '072503', '脾肾阳虚', 1, '', '腹大胀满，神倦乏力，脘闷纳呆，肢冷畏寒，食少便溏，腰酸膝软，面色huang(恍)白。舌质淡白，脉沉细。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '9cc3f010-f85c-48b4-9fc5-74ba8aa57285', '8219b66f-4567-4647-a700-704b45439f33', '07250303', '072503', '瘀血阻络', 1, '', '病延日久，肝脾肿大，面黄色暗，疲劳乏力，食少腹胀，形体消瘦，面颈红痣。唇舌紫暗，苔薄白，脉细涩。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '11af4a26-59a2-4cf3-9dc6-c95dab6d4179', '8219b66f-4567-4647-a700-704b45439f34', '07260101', '072601', '虫积肠道', 1, '', '脘腹隐痛，腹胀或腹泻， 肛门作痒，大便中有白色节片。苔薄白腻，脉细。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '80af08e4-fcbe-4188-9a94-b45c4e028097', '8219b66f-4567-4647-a700-704b45439f34', '07260102', '072601', '脾胃虚弱', 1, '', '纳食减少，腹胀便溏，消瘦乏力。苔薄白，脉细弱。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6abcba3c-1ce3-4309-bbea-b2ebbcc32855', '8219b66f-4567-4647-a700-704b45439f35', '07260201', '072602', '囊虫侵脑', 1, '', '发作性昏倒或抽搐，反复发作。头痛呕吐，视力模糊或失明，甚者精神异常。舌苔白腻，脉弦滑。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6aa8ceda-22f8-4a91-89cf-7912a72888d5', '8219b66f-4567-4647-a700-704b45439f35', '07260202', '072602', '虫痰互结', 1, '', '肌肉、皮下结节分批出现，结节可活动，头部及躯干处多见，不痛不痒，可自动消失。苔腻，脉滑。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b3fe5f22-89be-472e-9fd6-a6d921124c52', '4c1dee7e-6020-4356-b270-3c5ab090d9fe', '072701', '0727', '脾虚湿滞', 1, '', '面黄虚浮，善食无力，食后腹胀。或嗜异物（如生米、茶叶、木炭、尘土等），精神疲乏。舌淡，苔薄，脉濡。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '75ae6657-b341-4739-b7ca-7e2097acf50b', '4c1dee7e-6020-4356-b270-3c5ab090d9fe', '072702', '0727', '气血亏虚', 1, '', '面黄或苍白，下肢或全身浮肿，头昏耳鸣，或眩晕眼花，心慌气短，食欲欠佳。舌质淡胖，苔薄，脉细弱。', '2008/04/10 03:43:36', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '781764e3-cef6-4fb2-af07-c33aa529ac52', '892a6cc0-313f-4118-81ae-e090b8353c17', '090101', '0901', '风热外袭', 1, '', '针眼初起，痒痛微作，局部硬结，微红微肿，触痛明显。苔薄黄，脉浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '95a69a66-f521-4289-be62-951022463519', '892a6cc0-313f-4118-81ae-e090b8353c17', '090102', '0901', '热毒炽盛', 1, '', '胞睑红肿疼痛，有黄白色脓点，或见白睛壅肿，口渴便秘。舌红，苔黄或腻，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1bd92244-f829-4575-a261-3639b459fedc', '892a6cc0-313f-4118-81ae-e090b8353c17', '090103', '0901', '热毒内陷', 1, '', '胞睑肿痛增剧，伴见头痛，身热，嗜睡。局部皮色暗红不鲜，脓出不畅。舌质绛，苔黄糙，脉洪数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4fc83e93-d65b-47d8-9b9c-a3235b9701f1', '892a6cc0-313f-4118-81ae-e090b8353c17', '090104', '0901', '脾虚湿热', 1, '', '针眼屡发，面色少华，多见于小孩，偏食，便结。舌质红，苔薄黄，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f892e582-9fd7-4460-8b06-9e534bd4a404', 'ea9a7039-1070-4a80-a378-b49dd4bba0a4', '090201', '0902', '痰湿结聚', 1, '', '胞睑内生硬结，隆起，不红不痛，皮肤推之能移，病程缓慢，逐渐增大。苔薄腻，脉滑。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c7498a71-b323-456d-bcf4-f74a0ca8ae56', 'ea9a7039-1070-4a80-a378-b49dd4bba0a4', '090202', '0902', '痰热搏结', 1, '', '痰核处皮色微红肿，初硬渐软，按压疼痛，相应睑结膜面呈紫红色。舌红苔薄黄腻，脉滑数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '89edb967-cc19-4418-b9bf-0f5255639460', '21cf4e75-14b9-43fc-a20a-50dec18034da', '090301', '0903', '风热外袭', 1, '', '睑弦红赤，有鳞屑脱落，刺痒灼痛，干涩不适。舌红，苔薄黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '194f3076-6814-40f9-94fa-1221fe3a780e', '21cf4e75-14b9-43fc-a20a-50dec18034da', '090302', '0903', '湿热壅盛', 1, '', '睑弦红肿溃烂，垢腻胶粘，或有小出血，睫毛脱落，痒痛兼作。舌红，苔黄腻，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'eb8e1132-477b-4a1a-aeba-fff11ab2d922', '21cf4e75-14b9-43fc-a20a-50dec18034da', '090303', '0903', '心火上炎', 1, '', '内外眦部为主，睑弦红赤，刺痛皲裂，小便短赤。舌红，苔黄腻，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '07bcc297-03c3-48ca-b55b-f1d2583b1a22', '21cf4e75-14b9-43fc-a20a-50dec18034da', '090304', '0903', '血虚风燥', 1, '', '睑弦红赤反复发作，皮肤燥裂，或有脱屑，痒涩不舒。舌淡，苔薄黄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e9208e2d-746c-419c-9195-5f523abc7c35', '98763218-44e3-4b13-9ac3-5a169195684b', '090401', '0904', '风热外袭', 1, '', '胞睑红肿，边界清，边缘有小疱疹。发热，恶寒，头痛。苔薄白，脉浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '45f53626-f73a-4500-9950-75d26941f303', '98763218-44e3-4b13-9ac3-5a169195684b', '090402', '0904', '热毒炽盛', 1, '', '胞睑漫肿暗红，肿硬重坠，剧痛，或深部硬结处变软，成脓。身壮热，口渴欲饮，溲赤便干。舌红，苔薄黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a09de919-3a2f-4fa4-a98a-eeae3a108a4e', '29383d83-84c4-4711-a0c0-f97190cf6cbf', '090501', '0905', '风热外侵', 1, '', '胞睑红赤，出现丘疹、小泡、刺痒或灼痛，或伴有发热、恶寒、头痛。苔薄黄，脉浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bcde16f1-4044-4662-9a51-b48a102247a7', '29383d83-84c4-4711-a0c0-f97190cf6cbf', '090502', '0905', '湿热壅盛', 1, '', '胞睑红肿，疱疹，脓疱溃烂浸淫，或有胸闷、纳呆，大便干结。舌红苔白贰或黄腻，脉滑数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '83ff8fb7-de53-43d2-ac8d-4ed6c8b6b700', '29383d83-84c4-4711-a0c0-f97190cf6cbf', '090503', '0905', '热毒炽盛', 1, '', '胞睑红赤，疼痛难忍，疱疹群集成簇，其分布不超过颜面中线，日久成脓疱，破溃后结痂遗留瘢痕，并可引起黑睛生翳，瞳神紧小等并发症。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '657fdd68-3fed-4887-b4b5-1764ad37d7a7', '29383d83-84c4-4711-a0c0-f97190cf6cbf', '090504', '0905', '气阴两伤', 1, '', '胞睑红肿渐减，痂皮剥落，神疲乏力，纳食不馨，大便溏薄，口干欲饮。苔薄，舌红少津，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fb7dc84e-bcfe-497f-8f00-9573aa721c2a', '31952cfa-f7ad-40b9-b74e-776b49d404e3', '090601', '0906', '风热壅盛', 1, '', '睑内面血管模糊，眦部红赤颗粒累累，目痒涩痛，生眵、流泪。舌红，苔薄黄，脉浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '881870b0-e01f-48c7-9653-039c140bfdb2', '31952cfa-f7ad-40b9-b74e-776b49d404e3', '090602', '0906', '湿热蕴结', 1, '', '睑内面红赤及颗粒丛生，血管模糊，垂坠难开，或有赤脉下垂，痒痛交作，羞明泪粘。舌红，苔黄腻，脉濡数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '681a10b5-04ef-4488-a432-f035e104daff', '31952cfa-f7ad-40b9-b74e-776b49d404e3', '090603', '0906', '血热瘀滞', 1, '', '胞睑厚硬，颗拉红坚，或有白色条纹，风轮受侵，赤膜下垂，星翳迭起，畏光泪出，涩痛难睁。舌暗红，苔薄黄，脉数有力。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '31014f33-e863-4a2c-b830-056718bafe04', '31952cfa-f7ad-40b9-b74e-776b49d404e3', '090604', '0906', '沙眼分期', 1, '', 'I （进行期）上穹窿部和上睑结膜有活动性病变，血管模糊，乳头增生，滤泡形成。II （退行期）有活动性病变，同时出现瘢痕。III（完全结瘢期）仅有瘢痕而无活动件病变。诊断沙眼之轻重，将上睑分成三等分，活动性病变未超过睑结膜1/3者为轻"＋"，1/3～2/3者为中"＋＋"，超过2/3者为重"＋＋＋"。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5a596d27-6c83-4825-92a3-038b2fc61dfa', '312748ce-f6c8-417e-911e-0681f4eb32ed', '090701', '0907', '肝经风热', 1, '', '胞睑频频眨动，干涩，刺痒，畏光，白睛微红，睑内面颗粒丛生或有眵干结。舌红，苔薄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '568f946f-0387-440a-83bb-8714db5abbd6', '312748ce-f6c8-417e-911e-0681f4eb32ed', '090702', '0907', '脾虚肝旺', 1, '', '双眼胞睑频频眨动，眼干涩，常喜揉拭，白睛微红，干燥无泽，形体消瘦，烦躁喜怒，饮食偏嗜。舌红，苔薄白，脉弦细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7ab2fc14-feb6-4b94-bdab-83753b278cf4', '312748ce-f6c8-417e-911e-0681f4eb32ed', '090703', '0907', '肺阴不足', 1, '', '胞睑频频眨动，干涩刺痒，白睛淡红，泪少，畏光，口咽干燥。舌红少津，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c4c3ab1d-6ea5-410c-bd3f-6794816ab110', '4ceee186-4179-4c18-9416-014d5542a7b3', '090801', '0908', '风热邪毒', 1, '', '胞睑红肿如桃，睑闭不开，目赤、畏光、流泪，身热恶风、头痛。舌红，苔薄、脉浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6bed79c3-a860-4274-947c-95ef2dba8fde', '4ceee186-4179-4c18-9416-014d5542a7b3', '090802', '0908', '热毒炽盛', 1, '', '胞肿如桃，白睛赤肿高起，热泪频流，口渴引饮，溲赤便秘。舌红，苔薄黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6c873d0c-56c2-4258-85f9-8862863f2bdd', '4ceee186-4179-4c18-9416-014d5542a7b3', '090803', '0908', '热毒内陷', 1, '', '胞睑漫肿，皮色暗红，灼热疼痛，眼珠突起，转动不灵，壮热口干，头痛或见神昏谵语。舌绛，苔薄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f2a85f06-1413-46a2-9a38-73edd4a4ddbc', 'db617603-dc0b-4c00-bae9-b7ef5205f2a3', '090901', '0909', '脾虚气弱', 1, '', '胞肿虚软如球，皮色正常，喜按，喜热熨，时发时止，神疲乏力，咳嗽气短，食少便溏。舌淡，苔白，脉弱。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1b696ff0-73fc-4411-bd95-c94d71f70ddd', 'db617603-dc0b-4c00-bae9-b7ef5205f2a3', '090902', '0909', '心脾两虚', 1, '', '胞肿虚软如球，神气困顿，虚烦失眠，怔忡健忘。舌淡红，苔白，脉细无力。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0849199c-af6c-4328-b575-1f4b6a172678', 'db617603-dc0b-4c00-bae9-b7ef5205f2a3', '090903', '0909', '脾肾阳虚', 1, '', '胞虚如球，皮色发白，或兼面肿，腰膝酸痛，倦怠无力，小便清长。舌淡胖嫩，脉细弱无力。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '661e0597-415e-477d-8087-3dd97ad8bcf2', 'd6508d74-d165-4a0b-bff2-103009c6bb09', '091001', '0910', '脾虚气弱', 1, '', '上胞提举乏力，掩及瞳神，晨起或休息后较轻，午后或劳累后加重，每伴神疲肢倦，食欲不振等症。舌淡，苔薄，脉弱。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2755aa3e-6cc5-40e3-9bcf-5d0f581e86ca', 'd6508d74-d165-4a0b-bff2-103009c6bb09', '091002', '0910', '风邪袭络', 1, '', '多为单侧上胞下垂，起病突然，多伴有目珠转动失灵，目偏视，视一 为二，常伴眉额酸胀。舌红，苔薄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0c098c91-51ba-4e53-b965-3061c3932d4c', 'd6508d74-d165-4a0b-bff2-103009c6bb09', '091003', '0910', '先天不足', 1, '', '多自幼单侧或双侧上胞下垂，常与遗传有关。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e0f97670-3a11-469e-97a2-9f255e929593', '62fe2cef-99fc-4ed1-89eb-f5dfdd4cef14', '091101', '0911', '血虚生风', 1, '', '胞睑振跳牵拽面颊，眉紧肉跳，头昏目眩。舌质淡红，苔薄，脉弦紧。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '86773697-5175-4b77-ad97-c32f4511b024', '62fe2cef-99fc-4ed1-89eb-f5dfdd4cef14', '091102', '0911', '心脾两虚', 1, '', '胞睑跳动，时疏时频，劳累或情绪紧张时加重，虚烦失眠，怔忡健忘。舌质淡，脉细弱。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5e0ac03e-662e-45af-8bb1-8d75dee20e36', '62fe2cef-99fc-4ed1-89eb-f5dfdd4cef14', '091103', '0911', '肝风内动', 1, '', '胞轮振跳，牵拽面颊或口角，耳鸣头胀，烦躁易怒。舌红，苔薄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3318ebde-8311-4ffb-b4fc-9e87c144a257', '4c697fcc-f600-491a-b8aa-b5a1a2970831', '091201', '0912', '肝肾亏损', 1, '', '溢泪清稀，视物模糊，头晕耳鸣，腰膝酸楚。舌红，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '03e8e4ba-2436-4381-8083-966e91e650e6', '4c697fcc-f600-491a-b8aa-b5a1a2970831', '091202', '0912', '气血两虚', 1, '', '无时泪下，不耐久视，面无华色，心悸健忘，神疲乏力，或产后失血过多。舌淡，苔薄，脉细弱。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6d98281b-58af-42d1-814e-c35759925aac', '4c697fcc-f600-491a-b8aa-b5a1a2970831', '091203', '0912', '风邪外袭', 1, '', '冷泪绵绵，平日双眼常感隐涩不爽，见风头痛，迎风泪出增剧。舌红，苔薄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ac0405d0-539a-4d7b-8ab1-0e3d6bdd4337', 'fcd6e54e-d8d3-47be-a8da-be359bcb4f5d', '091301', '0913', '风热外袭', 1, '', '患部红肿疼痛，初起泪热生眵，或见恶寒发热。舌红，苔薄黄，脉浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '896942f7-6a81-495d-a780-eb44c2067428', 'fcd6e54e-d8d3-47be-a8da-be359bcb4f5d', '091302', '0913', '热毒炽盛', 1, '', '患处红肿高起，疼痛拒按，红肿可蔓延至面颊及胞睑，耳前或颔下有眷核及压痛，可伴有身热口渴，大便秘结，小便赤涩。舌质红，苔黄厚，脉数有力。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ab2b2284-88dc-4c10-8975-87fa6de399d5', 'fcd6e54e-d8d3-47be-a8da-be359bcb4f5d', '091303', '0913', '正虚邪留', 1, '', '患处时发微红微肿，微有压痛，但不溃破。或溃后漏口难敛，脓汁常流不绝。面色苍白，神疲食少。舌淡，苔薄，脉细弱。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b8d3bb19-3192-4214-8fd9-30ea53c4aa06', '369228ec-2111-405c-85c0-ba0eec0a4fa5', '091401', '0914', '风重于热', 1, '', '胞睑微红，白睛红赤，痒涩并作，羞明多泪，伴见头痛鼻塞，恶风发热。舌红，苔薄白，脉浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd8b46a36-f89c-49f6-b3ea-59b6bc9e5038', '369228ec-2111-405c-85c0-ba0eec0a4fa5', '091402', '0914', '热重于风', 1, '', '胞睑红肿，白睛红赤壅肿，热泪如汤。或眵多胶结，怕热畏光，口干溺黄。舌红，苔黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b2519940-2720-47e6-9140-659044d9cd9d', '369228ec-2111-405c-85c0-ba0eec0a4fa5', '091403', '0914', '风热俱盛', 1, '', '胞睑红肿，白睛红赤壅肿，睑内面或有伪膜。患眼沙涩，灼热，疼痛。舌红，苔黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '14580874-83fc-4eeb-a32f-25c26119c4c9', 'dd2c564f-72cc-4b3f-bac5-1294f2189a48', '091501', '0915', '风热外袭', 1, '', '白睛红赤，沙涩灼热，羞明流泪，眵多清稀，头额胀痛。舌红，苔薄白或薄黄，脉象浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cc5618b3-4691-441b-aa9f-6409e8408835', 'dd2c564f-72cc-4b3f-bac5-1294f2189a48', '091502', '0915', '热毒炽盛', 1, '', '胞睑红肿，白睛赤肿，白睛溢血，黑睛生星翳。羞明刺痛，热泪如汤，口渴引饮，溲赤便结。舌红，苔黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '377d69b9-2ac2-489e-ae14-f70fa2cf3156', '929c5145-f8d0-4149-bd40-dce1eaba167c', '091601', '0916', '风热挟湿', 1, '', '痒如虫行，白睛红赤污浊，黑睛与白睛交界处，有黄褐色胶样隆起，眼睑胶粘。舌红，苔薄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '31981462-8a99-4ea6-9e59-58c926ebb3a1', '929c5145-f8d0-4149-bd40-dce1eaba167c', '091602', '0916', '湿热壅盛', 1, '', '眼痒难忍，碜涩流泪，睑内卵石样，白睛红赤，色泽污秽或兼黑睛边缘胶样隆起，甚则黑睛起翳疼痛，畏光流泪。舌红，苔薄黄腻，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5ab77e6e-45c8-458f-907f-bff5b2d86f0b', '149d25ba-5d32-44e5-8680-89bf2800f409', '091701', '0917', '肺阴不足', 1, '', '目珠干燥乏泽，干涩、、磨痛、口干鼻燥，大便干。舌红少津，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '09f93b87-540e-41e9-bec9-c0c72b7de76c', '149d25ba-5d32-44e5-8680-89bf2800f409', '091702', '0917', '阴虚湿热', 1, '', '目珠干燥乏泽、干涩、疼痛、畏光，视力模糊，口鼻干燥，关节疼痛，溲黄，便干。舌红，苔薄黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '323f5037-f5b1-4aa1-86ec-ad85ae7fd183', '149d25ba-5d32-44e5-8680-89bf2800f409', '091703', '0917', '气阴两虚', 1, '', '目珠干燥乏泽、干涩、畏光，眼疲劳，视力模糊，口干唇燥裂，神疲乏力。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'fcf0c853-11c8-4cc7-93a8-3c2191dbe0d9', 'be0a474a-416b-497f-9dd5-d1fedf5a4944', '091801', '0918', '肺燥郁热', 1, '', '颗粒小泡每发于白睛，赤脉较粗，干涩不舒，或多眵。舌红，苔黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '244b2cc2-6760-4119-9c07-8de0e7897440', 'be0a474a-416b-497f-9dd5-d1fedf5a4944', '091802', '0918', '气火郁结', 1, '', '颗粒小泡侵及黑睛，并有赤膜伸入，畏光流泪，涩痛难开，口苦咽干，烦躁不宁。舌红，少苔，脉弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e41e0396-fb34-4428-90d4-9fa86c1a2d1f', 'be0a474a-416b-497f-9dd5-d1fedf5a4944', '091803', '0918', '肺阴不足', 1, '', '小泡颗粒经久不退，：或反复发作，赤脉纤细，或红赤不甚，自觉眼干涩，口鼻干燥。舌质红，少苔，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dc8ab94e-adb4-4419-a22e-e6478439ff4c', 'be0a474a-416b-497f-9dd5-d1fedf5a4944', '091804', '0918', '好转', 1, '', '颗粒小泡红赤减退，症状改善。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'aa1c689a-ba6b-4d26-886e-b7dc779c7c06', '9dabaf71-6284-40aa-ac0e-38eab8646f61', '091901', '0919', '肺经郁火', 1, '', '白睛结节隆起，色紫红，压痛，眼痛。舌红，苔薄黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c5c54f68-bae8-4203-aae8-12ccd3df2c85', '9dabaf71-6284-40aa-ac0e-38eab8646f61', '091902', '0919', '火毒蕴结', 1, '', '结节隆起较大，或连缀成环，红赤压痛，眼灼痛，口苦，溲黄。苔黄，舌红，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'd56f18fd-db53-4a88-8aef-da4e7bf11db3', '9dabaf71-6284-40aa-ac0e-38eab8646f61', '091903', '0919', '风湿凌目', 1, '', '白睛结节隆起反复发作，伴有关节痛，肢节肿胀。舌红，苔黄腻，脉滑数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '11e22f31-0a8c-4e13-8425-9fbc98f5b300', '9dabaf71-6284-40aa-ac0e-38eab8646f61', '091904', '0919', '肺阴不足', 1, '', '白睛红赤节结时隐时现，反复发作，眼干涩，形体消瘦，盗汗低热。舌质红，苔薄，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dd93f5ed-d0f3-4e50-b825-2cd9a1217be2', '14d06bc4-ecf7-4611-a870-68970cf4ae25', '092001', '0920', '肝经风热', 1, '', '黑睛猝起星点散翳，色灰白，抱轮微红，畏光流泪，碜痛，或伴鼻塞，头痛、咽痛。舌红，苔薄黄，脉浮数或弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0a32f022-34bc-4d40-892b-7b96e0de7760', '14d06bc4-ecf7-4611-a870-68970cf4ae25', '092002', '0920', '肝胆火炽', 1, '', '黑睛星点密集，或成树枝状、地图状，抱轮红赤显著，畏光流泪，灼热疼痛，口干口苦，溲黄赤。舌质红，苔黄，脉弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b6536975-01df-409b-8c06-f73eafe59537', '14d06bc4-ecf7-4611-a870-68970cf4ae25', '092003', '0920', '湿热蕴伏', 1, '', '黑睛星翳，反复发作，缠绵不愈，头重胸闷，溲黄便结，口腻。舌红，苔黄腻，脉滑。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '897e7fb6-1a52-4e88-95ad-cfb7036a98f5', '14d06bc4-ecf7-4611-a870-68970cf4ae25', '092004', '0920', '正虚邪留', 1, '', '星翳不敛，抱轮微红，干涩羞明，视物模糊，常易外感，以致病变反复时好时剧，神疲乏力。舌淡，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7c6f7ae1-0023-4707-973c-9b14ae5387e1', '2c8bcd98-4f64-4a57-82fa-b6f7cc7ece10', '092101', '0921', '肝经风热', 1, '', '黑睛翳呈点片，略形隆起，周围黑睛晦浊，抱轮红赤，羞明流泪，沙涩刺痛。舌红，苔薄白或薄黄，脉浮数或弦数。 。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0b5bb647-10b6-44fd-818b-83df5518888c', '2c8bcd98-4f64-4a57-82fa-b6f7cc7ece10', '092102', '0921', '肝胆火炽', 1, '', '黑睛翳障成片而陷下，色如凝脂，黄液上冲，胞睑红肿，白睛混赤，神水混浊，瞳神紧小，疼痛如锥，口苦咽干，溺赤便结。舌红，苔黄，脉象弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2255a59b-4958-4cd7-9dd3-a304dbacfce8', '2c8bcd98-4f64-4a57-82fa-b6f7cc7ece10', '092103', '0921', '阳明腑实', 1, '', '黑睛凝脂深陷如窟，黄液上冲日增，小便短赤，大便硬结，数日不解。舌红，苔黄腻，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5ca66fce-6c07-4226-8772-8a899ccb4de1', '2c8bcd98-4f64-4a57-82fa-b6f7cc7ece10', '092104', '0921', '正虚邪实', 1, '', '黑睛凝翳久未平复，抱轮红赤，羞明，流泪，刺痛等症，时轻时重。舌红少津，苔薄黄，脉弦细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f18ff0a6-62a8-4e37-ac28-e6f3c75e6311', '99711dac-0e1c-4368-bd01-c9c6d3fda7a8', '092201', '0922', '肝经风热', 1, '', '黑睛生翳，胞睑红肿，白睛混赤，眼痛，畏光流泪。苔薄黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4e7f670d-1305-4b02-86d8-03e63bb94c39', '99711dac-0e1c-4368-bd01-c9c6d3fda7a8', '092202', '0922', '肝胆火炽', 1, '', '翳障侵蚀扩大，瞳神紧小，目赤剧痛，畏光难睁，热泪如汤，溲赤便秘。舌红，苔黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'da30013a-cc35-4985-8a39-688dcd1c15b8', '99711dac-0e1c-4368-bd01-c9c6d3fda7a8', '092203', '0922', '阴虚火旺', 1, '', '病程迁延，翳障时起时伏，抱轮微红，目刺痛，畏光流泪，口干咽燥。舌红，苔薄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '80d9b6bd-15e6-4fb5-bed7-048b76820711', '99711dac-0e1c-4368-bd01-c9c6d3fda7a8', '092204', '0922', '阳虚寒凝', 1, '', '黑睛四周起翳，形如花瓣，抱轮暗红，眼痛，发於高龄体弱，或过用寒凉，迁延不愈，口不渴。舌淡，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b0fbd2d7-89e4-459d-acd3-44cf198dd890', 'f3d039ff-e70f-44ef-8544-e43ba4e89b84', '092301', '0923', '肝经风热', 1, '', '初起黑睛生翳混浊，抱轮红赤，眼疼，畏光，流泪，头痛。舌红，苔薄，脉浮数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bf7f5596-5b15-4a8c-ad4e-69f98e3a2618', 'f3d039ff-e70f-44ef-8544-e43ba4e89b84', '092302', '0923', '湿毒蕴结', 1, '', '黑睛漫珠混浊，赤脉伸入，呈毛刷状，黑睛后壁有沉着物，神水混浊，白睛混赤，眼痛、畏光、流泪，口干口苦，溲黄便结。舌红，苔黄腻，脉滑数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e794784d-9112-4aea-bb44-02adfef2f441', 'f3d039ff-e70f-44ef-8544-e43ba4e89b84', '092303', '0923', '阴虚火旺', 1, '', '黑睛混浊，呈斑片状，灰黄色，赤脉蔓入，位浅者呈分枝状，迁延日久，抱轮微红，干涩隐痛，口干咽燥，五心烦热。舌红少苔，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8cfc51d9-44d9-44fb-8fa7-28e79ece02da', 'efc2c926-83b2-4fc1-9d4a-46f1ea93c79e', '092401', '0924', '肝经火盛', 1, '', '风轮赤豆猝起，畏光，流泪，沙涩疼痛。舌红，苔黄，脉弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0e9fdc45-e619-4719-9461-e916cfbd2b5a', 'efc2c926-83b2-4fc1-9d4a-46f1ea93c79e', '092402', '0924', '阴虚火旺', 1, '', '风轮赤豆乍起乍伏，畏光少泪，隐隐涩痛，形体瘦羸，午后潮热，颈部瘰疠。舌红，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3afd0b74-7733-48a2-9e77-6ff70d3220c3', 'efc2c926-83b2-4fc1-9d4a-46f1ea93c79e', '092403', '0924', '脾虚挟痰', 1, '', '风轮赤豆反复牵延，畏光流泪，面色恍白，纳食不馨，瘰疠成串。舌淡，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cde89de6-a00c-43d3-8035-bda1d14d6b02', '13d310f2-1fa7-4cae-954c-1d6b3dbc4783', '092501', '0925', '脾虚气弱', 1, '', '白睛干燥，夜盲，胃纳不振，形体消瘦，面色无华。舌质淡，苔薄，脉细弱。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ba82ef93-8aec-40cf-b7ee-bb0493b48d45', '13d310f2-1fa7-4cae-954c-1d6b3dbc4783', '092502', '0925', '虫积化疳', 1, '', '眼珠晦暗不泽，毛发枯槁稀疏，咬甲挖鼻，寝则磨牙，便溏腹痛，大便有虫卵。舌淡，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '71bfeec3-ab68-42ad-8d8e-f72d3d8e840c', '13d310f2-1fa7-4cae-954c-1d6b3dbc4783', '092503', '0925', '脾虚肝热', 1, '', '白睛干燥，黑睛溃疡，黄液上冲，抱轮红赤，畏光闭目。烦躁不宁。舌红，苔薄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f8f5d924-f1ad-4b00-a358-24b260841086', '13d310f2-1fa7-4cae-954c-1d6b3dbc4783', '092504', '0925', '脾肾阳虚', 1, '', '白睛干燥无泽，黑睛混浊呈乳白色，形寒面白，四肢不温，大便溏薄，完谷不化。舌淡少津，脉细无力。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c867ae62-0dbe-4d35-933f-272ad10a99b2', 'c042c8e6-50dd-406e-988e-6c30dfad7e0f', '092601', '0926', '风热壅盛', 1, '', '赤膜侵及黑睛，怕热羞明，沙涩刺痒，甚则疼痛。舌红，苔黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '771a3aa8-29ba-4a1c-86c0-317f810c5af1', 'c042c8e6-50dd-406e-988e-6c30dfad7e0f', '092602', '0926', '血热瘀滞', 1, '', '黑睛满布血翳，甚则堆积如肉，白睛红赤，畏热羞明，目珠疼痛，口苦咽干。舌红，苔黄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8a677ef0-bcc2-4f11-ac30-25645a65101f', '04d5aa76-ca6f-4ac5-831e-6bf5f3d2ea59', '092701', '0927', '余邪未尽', 1, '', '翳障边清，变薄，表面光滑，眼红、眼痛、畏光及流泪始退，仅感眼紧涩，轻微羞明，无其他不适。舌红，苔薄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cd18dbd6-7038-4e1f-8ce2-cd12d280d2d8', '04d5aa76-ca6f-4ac5-831e-6bf5f3d2ea59', '092702', '0927', '阴津不足', 1, '', '宿翳已成，眼干涩，口干。舌红无苔，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3d06dead-674e-4fc0-8256-9a819a33e68b', '04d5aa76-ca6f-4ac5-831e-6bf5f3d2ea59', '092703', '0927', '气血凝结', 1, '', '宿翳日久或赤脉长入，形如毛刷，或呈分枝状，视力昏蒙，无其他症状。舌红，苔薄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '991463f3-68f0-44a7-8435-51f8200fefc8', '6bc75893-48a0-4f2c-ae0c-503aacb9615f', '092801', '0928', '肝经风热', 1, '', '瞳神紧小，抱轮红赤，黑睛后壁有灰色点状沉着物，神水不清，畏光，流泪，目珠坠痛，头额痛。舌红，苔薄白或微黄，脉浮数或弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0c673b15-36ac-4dfb-8db3-4f913a39dcbd', '6bc75893-48a0-4f2c-ae0c-503aacb9615f', '092802', '0928', '肝胆湿热', 1, '', '瞳神紧小，抱轮红赤，黑睛后壁灰色沉着物密集，神水混浊，目珠坠胀疼痛，畏光。灼热，多泪，视物模糊，口舌生疮，阴部溃疡，口干苦，溺赤便结。舌红，苔黄而糙，脉弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '01978d40-9f78-4fc1-a0a2-3230e2014c84', '6bc75893-48a0-4f2c-ae0c-503aacb9615f', '092803', '0928', '风湿化火', 1, '', '瞳神紧小，抱轮红赤持久不退或反复发作，黑睛后有灰色沉着物，神水浑浊，瞳神有白膜粘着，骨节酸楚，或小便不利，或短涩灼痛。苔黄腻，脉滑数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '334106c1-8e5c-41fb-a0cc-c7d62f1a1162', '6bc75893-48a0-4f2c-ae0c-503aacb9615f', '092804', '0928', '阴虚火旺', 1, '', '瞳神紧小或干缺，赤痛时轻时重，干涩昏花，口干咽燥，口舌生疮，心烦失眠。舌红，苔薄，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b7acc971-9b12-43d5-9ffa-5c1441de1cbe', '6bc75893-48a0-4f2c-ae0c-503aacb9615f', '092805', '0928', '脾肾阳虚', 1, '', '瞳神紧小或干缺，视物模糊，眼疲劳，或长期应用皮质类固醇，体胖乏力，动辄心悸，气短。舌淡，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b075824a-b5f4-4e25-b272-208f4ae72462', '7904528d-72e3-4dd9-82f7-735bc02d1588', '092901', '0929', '脾胃积热', 1, '', '黄液上冲，抱轮红赤，目珠赤涩疼痛，羞明流泪，大便秘结，小便赤涩。舌红，苔黄腻，脉滑数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '07f22ff7-b8f6-47d1-ac0b-b0ca3fafc20b', '7904528d-72e3-4dd9-82f7-735bc02d1588', '092902', '0929', '肝胆湿热', 1, '', '黄液上冲，白睛混赤，黑睛翳障混浊，瞳神紧小，头目剧痛，羞明流泪，口苦咽干，阴部溃疡。舌红，苔黄，脉弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b30d7581-35f3-4822-85af-d2a07b0e6735', '7904528d-72e3-4dd9-82f7-735bc02d1588', '092903', '0929', '阴虚火旺', 1, '', '黄液上冲，反复发病，白睛混赤，瞳神干缺，口舌生疮，口干咽燥，阴部溃疡。舌红，苔薄，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4feeb17d-7420-4064-accc-477f82dccc44', '21b6439b-b931-4549-bf41-14308f2eb3da', '093001', '0930', '肝胆火炽', 1, '', '血灌瞳神，血色鲜红，口苦咽干。舌红，少苔，脉弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '761e7e58-b76d-4703-bfef-7677c68f8bc9', '21b6439b-b931-4549-bf41-14308f2eb3da', '093002', '0930', '阴虚火旺', 1, '', '血灌瞳神，积血时有反复，眼珠隐痛，头晕耳鸣，失眠多梦，舌红，少苔，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b41b4f3b-59c7-4393-9ad6-1c2bc782103b', '21b6439b-b931-4549-bf41-14308f2eb3da', '093003', '0930', '外伤目络', 1, '', '发生于撞击伤目或手术之后，血灌瞳神，目珠胀痛牵及头额，视力下降。舌红，苔薄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b4a72f8d-e8b9-432b-a638-080fc8333dd4', 'afcea8f7-c9eb-4b5f-8aba-ce286e13968f', '093101', '0931', '风火攻目', 1, '', '黑睛混浊如哈气，瞳神散大，隐隐绿色，头痛如劈，目胀欲裂，视力锐减，烦躁口干。舌红，苔薄黄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f5286f8e-3901-43a3-9b0e-b59fb25e0d4f', 'afcea8f7-c9eb-4b5f-8aba-ce286e13968f', '093102', '0931', '饮邪上泛', 1, '', '瞳神散大，气色混浊，头痛眼胀，干呕吐唾沫，食少神疲，四肢厥冷。舌淡，苔薄，脉细弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b99dbb77-c142-4f23-b359-e421d4893422', 'a5843a38-f85d-4cb0-8d7a-8a4aad937501', '093201', '0932', '肝肾阴虚', 1, '', '晶珠混浊，视物昏蒙，头晕耳鸣，腰膝酸软。舌红，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6c7ec8e7-7d49-4fc3-9395-cea114cff41d', 'a5843a38-f85d-4cb0-8d7a-8a4aad937501', '093202', '0932', '脾肾阳虚', 1, '', '晶珠混浊，视物昏蒙，形寒肢冷，面色恍白，喜热恶冷，大便溏薄，小便清长。舌质淡，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '22f35d61-5445-4d54-bba5-44feda152f76', 'a5843a38-f85d-4cb0-8d7a-8a4aad937501', '093203', '0932', '气血两虚', 1, '', '晶珠混浊，视物昏花，不耐久视，眉棱骨酸痛，神疲懒言，肢软乏力。舌淡，苔白，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f4d37cde-bc87-4507-9461-dfccc77e194d', 'a5843a38-f85d-4cb0-8d7a-8a4aad937501', '093204', '0932', '脾虚湿热', 1, '', '晶珠混浊，干涩昏花，口干不欲饮。舌红，苔黄腻，脉滑数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '24d68a01-691e-49b4-9669-ad44869653a8', '52afacdb-1061-40ce-b1f5-c03a6c0bae53', '093301', '0933', '湿热蕴结', 1, '', '自觉眼前黑影浮动。检查玻璃体有尘状、絮状、团状混浊。视物昏蒙，胸闷纳呆，口苦心烦，头重。舌红，苔黄腻，脉滑数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'e9bc23ee-b8fd-4531-8f05-2946e29efb9b', '52afacdb-1061-40ce-b1f5-c03a6c0bae53', '093302', '0933', '气滞血瘀', 1, '', '自见眼前黑花，呈点状、块状红色混浊，视力突降。情志不舒，胸闷胁胀，口苦。舌有瘀斑或紫暗，苔黄，脉弦涩。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '1057dd4e-84b6-4018-a4e3-2aeb5d6e70e3', '52afacdb-1061-40ce-b1f5-c03a6c0bae53', '093303', '0933', '肝肾阴虚', 1, '', '眼前黑花飘动，时隐时现，如环状，或有闪光感，逐渐进展，或伴能近怯远，视物昏蒙，眼干涩，易疲劳。舌红，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bcfec469-e3c8-4361-99cf-98a563fcff5f', '40dc233d-1c6d-450d-8c12-6afcc3bc0388', '093401', '0934', '肝郁气滞', 1, '', '视物模糊，目珠胀痛，头晕目眩，伴情志不舒，胸闷叹息，口干口苦。舌质红，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4540cfec-151c-48c5-8cf2-25d8b858d45c', '40dc233d-1c6d-450d-8c12-6afcc3bc0388', '093402', '0934', '肝肾阴虚', 1, '', '视物昏蒙，眼前有暗影遮挡，眼干涩不爽，病程日久或伴五心烦热，头晕耳鸣。舌质红，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3bd7800b-e1a1-4453-b5e3-b9ab6e9c88f9', '40dc233d-1c6d-450d-8c12-6afcc3bc0388', '093403', '0934', '气血两虚', 1, '', '视物昏蒙，目涩难睁，神疲乏力，面色无华，心悸怔忡，纳食不馨。舌质淡，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5a1a3d7c-4a62-472c-afe0-df1b0fdaf73b', '40dc233d-1c6d-450d-8c12-6afcc3bc0388', '093404', '0934', '湿浊上泛', 1, '', '视物模糊，眼前有灰黄色暗影遮挡，或头目胀痛，恶心呕吐，伴胸闷纳呆。舌红，苔薄腻，脉缓。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7997f150-4508-4db7-9e66-247b38fba656', '0315f6db-af0d-4d96-9d79-51b7d1c27cc2', '093501', '0935', '痰湿结聚', 1, '', '视直如曲，视物昏蒙或视大为小，胸闷纳呆，口渴不欲饮。舌质红，苔黄腻，脉弦滑。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '768dc745-fd3c-463f-9315-1197793bab71', '0315f6db-af0d-4d96-9d79-51b7d1c27cc2', '093502', '0935', '阴虚火旺', 1, '', '视直物为弯曲之状，视物昏花，眼干涩不适，头晕耳鸣，口咽干燥，心烦少寐，腰酸腿软。舌红，少苔，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '40186c86-b8ab-43a5-85d4-1f400073043e', '0315f6db-af0d-4d96-9d79-51b7d1c27cc2', '093503', '0935', '瘀血内滞', 1, '', '视直如曲，视物模糊不清或有暗影遮挡。舌色紫暗，有瘀斑，苔薄，脉细涩。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ebf11e3e-e53f-4700-9675-fcb893709dad', 'f3c99ece-2f68-4141-94c2-2d62f23beb6a', '09360101', '093601', '气滞血瘀', 1, '', '视力骤降失明，眼底可见动脉阻塞性改变，情志郁结，头晕耳鸣，胸闷。舌质紫暗，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '08c591e6-ea9b-4aaa-9d06-fad3b02fc3ef', 'f3c99ece-2f68-4141-94c2-2d62f23beb6a', '09360102', '093601', '气血两虚', 1, '', '瘀阻日久，视力未复，视神经乳头色苍白，视网膜水肿已消，然视网膜血管纤细成白线状或伴有白鞘，黄斑区色素紊乱，头晕乏力。舌淡，苔薄，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b5686245-7019-4002-9cd8-77365b192ce9', 'f3c99ece-2f68-4141-94c2-2d62f23beb6a', '09360103', '093601', '肝阳化风', 1, '', '突然失明，自然缓解，视力恢复，可能反复发生，最终失明不能恢复，伴肢麻头晕耳鸣。舌质红，苔薄脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0d16e820-1888-408a-8a6e-f6a7f4e7aa0a', '8f0641cd-83d0-4a1a-95eb-0a62f4584535', '09360201', '093602', '肝胆火炽', 1, '', '视网膜出血、色红，视力突然下降，头痛眼胀，口苦口干，心烦失眠。舌红，苔薄黄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '0f4a2f95-3d31-4025-98b8-469fad8cc9e0', '8f0641cd-83d0-4a1a-95eb-0a62f4584535', '09360202', '093602', '血热瘀滞', 1, '', '视网膜出血、渗出、水肿，突然视力下降，头目胀痛，烦躁易怒，口干喜饮，或伴有肢体瘀斑，结节压痛。苔薄，舌红，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cc9128a1-3e7f-43e4-a758-3b03e00afce7', '8f0641cd-83d0-4a1a-95eb-0a62f4584535', '09360203', '093602', '气滞血瘀', 1, '', '视力骤降未复，或云雾移睛，有团块状红色混浊，或视网膜出血日久虽减未消。舌暗，苔薄，脉弦。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bd424765-6e53-49cb-811a-b7a2bc593cf2', '8f0641cd-83d0-4a1a-95eb-0a62f4584535', '09360204', '093602', '阴虚火旺', 1, '', '病程以久，视力未复， 视网膜出血色暗红，伴五心烦热，口干饮不多。舌红，苔薄，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a3e6ec45-176c-41d8-8309-521e94bc283a', '7af7411f-ad2e-4531-bcec-beac02a6ab6d', '09360301', '093603', '肝胆火炽', 1, '', '视力骤降，玻璃体积血，头目胀痛，口干口苦，心烦失眠。舌红，苔薄，脉数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '493570d1-a32a-4d00-a4d1-cd224cffcda3', '7af7411f-ad2e-4531-bcec-beac02a6ab6d', '09360302', '093603', '阴虚火旺', 1, '', '病情迁延，玻璃体积血反复发作，潮热颧红，五心烦热，口干唇燥。舌质红，苔薄，脉细数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '8124ed93-bd54-4a73-b474-439c572875b0', '7af7411f-ad2e-4531-bcec-beac02a6ab6d', '09360303', '093603', '心脾两虚', 1, '', '出血反复发作，面色恍白，纳食不馨，心悸失眠，神疲乏力。舌质淡，苔薄白，脉细。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '5e116577-6e55-4289-ba15-f907d0e8831f', 'b0f6e431-f4f5-4052-aa78-61ac70cc5a9e', '09360401', '093604', '肝火亢盛', 1, '', '视力骤降，头目胀痛，眼珠压痛，转动时牵拉痛，视神经乳头充血、水肿，口苦咽干，性情急躁。舌红，苔黄，脉弦数。', '2008/04/10 08:34:51', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '809bc0fb-a6ad-4a63-909d-a7dae8c6bcca', 'b0f6e431-f4f5-4052-aa78-61ac70cc5a9e', '09360402', '093604', '肝郁气滞', 1, '', '视力骤降，眼胀痛，转动痛，压痛，眼底无明显异常，情志抑郁，胸闷叹息，口干唇燥。舌红，苔薄白，脉弦。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '549e0450-6969-41d1-aae6-28f5012e4579', 'b0f6e431-f4f5-4052-aa78-61ac70cc5a9e', '09360403', '093604', '阴虚火旺', 1, '', '视力骤降，五心烦热，潮热颧红，口干唇红。舌红，苔薄，脉细数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'cc62aad8-9632-461b-bbcb-04581778321d', 'b0f6e431-f4f5-4052-aa78-61ac70cc5a9e', '09360404', '093604', '气血两虚', 1, '', '视力骤降，起于大失血或产后哺乳期，头晕耳鸣，神疲乏力，面色苍白，动则气短，心悸少眠。舌质淡，苔薄，脉细。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b1e23eb6-c377-45c4-8402-d3d98e73308e', '11351975-3c25-4e51-b56d-b09127347f8f', '094101', '0941', '肝郁气滞', 1, '', '双眼先后或同时发病，视物模糊，中央有大片暗影遮挡，日渐加重而盲无所见，曾有日珠转动时牵拉痛和压痛。心烦、郁闷，口苦胁痛。舌红，苔薄，脉弦。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '01a47ce7-e15e-46af-8f10-377977e2e8f0', '11351975-3c25-4e51-b56d-b09127347f8f', '094102', '0941', '脾虚湿泛', 1, '', '视力昏蒙，头重眼胀，或有胸闷泛恶，眼压偏高，久则视野缩小，以至失明。舌淡，苔薄白，脉滑。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ea57d5b1-835b-4496-84d7-45edd301c1cd', '11351975-3c25-4e51-b56d-b09127347f8f', '094103', '0941', '肝肾阴虚', 1, '', '双眼昏蒙，眼前有黑影遮挡，渐至失明，双眼干涩，头晕耳鸣，遗精腰酸。舌质红，苔薄，脉细。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c88e929c-767a-4243-8ee5-a34373c2abc7', '11351975-3c25-4e51-b56d-b09127347f8f', '094104', '0941', '气血两虚', 1, '', '视力渐降，日久失明，面乏华泽，神疲乏力，懒言少语，心悸气短。舌质淡，苔薄，脉细。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3c85b09d-6e51-4676-9aa8-dd826380dca9', '11351975-3c25-4e51-b56d-b09127347f8f', '094105', '0941', '脾肾阳虚', 1, '', '久病虚羸，目无所见，畏寒肢冷，面色发白，腰膝酸软，大便溏薄，阳萎早泄，女子带下清冷。舌淡，苔薄白。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '00ec6aef-fcad-4dc3-af77-3f30d579cd3f', 'f24ca2b3-80b0-471a-beb8-7ddd871c75e2', '094201', '0942', '脾肾阳虚', 1, '', '夜盲，视野缩小，面色萎黄，神疲乏力，畏寒肢冷，耳鸣耳聋，阳萎早泄，女子月经不调，量少色淡。舌质淡，苔薄，脉细无力。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '30395fda-ebd8-4b52-b7fc-19aa4e1c7b62', 'f24ca2b3-80b0-471a-beb8-7ddd871c75e2', '094202', '0942', '肝肾阴虚', 1, '', '夜盲，视野缩小，眼干涩，头晕耳鸣，失眠梦扰，口干，腰膝酸软。舌红，少苔，脉细数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2e896c3f-db3a-4668-bf1f-2d31b85b4b10', 'f24ca2b3-80b0-471a-beb8-7ddd871c75e2', '094203', '0942', '脾虚气弱', 1, '', '夜盲，视野缩小，面乏华泽，肢体乏力，纳食不馨，口淡无味，视物疲劳，不能久视，或有便溏泄泻。舌质淡，有齿痕，苔薄白，脉细弱。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '4f233b7f-cdae-47e2-a716-8683aa992ca6', 'f24ca2b3-80b0-471a-beb8-7ddd871c75e2', '094204', '0942', '气虚血瘀', 1, '', '夜盲，视野狭窄，视力模糊，病程日久，视神经乳头腊黄色，视网膜血管纤细，脉络膜血管硬化。舌质暗，苔薄，脉细。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b3308030-dfa5-4fa3-b553-ec06a9da795e', '34e4a6b8-ab92-455b-acbf-c8dcf7cfe183', '094301', '0943', '心阳不足', 1, '', '视近清晰，视远模糊，或伴心烦失眠健忘，神倦乏力。舌淡，苔白，脉弱。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6415dbb7-7a91-47a7-b7d6-2bc3036f384e', '34e4a6b8-ab92-455b-acbf-c8dcf7cfe183', '094302', '0943', '脾虚气弱', 1, '', '视近清晰，视远模糊，视疲劳，喜垂闭。或病后体虚，食欲不振，四肢乏力。舌淡红，苔薄白，脉弱。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '44e268ac-0fb8-4d7a-b969-988b892bb571', '34e4a6b8-ab92-455b-acbf-c8dcf7cfe183', '094303', '0943', '肝肾亏虚', 1, '', '远视力下降，眼前黑花飞舞，头昏耳鸣，腰膝酸软。舌淡红，无苔，脉细。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'dd53a02c-58c8-4422-b1fe-facfad5324a5', '34e4a6b8-ab92-455b-acbf-c8dcf7cfe183', '094304', '0943', '肝血不足', 1, '', '远视力下降，视疲劳，视物变形，眼底检查可见黄斑部萎缩斑或出血，面色不华。舌淡，苔薄白，脉弱。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'bd04fe37-8803-4b14-ae1c-91070b87bcd2', 'ce016986-f424-4405-a064-9716811fb397', '094401', '0944', '风邪袭络', 1, '', '目偏斜，复视，或伴上胞下垂，发病急骤或有眼疼，头痛发热。舌红，苔薄，脉弦。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '946d63e4-60b0-4be3-8802-e91eb80c3c5a', 'ce016986-f424-4405-a064-9716811fb397', '094402', '0944', '风痰入络', 1, '', '目偏斜，复视，头晕，呕恶。舌红，苔腻，脉弦。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b27ac638-1d62-4756-baf0-2d543868e805', 'ce016986-f424-4405-a064-9716811fb397', '094403', '0944', '肝风内动', 1, '', '突发目偏斜，头晕耳鸣，面赤心烦，肢麻。舌红，苔黄，脉弦。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '6a545982-5a8e-4a50-929b-d7f48a305ab9', 'ce016986-f424-4405-a064-9716811fb397', '094404', '0944', '外伤瘀滞', 1, '', '外伤后目偏斜，或有胞睑、白睛瘀血，眼疼，活动受限，视一为二。舌红，苔薄，脉弦。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'f439067f-df3e-41c8-8bfa-150900872339', '17ab75e2-a57b-47b2-a1b7-fccc2cd26041', '094501', '0945', '先天不足', 1, '', '眼球颤动，左右摇摆，视力模糊，自幼禀赋不足有胎患内障，视物易色等病。舌淡，苔薄，脉细。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7ad729e4-ced6-4c18-ae06-e3ea6616c54a', '17ab75e2-a57b-47b2-a1b7-fccc2cd26041', '094502', '0945', '肝风内动', 1, '', '眼球颤动，头晕目眩，耳聋耳鸣，或伴见站立不稳，摇晃不定，或步态不稳，动作不协调等。舌红，苔薄黄，脉弦。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '2856e961-44a2-4b7b-b7c9-a6f88e6476c5', '17ab75e2-a57b-47b2-a1b7-fccc2cd26041', '094503', '0945', '湿浊上泛', 1, '', '眼球震颤，头晕耳鸣，甚则头晕如旋，恶心呕吐唾沫，胸闷。舌淡，苔薄腻，脉滑。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7801c809-ca66-43f8-afbb-ca5895cb9cfe', 'b0d299c7-3050-4456-a5a4-7272afd4d490', '094601', '0946', '风热上扰', 1, '', '眉棱骨部疼痛，鼻塞，身热，恶风。舌红，苔薄，脉浮数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '062f5df1-4a15-4fa2-9d2a-8b5deaabc256', 'b0d299c7-3050-4456-a5a4-7272afd4d490', '094602', '0946', '肝火上炎', 1, '', '眉棱骨痛，眼胀痛，或有头晕目赤，胸胁胀痛，烦躁易怒，口苦咽干，小便短赤，大便干燥。舌质红，苔薄白或薄黄，脉弦数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'b8bb0bd0-c738-4a9b-bed7-99ef206700ea', 'b0d299c7-3050-4456-a5a4-7272afd4d490', '094603', '0946', '风痰上攻', 1, '', '眉棱骨痛，甚则眼眶深部胀痛，白天症状较轻，暮后症状加重，头目眩晕，目喜闭，胸闷泛恶，纳食不馨。舌红，苔薄白腻，脉弦滑。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a9e92d18-d3bc-45b3-8570-cf5b1bde7e7a', 'b0d299c7-3050-4456-a5a4-7272afd4d490', '094604', '0946', '肝血不足', 1, '', '眉棱骨痛，羞明畏光，干涩，不欲睁眼，不能久视，妇女月经期间症状有加重趋势。舌质淡，苔薄，脉细弱。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7ea74806-efe5-49f6-9371-f9fcc03a4c5e', '2b1ce67c-586e-44ae-a5e0-02f9632a6d1b', '094701', '0947', '热毒炽盛', 1, '', '眼珠高起，红赤凝定，眼疼头痛，高热寒战，恶心呕吐，昏睡。舌红，苔黄，脉弦数。 一', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3d97ba73-9fc8-4c5d-892e-efd02641138b', '2b1ce67c-586e-44ae-a5e0-02f9632a6d1b', '094702', '0947', '痰瘀博结', 1, '', '眼珠突出，眼睑不能闭合，胞睑肿胀，白睛红肿，热泪频流，神疲乏力，胸闷纳呆。舌淡.苔薄腻，脉细。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '922ac8f2-df02-470d-b133-b65f34cdf0ac', '2b1ce67c-586e-44ae-a5e0-02f9632a6d1b', '094703', '0947', '郁火伤阴', 1, '', '眼珠突出，白睛红赤，心烦多汗，失眠心悸，形体瘦弱，手指颤动。舌质红，苔薄，脉弦数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '638ad713-8e92-45c4-bf3c-c60349cc79c0', '901b0967-208f-4639-aeb0-c3c8aae2fc83', '094801', '0948', '风火热毒', 1, '', '眼球突出，白睛红肿，发热恶寒，头痛剧烈。舌红，苔黄，脉弦数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '90b03d53-8b00-4cf7-a464-27b7570f81a5', '901b0967-208f-4639-aeb0-c3c8aae2fc83', '094802', '0948', '热毒炽盛', 1, '', '眼球突起，眼睑红肿，白睛红赤壅盛，眼球不能转动，头眼剧痛，恶心呕吐，壮热神昏，面赤气粗，溲赤。舌红绛，苔黄糙，脉数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '922a70b5-ebc5-45a2-8efe-0af3c8c246f3', '8b804aa1-ab5c-4db9-88db-cb340d8aa1ff', '094901', '0949', '络伤出血', 1, '', '胞睑青紫肿胀，重坠难睁，或白睛溢血，眼珠完好，视力如常，或头额作痛。舌质正常，苔薄白，脉平。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'a835a47a-ab7c-48d6-9f08-f842fed98bf3', '8b804aa1-ab5c-4db9-88db-cb340d8aa1ff', '094902', '0949', '瘀血化热', 1, '', '胞睑青紫肿胀， 日久难消，疼痛灼热，眼珠端好，视力正常，口渴喜饮，溲黄，便结。舌红，苔黄，脉数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'ee201e82-282b-401f-9b46-44211984880c', 'c9b13ec6-3163-4ec4-8730-3f5504791d1b', '095001', '0950', '气滞血瘀', 1, '', '视力剧降，眼珠刺痛，或胀痛，白睛或黑睛破裂，或白睛溢血，或血灌瞳神，或晶珠混浊，或伴头痛。舌质红，苔薄黄，脉弦或数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( 'c12a3b09-68f3-4e31-956c-efa04e128157', 'c9b13ec6-3163-4ec4-8730-3f5504791d1b', '095002', '0950', '脓毒侵袭', 1, '', '目珠疼痛难忍，热泪频流，畏光难睁，视力剧将，胞睑红肿，白睛抱轮红赤或混赤，白睛或黑睛破损。或珠内组织脱出，创口污秽，或黄液上冲。眼珠变软，拒按。舌红，苔黄，脉数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '7f21af7d-61dc-4479-94ea-06fe187d2710', 'c9b13ec6-3163-4ec4-8730-3f5504791d1b', '095003', '0950', '感伤健眼', 1, '', '伤眼白睛或黑睛破损，红赤难退，或反复发作，迁延难愈。或眼内异物存留，而健眼自觉视物模糊不清或视力剧降，羞明流泪，白睛抱轮红赤，黑睛后有沉着物，神水混浊，瞳神缩小，或干缺，神膏混浊。眼底视乳头充血，水肿，视网膜反光增强等。舌质红，苔薄黄，脉细数。', '2008/04/10 08:34:52', '', 3 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '3f5b4661-b333-4017-9f08-78467bd8e240', '10e5c924-b9f4-4a94-be90-b480b7049a34', '010101', '0101', '气不摄血', 1, '', '月经提前，质稀色淡，神疲乏力，气短懒言，小腹空坠，纳少便溏。舌质淡，脉弱。', '2008/04/09 02:50:55', '', 0 );
INSERT INTO semiotic ( SEMCid, CDISid, code, groupCode, name, isClassical, SPETid, illustration, createDay, optrid, state ) VALUES ( '679e13d3-7436-4a49-beeb-4ce507a21c90', '10e5c924-b9f4-4a94-be90-b480b7049a34', '010102', '0101', '血热内扰', 1, '', '月经提前，量多，色红质粘，夹有小血块，烦热口干，尿黄便艰。舌质红，苔黄，脉滑数。', '2008/04/09 02:50:55', '', 0 );
