INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'c2d12a7f-7d98-4512-93b4-10b0e7a2f503', '0833', '青蛇毒', '08', 2, 1, '', '青蛇毒是因湿热之邪外侵，以致气血凝滞，脉络滞塞不通而成，呈局部筋脉肿胀色红，灼热疼痛。相当于血栓性浅静脉炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '09071624-e613-4732-ad5a-c7c75ba046a9', '0834', '股肿', '08', 2, 1, '', '股肿是因瘀血阻于阴脉，痹着不通，营血逆行受阻，水津外溢，导致下肢胀痛，皮色发白，肢体增粗为特征的疾病。相当于下肢血栓性深静脉炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '3d67d187-99ee-4353-b0f4-fc0c3fa9f6d2', '0835', '水火烫伤', '08', 2, 1, '', '水火烫伤是因热毒之气炽盛，腐烂皮肉，甚者火毒内攻可现不同脏腑兼证。相当于烧伤。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f18b6b67-2a19-493f-ab62-98c5a9e7b510', '0836', '冻疮', '08', 2, 1, '', '冻疮是因受寒邪侵袭，气血瘀滞，皮肤紫红麻木刺痛，或局部肌肤坏死，甚者可致全身冻僵。相当于冻伤。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '7ceaa2cd-e61b-4199-a1d7-c901cebeb3ce', '0837', '毒蛇咬伤', '08', 2, 1, '', '毒蛇咬伤是被有毒之蛇咬伤，毒液侵入伤口，气血受伤，内攻脏腑而发生的危急重症。因毒性不同而表现多种全身中毒症状。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '25c64bd9-9ef8-4554-8334-a76a8196f2c0', '0838', '破伤风', '08', 2, 1, '', '破伤风是由破伤感受风毒之邪，引起以全身和局部肌肉强直性和阵发性痉挛为特征的疾病。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '82f50cdf-8e9d-4135-9be2-956bfd92f192', '03', '中医肛肠科', '-1', 1, 1, '', '', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '1831225c-6b16-4417-af94-9a0efdabf978', '0301', '内痔', '03', 2, 1, '', '内痔系发生于齿线以上的静脉曲张团块，又称"里痔"。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '0ab0b5df-7d6a-4ccd-9ac1-f2b69806bd2b', '0302', '外痔', '03', 2, 1, '', '外痔系发生于齿线以下的静脉曲张团块或赘皮。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'be8f7535-c637-41ef-9f26-2dcf569e3d33', '0303', '混合痔', '03', 2, 1, '', '混合痔系发生于同一方位齿线上下，形成一体的静脉曲张团块。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '7f06b32b-f70b-4134-9318-b788ac9ebd4b', '0304', '肛裂', '03', 2, 1, '', '肛裂系指肛管皮肤及皮下组织裂开或形成溃疡。又名"钩肠痔"、"裂肛"。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '096dc779-dd53-4916-8fa4-cf3a94b8cc62', '0305', '肛痈', '03', 2, 1, '', '肛痈系肛管直肠周围软组织间隙急性感染所形成的化脓性病变，又称肛门直肠周围脓肿。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '95617344-0f74-4eb5-ae09-d8e8fbb9522c', '0306', '肛漏', '03', 2, 1, '', '肛漏系肛痈成脓自溃或切开后所遗留的腔道。义称痔漏、痔疮。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'fcc703eb-1a63-40e4-9854-6b9ee8f88bf9', '0307', '脱肛', '03', 2, 1, '', '脱肛主要指直肠粘膜或直肠全层脱垂，少数可发生部分乙状结肠脱垂，又称直肠脱垂。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f45ae0b0-a86c-4b11-92b2-fd0d323a4b6d', '0308', '悬珠痔', '03', 2, 1, '', '悬珠痔系正常肛乳头因慢性炎症刺激所致纤维结缔组织增生，又称肛乳头纤维瘤。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '16f0ef9c-e5c5-41a1-94c1-8276ba925cf0', '0309', '骶尾部畸胎瘤', '03', 2, 1, '', '骶尾部畸胎瘤是起源于胚胎原始细胞，生长于骶尾部的肿瘤。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '1fb14ffb-bce9-42e0-a58f-a003aac8df53', '0310', '骶尾部囊肿窦', '03', 2, 1, '', '骶尾部囊肿窦指位于骶尾部的囊肿因感染所形成的窦道。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4a84d33f-527d-4f04-b02f-9c5ee3eafd03', '0311', '息肉痔', '03', 2, 1, '', '息肉痔指发生于直肠内粘膜上的赘生物，有蒂或无蒂，又称直肠息肉。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '75201b21-dd7c-4727-a6b6-789aa63086ad', '0312', '肛门直肠狭窄', '03', 2, 1, '', '肛门直肠狭窄指任何原因所致肛门、直肠腔道变窄，并导致排便困难。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '939be377-c1d1-45fa-980c-af038e2dbc5f', '0313', '肛门失禁', '03', 2, 1, '', '肛门失禁指肛门完全或不完全失去控制排便的能力。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'b2cf1c63-313d-4fe2-aed0-16e5747a38e3', '0314', '直肠前突', '03', 2, 1, '', '直肠前突系抬直肠前下段向阴道方向突出，形成一个囊腔，尤在努挣排便时为甚，致粪便滞留，出现排便困难。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'abb6ff75-7a70-46a9-ad1f-baf3ac64143d', '0315', '盆底肌痉挛综合征', '03', 2, 1, '', '盆底肌痉挛综合征系由于盆底肌痉挛（反常收缩），出现以排便困难，肛门不适为主要症状的疾病。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'e4982182-71b4-4fd5-8b9a-fbf02d947f0d', '0316', '肛门湿疡', '03', 2, 1, '', '肛门湿疡是一种常见的非传染性皮肤病，病变多局限于肛门周围皮肤。相当于肛门湿疹。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '22c16f97-f663-46e4-b52f-21713644876f', '0317', '锁肛痔', '03', 2, 1, '', '锁肛痔系发生在肛管直肠的恶性肿瘤。相当于肛管直肠癌。', '2008/04/09 04:09:55', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '2602967d-03b5-411a-a97d-e6578288a042', '04', '中医皮肤科', '-1', 1, 1, '', '', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '0204b76e-0e41-4375-b449-efece3765811', '0401', '黄水疮', '04', 2, 1, '', '黄水疮是一种因毒热郁于皮毛，以流黄水、浸淫成片为特征的皮肤传染病。相当于脓疱疮。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'e763afd8-7fb2-4275-ab07-ae811ac6aef8', '0402', '流皮漏', '04', 2, 1, '', '流皮漏又称鸦啖疮，是一种皮肤损害为深红色浸润斑块，表面有棕红色结节，愈后形成萎缩性瘢痕的皮肤病。相当于寻常狼疮。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '7ff56278-7880-4610-ba61-d6245b863efa', '0403', '腓踹疽', '04', 2, 1, '', '腓踹疽是发生在小腿肚，以暗红色硬结，可溃烂而久不收口为特征的疮疡，类似于硬红斑。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '3b8160c9-f835-400f-b718-67160197e4e6', '0404', '肥疮', '04', 2, 1, '', '肥疮是多发生在头部的一种癣，以结黄痂、发秃落为特征。相当于黄癣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '1a12d225-9f82-4bf5-a7c2-28a7a120f953', '0405', '白秃疮', '04', 2, 1, '', '白秃疮是多发生在头部的一种癣，以脱白屑，久则毛发折断为特征。相当于之白癣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '72435478-e2f2-432c-b315-96f9427ea871', '0406', '圆癣', '04', 2, 1, '', '圆癣是发生在平滑皮肤的一种癣，以疹如钱币有匡廓，瘙痒为特征。相当于体癣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '8cc7202b-4528-4f00-a915-8509b662022b', '0407', '鹅掌风', '04', 2, 1, '', '鹅掌风是一种发生在手掌部的皮肤病，、以皮肤粗糙、变厚、于裂为特征。相当于手癣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '79a8c923-fe03-4815-87b5-fa202cce7477', '0408', '脚湿气', '04', 2, 1, '', '脚湿气是发生在足部的皮肤病，以足丫白斑湿烂或足跖、趾间起水疱为特征。相当于足癣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '498f67ed-1f95-482f-83f8-7dd3fed78993', '0409', '灰指（趾）甲', '04', 2, 1, '', '灰指（趾）甲多由于鹅掌风或脚湿气日久延及爪甲，以指（趾）甲增厚色灰，失去光泽为特征。相当于甲癣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '19f2c756-1d11-44d1-bf88-0ed2e178e756', '0410', '疥疮', '04', 2, 1, '', '疥疮是由疥虫侵袭皮肤所致。以指缝、手腕、脐周、阴股部等皮肤发生水疱、隧道，夜间痒甚为特征的传染件皮肤病。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'a0109f7c-f5c5-4f43-b1c1-4c3b1abc8ef1', '0411', '蛇串疮', '04', 2, 1, '', '蛇串疮是因肝脾内蕴湿热，兼感邪毒所致。以成簇水疮沿身体一侧呈带状分布，排列宛如蛇行，且疼痛剧烈为特征的皮肤病。相当于带状疱疹。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '30fe8813-318d-440b-b497-2c66861d8e21', '0412', '热疮', '04', 2, 1, '', '热疮多因内热炽盛所致。以皮肤粘膜交界处发生成群水疱，痒痛相兼为特征。相当于单纯疱疹。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f8f86121-e8e3-418f-9810-1b4d698fcfb3', '0413', '扁瘊', '04', 2, 1, '', '扁瘊是一种多发生于颜面或手背的米粒大小、扁平，稍高起皮面的小疣。相当于扁平疣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '566f6d3b-21c8-47da-b194-ebc4fb7b7921', '0414', '疣目', '04', 2, 1, '', '疣目为好发于手、足、头皮，大如黄豆，粗糙而坚硬，表面呈刺状，相当于寻常疣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '727a9939-d242-4406-a12a-5a7d686c4e18', '0415', '鼠乳', '04', 2, 1, '', '鼠乳多生在躯于、四肢呈绿豆至黄豆大小，半球形隆起，疹中央有脐窝，形如鼠乳。相当干传染性软疣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '82630884-dfae-422c-af1c-8cd4419071b7', '0416', '鸡眼', '04', 2, 1, '', '鸡眼是因足部（亦偶见于手部）长期受挤压或压迫所致，其根陷肉里，顶起硬结，形似鸡眼的皮肤病。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'a85fb0dd-d808-480a-b9ed-bae0f84832e6', '0417', '胼胝', '04', 2, 1, '', '胼胝是指因手足久受摩擦压迫所致，其特征为皮厚涩而圆短如茧。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '3e6f90a6-be93-4726-b98b-d6a39c709c67', '0418', '湿疮', '04', 2, 1, '', '湿疮是由禀性不耐，风湿热邪客于肌肤而成。皮疹呈多种形态，发无定位，易于湿烂流津的瘙痒性渗出性皮肤病。相当于湿疹。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ad6dcbf2-d476-4b8c-8c8f-c00bde373aee', '0419', '四弯风', '04', 2, 1, '', '四弯风是因禀性不耐或脾虚湿盛所致。好发于四肢弯曲处，以皮肤干燥肥厚，抓破流津，瘙痒无度为特征的一种慢性皮肤病。相当于异位性皮炎。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'a32700f2-b85c-49d0-b986-9d356fc642c3', '0420', '瘾疹', '04', 2, 1, '', '瘾疹又称bei(倍）lei （蕾）。其特征为身体瘙痒，搔之出现红斑隆起，形如豆瓣，堆累成片，发无定处，忽隐忽现，退后不留痕迹。相当于荨麻疹。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ca50967f-9424-4e08-bda6-47338520d3b0', '0421', '土风疮', '04', 2, 1, '', '土风疮指因肌腠虚疏，复感风邪所致，以状如风疹而头破，乍发乍瘥为特征的皮肤病。类似于丘疹性荨麻疹。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '57ba211e-598e-4b2d-99f9-cf2c63271bdc', '0422', '药毒', '04', 2, 1, '', '药毒是指因禀赋不耐，药毒内侵所致的皮肤发疹。相当于药物性皮炎。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6688fcdb-4463-4c15-b8f4-069d2cfdea35', '0423', '天疱疮', '04', 2, 1, '', '天疱疮是由心火脾湿，兼感风热暑湿之邪，薰蒸肌肤而成。以皮肤起燎浆水疱，小如芡实，大如棋子，皮破流津，缠绵不愈的大疱性皮肤病。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'a18c45c7-6a82-4cb6-97d4-e57c29234351', '0424', '火赤疮', '04', 2, 1, '', '火赤疮是以水疱成群，多呈环状排列，并伴有红斑，瘙痒难忍为特征的大疱性皮肤病。相似于疱疹样皮炎。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '7459fbd6-4a60-4da1-bd51-f532edb3ab40', '0425', '登豆疮', '04', 2, 1, '', '登豆疮是因表虚里实，毒热入于营血所致。以皮肤大片潮红，出现群集帽针头至粟粒大小脓疱为特征的皮肤病，相似于疱疹样脓疱病。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', '0426', '红蝴蝶疮', '04', 2, 1, '', '红蝴蝶疮是一种面部常发生状似蝴蝶形之红斑，并可伴有关节疼痛、脏腑损伤等全身病变的系统性疾病。相当于红斑性狼疮。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ddef2ce8-a9dd-4f36-aadf-37d00cdc9d46', '0427', '皮痹', '04', 2, 1, '', '皮痹是以皮肤肿胀、硬化，后期发生萎缩的皮肤病。可局限于某一部位，亦可累及全身。相似硬皮病。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ef9fb9d7-50f7-450e-9d78-e28e30b03d96', '0428', '狐惑病', '04', 2, 1, '', '狐惑病是一种以口咽、阴部蚀烂，目赤如鸠眼为特征的综合性皮肤病。类似于白塞氏综合证。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'e111efd8-506c-4d38-8d8f-9c0d1f49409b', '0429', '猫眼疮', '04', 2, 1, '', '猫眼疮因其疹形如猫之眼而得名。多发于春秋季，好发于手足，可累及口腔及阴部。相当于多形性红斑。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '1b49b96f-a69c-4477-b4b9-48ac0576b0e5', '0430', '瓜藤缠', '04', 2, 1, '', '瓜藤缠是以小腿起红斑结节，犹如藤系瓜果绕胫而生为特征的皮肤病。类似于结节性红斑。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6ce15cfc-1f03-4ab5-88b8-486ef03f3867', '0431', '风热疮', '04', 2, 1, '', '风热疮是以躯干起淡红色斑片，多随皮纹排列，脱屑如糠秕之状为特征的皮肤病。相当于玫瑰糠疹。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'fbcc7440-3df6-46e8-8543-172c364fdd6e', '0432', '白H', '04', 2, 1, '', '白H是以皮肤上起红色斑片，上覆多层白色皮屑，抓去皮屑可见点状出血为特征的皮肤病，相当于银屑病。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'e7e4cbf2-07e9-4992-ad53-bfecaf99ae63', '0433', '摄领疮', '04', 2, 1, '', '摄领疮是好发在颈部两侧，以皮肤粗糙肥厚，剧烈瘙痒为特征的皮肤病。相当于神经性皮炎。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '9be8370a-bc77-4305-a99e-a8e9e1c488e5', '0434', '顽湿聚结', '04', 2, 1, '', '顽湿聚结是因体内蕴湿，外感风毒或虫咬所致，以四肢发生灰褐色坚实结节，伴奇痒为特征的皮肤病。相当于结节性痒疹。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6e4e72f1-8318-456c-95a0-2a350071a4dd', '0435', '紫癜风', '04', 2, 1, '', '紫癜风是以皮肤出现紫红色扁平皮疹，自觉瘙痒，可发于全身各处，常累及口唇为特征的皮肤病。相当于扁平苔癣。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6b0ffbd1-4bc5-4b60-acc4-ecf20d859849', '0436', '白驳风', '04', 2, 1, '', '白驳风是以皮肤变白，形状不一，并不痒痛为特征的皮肤病。相当于白癜风。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ba030faf-6167-491e-9a1b-9cc1d0251b99', '0437', '黧黑斑', '04', 2, 1, '', '黧黑斑是发生在面部的黄褐色或灰黑色斑片，不高出皮肤，常见于鼻背两侧。类似于黄褐斑。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '1a23a39f-2533-4b70-b25d-834237a8224f', '0438', '蟹足肿', '04', 2, 1, '', '蟹足肿是创伤或自发而起。在皮肤上出现肥大而坚实的斑块，形状如蟹足的一种皮肤病。相当于瘢痕疙瘩。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '8798cc4d-8c99-4383-92ef-c93ed78f3444', '0439', '酒糟鼻', '04', 2, 1, '', '酒糟鼻是发生在鼻准头及鼻两侧，以皮肤潮红、丘疹、脓疱，甚则鼻头增大变厚为特征的一种皮肤病。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6633b88d-5692-4dd3-aa5c-4ce1e8ae1acc', '0440', '粉刺', '04', 2, 1, '', '粉刺多发于面部，以丘疹、脓疱、结节有时可挤出白色碎米样粉汁为特征的一种皮肤病。相当于寻常性痤疮。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '9e1b6da9-39f5-4d21-b00f-96bdc330b7bb', '0441', '面游风', '04', 2, 1, '', '面游风是多发生于面部，以皮肤油腻或干燥，结黄痂或起白屑，痒甚为特征的皮肤病。类似于脂溢性皮炎。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '432f866d-a069-4bf2-bb0e-78efecf1c511', '0442', '淋病', '04', 2, 1, '', '淋病多为淋球菌感染而引起，以尿频、尿急、尿道刺痛或尿道溢脓，甚至排尿困难为主要临床表现的性传播疾病。', '2008/04/09 04:59:42', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '23b59ca9-b01d-4cd9-966f-16b460f3fd90', '05', '中医儿科', '-1', 1, 1, '', '', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '08ecbb6e-3f5c-4dcb-a5fc-0ad49b845281', '0501', '感冒', '05', 2, 1, '', '感冒指由外感风邪，客于肺卫所致。临床以恶寒、发热、头痛、鼻塞、流涕、打嚏、咳嗽为主要症状。一年四季均可发生。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '444a26aa-31fe-435a-b7a7-f109a57952fe', '0502', '咳嗽', '05', 2, 1, '', '咳嗽指由外邪袭肺，肺失宣肃而致咳嗽的病症。其长期不愈者为慢性咳嗽。多见于急、慢性支气管炎。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ce76695a-2bc9-4624-9fee-53628bf07a3e', '0503', '哮喘', '05', 2, 1, '', '哮喘由感受外邪，或因伏痰夙根复加外感、饮食等因素诱发，以喉间痰鸣有声，呼吸困难为主要临床表现的疾病。常见于哮喘性支气管炎或支气管哮喘。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f640ec4f-9c56-42b7-830f-df869ff382eb', '0504', '肺炎喘嗽', '05', 2, 1, '', '肺炎喘嗽由外邪犯肺，邪壅肺气而见发热、咳嗽、气喘、鼻煽为主症的疾病。若正虚邪恋，可迁延难愈。常见于支气管肺炎、大叶性肺炎、迁延性肺炎。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'fb8b15c6-1cfe-43c9-9eeb-dc384a7b2854', '0505', '鹅口疮', '05', 2, 1, '', '鹅口疮是由感受邪毒，内因心脾积热，上熏口舌，而致口腔出现成片白屑，状如鹅口的疾病。相当于霉菌性口腔炎。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '54c9d129-2b5c-49f3-ac22-60febd35911c', '0506', '泄泻', '05', 2, 1, '', '泄泻是由外感时邪，或内伤乳食而致大便次数增多的疾病。如病久不愈，常可导致疳症。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f30decc7-48ed-499f-9b75-9c249186b214', '0507', '厌食', '05', 2, 1, '', '厌食指由脾胃失调，不欲进食，甚至拒食的疾病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '446ee709-7376-45c1-83ef-3815c5943a8e', '0508', '疳症', '05', 2, 1, '', '疳症指由喂养不当，脾胃受伤，影响生长发育的病症。相当于营养障碍的慢性疾病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6b70500c-8b09-4a9d-8cca-2f8a20439501', '0509', '水肿', '05', 2, 1, '', '水肿是由外感风邪或邪毒入侵，导致肺、脾、肾功能失调，水道不利，水湿溢于肌肤而致全身水肿。多见于急、慢性肾炎，肾病综合征。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'e1217f88-5139-4781-bb76-dcd40a1570fb', '0510', '麻疹', '05', 2, 1, '', '麻疹指由感受麻疹疫毒所致，以发热咳嗽，泪水汪汪，唇内颊"麻疹粘膜斑"及满身布发红疹为特征，好发于冬春季节的乙类传染病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '8811e45f-75c8-43d8-b6ea-6872063672dc', '0511', '风痧', '05', 2, 1, '', '风痧指外感风痧时毒，蕴于肺脾，发于肌肤而出现的淡红色斑丘疹，症状轻浅。好发于5岁以下小儿，相当于丙类传染病的风疹。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'e4bfe5da-a85d-4fa2-8b87-f7e7ddf9c9d2', '0512', '丹痧', '05', 2, 1, '', '丹痧由丹痧疫毒感染，以肌肤布有弥漫性鲜红细小皮疹若丹，咽喉肿痛或有腐烂，口唇苍白为特征的传染病。相当于猩红热。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'cadd2374-0df7-4a70-856d-2ae065ebc1f3', '0513', '水痘', '05', 2, 1, '', '水痘指由感染时行病毒，蕴于肺脾，发于肌肤，皮肤出现红色丘疹，中有水疱的传染病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4726022d-0426-48b0-9796-860cf7917402', '0514', '痄腮', '05', 2, 1, '', '痄腮是由风温邪毒引起，以发热、耳下腮部肿痛为主症的急性传染病，相当于流行性腮腺炎。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '8a5810e2-7932-4fe2-8237-b7c43dd76b7f', '0515', '顿咳', '05', 2, 1, '', '顿咳是由感染时行疫毒，客于肺系所致，以阵发痉挛性咳嗽，咳后有特殊的吸气性吼声为特征。相当于百日咳。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '9cb534e1-babe-480a-8c95-737687c91de2', '0516', '暑瘟', '05', 2, 1, '', '暑瘟由感受暑疫邪毒所致，以发热、头痛、呕叶、项强为主症。重症可突然高热、神昏抽搐而生内闭外脱危象。相当于流行性乙型脑炎。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ca31a2aa-fc6e-47de-b6c3-9fff05ab59cc', '0517', '夏季热', '05', 2, 1, '', '夏季热是由于小儿不耐暑气的熏蒸，蕴于肺胃，以致长期发热，汗闭，口渴，多尿为主症。其特点为体温常随气温的变化而升降的季节性疾病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'c00cdad0-0123-4831-9049-e55f82b4a2ea', '0518', '疰夏', '05', 2, 1, '', '疰夏是指春夏之交，开始全身倦怠，食欲不振，大便不调，身微热，但体温多正常，秋凉后多可自愈的季节性疾病，有别于夏季热。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '2bcfb60a-48b6-40e7-9bc8-ea09b38c39a8', '0519', '遗尿', '05', 2, 1, '', '遗尿是指5岁以上幼童，不能自主控制排尿，经常入睡后遗尿者。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '186120d2-8b08-4f10-9309-2af18ef0650a', '0520', '胎黄', '05', 2, 1, '', '胎黄是由胎儿时期感受湿热，或瘀热内阻，出生后全身皮肤、巩膜发黄为主要症状的疾病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'd431a08e-5ecd-4971-8ebb-c4002de099e9', '0521', '疫毒痢', '05', 2, 1, '', '疫毒痢是由进食不洁之物，而突然发生高热，昏迷，惊厥，便下脓血（或无）等为主要症状的急性肠道传染病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '8e739ce6-1ad6-4048-88d3-de54e87ee801', '0522', '奶癣', '05', 2, 1, '', '奶癣又名胎疮，是指哺乳期婴儿因风湿热邪浸淫皮肤，而以面部出现的湿性或干性皮疹的疾病。相当于婴儿湿疹。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '779483ea-0a4a-421b-86a6-06acb215c235', '0523', '积滞', '05', 2, 1, '', '积滞是由乳食内积，脾胃受损而引起的肠胃疾病，临床以腹泻或便秘、呕吐、腹胀为主要症状。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'b4caa70c-e42f-4de2-9fad-572051696b01', '0524', '佝搂病', '05', 2, 1, '', '佝偻病是由先天不足或营养缺乏而致的发育迟缓，骨软变形的疾病。相当于中医学五迟、五软、鸡胸、肾疳之类的疾病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '02a9f1e8-6008-48d8-8d71-bc82ff28260b', '0525', '营养性贫血', '05', 2, 1, '', '营养性贫血是由后天失调，气血生化乏源而致的气血虚弱性疾病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '8e275bc4-8e1f-480c-b814-ef957f35cf50', '0526', '多动症', '05', 2, 1, '', '多动症是指学龄儿童由于肝气偏旺，阴阳失凋，心肾不交而致的好动不安，冒失无礼貌，脾气倔强的病症。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'af10bb4e-95a0-4cbe-a7fd-1b98d222791e', '0527', '解颅', '05', 2, 1, '', '解颅是由先天不足，颅内受损，或因热毒壅滞，水停于脑，致以头颅增大，前囟和颅缝开解为特征的疾病。相当于脑积水。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'a639fbd7-10da-4494-b69b-97e4040b0c73', '0528', '夜啼', '05', 2, 1, '', '夜啼是指1岁以内的哺乳婴儿，因寒、热、受惊等而致的夜间定时啼哭，甚则可通宵达旦的疾病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '077c39c0-7551-4976-b477-7ca39aa1b379', '0529', '奶麻', '05', 2, 1, '', '奶麻指哺乳期婴儿因感受时行疫毒，发病急，体温高，热退后肌肤出现玫瑰色细散皮疹的疾病。相当于幼儿急疹。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '377ee666-d3e3-4596-9c87-161f888f7bbf', '0530', '新生儿肺炎', '05', 2, 1, '', '新生儿肺炎是指出生后感受外邪，以致肺气郁闭或心血瘀阻而出现以不乳喘憋为主症的疾病。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '5a7d6446-12da-4562-b450-133afb6c1819', '0531', '蛔虫病', '05', 2, 1, '', '蛔虫病为肠道寄生虫病，儿童发病率颇高。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'dc2aab39-00f9-4699-85df-8d02553b8b3a', '0532', '蛲虫病', '05', 2, 1, '', '蛲虫病为肠寄生虫病，多见于1～5岁小儿，以肛门或外阴部作痒，夜间为甚，见有虫形细小如线头，俗称为"线虫"。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '736b5522-df34-468e-b66b-13d535369e37', '0533', '姜片虫病', '05', 2, 1, '', '姜片虫病为肠道寄生虫病，多见于5岁以上儿童。吐出物或大便排出成虫，其色赤如生肉，亦称"赤虫"。', '2008/04/10 12:04:44', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4726022d-0426-48b0-9796-860cf7917406', '051002', '逆证', '0510', 3, 1, ' ', '热毒炽盛者，可出现下列严重证候', '2008/04/10 12:04:44', ' ', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '30ffd830-ae1f-4551-b0b5-93861c9964f9', '07', '中医内科', '-1', 1, 1, '', '', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '88b89644-866f-4f3b-b7bb-fbb65f6ed925', '0701', '感冒', '07', 2, 1, '', '感冒病系外感风邪，客于肺卫，以鼻塞、流涕、咳嗽、恶寒、发热、头身疼痛为主要临床表现。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '61bdfcd4-438d-4b49-9b17-ef338f395d67', '0702', '咳嗽', '07', 2, 1, '', '咳嗽是因邪客肺系，肺失宣肃，肺气不清所致，以咳嗽、咯痰为主要症状的病症。多见于急、慢性支气管炎。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '305694af-7bb8-45de-aef9-0cabd48a6525', '0703', '风温肺热病', '07', 2, 1, '', '风温肺热病是由风热病邪犯肺，热壅肺气，肺失清肃所致，以发热，咳嗽，胸痛等为主要临床表现。相当于急性肺部炎性病变。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'fd2be8fe-8064-4c6a-9784-b965e581366c', '0704', '肺痈', '07', 2, 1, '', '肺痈是由风热邪毒蕴滞于肺，热壅血瘀，血腐化脓而成。以发热，胸痛，咳吐腥臭脓血痰为主要症状的肺化脓症。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'e0266cae-83ae-4168-9572-fbd03f9eaa62', '0705', '肺痨', '07', 2, 1, '', '肺痨系由感染“瘵虫”所致的肺部慢性消耗性传染性疾患。可见咳嗽、咯血、潮热、盗汗、消瘦等主症。相当于肺结核。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', '0706', '咯血', '07', 2, 1, '', '咯血是肺络受伤，血溢脉外，以咳嗽，咯血，或痰中带血等为主要表现。多见于支气管扩张。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '79f76a66-ee21-4ae9-b0e2-f612c16bc1ff', '0707', '悬饮', '07', 2, 1, '', '悬饮是指肺气不足，外邪乘虚侵袭，肺失宣通，胸络郁滞，气不布津，以致饮停胸胁，出现咳唾胸胁引痛，或见胁肋饱满。多见于渗出性胸膜炎。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '497f4299-c6fc-48ea-9420-ee4f5d47b841', '0708', '喘病', '07', 2, 1, '', '喘病是因久患肺系疾病或他脏病变影响，致肺气上逆，肃降无权，出现气短喘促，呼吸困难，甚则张口抬肩，不能平卧等症。多见于阻塞性肺气肿、肺原性心脏病、心肺功能不全等。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ac6cd916-545e-441e-a85a-d38b612e511d', '0709', '哮病', '07', 2, 1, '', '哮病系宿痰伏肺，因外邪、饮食、情志、劳倦等因素，致气滞痰阻，气道挛急、狭窄而发病。以发作性喉中哮鸣有声，呼吸困难，甚则喘息不得平卧为主要表现。相当于支气管哮喘、喘息性支气管炎。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'dc721271-afd0-4be7-b9c1-08ff2a847d95', '0710', '胃脘痛', '07', 2, 1, '', '胃脘痛系因胃气郁滞，气血不畅所致。临床以上腹部近心窝处经常发生疼痛为主症。多见于胃、十二指肠炎症、溃疡、痉挛等疾病。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '9b6fb7f8-5285-46cf-a39b-fb4aa9f76ca5', '0711', '噎膈', '07', 2, 1, '', '噎膈系因痰气交阻，或痰瘀阻滞，致胃失和降，出现饮食梗噎难下，或食入即吐为主要临床表现的病症。多见于食道、贲门部疾病。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'd162865b-92be-4d8d-a0a0-e1fb08b926a3', '0712', '呕吐', '07', 2, 1, '', '呕吐系因胃失和降，胃气上逆，而出现以胃内容物从口吐出为主要临床表现的病症。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '5835b53c-98b1-41d3-a851-5485ca48c0b3', '0713', '吐血', '07', 2, 1, '', '吐血系胃络受损，络伤血溢，出现血从口中呕吐而出的病症。指上消化道出血。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'd9daa152-8514-4d79-ab96-d36a45ec6ab5', '0714', '便血', '07', 2, 1, '', '便血系胃、肠络脉受损，出现血液随大便而下，或大便呈柏油样为主要临床表现的病症。可见于消化道出血。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'fdacd65f-4e13-4eff-8a49-3d49415fe8ce', '0715', '痢疾', '07', 2, 1, '', '痢疾系因感受湿热病毒，积滞肠腑，脂膜血络受伤，以腹痛、腹泻，里急后重，大便呈赤白粘冻或脓血为主要临床表现。相当于细菌性痢疾或肠阿米巴病。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6ea8328b-0849-4957-81bf-16b9ea0b6b54', '0716', '泄泻', '07', 2, 1, '', '泄泻系因感受外邪，或饮食内伤，致脾失健运，传导失司，以大便次数增多，质稀溏或如水样为主要表现的病症。相当于急、慢性肠炎或肠功能紊乱等疾病。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '83886669-4da0-48aa-9dde-c1169e25be3c', '0717', '黄疸', '07', 2, 1, '', '黄疸系感受湿热病邪，阻滞肝胆，气机受阻，疏泄失常，胆汁外溢所致。以目黄、身黄、溲黄为主要临床表现的病症。多见于肝胆系统疾病。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'bc25108c-8066-417d-bfac-30872064b1ab', '0718', '水臌', '07', 2, 1, '', '水臌系因肝脾受伤，疏运失常，气血交阻，致水气内停，出现腹满胀大为主要临床表现的病症。相当于肝硬化腹水。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'c8b12adb-edd9-418e-a943-b52fa6095876', '0719', '便秘', '07', 2, 1, '', '便秘系因气阴不足，或燥热内结，腑气不畅所致，以排便间隔时间延长，大便干结难解为主要临床表现的病症。常指习惯性便秘。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '7868745d-8e39-464d-ab60-caa4f0df62b2', '0720', '紫癜', '07', 2, 1, '', '紫癜是由血络受伤，血渗于肌肤之间，皮肤表现点状或片状青紫斑块的病症。相当于原发性血小板减少或血管性、过敏性紫癜。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '54db04ee-9f22-4375-bd65-9e62353edaa3', '0721', '湿温', '07', 2, 1, '', '湿温是由湿热病邪，阻遏脾胃，壅滞肠道，致身热不扬，头重身困，胸痞腹胀，苔腻脉缓等为主症的肠道传染病。多见于伤寒、副伤寒。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'b4810674-76ab-4ead-8567-e7bf9ad76ddc', '0722', '霍乱', '07', 2, 1, '', '霍乱系由饮食不洁，感受疫毒，致突然剧烈泄泻，继则呕吐，呈挥霍撩乱之势的烈性传染病。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '47543a10-4b00-4745-953e-5ed307906d68', '0723', '中暑', '07', 2, 1, '', '中暑是夏季在烈日或高温环境下劳动，因暑热侵袭，致邪热内郁，体温调节功能失常，所发生的急性病变。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4815e524-7678-4b6a-be28-f7cd71c066bd', '0724', '疟疾', '07', 2, 1, '', '疟疾是由感受疟邪所致的传染病。以寒战壮热，汗后热退，休作有时为主症。据休作时间分每日疟、间日疟、三阴疟；据证候分温疟、瘅疟、牝疟、疫疟等。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '8219b66f-4567-4647-a700-704b45439f28', '0725', '血吸虫病', '07', 2, 1, '', '血吸虫病是因日本裂殖体血吸虫寄生在人体门静脉系统所引起的地方性疾病。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '7c9d8faa-5bf6-43af-9ae3-7c424f053d7c', '0726', '绦虫病、囊虫病', '07', 2, 1, '', '绦虫病是由猪、牛绦虫寄生于人体小肠所致的疾病。囊虫病是由绦虫囊尾蚴寄生于人体各部组织引起的疾病。二者均属肠寄生虫病。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4c1dee7e-6020-4356-b270-3c5ab090d9fe', '0727', '钩虫病', '07', 2, 1, '', '由于钩虫寄生于人体肠道所致的寄生虫病。别名“桑叶黄”、“黄胖病”、“懒黄病”。', '2008/04/10 03:43:35', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6c7021d3-ecca-4be4-934a-c44bdc790036', '08', '中医外科', '-1', 1, 1, '', '', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '91884d9e-aaa2-4434-98e7-21ff5faaf66e', '0801', '疖', '08', 2, 1, '', '疖是指肌肤浅表部位感受火毒，致局部红肿热痛为主要表现的急性化脓性疾病。包括有头疖、无头疖、蝼蛄疖、疖病。相当于疖、皮肤脓肿、头皮穿凿性脓肿及疖病。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '47002e12-49d8-4984-9697-bf81a3d4e3de', '0802', '痰毒', '08', 2, 1, '', '痰毒是感受风热湿毒，气血被毒邪壅塞于皮肉之间，继而炼液成痰，痰毒互阻，结块而肿的急性化脓性疾病。本病包括颈痈、腋痈、胯腹痈。相当于急性化脓性淋巴结炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'a88cc32f-cb01-4027-8150-ed12a2e479a3', '0803', '有头疽', '08', 2, 1, '', '有头疽是因外感风热、湿热、火毒之邪，气血瘀滞，结聚于肌肤间的急性化脓性疾病。以局部红肿热痛，有多个脓栓堆积为特征。多发于中老年尤其以消渴病多见，易出现“陷证”。相当于痈。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '46759b5b-96b9-4a30-9ecb-b0f982f0ee5c', '0804', '颜面疔疮', '08', 2, 1, '', '颜面疔疮是感受火热之毒，或因昆虫咬伤，皮肤破损染毒，蕴蒸肌肤，气血凝滞而发生的病变迅速、危险性较大的急性化脓性疾病。相当于颜面部疖和痈。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f21ec997-aa76-4744-8d90-97c362d003af', '0805', '手部疔疮', '08', 2, 1, '', '手部疗疮是因外伤后感染毒邪，火毒阻于皮肉，患处出现红肿剧烈疼痛，容易损骨的急性化脓性疾病。包括甲沟炎、化脓性指头炎、手指化脓性健鞘炎、掌中间隙感染等。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '81fdb486-7c56-426d-b90a-6ac673829446', '0806', '红丝疔', '08', 2, 1, '', '红丝疔多因手足皮肤损伤，感染邪热火毒，走注经络，呈红丝显露，迅速上攻手膊或小腿的急性感染性疾病。邪毒重者可内攻脏腑，发生“走黄”。本病指急性淋巴管炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'c1b52342-59a6-4171-ba7e-2790fa286794', '0807', '烂疔', '08', 2, 1, '', '烂疔多由皮肉破损，接触污泥、脏物，感染毒邪，以致毒聚肌肤，皮肉迅速腐烂，流出臭秽污血的危急重症。毒邪走散可并发“走黄”。相当于气性坏疽。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '69ad1f1f-1be7-411f-91c2-1494bff5b93b', '0808', '发颐', '08', 2, 1, '', '发颐多由外感或手术后，汗出不畅，余邪热毒未能外达，结聚于颐颌之间的急性化脓性疾病。以颐颌部肿胀疼痛，张口受限，伴高热为特征。相当于急性化脓性腮腺炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'd1defda5-27db-4535-b374-dd10662aadda', '0809', '丹毒', '08', 2, 1, '', '丹毒多先由皮肤、粘膜破损，外受火毒与血热搏结，蕴阻肌肤，不得外泄，致患部鲜红灼热，有如涂丹为特征的急性感染疾病。生于下肢者称“流火”；生于头面的称“抱头火丹”；新生儿多生于臀部，称“赤游丹”。相当于急性网状淋巴管炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', '0810', '流注', '08', 2, 1, '', '流注是因感染邪毒，流窜血络，阻于膜理肌肉之间，出现一处或数处漫肿、微热疼痛的脓肿。有暑湿流注，湿痰流注，余毒流注，瘀血流注，骼窝流注之分。相当于脓血症、肌肉深部脓肿和骼窝脓肿。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4f952459-5dc6-48bc-9b12-dec0d53c5295', '0811', '瘿瘤', '08', 2, 1, '', '瘿瘤是内有郁火，外感风热邪毒，结于瘿囊而成。证见喉结两旁结块肿硬疼痛。相当于急性甲状腺炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '130c5f08-714f-4cb5-96cf-f52fdb21daa9', '0812', '肉瘿', '08', 2, 1, '', '肉瘿多因情志内伤，痰浊凝结，聚而成块，发于结喉两旁，随吞咽而上下移动。相当于甲状腺腺瘤。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '3f80a5bc-36cd-43d7-a1dc-7780dbec4fd1', '0813', '瘰疬', '08', 2, 1, '', '瘰疬因肝气郁结，气郁化火，灼津为痰，结于颈项而发病。以结核累累成串，溃后脓出清稀，疮口经久不愈为特征。相当于颈部淋巴结结核。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ffdd213d-59bf-4677-9b95-9fc12354b47b', '0814', '乳痈', '08', 2, 1, '', '乳痈多因乳头破碎，风邪外袭，或乳汁淤积，乳络阻滞，郁久化热而成。以乳房部结块肿胀疼痛，溃后脓出稠厚为特征。相当于急性乳房炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '2f7c7b5f-7e7d-46fb-a677-a1d8787901fb', '0815', '乳痨', '08', 2, 1, '', '乳痨多由素体肺肾阴虚，或先患肺痨、瘰疬而后继发乳痨。乳房结块不痛，数月后肿块化脓，脓出稀薄，疮口不易收敛，又名乳痰。相当于乳房结核。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '2fcc4ba9-58f7-46f3-a976-95763a11c476', '0816', '乳疠', '08', 2, 1, '', '乳疠多因肝肾不足，痰瘀凝结而成。乳房结块发生在乳晕部一侧或二侧，呈半球形，触之疼痛。相当于乳房异常发育症。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4df24a9c-9a2a-4157-ae47-1f483416816b', '0817', '乳岩', '08', 2, 1, '', '乳岩由情志内伤，冲任失调，气滞痰瘀互结而成。乳房部结块，质地坚硬，高低不平，病久肿块溃烂，脓血污秽恶臭，疼痛日增，后期可病灶转移。相当于乳癌。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'bd1985d7-eaab-41bb-945e-40041c0b5e09', '0818', '乳癖', '08', 2, 1, '', '乳癖是由情志内伤，冲任失调，痰瘀凝结而成。乳房肿块和疼痛与月经周期相关。相当于乳腺增生病。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f3e5f2d9-31be-408c-ad93-151f022f679f', '0819', '粉刺性乳痛', '08', 2, 1, '', '粉刺性乳痈由肝郁化热，肉腐化脓而成。多有先天性乳头凹陷畸形，乳头常有粉渣样物排出。乳晕或乳房部出现肿块，溃后形成瘘管，与乳头相通，经久反复不愈。相当于浆细胞性乳腺炎或导管扩张症。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '06da98e8-b46f-416b-bf1c-d67b2b30d5eb', '0820', '乳衄', '08', 2, 1, '', '乳衄是因肝脾两伤，肝经火郁、脾失统血而发。证见乳窍溢出血性液体，在乳头或乳晕部可摸及肿块。相当于乳腺导管乳头状瘤及乳腺导管乳头状癌。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '74cb80ea-1bff-4bcc-aeba-841110d6a14d', '0821', '乳核', '08', 2, 1, '', '乳核由冲任失调，情志所伤，血瘀痰凝互结于乳房而成。肿块表面光滑，质地坚硬，边界清楚，推之活动不痛，与月经周期无关。相当于乳腺纤维腺瘤。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'e29108bf-0c6c-4a67-b5f3-37424cb70411', '0822', '肠痈', '08', 2, 1, '', '肠痈是因饮食不节，湿热内阻，致败血浊气壅遏于阑门而成。以转移性右下腹痛为本病主要症状。相当于急性阑尾炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '98d1c7df-baef-4b8c-aef2-57a457e13b1e', '0823', '水疝', '08', 2, 1, '', '水疝是由先天肾气不足，脾失健运及外伤等，使水湿内停于肾子（睾丸）及子系（精索）所致。以阴囊下垂肿大或子系处有痰包为特征。相当于睾丸鞘膜积液及精索囊肿。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '5a4dc62d-052f-4cf0-973c-36ad3e4648c9', '0824', '子痈', '08', 2, 1, '', '子痈是由湿热下注厥阴之络，致气血凝滞而成。以阴囊胀痛下垂，睾丸肿胀疼痛为特征。相当于急性附睾炎，慢性附睾炎与急性睾丸炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '03fb439b-d2a9-44c7-9e3a-8c19fe3b7aab', '0825', '子痰', '08', 2, 1, '', '子痰系由肝肾阴亏，痰湿乘虚入络，凝于肾子而生。以附睾肿大，硬结触痛，溃后流脓清稀，形成瘘管为特征。相当于附睾结核。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '32abd504-6f65-4530-89d7-af814f529ded', '0826', '精癃', '08', 2, 1, '', '精癃是由肾元亏虚等多种原因，导致精室肿大，膀胱气化失司，以排尿困难和尿潴留为主要临床表现的疾病。相当于前列腺肥大、增生症。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '7934a3b3-43ff-4be4-9b63-b80f5789c3ae', '0827', '精浊', '08', 2, 1, '', '精浊是由肾虚或湿热瘀滞等病邪，下注于精室，导致会阴坠胀疼痛，尿液常有精液溢出为主要临床表现的疾病。相当于前列腺炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '1bc5a4af-6342-431f-8b7d-00876f54693d', '0828', '臁疮', '08', 2, 1, '', '臁疮多由久站或过度负重，而致小腿筋脉横解，青筋显露，瘀停脉络，久而化热，或小腿皮肤破损染毒，湿热下注而成，疮口经久不愈。相当于下肢慢性溃疡。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '5ff2f19d-720d-4331-8870-8a76088b9c1c', '0829', '褥疮', '08', 2, 1, '', '褥疮又名“席疮”。多因久病卧床，气血运行失畅，肌肤失养，每因摩擦皮破，染毒而成。多发于尾骶、肘踝、背脊等容易受压部位，以皮肤破溃，疮口经久不愈为特征。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '33c518c4-703f-4052-8ebf-bdb9ae3e6d79', '0830', '附骨疽', '08', 2, 1, '', '附骨疽是因毒邪深袭，阻于筋骨，经络壅塞，气血凝滞所成。相当于急、慢性化脓性骨髓炎。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4c9d8db8-1197-4c6c-9587-531ba0aed57e', '0831', '流痰', '08', 2, 1, '', '流痰是因先天肝肾不足，后天失调，骨骼空虚，风寒湿痰之邪，乘隙而入，流注于筋骨关节而成。本病以脓肿旁流和溃后脓液中伴败絮状痰样物为特征。相当于骨与关节结核。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'c947406e-6e96-44b9-bc7b-3205d076ca8c', '0832', '脱疽', '08', 2, 1, '', '脱疽是因先天不足，正气衰弱，寒湿之邪侵袭，致瘀阻脉络，气血不畅，甚或痹阻不通而发病。初起肢冷麻木，后期趾节坏死脱落，黑腐溃烂，疮口经久不愈。相当于血栓闭塞性脉管炎和闭塞性动脉硬化症。', '2008/04/10 08:17:13', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '33c518c4-703f-4052-8ebf-bdb9ae3e6d80', '082401', '急性子痈', '0824', 3, 1, ' ', ' ', '2008/04/10 08:17:14', ' ', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '33c518c4-703f-4052-8ebf-bdb9ae3e6d81', '082402', '慢性子痈', '0824', 3, 1, ' ', ' ', '2008/04/10 08:17:14', ' ', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'eefff949-7bca-451d-8354-730d8e08c505', '09', '中医眼科', '-1', 1, 1, '', '', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '892a6cc0-313f-4118-81ae-e090b8353c17', '0901', '针眼', '09', 2, 1, '', '针眼是因感受外邪，胞睑边缘生小硬结，红肿疼痛，形如麦粒的眼病。相当于麦粒肿。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'ea9a7039-1070-4a80-a378-b49dd4bba0a4', '0902', '胞生痰核', '09', 2, 1, '', '胞生痰核是因痰湿凝集胞睑皮下生核状硬结，不红不痛，皮色如常，推之能移的眼病。相当于霰粒肿。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '21cf4e75-14b9-43fc-a20a-50dec18034da', '0903', '睑弦赤烂', '09', 2, 1, '', '睑弦赤烂是由风湿热邪蕴结引起，以胞睑边缘赤肿溃烂，刺痒灼痛为主要表现的眼病。相当于睑缘炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '98763218-44e3-4b13-9ac3-5a169195684b', '0904', '眼丹', '09', 2, 1, '', '眼丹是因热毒内蕴，引起胞睑红肿高起，质硬，边界清楚，皮色光滑，鲜红如徐丹砂的眼病。相当于眼睑丹毒。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '29383d83-84c4-4711-a0c0-f97190cf6cbf', '0905', '风赤疮痍', '09', 2, 1, '', '风赤疮痍是因脾胃湿热，复感风邪，客於胞睑，以致胞睑皮肤红肿疼痛，起丘疹，小泡，继则引起脓疱，溃烂的眼病。相当于某些眼睑皮肤炎症。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '31952cfa-f7ad-40b9-b74e-776b49d404e3', '0906', '沙眼', '09', 2, 1, '', '沙眼是由沙眼衣原体感染引起的传染性结膜角膜炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '312748ce-f6c8-417e-911e-0681f4eb32ed', '0907', '目刽', '09', 2, 1, '', '目刽是风邪侵目或精血不足，目失濡养引起的以胞睑频频眨动，不能自主控制的状态，常见于沙眼、浅层点状角膜炎、角膜软化症等。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4ceee186-4179-4c18-9416-014d5542a7b3', '0908', '胞肿如桃', '09', 2, 1, '', '胞肿如桃是因实热雍盛，脉络阻滞，以胞睑高度红肿。垂肿如桃，疼痛等为主要表现的眼病。相当于重度眼睑炎性水肿。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'db617603-dc0b-4c00-bae9-b7ef5205f2a3', '0909', '胞虚如球', '09', 2, 1, '', '胞虚如球是因脾虚气弱，水湿上泛，以胞睑肿胀，皮色正常，虚软如球，按之不痛为主要症状的眼病。相当于眼睑非炎性水肿。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'd6508d74-d165-4a0b-bff2-103009c6bb09', '0910', '上胞下垂', '09', 2, 1, '', '上胞下垂是因气虚不能上提，血虚不能养筋，或风邪中络而致，以上睑提举无力或不能抬起以致睑裂变窄，甚至遮盖部分或全部瞳神，影响视力的一种眼病。即上睑下垂。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '62fe2cef-99fc-4ed1-89eb-f5dfdd4cef14', '0911', '胞轮振跳', '09', 2, 1, '', '胞轮振跳是因气血不和致眼睑不自主牵拽跳动的疾病。即眼睑轮匝肌痉挛。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '4c697fcc-f600-491a-b8aa-b5a1a2970831', '0912', '冷泪症', '09', 2, 1, '', '冷泪症是因肝虚泪窍约束无力，或风邪引起泪液频频外溢，但无热感目无赤痛的眼病。又分“无时冷泪”，“迎风冷泪”。相当于泪道不通或不畅，及泪囊功能不全引起的溢泪。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'fcd6e54e-d8d3-47be-a8da-be359bcb4f5d', '0913', '漏睛疮', '09', 2, 1, '', '漏睛疮是因热毒蕴结内眦部近睛明穴下方，突发红肿高起，继而破溃出脓的一种眼病。即急性泪囊炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '369228ec-2111-405c-85c0-ba0eec0a4fa5', '0914', '暴风客热', '09', 2, 1, '', '暴风客热是因风热之邪突然侵目，白睛粹然红赤，生眵流泪的眼病。相当于某些急性卡他性结膜炎和过敏性结膜炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'dd2c564f-72cc-4b3f-bac5-1294f2189a48', '0915', '天行赤眼', '09', 2, 1, '', '天行赤眼是因外染天行疫疠之气，白睛红赤，相互传染易引起流行的眼病。相当于流行性角膜结膜炎，流行性出血性结膜炎等。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '929c5145-f8d0-4149-bd40-dce1eaba167c', '0916', '时复症', '09', 2, 1, '', '时复症是因时邪与湿热交阻，发病时白睛红赤，奇痒难忍，每年至期而发，过期乃愈，呈周期性反复发作的眼病。相当于春季卡他性结膜炎等病。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '149d25ba-5d32-44e5-8680-89bf2800f409', '0917', '神水将枯', '09', 2, 1, '', '神水将枯是气郁化火，津液亏损，泪液减少以致目珠干燥失泽的眼病，相当于泪腺分泌降低引起的结膜干燥症。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'be0a474a-416b-497f-9dd5-d1fedf5a4944', '0918', '金疳', '09', 2, 1, '', '金疳是因燥热伤阴，肺失肃降，白睛表层生圆形小泡如玉粒，周围绕以赤脉的眼病。相当于泡性结膜炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '9dabaf71-6284-40aa-ac0e-38eab8646f61', '0919', '火疳', '09', 2, 1, '', '火疳是因火邪郁结，白睛深层有结节隆起，色紫红，疼痛拒按的眼病。相当于前巩膜炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '14d06bc4-ecf7-4611-a870-68970cf4ae25', '0920', '聚星障', '09', 2, 1, '', '聚星障是因外感风邪，挟热化火，致黑睛上生细小星翳伴有涩疼、畏光、流泪的眼病。相当于单疱病毒性角膜炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '2c8bcd98-4f64-4a57-82fa-b6f7cc7ece10', '0921', '凝脂翳', '09', 2, 1, '', '凝脂翳是由风热邪毒入侵，致风轮黑睛生翳，表面色白或黄，状如凝脂，发病迅速，或伴黄液上冲的眼病。相当于匐行性角膜溃疡和绿脓杆菌性角膜溃疡。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '99711dac-0e1c-4368-bd01-c9c6d3fda7a8', '0922', '花翳白陷', '09', 2, 1, '', '花翳白陷是因肝经结热，或感外邪，致黑睛生翳、混浊灰暗，四周高起，中央低陷，形如花瓣的眼病。相当于某些角膜溃疡。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f3d039ff-e70f-44ef-8544-e43ba4e89b84', '0923', '混睛障', '09', 2, 1, '', '混睛障是因邪伏风轮，气血瘀滞，黑睛深层漫珠一片翳障混浊，以致黑睛呈镜面哈气状的眼病。相当于基质性角膜炎等病。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'efc2c926-83b2-4fc1-9d4a-46f1ea93c79e', '0924', '风轮赤豆', '09', 2, 1, '', '风轮赤豆是因脾虚挟痰，肝经积热，黑睛生翳，有赤脉从白睛追随环绕状如赤豆的眼病。相当于束状角膜炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '13d310f2-1fa7-4cae-954c-1d6b3dbc4783', '0925', '疳积上目', '09', 2, 1, '', '疳积上目是由小儿疳积引起的，初为于涩、夜盲，日久黑睛生翳，甚则溃破穿孔的眼病。相当于维生素A缺乏引起的角膜软化症。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'c042c8e6-50dd-406e-988e-6c30dfad7e0f', '0926', '血翳包睛', '09', 2, 1, '', '血翳包睛是由风热壅盛，气血瘀滞，以致赤脉从黑睛四周侵入，密集形成翳膜，漫散黑睛的一种眼病。相当于全角膜血管翳。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '04d5aa76-ca6f-4ac5-831e-6bf5f3d2ea59', '0927', '宿翳', '09', 2, 1, '', '宿翳是在黑睛疾患痊愈后，结成搬痕，表面光滑，边缘清楚，无红赤疼痛的眼病。古代根据瘢痕的厚薄、形状，有"冰暇翳"、"云翳"、"厚翳"、"斑脂翳"等命名。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '6bc75893-48a0-4f2c-ae0c-503aacb9615f', '0928', '瞳回神紧小', '09', 2, 1, '', '瞳神紧小是因邪热灼伤黄仁以致瞳神缩小，展缩失灵，伴抱轮红赤，黑睛后壁有沉着物，神水混浊，视力下降的眼病。相当于前部色素膜炎。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '7904528d-72e3-4dd9-82f7-735bc02d1588', '0929', '黄液上冲', '09', 2, 1, '', '黄液上冲是因黄仁为邪热所伤，灼津为脓、黑睛与黄仁之间，积有黄色脓液的眼病。即前房积脓。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '21b6439b-b931-4549-bf41-14308f2eb3da', '0930', '血灌瞳神', '09', 2, 1, '', '血灌瞳神是因目内之血不循经而行，溢于瞳神前后的眼病。即前房积血或玻璃体积血。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'afcea8f7-c9eb-4b5f-8aba-ce286e13968f', '0931', '绿风内障', '09', 2, 1, '', '绿风内障是因暴怒忿郁，气郁化火，风火痰浊上攻，或脾胃虚寒，饮邪上泛，致黑睛气色混浊，瞳神散大不清呈隐隐绿色，伴有头痛眼胀，恶心呕吐，视力急剧下降的眼病。相当于闭角型青光眼急性发作期。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'a5843a38-f85d-4cb0-8d7a-8a4aad937501', '0932', '圆翳内障', '09', 2, 1, '', '圆翳内障是因高年体弱，精气日衰，目失涵养所致晶珠混浊，视力渐降，最终瞳神内呈圆形银白色翳障，视力障碍的眼病。相当于老年性白内障。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '52afacdb-1061-40ce-b1f5-c03a6c0bae53', '0933', '云雾移睛', '09', 2, 1, '', '云雾移睛是因神膏为邪所乘，混浊不清所致，眼外观端好，自觉眼前有蚊蝇或云雾样黑影飞舞飘移，甚者视物昏蒙的眼病。即玻璃体混浊。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '40dc233d-1c6d-450d-8c12-6afcc3bc0388', '0934', '视瞻昏渺', '09', 2, 1, '', '视瞻昏渺是因气血失调，精气不能上荣於目所致，自觉视力下降，视物昏蒙而外眼无异的眼病。相当于慢性球后视神经炎、中心性浆液性脉络膜视网膜病变等。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '0315f6db-af0d-4d96-9d79-51b7d1c27cc2', '0935', '视直如曲', '09', 2, 1, '', '视直如曲是因视衣津液输布失调或血溢络外，导致患眼视正直之物如弯曲之状的眼病。相当于中心性浆液性脉络膜视网膜病变等黄斑部疾病。', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( '3dedca7d-a754-4fc5-8911-6fcf6e57c271', '0936', '暴盲', '09', 2, 1, '', '暴盲是由视衣目系脉络阻滞，气机郁闭，导致神光离散，而出现视力急骤下降失明的内障眼病。多见于西医之许多急性视力障碍眼底病，主要有视网膜中央动脉阻塞，眼内出血和急性视神经炎等，兹分述如下：', '2008/04/10 08:34:50', '', 3 ) ;
INSERT INTO chinese_disease ( CDISid, code, name, parentcode, level, isClassical, SPETid, illustration, createDay, optrid, state )  VALUES ( 'f3c99ece-2f68-4141-94c2-2d62f23beb6a', '093601', '视网膜中央动脉阻塞', '0936', 3, 1, '', ' ', '2008/04/10 08:34:50', '', 3 ) ;
