INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('ab6ad09f-1faf-4625-8df1-5163ee5db8c5','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','fd513872-a870-45eb-9b86-3c5ed20b157d','bl201001070004','','1','李某','42','0','1','0','0','','','','','','','0','0','','','','','乳腺增生病','每遇精神紧张时则加剧。就诊时，双乳胀痛，胃纳不馨，夜寐不香，动则汗出，大便干结。B超提示：双乳腺增生病。查体：两乳散在数十枚小结块，质地中等，有压痛，舌红苔薄，脉弦细。','1','并督促患者注意休息，按时服药，定期复查，保持情绪稳定，精神舒畅。');
