INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','乳腺增生病中医称“乳癖”，明代陈实功认为“多由思虑伤脾，恼怒伤肝，郁结而成也。”肝主疏泄，肝气宜舒畅而条达，若情志不遂，忧郁不解，则可导致肝气郁结，气机瘀滞；乳络经脉阻塞不通，不通则痛而引起乳房疼痛，肝气郁久化热，热灼阴液，气滞血凝而形成乳房结块。故以柴胡，香附，郁金，八月扎疏肝理气，元胡，合欢皮，川楝子理气止痛。白术，赤白芍，茯苓，莪术健脾化痰，活血消块。炒枣仁，柏子仁，生地，石斛，远志肉清心养阴除烦。该患者正值更年期，工作压力大，加之病程较长，心情烦躁忧虑，病情时有反复。故加以百合知母汤，甘麦大枣汤以助调心，同时向其分析病情，介绍成功病例，稳定情绪，帮助树立与病情作斗争的信心。心情舒畅，治疗亦取得了成功。','3','2010-1-7 20:59:28','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
