INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('长期吸烟史，平均25支/天，不嗜酒，正常婚育。','无特殊家族遗传倾向性疾病','青霉素过敏史,否认其它药物及食物过敏史。','慢性阻塞性肺疾病','患者有长期吸烟史，平均25支/天。近30年来反复咳嗽咯痰，曾于外院肺功能检查示混合性通气障碍。此次因受凉后3天前出现咳嗽加重，咳甚出汗，咯痰色白量多呈泡沫样，咯之不尽，胸闷气促，无发热恶寒，无胸痛咯血，自服吉诺通，头孢克洛等药物后无明显缓解。','4','偶有咳嗽少痰，精神振，胃纳可。','慢性阻塞性肺病急性感染见大量咯痰，临床常规治疗往往以抗菌药物为主，有些患者反复继发感染，反复运用抗生素，容易造成脾胃功能减退，且患者年老加久病之后，脾胃机能虚弱，两种因素叠加更易导致脾胃运化失司，津液停聚而生痰饮。邵老指出“脾为生痰之源，肺为储痰之器”，治疗当从其根本，痰湿阻肺仅用一般清肺化痰药物疗效平平，而佐以健脾化湿法常可获效，培补脾土以健脾祛湿化痰，排痰化湿需要一个过程，用药宜渐消缓化，如用猛剂急攻则痰未消而正气伤。邵师常用二陈汤为底方加减来健脾。','3','2008-10-22 21:08:59','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('7082db30-e1f0-4f40-b223-b40bb759e082','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200810220003','2009030401','2','易信菁','52','1','1','0','0','','','','','','','0','1','','未婚未育，否认服用含性激素保健品。','否认乳腺癌家族史。','','中医：乳岩（气阴两虚，冲任失调，心神不宁）');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('西医：乳腺癌术后','患者于2003年6月5日在肿瘤医院作右乳癌改良根治术，病理：浸润性导管癌II级，ER（＋），PR少（＋），neu（±），ALN0/9(-)，术后CMF×4次。左乳胀痛伴有结块，已停经。自觉神疲乏力，口干欲饮，胃脘胀痛，食欲不振，嗳气，夜寐不易入睡，睡则易醒。舌质淡红，苔薄，脉细。','4','','','3','2008-10-22 20:46:31','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('d9cf880e-8203-42da-8d46-03d7542e0595','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200810230001','08102301','1','张珍聪','','68','1','1','0','0','','退休','','','','','0','0','患者既往有脑梗史，有血压偏高史，血压最高148/90mmHg，有肺结核病史。否认糖尿病、冠心病等慢性病史，否认肝炎等传染病史，否认外伤手术史，否认输血史。','出生、生长在上海，无疫水疫区接触史，预防接种史不详；无烟酒等不良嗜好，正常婚育。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','无特殊家族遗传倾向性疾病','无','慢性阻塞性肺疾病','患者近10年来反复咳嗽咯痰气急，多次住院治疗。1月前因受凉后出现发热，最高达39.2℃，咳嗽少痰，胸闷痛，咽痒，稍有气促，口服头孢呋辛、喘定、阿斯美等药物后缓解。现有低热烦躁，咽痒咳嗽少痰，痰色白质粘，气急，四肢冷，汗出较多，夜寐多梦难安，二便尚调。舌红苔薄黄，脉细。','2','诸症缓解','病后体虚，表虚受风，以致营卫不和，腠理不密，邵老以桂枝温经解肌，白芍药和营敛阴，两药合用，一收一散，调和营卫。在理气止咳的基础上兼以益气、固表、养心，标本同治。三诊、四诊又以仙灵脾、女贞子、杜仲等补肾固本，以达扶正之效。','3','2008-10-23 20:12:17','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('15f8cba3-f231-4943-be9c-7d6d4112e17b','7eb3964c-9143-48a9-a450-a40d6b8d80b1','170cccdc-d920-4cc3-906b-dd6816a7b8cb','bl200903090014','sq20090309001','1','陆震崖','55','0','1','0','0','','','','','','','0','0','','','','','','强直性脊柱炎已有2年余，腰脊反复疼痛，晨僵，两膝关节反复肿胀，四肢关节游走窜痛，右足跟疼痛，局部无肿胀，叩击痛阳性，胃纳二便尚可，苔薄，脉细沉。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('体格检查：腰椎活动范围减少，前屈50度，后伸10度。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('X线片示：腰椎曲度变直，腰椎关节韧带骨化明显。CT显示骶髂关节边缘硬化，间隙尚可。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','2','','强直性脊柱炎主要累及脊柱、中轴骨骼和四肢大关节，以腰痛为突出症状，病变以骨关节病变为主。其病在肾府，其损为肾所主之骨骼，其病位深、病程长，一般需要长时间的治疗，导师施杞认为，本病的病机关键是气虚血瘀，风湿痹阻，肝肾不足，治疗的重点是益气化瘀，祛风除湿，舒筋通络，补益肝肾。全方采用黄芪、党参、白术健脾益气，赤芍、三棱、莪术活血化瘀，鸡血藤、老鹳草、青风藤祛风除湿、舒筋通络，仙灵脾、益智仁、台乌药、制川乌等补益肾阳，散寒止痛，当归、白芍养血柔肝，取治风先治血，血行风自灭之意，芍药与甘草酸甘化阴，缓急止痛，从而达到标本兼治的目的，由于本病病程漫长，因此要求患者坚持治疗，经过一个多月的治疗，症状消失，但本病易发复发作，因此，复发时要及时治疗，以防病情发展，由于当时关节疼痛肿胀，屈伸困难，乃湿邪化热，急当清热解毒，祛风除湿，故用柴胡、子芩、金钱草、蒲公英等清热解毒，配以秦艽、羌活、独活、鸡血藤祛风除湿佐以益气补肾之剂，乃急则治标之法。通过本病的治疗，可以发现该病的缓解期，当以治本为主，以益气养血，补益肝肾为主，发作期主要表现为湿邪化热，急当清热解毒，祛风利湿。故对于这种需要长期治疗的患者，一方面要鼓动患者的积极性，使之能很好地配合治疗；另一方面更应当精心调治，将调与治有效地结合，方能取得好的疗效。');
