INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('797f5885-6366-4752-a722-7b5a9806a798','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902010002','031277','1','孙某','37','0','0','0','0','','','','','','','0','0','慢性乙肝史','','','','','“大三阳”十年余，肝功能反复异常。刻下肝区时有隐痛，恶心无呕吐，胃纳一般，失眠，大便日行二次。','2','随访3月肝功能基本正常，无腹水.','','肝硬化腹水，是由于邪毒内伏，肝郁血瘀，肝络痹阻，气化失司，水湿内停，三焦壅塞，致肝脾肾三脏俱损，属本虚标实之重病，逐水攻下法为治标的重要手段之一，但在治疗过程中，应注意不宜攻伐过猛，须遵循《素问&#8226;至真要大论》所言“衰其大半而止”的原则。','1','2009-2-1 16:05:32','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('cf3efcf5-22ba-4b08-a98e-c498cdfd9c4f','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902160001','25983','1','赵修山','33','0','0','0','0','','','','','','','0','0','乙肝史','','','','','患者88年患甲肝，89年12月体检为乙肝大三阳，肝功能正常，B超未异常提示。近半月来肝区隐痛，中上腹不适，胃纳尚可，倦怠乏力，口干，二便自调。舌微红，苔薄白，脉细弦','2','1991年4月8日随访，患者以上方稍出入，守服15个月，病情渐稳定。1991年3月28日复查乙肝两对半已全部转阴，肝功能正常，B超未提示异常。嘱上方加茅根30g，石斛10g，黄精30g，继服14贴以巩固疗效。','慢性乙型肝炎病毒属疫毒范畴，患者症状典型，以胁痛为主症，又伴有中上腹不适，为肝病及脾、木强克土之证，先生组方中，除采用丹参、郁金、杞子、石斛、生黄芪等扶正达邪外，又以佛手、甘松、八月札、制香附、延胡索、川楝子理气止痛，炙鸡金、越鞠丸以助脾运。乙肝病毒为热毒，再配以虎杖、败酱草清热解毒，又兼有防止谷丙转氨酶、总胆红素升高的作用。纵观全方，以扶正解毒、健脾理气为线索，体现中医辨证论治，以个体为主体的思辨特点。','1','2009-2-16 7:56:15','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('e1e23cda-ff4f-4a30-8342-f4c3e7246271','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200810230006','08102304','1','陈询','50','1','0','0','0','','工人','','','','','0','0','无殊','出生、生长在上海，无疫水疫区接触史，预防接种史不详；无烟酒等不良嗜好史，正常婚育。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','无特殊家族遗传倾向性疾病','诉有药物过敏史，鱼腥草、青霉素可能大.','慢性阻塞性肺疾病急性加重期','患者10年来反复咳嗽咯痰气急，4天前因天气变化受凉后出现发热，最高达39℃，自服安乃近后热退。现仍有咳嗽，痰黄质粘难咯，咽痛，活动后气喘，口干不欲饮，纳呆，二便尚调。舌红苔黄腻脉滑数。','2','诸症消失。','邵老治咳时非常重视治脾，顾护中土脾胃之气，处方中每每加入助胃健脾之品。脾胃乃后天之本，饮食入胃需要脾胃的运化升清，同样汤药入胃也需要脾胃输布。若脾胃受损或因虚不升，则方药再切证也无法取效。呼吸道疾病患者多有痰饮于内，治痰不理脾胃，非其治也。因脾乃生化之源、五脏之本，善治痰者，不治痰而先治脾，脾得健运，而痰自化矣，痰化则气道自然通畅。此是本病例邵师诊治精髓妙处之所在。','3','2008-10-23 21:32:56','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
