INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('818582b2-1e81-409a-a0f1-d46ada32cd4c','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200810290002','','2','权旭','40','10','1','0','0','','财务','13916348842','沪青平1481号B6－601','999999','310229','0','0','否认服用保健品。','已婚已育，双乳哺乳少于6个月。','否认乳腺家族史。','否认药物食物过敏史。','左乳腺癌术后','2007年8月6日在美国行左乳全切＋前哨淋巴结活检，病理：浸润性小叶癌，ALN（－），ER80％（＋），PR95%（＋），HER2（－），术后CTX＋EPi共4次，T×4次，并作左乳全切术＋双侧卵巢＋全子宫切除术，口服瑞宁得内分泌治疗。精神、食欲均可，夜寐易醒，醒后不易入睡，梦多，口干欲饮，关节酸痛。舌胖大，边有齿痕，舌质红，苔薄腻，脉细。','0','','','3','2008-10-29 21:20:08','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('78530e92-c808-4806-b433-68565f25a03c','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','58e95a10-2ba8-4420-bd84-aa822d45f7b6','bl200811210001','08111203','1','李钰','48','2','1','0','0','不详','工人','不详','不详','','','0','0','否认高血压、糖尿病等其他内科系统疾病史。否认外伤、手术、输血史，否认肝炎等传染病史。否认外伤手术史。','出生于上海，长期居住于上海；无疫区疫水接触史，否认吸烟史，预防接种情况不详。','无特殊家族遗传倾向性疾病','海鲜、花粉、螨虫过敏，否认其他药物食物过敏史','支气管哮喘','有哮喘病史十余年,','常因冷空气、刺激性气味接触后发作。1','月前因感冒后痰鸣咳喘又作,','痰多色白质粘,','排吐不利,','胸闷气急,','鼻塞流涕,','喷嚏频作,咽痒则咳喘作，夜间尤甚影响夜寐，纳食不香，舌淡红苔白腻，脉弦滑。','2','至今咳喘未再反复。','《内经》无哮证病名,','但似有哮证记载,','如《素问·阴阳别论》“······起则熏肺,','使人喘鸣。”《金匮·肺痿肺痈咳嗽上气病脉证并治》:','“咳而上气,','喉中水鸡声,','射干麻黄汤主之。”指出了哮证发作的特征和治疗。哮证之治,','后世多宗朱丹溪“未发以扶正为主,','既发以攻邪为急”之论,','以此为要则,各臻细密。邵师认为,','哮喘病屡发屡止的因素,','是由于宿痰内伏于肺,','复加外感、饮食、情志、劳倦等因素,','引动宿痰,','痰随气升,','气因痰阻,','相互抟结,','壅塞气道,','肺气宣降功能失常所致。在治疗上,强调治喘先治痰,','治痰宜调气,','方麻黄、杏仁、姜半夏、陈皮、茯苓、苏子、莱菔子、白芥子、甘草祛痰平喘,再合紫草、茜草、地肤子凉血祛风而抗过敏,','苍耳子、辛夷祛风通窍。全方切准哮喘急性发作期的病机本质,','使喘能平、痰能化,','迅速控制症状,','缓解患者的痛苦。','3','2008-11-21 20:18:24','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('8f682629-f116-4791-9080-60994ac798af','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl201001080001','1048309','2','陈仲达','68','10','0','0','0','不详','退休','无','江西','361101','','0','0','有慢支、慢阻肺病史，否认其他内科系统疾病史，否认外伤手术史，否认传染性疾病史，否认过敏史。','自幼生长江西，育一子。','无','无','慢支','近10年来，每逢气候变化则咳嗽咯痰，被诊断为慢性支气管炎、肺气肿、慢性阻塞性肺疾病。近8月来活动后气急，咯痰、痰色白量少，质粘，怕冷，胃纳一般，大便尚调，夜寐欠安。求中药调理','4','后续以三桑汤加减至今，患者咳喘基本缓解，急性加重次数较往年明显减少。','邵老认为，患者肺病日久及肾，肺、肾俱虚。肺为气之主，肾为气之根。咳喘之因，在肺为实，实则气逆；在肾为虚，虚不纳气，多因肾精不足，致肺、肾出纳失常。故咳喘治病主在乎肺，又关乎肾，其治不离肺肾。','3','2010-1-8 13:07:39','8a3b5ff6-bfa8-4c52-af8a-c3e5d6f1689d',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('6f07f135-1297-4c77-b60f-d09541cad241','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','58e95a10-2ba8-4420-bd84-aa822d45f7b6','bl200811110001','08111101','1','吴静芳','','','60','1','1','0','0','','退休','','','','','0','0','既往有鼻窦炎史，咽炎史。平素易感，否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。否认其他手术、外伤史。','出生于上海，长期居住于上海；无疫区疫水接触史，否认吸烟史，预防接种情况不详。','否认家族遗传倾向性疾病','青霉素过敏史,否认其它药物及食物过敏史。','慢性支气管炎','近3年来反复咳嗽咯痰，易感冒，感冒后咳嗽时间较长，咳嗽剧烈时并见气急。1周前受凉后出现咳嗽，咯痰色白质稀，稍气促，胸闷，畏寒，纳呆。舌淡红苔薄白腻脉弦细。自服阿莫西林等药物后无明显缓解。','2','诸症消失。','邵老在治疗肺系疾病时注重整体辨证，“局部疾病勿忘全身情况，要处理好局部和整体的辨证关系”。邵老综合“肺主皮毛”，“肺开窍于鼻”，“肺与咽喉相通”，“咽、喉、鼻是肺之门户”，提倡肺与鼻咽同治。本案患者素有鼻窦炎基础疾病史，临床出现咳痰喘的症状类似于西医变异性咳嗽（哮喘），虽无哮鸣音，但根据临床辩证应归为咳嗽','哮病范畴，邵师的川芎平喘汤是治疗咳嗽变异性哮喘的常用自拟方，再加上黄芩、辛夷、路路通等祛风抗过敏之品，应能取到祛风平喘的疗效。用药时注意宣通鼻窍与宣肺祛风配伍应用，是本病例精髓所在。','3','2008-11-11 16:11:00','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('a747c830-9022-4084-96ad-712ea0dd1823','cad88fd2-8757-43c2-b91f-0cab72c5fccf','6c313cac-6079-4036-a479-b5d5e64a3ffe','bl200811110002','94/32522','1','郎雪娣','43','0','1','0','0','不详','职员','不详','不详','310000','310000','0','0','既往有支气管哮喘病史。曾用博利康尼、喘定片、速克喘、酮替芬等，其中速克喘2月中用26#，静滴丁胺卡那、先锋6号20天，同时用中药20余贴，咳嗽减半，黄痰不能消失。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','无吸烟酗酒史，无疫水疫区接触史，生活作风良好。','无家族遗传病史。','无实物药物过敏史。','不详','咳＋＋，喉痒，痰＋＋＋，小，白>黄，易，胸闷，喘＋＋，哮＋＋，夜间坐起半小时2次，用气雾剂吸入后稍减，纳平，口干多饮，喜热，大便2－3日一行，干结，夜寐不安，苔淡黄腻，质暗红，脉细弦滑（98）。');
