INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('df760493-7ba5-4dec-a482-e685ad1854a7','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200810290003','','2','葛金花','50','8','1','0','0','','','','','','','0','0','','已婚已育。双乳哺乳<6月。','否认乳腺癌家族史。','','左乳腺癌术后','08年1月24日在瑞金医院行左乳癌改良根治术，病理：浸润性小叶癌，ALN：4/15(+),ER（＋），PR（＋），HER2基因扩增（＋＋＋），术前CEF×2次，术后泰素帝×2次，Herceptin一次。神疲乏力，不思饮食，夜寐不能入睡。右乳增生结节胀痛不适，舌质淡红，苔薄白，脉细。','0','','','3','2008-10-29 21:46:16','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('6257630d-d2e0-407f-b778-f1e95f0d3edc','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200811120001','05111201','1','莫东海','80','1','0','0','0','','退休','','','','','0','0','慢支，高血压，前列腺肥大史，余无殊。','出生于上海，长期居住于上海；无疫区疫水接触史，有吸烟史30多年，约1包/天，现已戒烟十年；预防接种情况不详。','否认家族遗传倾向性疾病','青霉素过敏史,否认其它药物及食物过敏史。','慢性阻塞性肺疾病急性加重期','患者有反复咳嗽咯痰史20余年，10年前出现气促，每年冬春两季易发，多次住院予抗感染解痉平喘治疗，有长期吸烟史50余年。近3天来因气候原因咳嗽时作，胸闷气急，动则尤甚，咯痰色白质稀，夜尿多，畏寒，自服必嗽平、诺尔彤无明显好转，无发热恶寒，无夜间不能平卧。舌红苔白腻脉滑。','4','症情缓解','“肺为气之主，肾为气之根”。肺之出气，肾之纳气，相辅相成。肺的宣肃功能正常，则吐浊吸清，呼吸调匀。肾主纳气，有助于肺气肃降。呼吸运动虽为肺所主，但需要肾纳气功能的协助，才能使肺吸入的清气下归于肾而为人体所用。呼吸要保持一定的深度，也有赖于肾的纳气功能。此患者肺失宣肃，气机不畅，津液输布失常，久病累及于肾，故见咳嗽咯痰，气促动则尤甚，此皆肺气亏虚，肾虚不纳，痰饮喘咳之为患也。其虚实挟杂，治疗上当肺肾同治，补肾纳气以扶正固本，化痰平喘以治肺，药证相投，疗效自著。三桑汤是邵师治疗慢性阻塞性肺疾病稳定期见咳痰喘症状而设的自拟基本方，此方药物组成涉及肺脾肾三脏，兼顾咳痰喘三症，对症施治。');
