INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('3a635cbc-e6ac-4f46-b6a0-f569296d2d99','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200811120002','08111201','1','钱嫄瑾','','77','1','1','0','0','','退休','','','','','0','0','无殊','出生于上海，长期居住于上海；无疫区疫水接触史，有吸烟史六十多年，现已戒烟四年；预防接种情况不详。','无特殊家族遗传倾向性疾病','否认药物食物过敏史','慢性阻塞性肺疾病急性发作期','患者有反复咳嗽咯痰史10余年，3年前出现气促，每年冬春两季易发，多次住院予抗感染解痉平喘治疗。1周前因受凉后出现咳嗽时作，胸闷气急，动则尤甚，咯痰色白质稀泡沫样，畏寒，口干不欲饮，下肢稍肿，纳寐可。舌红苔白腻脉弦滑。','2','症情平稳','本案患者为久患肺病后，由肺影响到心肾功能。痰饮内聚，津液不得上承，气机运行不利，因而出现气急。邵老临诊时常利水以化痰、健脾而平喘。运用利水之品，相当于西医运用利尿剂，可以减轻心脏负荷，降低肺动脉高压，从而使水肿，咳喘、胸闷等一系列临床症状得到缓解。健脾利水常用药：陈葫芦、猪苓、茯苓、薏苡仁、防己等品，其利水而不伤正，助脾运化而又能提高人体正气，可谓一举多得，现代药理也证明上述这些药物具有提高机体免疫的功能，这在肿瘤患者的中药组方中是比较常见的。如何选择利水之品，从此病例中可以看出邵师用药之精妙，不贪一时急功，驱邪之时兼顾病家一身正气根本的远见。','3','2008-11-12 17:41:22','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('94a087b4-9016-482f-9628-4ab41f752ebe','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','58e95a10-2ba8-4420-bd84-aa822d45f7b6','bl200811150001','08111501','1','徐惠芳','35','1','1','0','0','不详','教师','不详','不详','','','0','0','既往有过敏性鼻炎史。否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。否认手术、外伤史。','出生于上海，长期居住于上海；无疫区疫水接触史，否认烟酒等不良嗜好，预防接种情况不详。已婚，育有1子；配偶健在；均体健。','患者母亲、姐姐有哮喘病史，否认其他特殊家族遗传倾向性疾病。','有青霉素、头孢拉定药物过敏史。否认其他药物食物过敏史。','支气管哮喘','患者自幼有支气管哮喘病史，其母亲、姐姐均患该病。每于季节更替、劳累、紧张、异味刺激后发作。既往有过敏性鼻炎史。目前症情尚平稳，每天晨起鼻塞，易感冒，夜寐时稍有气急感，易疲劳，纳寐可，二便调，舌红苔薄白脉细。','2','诸症缓解。','遗传性支气管哮喘患者与先天禀赋有一定的关系，大多存在先天肾虚，肾为先天之本。《丹溪心法》认为：“未发以扶正气为主，既发以攻邪气为急”。本案患者，幼时患病，属禀赋不足，肺肾两虚，时发哮喘，内有“夙根”。故扶正以补肺益肾为主。辛芩汤祛风抗过敏治疗缓解哮病症状以为治标，玉屏风合补肾之品，补益肺肾，固表扶正以为治本。','2','2008-11-15 20:02:21','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('ac5a550b-274a-456b-b39e-69aa649bb0b5','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200811230001','08112301','1','王纪忠','76','2','0','0','0','不详','退休','不详','不详','','','0','0','既往有高血压、2型糖尿病、脑梗塞、慢性支气管炎史，详见现病史，有慢性萎缩性胃炎伴糜烂，应激性溃疡史，有心肌缺血史，否认其它慢性病史，否认肝炎、结核等传染病史，二十年前于普陀区人民医院行肾结石手术，有右腹股沟疝手术史，否认输血史。','出生于上海，长期居住于上海；无疫区疫水接触史，有长期吸烟史近二十年，每日20支，已戒多年。预防接种情况不详。','无特殊家族遗传倾向性疾病','青霉素过敏史，否认其他药物食物过敏史。','慢性阻塞性肺疾病','患者有多年吸烟史,','反复咳嗽,咯痰、气促20余年,','每逢天气变化时发作，一月前因受凉后，患者咳痰喘加重，咳嗽频作，夜间尤甚，','咯痰黄稠,','咯之不爽,活动后气促，双下肢不肿，纳寐一般，二便尚调，舌暗红苔黄腻，脉滑。','','2','半年咳嗽、咯痰未发。','本案邵师病机辨为痰热瘀血互结,,','方中炙麻黄宣肺平喘,','苏子、沉香降气平喘,三药合用使升降有序,','气机调畅。款冬花、黄芩、桑白皮清泻肺热以止咳平喘,','白前配前胡有润肺降气,','止咳化痰之效。又恐麻黄、苏子等耗气伤津,','予沙参、麦冬佐之,加以五味子又取生脉饮补益肺之气阴之意。辅以“性善破血,','散而不改,','泻而无补”的桃仁,','“降而行血”的丹参',',','以及活血通络的地龙。共奏活血行气,','清热化痰之功。');
