INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('b87866ea-cdfa-4048-92b5-23abff6cc66d','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200811230002','08112302','1','张露','68','0','1','0','11','不详','退休','不详','不详','','','0','0','否认其他内科慢性疾患；否认外伤、手术、输血史。否认肝炎伤寒结核等传染病史','出生于上海，长期居住于上海。无疫区疫水接触史。吸烟史五十余年，已戒烟20年。预防接种情况不详。','无特殊家族遗传倾向性疾病','有青霉素、头孢拉定药物过敏史。','copd','慢支史数十年，近日咳喘加剧伴见双下肢浮肿,经治疗1个月疗效不显著。刻下干咳少痰,甚则呛咳,大便干结,四五日一行。','2','病情无复发。','本案患者年高、久病、体弱,长期使用抗生素,菌群失调,抵抗力下降,非但咳嗽不愈,又添浮肿,是肺病及肾。辨证属肺肾不足,气阴两虚。治以润肺止咳、益肾化气。方中南沙参、北沙参、生地、麦冬、太子参益气养阴润肺;紫菀、冬花、百部开合肺气,润肺止咳;牛蒡子、鱼腥草清化热痰;山药、菟丝子、淫羊藿平补肾之阴阳;姜半夏、蒲公英升清降浊,补脾土而生肺金;茯苓渗湿健脾,白茅根凉血利尿而不伤阴。诸药合施,标本兼治,正复邪去,故疾病痊愈。','3','2008-11-23 21:56:41','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('fc587f90-437d-43e6-9246-d62a87dd6520','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','58e95a10-2ba8-4420-bd84-aa822d45f7b6','bl200811240001','08112401','1','贾方','54','2','0','0','0','','工人','','','','','0','0','有支气管哮喘史，详见现病史，否认高血压、糖尿病等其他内科系统疾病史。否认外伤、手术、输血史，否认肝炎等传染病史。','出生于上海，长期居住于上海；无疫区疫水接触史，有吸烟史30多年，约1包/天，现已戒烟十年；预防接种情况不详。','无特殊家族遗传倾向性疾病','否认药物食物过敏史。','支气管哮喘','患哮喘5年,','痰多气紧心累,','动则尤甚,','似觉有气上冲咽喉,','冬重夏轻,','精神萎靡,','面色晦暗。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','2','喘促缓解，余无不适。','本病例根据邵师运气津,','三脏同调,','分虚实,','标本兼治的学术思想指导下，患者接受治疗。方中组成以黄芪升举大气,','麻黄、桔梗、苏子、莱菔子、葶苈子、法夏等理气化痰,','补骨脂、北五味等纳肾平喘,','既散胸中结滞,','又疏通气道,','还能固敛浮气,','充分照顾到患者久病气虚,','痰阻气滞,','肾亏失摄等各方面具体情况,','是以取效甚捷。','3','2008-11-24 20:26:03','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('577b6daa-5422-41f5-a4fb-5c7fe42dc479','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200811240002','08112402','1','张恒','57','2','0','0','11','','工人','','','','','0','0','有慢性乙肝病史，一直服用中药；甲亢史10年，近来未服药，未复查。否认高血压、糖尿病等其他内科系统疾病史。否认外伤、手术、输血史，否认肝炎等传染病史。','出生于上海，长期居住于上海；无疫区疫水接触史，否认吸烟史，预防接种情况不详。','无特殊家族遗传倾向性疾病','青霉素过敏史，甘利欣、益肝灵过敏。','慢阻肺','反复发作性咳嗽10年余，咳嗽，咯痰量多，色白质稍稠，胸闷，喘促不能平卧，影响睡眠，时有汗出，唇绀。','4','无咳嗽，咯痰量少，喘息好转，精神振，胃纳好。','COPD的主要症状就是咳嗽与喘促，因此方中以杏仁、葶苈子为君药。杏仁味苦性微温，止咳平喘，','《本经》谓其：','“主咳逆上气雷鸣，喉痹。”','《本草便读》说：“功专降气，气降则痰消嗽止。”现代药理研究其有抑制咳嗽中枢而起镇咳平喘作用；葶苈子昧辛苦性大寒，泻肺平喘，利水消肿，《开宝本草》谓其“疗肺壅上气咳嗽，定喘促，除胸中痰饮。”','因此，葶苈子不但可以下气平喘，还能够泻膈上水气，现代药理研究证实其有增强心肌收缩力的作用。邵老师以两者共同缓解咳嗽、喘促等主要症状。。白芥子、莱菔子化痰利气，瓜蒌清热化痰，宽胸利膈，丹参活血化瘀，四者既可助君药降气止咳，又可治疗痰瘀实邪之本，故为方中臣药。黄芪平补脾肺之气，增强正气以趋邪外出，且本药补而不滞，无恋邪之弊。。甘草补中，培土生金。本病例充分体现了邵老师在治疗COPD上的驱邪补虚兼顾的思想。','3','2008-11-24 21:10:31','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('7d6dc378-f464-4025-a38b-8ea0c262b006','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200811240003','08112403','1','张炳中','77','1','0','0','11','','退休','','','','','0','0','有慢支史，详见现病史，否认高血压、糖尿病等其他内科系统疾病史。否认外伤、手术、输血史，否认肝炎等传染病史。','出生于上海，长期居住于上海；无疫区疫水接触史，有吸烟史30多年，约1包/天，现已戒烟十年；预防接种情况不详。','无特殊家族遗传倾向性疾病','有青霉素、头孢拉定药物过敏史。','copd','患者反复出现咳痰喘20余年，每因受寒后加重，经抗炎平喘治疗后能缓解。近期患者受寒又见胸闷气急，动则要喘，咳嗽有痰，痰多色白，粘稠难咯出，上午嗜睡，纳食一般，寐欠安；刻下：神清，气急貌，形体偏瘦，精神软，咳嗽，咯痰色白质粘量多；','2','药后诸症缓解。','本案为中医痰湿蕴肺型喘证。初诊时兼外感之征，治拟清肺化痰，同时加用健脾、平肝药物，帮助肃清气道。后症情好转则加强健脾补肾之效以巩固疗效，扶助正气。健脾可以生金，使肺气充沛、气机顺畅，又可以运化痰湿。如单纯以清肺化痰达不到治本的目的。因脾胃为后天之根本，脾弱则痰饮自生，素有“脾为生痰之源，肺为储痰之器”之说。肾为先天之本，津液有赖于原阳温熏推动，方可收到补肾健脾，温阳化痰之良效。','3','2008-11-24 21:33:03','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('660e41d9-1db7-4e94-b3a3-bea8bd64e6a7','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200812020001','08120201','1','陈庆业','69','1','0','0','0','不详','退休','不详','不详','','','0','0','有脑梗塞一次，具体不详，未留有后遗症。否认冠心病、高血压，糖尿病等其他内科疾病，否认肝炎、结核等传染病病史，否认手术外伤输血史。','出生于上海，长期居住于上海；无疫区疫水接触史，有吸烟史30多年，约1包/天，现已戒烟十年；预防接种情况不详。','无特殊家族遗传倾向性疾病','青霉素过敏史，否认其他药物食物过敏史。','慢性支气管炎','','肺气肿','慢性阻塞性肺疾病','患者有吸烟史30余年，有慢性咳嗽病史，每逢天气变化时加重，近5年来出现咳嗽咯痰加重，上二楼即觉气急。1月余前受凉后气急明显，近日见干咳较剧，气喘动则尤甚，纳食一般，二便尚调，夜寐欠安。舌红太薄白脉浮。','2','药后诸症缓解，发作次数减少，发作症状减轻，精神好转，胃纳好转，夜寐好转。','本案为中医脾肾两虚型喘证。初诊时兼外感之征，治拟清肺化痰，同时加用健脾、平肝药物，帮助肃清气道。后症情好转则加强健脾补肾之效以巩固疗效，扶助正气。健脾可以生金，使肺气充沛、气机顺畅，又可以运化痰湿。如单纯以清肺化痰达不到治本的目的。因脾胃为后天之根本，脾弱则痰饮自生，素有“脾为生痰之源，肺为储痰之器”之说。肾为先天之本，津液有赖于原阳温熏推动，方可收到补肾健脾，温阳化痰之良效。','3','2008-12-2 19:24:27','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('69798bef-68b3-4694-9721-3e462ab7ab51','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200812020002','08120202','1','段宝根','68','2','0','0','0','不详','退休','不详','不详','','','0','0','有慢支史，详见现病史，高血压20余年，否认糖尿病等其他内科系统疾病史。否认外伤、手术、输血史，否认肝炎等传染病史。','出生于东北，长期居住于东北。无疫区疫水接触史。吸烟史四十余年，1-2包/天，现1-2支/天。预防接种情况不详','无特殊家族遗传倾向性疾病','否认药物食物过敏史。','慢性支气管炎','','慢性阻塞性肺疾病','近20年来，每逢气候变化则咳嗽咯痰，近10年来活动后气急，并进行性加重，双下肢反复出现浮肿2年余，先后被诊断为慢性支气管炎、肺气肿、慢性阻塞性肺疾病、肺源性心脏病。4天前受凉后咳嗽气急加重，咯痰、痰色黄量多，质粘，双下肢浮肿，胃纳一般，大便尚调，夜寐欠安。','2','后续以三桑汤加减化裁温阳健脾，利水固表治疗至今，患者咳喘基本缓解，下肢偶在午后浮肿，感冒次数较往年明显减少，生活完全自理。','一：','','慢性支气管炎合并肺气肿，慢性阻塞性肺疾病、肺源性心脏病，临床上治疗颇为棘手，患者随着病情的进展，心肺功能不断恶化，严重影响生活质量，加上反复感染成为困扰临床治疗的难题。患者久病及肾，肺、脾、肾俱虚。肺为气之主，肾为气之根，肺主呼气，肾主纳气。咳喘之因，在肺为实，实则气逆，多因痰浊气阻；在肾为虚，虚不纳气，多因精气亏虚，而至肺、肾出纳失常。故咳喘之病主要在肺，有关乎肾，其治不离肺肾。又脾为痰饮形成之源，《内经》云：“诸湿肿满，皆属于肺。”《景岳全书》谓：“凡水肿等证，乃肺脾肾三脏相干之病，盖水为至阴，故其本在肾；水化于气，故其标在肺；水惟畏土，故其制在脾。”水液的运化不仅需要靠肺的通调水道，肾的气化功能，更依赖于脾的运化水湿功能，否则聚湿成痰，聚水成饮，故治痰当责治于脾。久病必瘀，又当活血化瘀。而寒，非温不化；饮，非利不去；瘀，非通不散。宣肺、温肾、健脾可利三焦，祛瘀逐饮；温通心阳，可以通络活血。温阳以“益火之源，以消阴翳”。在临床上慢性支气管炎、支气管扩张、哮喘、肺气肿、肺源性心脏病等慢性肺系疾病缓解期常常出现寒热夹杂、肺脾肾俱虚而肝火亢盛等表现。例如纳差、痰黄或粘稠难咯、怕冷尤其手足冷，伏天仍要穿棉鞋，伴盗汗，临床症状复杂多样，辨证选方颇为棘手，治疗上需要祛邪扶正并举、寒热兼顾。通过多年的观察，将桑白皮、桑寄生、桑椹子三味药组合用于此类患者，大多可取得理想疗效。方中桑白皮清肺降气，李东垣：“桑白皮，甘以补元气之不足而补虚，辛以泻肺气之有余而止咳。”如兼夹表证，可与桑叶同用，以增加疗效；桑椹子补肝肾之阴，又不过于滋腻碍邪；桑寄生补肝肾，且有通络活血之功，三者相伍，功补兼施，功不伤正，补补恋邪，以治痰俞于肺、肝肾不足的“咳喘气短”证。若偏于痰郁肺阻，可加清肺化痰的平地木、黄芩、瓜蒌仁、鱼腥草等‘寒痰为主者，加温肺化痰的苏子、前胡、半夏等；若偏于肝肾不足，则加调补肝肾阴阳之品，如附子、淫羊藿、补骨脂、巴戟天、杜仲、女贞子、枸杞子、何首乌、山茱萸等，营养之品同用，可“阴中求阳”、“阳中求阴”，根据具体情况有所偏重。平地木、功劳叶亦为邵老常用组合，可平肝理气；青皮、陈皮肝脾同治；防己、防风、猪苓、茯苓、车前草祛风利水，健脾止咳。桑叶、桑白皮、桑寄生、桑椹子乃邵师特色经验方“三桑汤”的主要组成部分，主要用于久病寒热夹杂，本虚表实之证，往往取得理想疗效。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('二：','','“喘肿”相当于中医内科中的溢饮、咳喘、水汽病等范畴，多见于现代医学中的慢性阻塞性肺疾病后期及肺心病阶段。健脾利水是治疗“喘肿”的基本原则。运用利水药减轻动静脉的阻力，使静脉回流畅通，肺动脉高压降低，从而使水肿、咳喘、胸闷等一系列临床症状得到缓解。通过健脾，提高机体免疫功能，预防呼吸道反复感染，防治疾病进一步加重。我们常用宣肺温阳利水法、温阳化饮利水法、疏肝清肺利水法、健脾纳肾利水法，辨证施治取得比较好的疗效。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('三：','“实喘责之于肺，虚喘责之于肾”，但临床上COPD后期往往虚实夹杂、寒热并存、痰湿内热、肾精脾阳肝阴肺气亏虚，相互影响，治疗上很困难。而三桑汤、平咳化痰合剂就是针对患者这一阶段的情况而设立的。在疾病缓解期亦可选择中成药，如六君子汤合玉屏风散等，佐补肾纳气之品。');
