INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('患者服药二月余，复查肝功能正常。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','按：本案系脂肪肝炎症反应期，可出现肝功能异常，此时痰瘀互结、疫毒犯肝为其病机的关键所在。张师认为，治疗重在痰瘀并治，清热解毒。谷丙转氨酶升高，多为热毒内盛所致，故急用垂盆草、六月雪、平地木、小蓟草、银花、连翘、黄芩、紫花地丁等药清肝解毒；用决明子、泽泻、丹参、郁金、莱菔子、生山楂、冬瓜子化痰活血；兼用陈皮和胃，杜仲补肾。现代药理证明：决明子有降血脂、保肝作用；金银花、连翘、黄芩均有降血脂、保肝、抑制多种细菌病毒作用；郁金有促进肝细胞损伤修复、保护肝细胞的作用；丹参对急性肝损伤有保护作用；生山楂具有降血脂作用。治疗采用多向调节，攻补兼施，以达到改善肝功能的目的。','3','2008-12-9 15:19:23','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('8506f2a0-6fe8-455a-a723-14322237cdf0','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200812090004','0057963','1','周某某','47','0','1','0','0','','','','','','','0','0','','','','','','一年来肝功能反复异常，乙肝“小三阳”，纳呆，时心悸，胸闷，情绪抑郁，大便二日一行，腰痠，夜寐不安。','2','随访：患者约每一月左右复查肝功能，GPT逐渐下降，2002年10月15日肝功能检查全部正常。现患者隔日服药，多次复查肝功能全部正常。','按：此病例为乙型肝炎，系疫毒内伏，肝气郁结，肝络痹阻所致。急性活动期表现为邪热鸱张，肝经有热，胆失疏泄，血清谷丙转氨酶、总胆红素升高，舌质红脉弦等，此时治宜清解疫毒，凉肝利胆为先，重用清热解毒之品，挫其邪势，五脏乃安。','1','2008-12-9 16:21:01','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('37dce175-db0c-4875-b523-3de5e102f075','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200812100001','0064576','1','张某','31','0','0','0','0','','','','','','','0','0','','','','','','99年婚检发现乙肝“大三阳”，HBV-DNA阳性。2001年曾出现肝功能异常，自2002年3月注射长效干扰素，当年5月肝功能恢复正常，2003年2月停用干扰素，4月份SGPT反跳至1000IU/L，经住院治疗后SGPT降至正常。近肝功能异常。刻下患者肝区疼痛，尿黄，精神不振，头晕不适，面部痤疮较多。','2','随访：10月18日再次复查肝功能均在正常范围。','此病例为乙型肝炎，系疫毒内伏，肝络不和所致。急性活动期表现为邪热鸱张，肝经有热，胆失疏泄，血清谷丙转氨酶、总胆红素升高，舌质红脉弦等，此时以实证为主，治宜清解疫毒，化痰活血，疏肝和络为先，重用清热解毒之品，挫其邪势，五脏乃安。故方中采用白花蛇舌草、垂盆草、六月雪、平地木、紫花地丁清热解毒降酶，郁金、赤芍活血利胆，田基黄、茵陈清热利湿退黄，苦参清热燥湿，龙胆草泻肝火清湿热，水牛角片清热凉血解毒。终使病情得到控制，肝功能恢复正常。','1','2008-12-10 13:53:41','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('0c04037c-b75c-4700-a448-9f66cc8e5021','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200812110001','0059306','1','金某某','60','0','0','0','0','','','','','','','0','0','','','','','','93年体检发现乙肝病毒阳性，2000年肝功能异常，今年6月出现因黄疸住院治疗，当时SGPT','>','500IU/L，7月2日SGPT正常。遂出院，来门诊就医。刻诊纳呆乏力，溺黄，面色少华。','2','患者服用本方一月自觉诸症减轻，后又加用银花，水红花子再服，至10月9日就诊复查肝功能完全正常（SGPT、SGOT、球蛋白、总胆红素均正常），AFP','<','3.89','ug/L','(正常值','<','10.90','ug/L)。目前病人已正常上班。','治乙肝主用生黄芪、白花蛇舌草、丹参、郁金四药。生黄芪，甘，微温，补中益气，利水托脓，具有提高补体水平，增强T细胞功能，诱生干扰素，抑制乙肝病毒繁殖的作用，是从虚的方面悟出的。白花蛇舌草，苦，甘寒，清热解毒，消痈利尿，具有抑制体液免疫功能的作用，是从实的方面考虑的。丹参，苦，凉，具有凉血活血，消痈除烦的功效，对已沉着的免疫复合物有促进吸收和清除的作用，是从改善肝脏病理出发的。郁金，辛，苦寒，具有行气解郁，祛瘀止痛，清除血中过甚抗原，防止免疫复合物的形成，是从恢复肝脏功能着眼的。四药共奏清热解毒、益气活血，抑制病毒、调控免疫之功。此患者不仅肝功能反复异常，且AFP明显增高，肝细胞损伤严重，在以上四药的基础上加用大量清热解毒之品，还用活血化瘀药，以改善微循环，增加肝脏血液供应，减轻了肝脏负荷，促进肝脏的修复和代偿。方中石见穿、莪术等消症积、通经脉，具有增强免疫力、反突变、抗肝纤维化、抗肿瘤作用，活血而不伤正；生地清热养阴，调控免疫……。诸药合用，共奏良效。','3','2008-12-11 8:35:35','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
