INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('952dd05a-080d-443e-a85b-d7bc221a6d2e','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200810230003','08102302','1','陈明珍','62','1','1','0','0','','退休','','','','','0','0','冠心病，高血压病史10余年，血压最搞170/100mmHg,口服珍菊，控制可。','出生、生长在上海，无疫水疫区接触史，预防接种史不详；无烟酒等不良嗜好史，正常婚育，育2子1女。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','无特殊家族遗传倾向性疾病','无','慢性阻塞性肺疾病','患者有反复咳嗽咯痰史10余年，近4年来咳嗽频作伴气促，每年冬春两季易发，多次住院予抗感染解痉平喘治疗。近1周来咳嗽时作，胸闷气急，动则尤甚，咯痰色黄质粘稠量多，汗多怕热，双下肢稍肿，自服必嗽平、博利康尼无明显好转，无发热恶寒，无夜间不能平卧。舌红台黄腻，脉滑数。','4','偶有咳嗽，少痰，无肢肿，续前方14贴后症情缓解。','本案患者除痰热壅于肺间外，并见水肿。故喘促明显，治疗以常规清肺化痰，兼顾利水消肿。《济生方.痰饮论治》说：“人之气道贵乎顺，顺则津液流通，绝无痰饮之患。”肺主通调水道，若饮留肺中，肺气不能布津，上逆而喘。利水的方法亦有很多种，需辨证施治。','3','2008-10-23 21:00:08','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('1c0978d1-af42-4e05-a29b-dc29cf3edd8b','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200810300002','00085211','1','周洪年','69','0','0','0','0','','','','上海市临平路51弄5号','','','0','0','有乙肝史及肝硬化史','','','','','有乙肝史及肝硬化史二年，腹水反复，近二月感腹胀，右胁胀痛，纳呆，尿少，消瘦，大便日一次，色黄，头昏，二下肢不肿。检查：面色晦滞，腹部膨隆，二下肢无压迹。','2','1月30日B超检查：肝硬化，未见腹水。A/G=48.9/27.6，γ球蛋白22%。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('继服上方，去黑白丑、陈葫芦，疗效满意。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','肝硬化腹水是由于邪毒内伏，肝郁血瘀，肝络痹阻，气化失司，水湿内停，三焦壅塞，致肝、脾、肾三脏俱损，属本虚标实之重症，逐水攻下法为治标的重要手段之一，虽属于权宜之计，但有积极的治疗作用。本例采用攻补兼施方法而获效，其中生大黄、葶苈子起到了推动荡涤的作用。','3','2008-10-30 16:08:27','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('074a1c2c-5830-4f3e-b1d6-b49ae42285e1','cad88fd2-8757-43c2-b91f-0cab72c5fccf','dbc4803a-edf7-47ee-a83b-7f3fd5447bc0','bl200810300003','94/51416','1','钱华屏','30','0','1','0','0','不详','职员','不详','不详','310000','310000','0','0','有支气管哮喘病史20余年，已服长效氨茶碱1#','qn一年。否认其他内科疾病史。否认外伤手术史。否认肝炎结核传染病病史。','久居上海，无疫区疫水接触史。无吸烟嗜酒史。','不详','无食物药物过敏史。','哮喘','咳（＋），痰5－6口，白粘，易，喘（±），哮（＋），凌晨哮喘发作，须坐起15分钟，鼻塞流涕，纳平，口干欲饮、喜温，大便正常。苔淡黄腻，质尖偏红，脉细弦。','2','','','3','2008-10-30 20:07:33','b7d5a22b-4519-43db-92c0-bbeb86040f51',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('eb00a622-f852-43e5-b083-4656a8ced320','868ee43d-9114-44ff-a87c-b6beb55a20d6','8bcf4438-fc6e-4966-839d-9b3b204f29ad','bl200812090001','0043668','1','卫某某','41','0','0','0','0','','','','','','','0','0','','','','','','一月来乏力，形体偏胖，大便日行2~3次。','2','随访半年,保持肝功能正常','脂肪肝炎症反应期，可出现肝功能异常，此时痰瘀互结、疫毒犯肝为其病机的关键所在。治疗重在痰瘀并治，清热解毒。谷丙转氨酶升高，多为热毒内盛所致，故急用垂盆草、六月雪、平地木、银花、连翘、紫花地丁等药清肝解毒；用决明子、泽泻、丹参、郁金、莱菔子、生山楂化痰活血。现代药理证明：决明子有降血脂、保肝作用；金银花、连翘、黄芩均有降血脂、保肝、抑制多种细菌病毒作用；郁金有促进肝细胞损伤修复、保护肝细胞的作用；丹参对急性肝损伤有保护作用；生山楂具有降血脂作用。治疗采用多向调节，以达到改善肝功能的目的。','1','2008-12-9 11:12:51','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('e1ebf760-e168-406c-ab65-22a271569770','868ee43d-9114-44ff-a87c-b6beb55a20d6','8bcf4438-fc6e-4966-839d-9b3b204f29ad','bl200812120001','1002263725-01 1','周某某','57','0','1','0','0','','','','','','','0','0','','','','','','脂肪肝五年，现肝功能异常，肝区不痛，纳可，夜寐安，喜甜食。','2','随访半年,肝功能保持在正常范围','饮食不节、嗜食肥甘厚味以致痰浊内生，痰瘀互结形成肝痞，当热毒内盛，气血失调时可出现肝功能异常，治疗当以化痰活血，清肝解毒为主，故急用垂盆草、六月雪、平地木、小蓟草、白花蛇舌草、连翘等药清肝解毒；用决明子、泽泻、丹参、郁金、莱菔子、海藻化痰活血。','1','2008-12-12 15:23:26','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
