INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('72a652a6-749a-48c6-aa10-0b10b5a6d5cc','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','95b5f8ca-9cee-43cb-a6f9-1261ab890ee5','bl200812280001','08122801','1','汪晓','','70','1','0','0','0','不详','退休','不详','不详','','','0','0','慢支、肺气肿30余年，余无殊。','出生于上海，长期居住于上海；无疫区疫水接触史，无烟酒等不良嗜好；预防接种情况不详。','无特殊家族遗传倾向性疾病','否认药物食物过敏史。','copd','患者慢支、肺气肿病史30年,咳嗽、咯痰、喘息反复发作，2天前因受凉后加重。自服抗生素（赛福欣）、氨茶碱,喷雾吸入喘康速、普米克,症状加重,症见咳嗽喘促,动则为甚,胸胁满闷,喉间痰鸣,痰黄白相兼,不易咯出,心悸汗出,不思纳谷。','2','患者痰消咳减，喘促缓解。','本例选方出自《金匮要略》,“胸痹心中痞气,气结在胸,胸满,胁下逆抢心,枳实薤白桂枝汤主之,人参汤亦主之”。与瓜蒌薤白半夏汤证比较,除喘息、咳嗽咯痰外,又出现心中痞气,气留胸间,胸胁胀满等慢性支气管炎、肺气肿、肺心病急性发作的症状和体征。其病位由肺及脾,由上及中,气痹邪恋,上逆心胸,形成虚实错杂之证。方中薤白、瓜蒌通阳化痰,厚朴、桂枝辛开展气,枳实消痞除满,半夏化痰降逆,佐以党参、白术、甘草,辅以桂枝、干姜甘辛养阳,以防喘脱。','3','2008-12-28 16:45:17','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('b1f4897b-5d20-46b9-97e7-24afd808d248','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','58e95a10-2ba8-4420-bd84-aa822d45f7b6','bl200812300001','08123001','1','倪晓丹','18','1','0','0','0','不详','学生','不详','不详','','','0','0','无殊','出生于上海，长期居住于上海；无疫区疫水接触史，预防接种情况不详。','父亲有过敏性哮喘，无其他特殊家族遗传倾向性疾病','有海鲜、菌菇类过敏，否认其它药物食物过敏史。','过敏性哮喘','既往患者有支气管哮喘病史10余年，中西药结合常规治疗后控制可，近两日受凉后咽干咽痒、干咳不适，舌红苔薄白脉浮。','2','服药后患者症状好转、稳定，发作次数明显减少，精神振，胃纳好，夜寐安。','本案患者肺脾两虚，外感风寒。患者年幼体弱，加上自幼患病久咳体虚，风寒外袭，虚不能祛邪于外，治疗以玉屏风散为主方补益肺气扶正，加用疏风解表化湿药祛邪，达到扶正祛邪的治疗目的，收效甚。','2','2008-12-30 10:21:49','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('8d2684b5-703e-41dd-ab16-2aa2a795aac2','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','58e95a10-2ba8-4420-bd84-aa822d45f7b6','bl200812300002','08123002','1','汪翠莲','55','2','1','0','0','不详','工人','不详','不详','','','0','0','既往有高血压史数年，血压最高160/90mmHg，不规则用药，血压欠监测。否认糖尿病等其他内科慢性疾患。有过敏性鼻炎史。曾有脑出血史，后遗右侧肢体麻木。否认外伤、手术、输血史。否认肝炎伤寒结核等传染病史','出生于上海，长期居住于上海；无疫区疫水接触史，预防接种情况不详。','无特殊家族遗传倾向性疾病','有螨虫过敏，否认其它药物食物过敏史。','支气管哮喘','既往患者有支气管哮喘病史30余，','昨夜受凉后今有发热（38.40C）；鼻塞流涕，咳嗽有痰，痰色淡黄量较多，纳差，二便尚调，寐欠安；','2','','药后日减好转，热退咳少，精神渐振，胃纳转好，夜寐安。','支气管哮喘属中医“哮证”、“喘证”范畴。其发症急，变化快，有反复发作之特点。该患者证属上焦火盛，痰热壅肺。邵师治以平喘止咳，清热化痰。方宗荆防败毒散、二陈汤等化裁：疏风清热，调和营卫、益气固表，方用荆芥、防风、黄芩疏风清热，','姜半夏、羌独活、葛根、焦六曲、谷芽、麦芽、姜竹茹、炙鸡金共凑健脾胃化痰湿之功，柴胡、前胡，赤芍、白芍，青皮、陈皮乃邵老经典药对，起到平肝调肝，从而调畅情志、健脾化湿，川桂枝、白芍调和营卫，加用辛夷、','路路通通窍，共凑疏风清热，调和营卫、益气固表，经过治疗、调理，收效甚。','3','2008-12-30 10:24:23','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('c216db78-a164-412d-aff0-896e8d2f014c','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','58e95a10-2ba8-4420-bd84-aa822d45f7b6','bl200812310001','08123004','1','顾勇','47','1','0','0','0','不详','工人','不详','不详','','','0','0','患者既往有高血压、高血脂、糖尿病史十余年，血压最高时达180/80mmHg，目前常服用洛汀新、兰迪，诺和灵、拜糖平，血压、血糖控制可，否认肝炎、伤寒等传染病病史。否认其他慢性病病史。否认手术、外伤史。','出生于上海，长期居住于上海；无疫区疫水接触史，按上海市防疫规定进行预防接种。','无特殊家族遗传倾向性疾病','青霉素过敏史.余无殊。','过敏性鼻炎，支气管哮喘','患者既往有支气管哮喘病史30余年，每年春夏交换季节天气变化较大时易感易发，平素用万托林气雾剂、必可酮气雾剂，今年自9月份来未有明显哮喘发作，但自感胸闷较重，易出虚汗，动则甚，无痰、纳可，脚不肿；舌淡红苔薄白脉沉细。','2','今天冬天哮喘未明显发作，血压、血脂、血糖控制可，盗汗、怕热等不适逐步缓解至无明显不适，纳可、寐安、二便调。','');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('患者辨证属肺热痰湿内盛，上见咳喘，下见水湿泛滥水肿，营卫失和而见易汉出、汗出较多，一方用桑叶皮、黄芩、射干、姜竹茹、胡颓叶清肺化痰湿，','止咳平喘，用女贞子、杜仲','、黑山栀、薏苡仁、石菖蒲、猪茯苓等补肾利水，水湿化、痰饮去，则气得以调畅、营卫和汉自止，三方加用焦六曲、谷麦芽、鸡内金等健脾胃消食之品，则患者脾胃功能渐复，脾主运化，水湿得运，气机得畅，病愈已！');
