INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','肺与肝同主气机，肺以降为顺，肝以升为柔，肝气郁而不舒或升发太过，都会导致肺气的肃降不利而出现咳逆之症，所以治疗这类哮喘患者，邵老尤重疏肝、平肝治疗。加之女子以肝为先天，患者产后体虚发哮喘，缓解期治多以补肾平肝，理气活血，无论是对月经的调理还是对哮喘的控制均或良效。');
