INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('3ae99c0b-282a-4a45-8f9e-4661a2aa3607','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200901060002','041187','1','曹某某','43','0','0','0','0','','','','','','','0','0','','','','','','乙肝病史8年，肝区疼痛，近二月腹胀如鼓，尿少，大便秘结不通，面色晦黄，纳呆恶心，神疲乏力，痛苦面容。','2','','乙型肝炎肝硬化腹水属中医“臌胀”范畴，历代医家一般分为“气臌”、“血臌”、“水臌”、“虫臌”，但气血水三者每多相因为患，非单纯为病，仅又主次之分。肝硬化腹水是由于疫毒内伏，肝络痹阻，气化失司，水湿内停，三焦壅塞，致肝脾肾三脏俱损，属本虚标实之重症，逐水攻下法为治标的重要手段之一，有积极的治疗意义。成败的关键在于攻补的时机与分寸。本案大量腹水，腹胀便秘，采用攻补兼施的方法而获效。虽然《内经》有“中满者泻之于内”，“下之则胀已”，但在治疗过程中，应注意不宜攻伐过猛，须遵循《素问&#8226;至真要大论》所言“衰其大半而止”的原则。其中生大黄、葶苈子、黑白丑等起推动荡涤的作用；用地鳖虫、山甲片以软坚散结；用木香、炙鸡金健脾助运。此治疗中充分考虑患者整体情况，攻补恰到好处，大获其效。','1','2009-1-6 13:57:26','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('3c37d3e0-fe2d-4ce1-8287-2777845e4e50','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200901060004','1008074056','1','王某','25','0','1','0','0','','','','','','','0','0','','','','','','二年前发现乙肝“大三阳”，近查HBV-DNA阳性，时感倦怠乏力，胃脘作胀，大便溏薄，日行2-3次，面色少华','2','3月21日方加减治疗四个月，7月2日复查HBsAG（+）、HBcAb（+），8月6日复查HBV-DNA阴性。','治乙肝主用生黄芪、白花蛇舌草、丹参、郁金四药。生黄芪，甘，微温，补中益气，利水托脓，具有提高补体水平，增强T细胞功能，诱生干扰素，抑制乙肝病毒繁殖的作用，是从虚的方面悟出的。白花蛇舌草，苦，甘寒，清热解毒，消痈利尿，具有抑制体液免疫功能的作用，是从实的方面考虑的。丹参，苦，凉，具有凉血活血，消痈除烦的功效，对已沉着的免疫复合物有促进吸收和清除的作用，是从改善肝脏病理出发的。郁金，辛，苦寒，具有行气解郁，祛瘀止痛，清除血中过甚抗原，防止免疫复合物的形成，是从恢复肝脏功能着眼的。四药共奏清热解毒、益气活血，抑制病毒、调控免疫之功。此患者乙肝“大三阳”，HBV-DNA阳性，在以上四药的基础上加用大量清热解毒之品，还用活血化瘀药，以改善微循环，增加肝脏血液供应，减轻了肝脏负荷。方中陈皮、陈香元、炙鸡金健脾和胃，即“见肝之病，知肝传脾，当先实脾。”之意；水牛角片、生地、赤芍、丹皮有犀角地黄汤之意，清热凉血养阴，调控免疫……。诸药合用，共奏良效。','1','2009-1-6 14:54:06','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('0a4a54ca-7ae2-4a4a-baef-0ab99da1934f','868ee43d-9114-44ff-a87c-b6beb55a20d6','8bcf4438-fc6e-4966-839d-9b3b204f29ad','bl200901060005','0248960','1','陆某某','44','0','0','0','0','','','','','','','0','0','脂肪肝二年，“小三阳”，HBV-DNA阴性。有饮酒史。','','','','脂肪肝,慢性乙型病毒性肝炎','刻下肝区胀痛，大便日行二次。','2','肝区疼痛明显减轻，1月15日复查肝功能全部正常。每日一剂，以资巩固。','脂肪肝可归属中医的“痰证”、“瘀证”、“积证”等范畴。病变部位在肝，与胆、脾、胃、肾有关。痰瘀互结是脂肪肝的病机关键所在，当热毒内盛，气血失调时可出现肝功能异常，治疗当以化痰活血，清肝解毒为主，且要重视多向调节。本案虽“小三阳”，但HBV-DNA阴性，SGPT升高系脂肪肝炎症反应期，此时痰瘀互结、疫毒犯肝为其病机的关键所在。张师认为，治疗重在痰瘀并治，清热解毒。谷丙转氨酶升高，多为热毒内盛所致，故急用垂盆草、六月雪、平地木、小蓟草、连翘、黄芩、紫花地丁等药清肝解毒；用决明子、泽泻、丹参、郁金、化痰活血；茵陈、田基黄清热退黄；兼用陈皮和胃。','3','2009-1-6 15:15:49','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
