INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('ea3a353d-8520-4a23-aebf-315f6f7fdfa8','24bff094-fe02-4f6d-b8b1-e3bc636c3cc4','c0ce72db-6eaf-4022-bb0e-25c0569b14d6','bl200901080003','','1','赵佳佳','4','10','1','0','13','','','','','','','11','1','','足月生产，无产伤史，母乳喂养，定期增加辅食，计划免疫','父母皆有过敏性鼻炎病史','过敏性鼻炎、哮喘发作史','','患儿因晚上咳嗽数日，哮喘发作2年于2007年5月23日首诊于时毓民门诊。患儿就诊前有2年的哮喘病史，平均1次/月，患儿有过敏性鼻炎。刻下症：患儿晚上轻咳，痰少难咳。咳嗽：（+）轻度，白天间断咳嗽，不影响生活；咯痰：轻度（+）喉中偶可闻及痰声；喘息：无。兼症：面色苍白，四肢发冷，咽轻度红肿，纳差，睡眠一般。舌象：舌淡红苔白腻；脉象：脉弱无力。','2','','','3','2009-1-8 14:30:19','46e3d3e3-c822-4047-91db-b0cf580e2734',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('0ca67c64-a37f-42aa-8c9d-521d0f3f4e95','24bff094-fe02-4f6d-b8b1-e3bc636c3cc4','c0ce72db-6eaf-4022-bb0e-25c0569b14d6','bl200901080004','','1','周旻昊','4','9','0','0','0','','','','','','','0','1','肺功能（PEF或FEV1）：中小气道阻力增加','足月生产，无产伤史，母乳喂养，定期增加辅食，计划免疫','父母皆有过敏性鼻炎病史','过敏性鼻炎、哮喘发作史','','患儿因夜间咳喘数日于2007年10月12日首诊于时毓民门诊。患儿就诊前共喘过多次，具体时间家长忘记，患儿有有过敏性鼻炎。刻下症：患儿夜间咳喘数日。咳嗽：（+）轻度，白天间断咳嗽，不影响生活；咯痰：轻度（+）喉中偶可闻及痰声；喘息：无。兼症：五心烦热，面色潮红，动辄汗出，晨起鼻痒善嚏，咽无红肿，纳差，睡眠一般，小便多，大便偏干。舌象：舌红少苔；脉象：脉弱无力。','0','','','3','2009-1-8 14:52:11','46e3d3e3-c822-4047-91db-b0cf580e2734',0,'0');
