INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('79a26cd4-3263-4871-842e-e0d28973a268','868ee43d-9114-44ff-a87c-b6beb55a20d6','8bcf4438-fc6e-4966-839d-9b3b204f29ad','bl200901120001','021837','1','张某','23','0','0','0','0','','','','','','','0','1','','','','','','形体偏胖，发现脂肪肝2月，上月体检肝功能异常，刻下无明显不适。','2','上方为主治疗至4月17日肝功能复查完全恢复正常，体重下降4公斤。','本案系脂肪性肝炎，出现肝功能异常，此时毒邪犯肝、痰瘀互结为其病机的关键所在。张师认为，治疗重在清热解毒，痰瘀并治。谷丙转氨酶升高，多为热毒内盛所致，故用垂盆草、六月雪、平地木、小蓟草、银花、连翘、黄芩、龙胆草等药清肝解毒以降酶，用决明子、泽泻、丹参、郁金化痰活血以降脂；兼用陈皮、茯苓醒脾运脾，调畅气机。本案汇清热解毒、化痰活血、理气健脾于一炉，共奏其效。','1','2009-1-12 9:59:09','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('2ffd4d06-4e79-45d8-ac2b-71ccf50bcf56','868ee43d-9114-44ff-a87c-b6beb55a20d6','8bcf4438-fc6e-4966-839d-9b3b204f29ad','bl200901120002','2000953194','1','黄某某','42','0','0','0','0','','','','','','','0','0','胃窦炎病史二年，HP(+)。','','','','','肝功能反复异常一年，体重不减，大便2-3次/日，纳佳。血压120/85mmHg','2','两月后复查肝功能正常，5.6肝功能：GPT34','u/L','(0-64),GOT25','u/L','(0-42),γ-GT63','u/L','(7-64),TC5.4','mmol/L,TG1.7','mmol/L.肝区不适症状好转。','脂肪肝祖国医学无此病名，根据其病因病机及证候，可归属“痰证”，“瘀证”，“肝郁”，“食积”。本案病家素食膏梁厚味，脾胃运化失司，水液输布失常，痰湿内生，日久血液运行受阻，滞留经脉，形成痰瘀互阻，肝络不和。因此化痰活血，疏肝和络是治疗脂肪肝的关键。方中泽泻、决明子、丹参、郁金、；海藻、','芦荟、生山楂化痰活血，疏理肝络；垂盆草、六月雪、平地木、龙胆草、紫地丁、','败酱草、','小蓟草、清热降酶；佩兰、','连翘、陈皮清热化湿。标本兼治，疗效显著。','1','2009-1-12 10:14:01','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('36772049-431a-4f19-bd9c-4fe0bb9b1075','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200901130001','2008074053','1','叶某','43','0','1','0','0','','','','','','','0','0','','','','','慢性乙型病毒性肝炎','2001年发现HBV-M:HBsAg(+)、HBcAb(+)、HBeAg(+)。肝功能异常。自服西药无效。肝功能反复波动。刻下胃脘作胀，纳呆食少，大便溏薄四月','2','随诊6月，8.11复查HBV-DNA（-）','我国是乙肝高发区，目前尚缺少有效方法，治疗乙肝，抑制病毒复制是关键。主张辨病与辨证相结合，共性与个性相结合，清除病毒与调控免疫相结合。苦参，苦，寒，归心、肝、胃、大肠、膀胱经。具有清热燥湿，杀虫，利尿','功效。《神农本草经》：“主心腹结气，徵瘕积聚，黄疸，溺有余沥，逐水，除痈肿，补中，明目止泪”。现代药理研究：苦参的根含有多种生物碱及黄酮类化合物。以苦参碱、氧化苦参碱为主，具有抗病原微生物，抗炎，升白免疫调控作用。文献报道：苦参具有抑制病毒复制，抗纤维化作用。苦参与益气活血药配伍，“效如桴鼓”。','3','2009-1-13 10:30:38','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
