INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('d8f3e322-6885-4bd2-a262-394c4f8e0f45','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200901130004','1000534043','1','王某某','45','0','0','0','0','','','','','','','0','0','慢性乙型病毒性肝炎病史5年','','','','','乙肝“大三阳”5年。一周前体检时肝功能异常。刻下口干，纳可，大便日一次','2','随访三月，肝功能正常，特别球蛋白明显下降在正常范围内，临床症状好转。','本案为慢性乙型合并脂肪肝，扶正解毒方与降脂理肝汤合用。球蛋白升高，与乙肝病毒复制及长期病毒感染肝脏慢性损伤有关。患者乙肝病史五年，病和较长，正气损伤，故用黄精补益肝肾。','1','2009-1-13 16:09:42','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('e3bb4d77-6e3e-423a-969b-9dfbff783618','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200901220001','023998','1','何某','15','0','0','0','3','','','','','','','3','1','乙肝“小三阳”三年','','','','慢性乙型病毒性肝炎','乙肝“小三阳”三年，近2年来多次肝功能异常，ALT最高320u/l。无黄疸。时有牙宣，胃纳尚佳。','2','随访八月，肝功能正常，临床症状好转。','本案为慢性乙型病毒性肝炎合并脂肪肝，肝功能指标异常，且舌质尖红，治疗中急以清肝解毒降酶之品，采用解毒扶正方与降脂理肝汤加减合用，加紫花地丁、败酱草、连翘、虎杖清热解毒、凉血消肿，疗效显著。','3','2009-1-22 14:43:44','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('927c9b7a-22fc-44ac-ba54-145dee154b98','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200901220002','100125420501','1','张某某','59','0','1','0','0','','','','','','','0','0','','','','','','2003年12月体检时发现肝功能异常，','HbsAg阳性。近一月来胃纳欠佳，舌上毛糙感，肝区无不适。精神尚可，夜寐欠安，易醒。大便日一次，成形。','2','肝功能正常，球蛋白下降。','张老治乙肝喜用白花蛇舌草、生黄芪、丹参、郁金四药，共奏清热解毒、益气活血之效，有抑制病毒、调控免疫之功。患者肝功能出现异常，加用大量清热凉血解毒之品，如水牛角片、紫花地丁、败酱草、虎杖等。患者肝纤维化指标亦有升高，故加入石见穿、莪术、水红花子、马鞭草、炙鳖甲等化瘀软坚。','1','2009-1-22 15:23:59','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
