INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('4949475e-2a24-4f69-a429-b032a67be685','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902010001','021771','1','庄某','28','0','0','0','0','','','','','','','0','0','有乙肝史','','','','','患者乙肝多年。肝功能反复异常。三年来反复乏力、肝区不适。刻下易疲劳，肝区时有不适，纳可。','2','随访4月肝功能正常。','乙型肝炎，病至乙肝病毒表面抗原、e抗原、核心抗体三项阳性，乙型肝炎病毒脱氧核糖核酸阳性，提示病毒仍在繁殖复制，加上肝功能异常，为邪热内盛。药用犀角（水牛角代）地黄汤与解毒扶正方加味而成，清热逐邪、凉血解毒为主。','1','2009-2-1 15:07:55','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('1e2fc751-0bbb-49ab-8895-c8741ff0f011','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902020004','021208','1','何某某','37','0','1','0','0','','','','','','','0','0','慢性乙肝史','','','','','发现乙肝三年半。肝功能反复异常。肝区隐痛半年。刻下恶心无呕吐，胸闷心慌,胃纳尚可，尿黄，大便日1次,舌质微红，苔薄白,脉细弦。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','2','随访三月:肝功能正常。特别是总胆红素下降，球蛋白恢复正常。','本案病毒性肝炎，总胆红素和乙肝病毒指标较高，是由邪热鸱张、肝胆湿热、疏泄不利所致，治宜清解疫毒、凉肝利肝为主，重用清热解毒之品。茵陈、田基黄清热利湿退黄。以苦咸寒之犀角凉血清心而解热毒，使火平热降，毒解血宁，张师善用清热解毒、凉血散瘀之犀角地黄汤降乙肝病毒指标，疗效显著。叶下珠具有明显抗乙型肝炎病毒及对肝损伤的保护作用。对于胁部胀痛，则施以延胡索、八月札、青皮、桔叶等理气之品。药后总胆红素下降，黄疸退去。','1','2009-2-2 15:07:30','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('a303607b-1391-4bde-8a6c-5bb2368f28fd','a2884c9d-9946-4415-a784-1f07c4248835','0b9bec07-3e3c-46b2-8c79-60de6b5c179d','bl200903130020','','1','李玉前','45','0','1','0','0','','','','','','','0','0','','','','','','发热咳嗽，畏寒肢冷，咳痰量少，色白清稀，时有虚汗，夜寐不安。舌质','','舌边尖稍红','','舌苔','','黄腻','','脉象','','细');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','2','咳嗽明显改善','患者反复咳嗽，经过多方治疗效果不显，西医用激素治疗，咳嗽好转，但副作用较大，中医选用生地、知母、元参等可以减轻激素的副作用，加用苍耳子、辛夷、千里光、徐长卿、菝葜等抗过敏药物，可以减少激素的用量。','2','2009-3-13 21:25:16','e12b2b6a-ee2f-4754-99a4-1aab7c7df17e',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('06f2e4b9-7d66-4216-ad45-6dfa3211a324','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902040018','0002368','1','孙海源','49','0','0','0','0','','','','上海市西藏中路584弄4号','','','0','0','慢性乙肝史','','','','','慢乙肝史5年，肝功能一直反复波动。92年5月中旬起，腹胀尿少，未予重视，6月末，突然呕血，经医院抢救血止。现肝区作胀，隐痛，纳呆，尿少，大便调，二下肢有压迹。检查：形体消瘦，精神软，心肺(-)，腹软，肝胁下示及，脾肋下可及3指，二下肢有压迹。舌质微红，舌苔薄白，脉象细。','2','','本例肝硬化、巨脾、食道静脉曲张症，经清解疫毒、活血疏肝、理气利水及外敷解毒消痞散治疗10个月，肝功能改善，巨脾缩小，食道静脉未再破裂出血。','1','2009-2-4 15:14:24','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('0880b5a4-4ab8-498b-a8f7-f9f56c5d500b','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','fd513872-a870-45eb-9b86-3c5ed20b157d','bl200902050003','4867','1','蔡瑛','44','0','1','0','0','','','','','','','0','0','否认其他手术史，否认高血压、糖尿病等内科疾病史，否认传染病史。','已婚未育。月经正常。','父亲有胃癌病史。','否认药食过敏史。','无','患者经前乳房胀痛，经量中，月经正常。婚后未孕。无乳腺癌家族病史。1984年左乳纤维腺瘤切除术，B超示：两乳小叶增生伴囊肿，左乳外侧混合性占位（良性）。','2','','','3','2009-2-5 10:55:13','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('2d7d2398-f3bc-4744-b7d5-5a53058f608a','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200902050004','2054','1','王冬青','48','0','1','0','0','','','','','','','0','0','否认其他手术史，否认高血压、糖尿病等内科疾病史，否认传染病史。','已婚已育，哺乳过。化疗后月经未来。','否认家族性肿瘤病史。','否认药食过敏史。','右乳癌术后','2008年4月7日患者在瑞金卢湾分院行右乳癌改良根治术。病理示：浸润性导管癌。ALN9/9.ER+++,PR++,neu+-。术后泰索帝+CTX+表阿霉素化疗2次。神疲乏力，食欲尚可。口腔溃疡，夜寐尚安，口干欲饮。','2','','','3','2009-2-5 11:12:54','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('ad1ef543-d7df-4289-a912-c6199f7ec3d6','868ee43d-9114-44ff-a87c-b6beb55a20d6','8bcf4438-fc6e-4966-839d-9b3b204f29ad','bl200902050006','0000529','1','施妹芳','55','0','1','0','0','','','','本市唐山路1357弄19号','','','0','0','患者62年1月患急性黄疸性肝炎，以后呈迁延性状态。93年6月23日行乳房肿瘤手术。','','','','','近三月来肝区胀、乏力、汗出，动则尤甚，纳谷尚可，夜寐一般，腰酸，下肢不肿，面色少华，大便日一次不成形。舌质尖红，舌苔薄白，脉象细。','1','其后继续服药，8月17日家属来告知，10月8日B超示：肝左叶囊肿、脂肪肝，经治6个月全部消失，唯精神略差，二便自调，遂原方去龙胆草、胡黄连，加乌鸡白凤丸每日2粒继服。','本例脂肪肝、肝囊肿患者，经用化痰活血、清热解毒、补肾益精之剂6个月，先肝功能恢复正常，腹围缩小，症状好转，继之B超证实肝囊肿及脂肪肝消失，再次证实了先生多向调节诊治思路的正常。','1','2009-2-5 12:37:49','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('474603c4-d1a9-4085-9dca-6d41c96eb7f3','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902050012','19','1','俞某','77','0','0','0','1','','','','','320000','320000','2','0','糖尿病史，冠心病史，高血压病史。','否认疫水、疫区接触史。否认烟酒等不良嗜好。','无','无','动脉硬化闭塞症','患者近半年来左足部及小腿','每行走则出现酸胀、疼痛，休息后缓解，再行再痛，并逐渐减少，现约行200米左右，左下肢凉感。糖尿病史6年，房颤2～3年，两足有足癣史。体检：左下肢稍显苍白，两足趾丫白糜，皮温低于对侧，两小腿色素广泛沉着，两下肢腘动脉（－），足背动脉、胫后动脉均（－），肢体抬高苍白试验二级/30秒，舌质红嫩，舌苔薄白，脉细。','2','','患者高年，又患多年的消渴，致使正气不足，气阴两虚，最易为外邪所乘；加之年老，脾肾亦虚，气血不足，难达四末，易为寒凝。因此治疗上扶正就很关键。奚老认为：扶正也应辨证而为，如若以气阴两虚为主，可益气养阴，临床常使用黄芪、黄精、制首乌、熟地、白芍等药物；若以肾阳亏虚为主，则加用温益肾阳，温熙四末之品，多用熟附片、肉桂、干姜、鹿角片、白芥子等药物。然而，对于糖尿病足和肢体动脉硬化闭塞症并疾，急性阶段，仍以湿毒浸淫，热郁脉络为主，清热解毒利湿仍为保肢治疗的不二法门，临床常使用茵陈、垂盆草、豨莶草、蒲黄、制军以及炙甘草等药物；慢性恢复期，则继续用软坚化瘀法，待干黑分界，行局部手术治疗。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('总之，本病证虽有血瘀表现，但其病机为正气亏虚、邪热阻络，仅凭大量活血化瘀之品，一不能驱邪于外，再不能解毒于内，反而耗伤正气，不可贸然使用。');
