INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('522d44a1-8832-4192-bc7f-661644582b38','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200902050013','5564','1','张志红','52','0','1','0','0','','','','','','','0','0','否认其他手术史，否认高血压、糖尿病等内科疾病史，否认传染病史。','已婚已育，哺乳过。','否认家族性肿瘤病史。','否认药食过敏史。','右乳癌术后','右乳癌术后1年10月余，夜寐醒后不易入睡，口干减轻，胃脘遇冷则痛。手足心发热，入睡后口干欲引。','0','','','3','2009-2-5 14:22:51','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('c3f96914-d816-4c53-99b9-3202fd3cb3a7','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902050015','0176012','1','阚忠生','45','0','0','0','0','','','','上海市闸北区北宝兴路10弄11号102室','','310000','0','0','乙肝病史','','','','','患者因中上腹不适，乏力，纳差半月余。去传总求诊，查HBsAg、HbeAg、HBcAb-IgM均阳性，肝功异常。4月2日以“乙肝”收住院。刻诊：现在市“传总”住院，倦怠乏力，腹胀，纳差，寐尚可，大便日行1次。舌质微红，舌苔薄白，脉象细弦','2','','乙型病毒性肝炎活动期，丙氨酸氨基转移酶等升高，大多为疫毒内伏，湿热壅滞，肝失疏泄，此时的治疗以清解疫毒为主。方中以垂盆草、六月雪、水牛角粉、小蓟草清热凉血降酶；龙胆草、连翘、生大黄、青黛、紫花地丁、贯仲清热解毒；因合并有纳差、腹胀等肝气犯胃症状，故加入枳壳、大腹皮、佛手、八月札等疏肝理气和胃。','1','2009-2-5 15:39:15','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('43fbf758-5e31-4284-9de9-c86305a68c85','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200902050017','096X','2','张妙芬','51','0','1','0','0','','','','','','','0','0','否认其他手术史，否认高血压、糖尿病等内科疾病史，否认传染病史。','已婚已育，哺乳过。月经正常。','否认家族性肿瘤病史。','否认药食过敏史。','左乳癌术后','2008年7月30日在一院作左乳癌改良根治术，病理示浸润性导管癌。ALN0/13(-)。ER-,PR-,neu-，术后CEF化疗2次，化疗期间食欲一般，夜寐梦多，口干欲饮。','2','','','3','2009-2-5 19:23:32','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('7397c561-b4a0-470e-b99a-688c3e27c50d','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902060001','0007821','1','王子安','67','0','0','0','0','中国外运金陵公司','','','上海市曲阳路560弄1705号','','','0','0','乙肝（小三阳）史，高血压史','','','','','患者有乙肝（小三阳）史十余年，GPT反复波动多年。近一月来，右胁作胀，乏力，纳谷一般，大便日行一次，成形，有高血压史五年，现服西药控制。舌质微红，舌苔薄白腻，脉细弦','1','','乙肝，多为疫毒内伏，致肝络不畅，机体免疫功能斧正，则造成一系列组织病理损害，治疗上，张师主张清解疫毒与调控免疫双管齐下，当病人肝功异常时，则以清解疫毒为主，大剂量清热解毒药物，张师喜用垂盆草、六月雪、虎杖、生大黄等，此患者服药一月，肝功即转正常，后每月复查，均在正常范围。','1','2009-2-6 8:17:25','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
