INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('28e78995-b5d3-4ce3-9efb-84f1ff070443','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902060008','0007927','1','陆文','27','0','0','0','0','','','','上海市浦东区峨山路660弄7号604室','','','0','0','乙肝病史','','','','','患者有乙肝病史6年，近因肝功异常（GPT','770','u/L','','黄疸68umol/L）而住市六医院。现肝功恢复已出院。但HBsAg、HBcAb，HBcAb-IgM','均为阳性，二周来常感胃脘不适，溲黄，伴有腰膝酸软，五心烦热，右胁作胀痛，纳可，大便日行2次，质烂。舌质尖红，舌苔薄白，脉象弦细','2','','本病因疫毒内伏，肝失疏泄','，肾失调控，先生临床观察本病200例，提出治疗要从整体着手，主张调控免疫与清除病毒双管齐下。现代医学认为：乙肝使得肝组织损伤，并非肝炎病毒在肝细胞内增殖的直接结果，而是由一系列免疫反应所导致的。因此，调控免疫在乙肝的治疗中显得更为重要。由于肾为先天之本，在人体中所处有重要地位，而且乙癸同源，母子相生，故认为补肾是调控免疫的重要环节。因此，先生在治疗乙肝时常以苁蓉、巴戟、寄生、枸杞、杜仲等补肾扶正来调控免疫，又以蛇舌草、连翘、水牛角粉来清解疫毒，两者并举，共同促进疾病向愈。','1','2009-2-6 13:45:14','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
