INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('乙肝史','','','','','患者有胆囊炎史30年，因反复发作，故于94年3月行胆结石胆囊切除术。术后肝区隐痛持续至今，腰背酸楚，时有胸闷气急，小溲色黄，面色萎黄，形体消瘦，精神疲软，巩膜无黄染，纳差，大便日1次，有乙肝病毒阳性。舌暗红，苔薄白，脉弦细','2','该患者经五个月左右中药内服外敷，取得良好疗效，乙肝表面抗原转阴，肝功能全部恢复正常。','治疗慢性乙肝，丹参、郁金、白花蛇舌草和生黄芪同用，可共奏清热解毒、益气活血、抑制病毒和调控免疫之功。本案合并有胆囊炎，治疗中加入疏肝利胆、清热利湿之品。待胁痛改善，小便转清，胆囊炎症状缓解，则针对肝硬化增加化瘀通络药。表面抗原和肝功能指标正常后加入太子参以扶正，增加机体免疫力。','1','2009-2-6 15:02:25','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
