INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','1','','在颈型的基础上，如果出现咽喉失畅甚者红肿疼痛等表现，是由于椎间盘退变释放的炎症介质刺激咽部造成，同时咽部炎症反应又可刺激相邻的椎间盘从而形成恶性循环。因此对颈咽型的患者治疗时要针对其咽喉的病变进行治疗，使病情逆转。本患者病情复杂，一方面表现出咽喉失畅，红肿等上盛的表现，同时又见四肢少温、小便清冷、频数等肾阳不足的表现，因此治疗上要寒热并用，清上温下。同时要益气和营解肌，达到标本兼治的目的。方中以黄芪、桂枝、白芍益气和营解肌；赤芍、当归、莪术、刘寄奴活血化瘀；板蓝根、大玄参、熟地、赤芍、虎杖根清热利咽；鸡血藤、青风藤祛风止痛；熟附片、川桂枝、山萸肉、巴戟天温补肾阳；台乌药温肾缩尿。共凑益气化瘀，温下清上之功。二诊时，患者咽喉失畅变化不明显，考虑为病情较重，药量太少太轻，故加用山豆根、重楼以增强清热利咽之功，则疗效明显。','1','2009-3-10 21:01:31','aebd52a0-9a7d-4837-a8c7-01860a89724e',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('d1aab261-3165-4281-88cd-a82ee42c6fcc','eec015e3-fecc-425c-9702-68291fead9b5','9f545bdf-e52a-4ece-9654-416392ca9a44','bl200903100016','','1','陈爱芳','19','0','1','0','0','','','','','','','0','1','','','','','','16岁初潮起，月经2-3月来潮一次，经量较少，16岁曾因左卵巢囊肿扭转而行左附件切除术，病理：左卵巢单纯囊肿。此次停经2月，BBT双相，形体渐肿嗜睡乏力，体毛较长，白带不多，大便干结，口干欲饮，舌质略红，舌苔薄，脉细。','1','月经规则，BBT双相，治愈。','','2','2009-3-10 21:51:52','26fd0ae7-13a0-4fca-8579-dd53c5e735b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('524cdc07-238f-4909-838c-ff36da46e188','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','170cccdc-d920-4cc3-906b-dd6816a7b8cb','bl200902110009','09021108','1','叶培英','62','8','1','0','0','','退休','','','','','0','0','否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。否认手术、外伤史。','出生于上海，长期居住于上海；无烟酒不良嗜好，无疫区疫水接触史，预防接种情况不详。','无特殊家族遗传倾向性疾病','否认药物食物过敏史。','急性支气管炎','患者发病之初曾遇恼怒之事，咳嗽反复不愈，X线胸透示:两肺纹理增粗。曾经西药治疗,收效不显。诊见：咳嗽咯痰,咳引两胁胀痛,咳甚气急,痰黄粘稠,面赤,头胀目眩,口苦咽干。舌红、苔黄,脉弦带滑。','1','无明显不适','前贤认为五脏皆能导致咳嗽,其中“肝咳之状,咳则两胁痛,甚则不可转,转而两胁下满”。肝经与肺相连,肝火上犯于肺,木火刑金,肺失肃降,则致咳嗽,胁痛。又口苦、心烦、脉弦均为肝经实火之征象,故以龙胆泻肝汤为主,疏肝解郁,清木泻火而获效。','3','2009-2-11 11:13:50','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('d7f250c7-3de3-499a-985f-3ed926b00709','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','170cccdc-d920-4cc3-906b-dd6816a7b8cb','bl200902110011','09021110','1','邱瑾','32','4','1','0','0','','文员','','','','','0','0','否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。否认手术、外伤史。','出生于上海，长期居住于上海；无疫区疫水接触史，按上海市防疫规定进行预防接种。','无特殊家族遗传倾向性疾病','无','急性支气管炎','2月前初患感冒发热，经治好转，而咳嗽迁延。近月来每晚12时许必剧咳一阵，咯出少量白色粘痰方止，咳甚汗出，面颊发红，其余时间几如常人尽或仅微咳，无其它症状，食寐均好。已用过多种抗目素、止咳药，并已服过参苏饮、止嗽散等化痰宣肺类中药，咳仍同前。脉弦数右寸略浮，舌质色红有弥散小红点，苔薄白欠润。','1','咳止病愈，余无明显不适','患者原由外邪侵袭太阳，治不如法致邪传少阳而化热留恋。子时为少阳胆经经气旺盛之时，本经气盛则能与邪争，欲逐之外出，因经枢不利而邪热循经上腾薰肺，致肺气上逆而为咳，咳则少阳邪热稍欲发越，故移时咳减止。治以小柴胡汤清散和解少阳郁热，辅佐杏仁、枇杷叶、橘红肃肺化痰，标本兼治，故收效甚捷。','3','2009-2-11 14:56:32','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('df0720d5-e0b2-4ef7-9d3e-2032d893acb6','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','170cccdc-d920-4cc3-906b-dd6816a7b8cb','bl200902110012','09021111','1','杨景','','58','1','0','0','0','','退休','','','','','0','0','慢支30余年，否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。05年在全麻下行右中叶肺切除术，否认其他手术、外伤史。','出生于上海，长期居住于上海；无疫区疫水接触史，有吸烟史40多年，约1包/天，现已戒烟十年；预防接种情况不详。','父亲死于肝癌，余无特殊家族遗传倾向性疾病','无','肺癌','患者自20岁起至今一直吸烟,每日1包许,伤害肺系。2002年时发胸痛,咳嗽,咯痰带血,在当地县人民医院用抗生素治疗,时好时发。2003年胸痛、咳嗽、咯血加剧,即到武汉军区总院诊治,胸片检查:外围性肺癌。胸部右侧斜位断层:右中叶新生物可能性大。支气管纤维镜报告:周围性肺癌。遂于2005年6月29日在该院外科住院,在全麻下行右中叶肺切除术。术中见一拳头大包块,内有组织坏死液,经病理切片断为“腺癌”。术后又在该院对症治疗数日,于2005年8月12日出院。出院后月余,胸痛咳嗽又发,即到当地县医院诊治,胸片检查:考虑肺癌复发。用中西药(药名未详)治疗无明显效果,转请先生诊治。刻诊所见:胸痛、咳嗽、痰少,不易咯出,每天痰中带血1~2口,血色暗红,右颈部有一鸡蛋大肿块,红肿疼痛,纳食欠佳,舌淡红苔薄黄,脉细数。大量吸烟,正气伤损,毒热壅肺,欠衍成肺癌也。肺癌手术,正伤更甚,阴液亏耗,而热毒壅肺,肺失肃降。','2','治疗2月余后，胸痛、咳嗽、咯血等症均缓解，随访3年多时间内未有复发,亦未服用其他药物。','肺癌胸痛咳嗽咯血反复发作4年,曾作肺癌切除手术,术后又反复发作,舌质红、苔薄黄,脉细数,是毒热壅肺,肺失宣肃,阴津耗伤使然,故先生用药,始以清热解毒、宣肺化痰、养阴润肺之重剂为治,首见捷效。药后数月,因患者食无禁忌,吸烟不止,胸痛诸证又作,则以清热解毒、宣肺利气、化痰止咳、凉血止血为治,药后缓解。时越3年,劳作过度,痼疾又发,而用清热解毒、宣肺化谈、凉血止血、养阴生津之法奏效。细审此病,前后长达4年,4年之中,胸痛、咳嗽、咯血大发4次,然病机总在毒热壅肺,正伤邪恋之间。是以临证用药,始终谨守病机,以清热解毒,宣肺利气、养阴生津为根本,不以久病虚损而忌用寒凉为拘,而又深合中医辨证论治之道。','3','2009-2-11 15:07:35','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('0da49f9b-24f6-47ba-a526-1f8d79ea629c','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','170cccdc-d920-4cc3-906b-dd6816a7b8cb','bl200902110019','09021117','1','','陈琳','8','8','1','0','0','','学生','','','','','2','1','否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。否认手术、外伤史。','出生于上海，长期居住于上海；无疫区疫水接触史，按上海市防疫规定进行预防接种。','无特殊家族遗传倾向性疾病','否认药物食物过敏史。','急性支气管炎','','反复发作性咳嗽5','年余，','屡治屡发，','渐至1','月数发，','形体瘦弱，','纳少神疲。','2','无咳嗽，精神振，胃纳好。','小儿患者咳嗽，长期肺气逆乱，心血必不和顺，每寓瘀血阻肺之机;','且咳嗽日久，肺气耗伤，气虚津无以化，聚湿生痰，常呈痰瘀互阻之候。又肺朝百脉，','肺气不足，','心营失其滋养，阴血亏虚气无所依，浮动扰肺，','咳嗽频作。临床多表现为咳嗽咯痰，胸闷憋满，或干咳痰少，入夜尤甚，伴心烦少寐，自汗盗汗，形神疲乏，','舌红苔腻，','或舌黯唇紫，','脉细数。其病程绵长，','反复难愈。《丹溪心法&#8226;咳嗽》云：“肺胀而嗽，','或左或右，','不得眠，','此痰挟瘀血碍气而病，','宜养心以流动乎气。”拟用四物汤加味治疗。邵师常以《内外伤辨惑论》之当归补血汤合《圣济总录》双仁丸加味，','方中黄芪补气养心生血，','当归益阴和营活血，','更兼杏仁、桃仁止咳平喘降肺气，活血祛瘀通血脉。若挟痰滞者加二陈三子养亲汤;若兼气阴不足明显者则加生脉散，俾气阴得养，痰瘀得化，','血脉通畅，肺之气机自得宣畅。','3','2009-2-11 18:35:14','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('d731bece-86c3-456d-81db-ef3a5a61f107','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','170cccdc-d920-4cc3-906b-dd6816a7b8cb','bl200902110021','09021117','1','王婷婷','36','2','1','0','0','','工人','','','','','0','0','否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。否认手术、外伤史。','','出生于上海，长期居住于上海；无疫区疫水接触史，预防接种情况不详。','无特殊家族遗传倾向性疾病','否认药物食物过敏史。','急性扁桃体炎','','','始因贪凉，吹空调受寒起病，恶寒，咽痛不适7天，误服清热养阴之剂而症情加重：头痛如劈，恶寒发热，体痛。咽痛，水浆不能下咽，痰涎涌甚，不渴饮。','1','药后诸症霍然而愈，诉无不适。','麻黄附子细辛汤原治太阳少阴两感证，邵师运用本方早已超出此范围，他的经验是，凡“身体不好，素禀不足，一旦感冒，易从少阴寒化(体强者在太阳)','，脉沉细、沉弱，欲寐无神，怕冷，手足发凉，或有头痛如劈，宜用麻辛附子汤或桂甘姜枣麻辛附子汤。本方是开门方，无闭门留寇之患，若开门不用麻、辛、桂，则附子无外驱风寒之力，故开门宜加之。','3','2009-2-11 18:52:27','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('118886d6-d640-4602-8c72-c398626162a7','eec015e3-fecc-425c-9702-68291fead9b5','9f545bdf-e52a-4ece-9654-416392ca9a44','bl200903090012','','1','施凌','29','0','1','0','0','','','','','','','0','0','','','','','','1年未孕，平时月经尚规则，量中，但经前乳痛，头痛。Lmp8.17，量中，伴腹痛。','1','末次月经10.6，证实妊娠。','','2','2009-3-9 17:01:12','26fd0ae7-13a0-4fca-8579-dd53c5e735b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('0effde99-cfbb-47fe-bbe1-1b89cde77a39','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','170cccdc-d920-4cc3-906b-dd6816a7b8cb','bl200902110024','09021117','1','王亮亮','32','4','0','0','0','','工人','','','','','0','0','否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。否认手术、外伤史。','出生于上海，长期居住于上海；无疫区疫水接触史，有吸烟史10多年，约1包/天；预防接种情况不详。','无特殊家族遗传倾向性疾病','否认药物食物过敏史。','支气管肺炎','10天前因感冒引起咳嗽、胸闷，服感冒药无效。3天前咳嗽胸闷加重伴发热、吐黄色脓痰，大便干结，体温39','℃，','服抗生素治疗无效。','1','诸症俱释，未诉明显不适','肺主气，经脉起于中焦胃腑，下络大肠，还循胃口上行，穿隔膜入于肺，又出于喉咙，经腋下循前臂至示指端交手阳明大肠经。由此可见，肺、胃、肠三者经脉相连，一脏有病可影响它脏。临床上自拟宣通汤，方中桔梗味苦性温、入肺经为肺经气分药，善于开提肺气，能宣肺利膈，利咽祛痰止咳;瓜蒌甘寒入肺、胃、大肠经，能散结下气利膈;大黄苦寒，入胃、大肠经，既能通腑清热又能泻火解毒。三药合用对肺、胃、肠病的实热证、湿热郁滞之证每多奏效;若为虚寒之证可在补虚温里的原则下佐以宣通汤，使肺气宣畅，腑气疏通。','3','2009-2-11 19:18:54','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('642f4284-b9ec-4bb4-8b85-6ae3ef2662c2','e3093b46-848d-4cfd-b2d7-1116c5c4fc74','58e95a10-2ba8-4420-bd84-aa822d45f7b6','bl200902110027','09021101','1','赵家良','32','1','1','0','0','','工人','','','','','0','0','否认肝炎、伤寒等传染病病史。否认冠心病、高血压、糖尿病等慢性病病史。否认手术、外伤史。','出生于上海，长期居住于上海；无烟酒等不良嗜好，无疫区疫水接触史，预防接种情况不详。','无特殊家族遗传倾向性疾病','海鲜过敏，否认其他药物食物过敏史。','支气管哮喘','该患者喘憋咳嗽6年。每于季节更替、劳累、紧张、异味刺激后均发作。多方救治效不佳，且逐渐加重。刻下症：喘憋咳嗽，喉中痰鸣，痰多黄粘难咯，夜间喘憋尤甚，不能平卧，咽痒而痛，纳少，眠差，大便干三日一行，小便黄','2','主症缓解，药后3月随访，哮喘未发作。','支气管哮喘属中医“哮证”、“喘证”范畴。其发症急，变化快，有反复发作之特点。该患者证属上焦火盛，痰热壅肺。邵师治以平喘止咳，清热化痰。方宗三拗汤，苇茎汤，三子养亲汤合止嗽散化裁：一方用苏子、莱服子、炙麻黄、五味子、白果平喘；用双花、连翘、公英、冬瓜仁、射干清热；用胆星、百部、前胡、紫苑、兜铃、杷叶等止咳化痰。二方去胆星而易瓜蒌、浙贝化痰；加此沙参、天麦冬养阴止渴；大青叶清热。最后在以上二方的基础上加减化裁而使喘平、咳','止、热清、痰消，','病告痊愈。','3','2009-2-11 19:33:56','75b7eb34-ac67-4496-82bd-18b3edb176b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('4d75f987-0c3f-488c-b6d5-36c52a5d5a9d','eec015e3-fecc-425c-9702-68291fead9b5','9f545bdf-e52a-4ece-9654-416392ca9a44','bl200903090013','','1','刘佩珍','32','0','1','0','0','','','','','','','0','0','','','','','','结婚5年未避孕而未孕。平时月经尚规则，量中，伴腹痛。3年前双卵巢内膜样囊肿手术，术后痛经依旧。Lmp12.27，伴腹痛。','平时烦躁易怒，乳房胀痛。','1','治疗6周期后妊娠','','2','2009-3-9 17:17:03','26fd0ae7-13a0-4fca-8579-dd53c5e735b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('6f10cf5b-063d-49ce-8f92-585b4db4117b','eec015e3-fecc-425c-9702-68291fead9b5','9f545bdf-e52a-4ece-9654-416392ca9a44','bl200903090006','','1','李国芬','34','0','1','0','1','','','','','','','5','0','','','','','','平时月经尚规则，伴腹痛。末次月经7.14，2002.11外院TV-Lap诊断为子宫内膜异位症，盆腔粘连。现结婚7年，性生活正常未避孕而未孕。','1','经上述周期治疗5个周期即妊娠','','2','2009-3-9 14:44:53','26fd0ae7-13a0-4fca-8579-dd53c5e735b7',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('fdfcaf8c-f976-4115-b55f-e76ef08fbaf7','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902030012','0984052','1','王雪金','50','0','0','0','0','','','','淮海中路927弄42号','','','0','0','患者有乙肝、肝硬化病史5年，97年8月因食道静脉曲张破裂出血，在曙光医院行脾切除','','','','','近4月来SGPT波动在70-140，球蛋白及γ-GT均升高，右胁胀闷，纳呆，寐不安，二下肢不浮肿，大便日行2-3次，质烂。检查：巩膜皮肤无黄染，浅表淋巴结不肿大，心肺检查无异常，腹软，肝脾肋下未及，二下肢不浮肿，腹部搏动性浊音阴性。舌质微红，舌苔薄白，脉细弦。','2','','治疗乙肝，先生一贯主张消除乙肝病毒与调控免疫双管齐下，而当乙肝病人出现GPT波动，先生认为要用祛邪泄热之品，以清热解毒、降酶为先，如此患者用垂盆草、六月雪、小蓟草、蛇舌草、紫花地丁等，但因考虑到此患者病程日久，肝已硬化，故又用了滋养敛肝的五味子，蒲公英是兼顾患者有胆囊疾患故。先生改原剂量：急性有热者，用祛邪泄热药物；慢性良好者，用滋养敛肝之物，若持续不降则要用活血化瘀药物。');
