INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('12c577e7-856a-416b-80d3-67fee6883e80','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902040013','14','1','毛娅民','63','0','1','0','4','','退休','','','320000','310103','0','0','糖尿病12年.','否认疫水、疫区接触史。否认烟酒等不良嗜好。','无','无','糖尿病足感染','糖尿病12年，血糖7','mmol/','L左右。去年右足趾麻木，趾端作痛，入夜痛甚，服弥可宝两月。曾用凯时治疗','。近一月，患者右足前半跖紫红加重，伴疼痛，肿胀明显，灼热亦甚。体查：右踝下紫绀，肿胀，趾体肿胀，趾丫白靡，渗出。两足背A、胫后A可及，抬高苍白试验（－）/30秒。','1','随访半年未发。','糖尿病足患者在肌腱变性坏死（筋疽）发病同时常伴有神经损害，一方面表现有筋疽的症状体征，另一方面表现有糖尿病神经损害的改变。如：患足麻木、或觉足部冰冷、或觉肢体灼热、创面多位于足底压迫部位，创面溃疡难愈，创面肉芽浅淡，夹腐，触痛不敏感等。急性阶段，多为湿毒阻络，表现红肿明显，自觉灼热，疼痛较甚，或不明显。治疗常以生地、水牛角、羚羊角粉、生石膏凉血解毒、炙全虫通络止痛、佐以白芍、地骨皮、甘草养阴缓急。慢性迁延阶段，常以气阴两虚，肾气亏损为主，治疗时常温清并用，以附片、炙麻黄、黄芪、鹿角片、桂枝温经通络，以茵陈、山枝、蚤休、垂盆草、黄芩解毒清热。','3','2009-2-4 14:00:13','dd0fba5a-4686-4322-9c50-835a603abdfe',0,'0');
