INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('307943ef-25de-4d1f-bf00-fe13ebd79153','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200902050001','9202','2','傅萍','32','0','1','0','0','','','','','','','0','0','否认其他手术史，否认高血压、糖尿病等内科疾病史，否认传染病史。','已婚已育，哺乳过。月经正常。','否认家族性肿瘤病史。','否认药食过敏史。','右乳癌术后','患者2008年4月4日在仁济医院行右乳癌改良根治术+假体植入术。病理示：浸润性导管癌。ALN0/13(-)，ER(+)，PR(-)，neu（++），术后泰索帝+EP化疗6次（CET）。精神食欲均可，口干。舌暗，苔薄，脉细。','0','','','3','2009-2-5 9:16:18','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('6d21ef42-af87-438e-bcac-ac2418dd9441','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902050005','0003752','1','周秀阳','62','0','1','0','1','','','','普陀区长寿路162号','','','0','0','患者有慢乙肝病史10余年，肝功能基本正常。','','','','','近月来，感肝区疼痛，神疲乏力，去普陀区中心医院就诊，B超示：胆结石、肝囊肿。患者很紧张，夜寐不安，大便日1次。检查：面色少华，巩膜无黄染，心肺(-)，腹软，肝脾胁下未及，二下肢浮肿。舌质微红，舌苔薄腻，脉象细。','2','2月复查B超、CT均未见肝囊肿，查肝功能SGPT正常，两对半中HBsAg转阴.','本症先生认为是湿热内蕴、肝郁气滞、壅塞不通，治拟疏肝理气、活血化瘀、清热解毒之法。方中丹参、郁金、白花蛇舌草、生黄芪四药共奏清热解毒、益气活血、抑制病毒、调控免疫之功；半枝莲、虎杖、苦参、败酱草加重清热解毒之效；白芥子、荷叶化痰消结；八月札、海藻、莪术理气化石；生黄芪、杞子益气养血，增强免疫调控，全方共奏清热解毒、活血化痰、疏肝和络、益气养血、调控免疫之功。故能使HBsAg转阴，肝功能正常，肝囊肿消失。','1','2009-2-5 12:23:01','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('8000ee40-2adc-47d1-bcbe-ba949db5f840','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902050008','0006345','1','陶建华','37','0','0','0','0','','','','怀德路287弄15号201室','','','0','0','慢性乙肝史','','','','','患者95年患乙肝，因GPT和黄疸住杨浦区传染病院治疗，96年以来持续小黄疸不退而担忧，反复求诊效不显，经人介绍来诊。现肝区疼痛，牙龈出血，溲黄。检查：面色黧黑，巩膜黄染。舌质尖红，舌苔薄黄，脉象弦细','2','','患者三年黄疸经先生按瘀血发黄治疗而愈，先生认为久黄要考虑瘀血不能单以清湿热之常规，当总胆红素高于30，先生临床常用赤芍、莪术、胡黄连来活血化瘀退黄，临床往往收到很好的疗效。','1','2009-2-5 13:38:24','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('74cdad45-74ed-42f8-935b-982dbf0007e3','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902050009','17','1','房某','80','0','0','0','1','','退休','','','310000','310103','0','0','糖尿病已10年，高血压病15年。','否认疫水、疫区接触史。嗜好烟酒，目前已戒。','无','无','糖尿病足','糖尿病已10年，血糖已控制，9月前右2趾及右4趾出现水疱，继之水疱破溃，溃疡，坏死，渐收干，现两足趾干痂形成，分界出现，局部少许渗出，分界处稍肿，两足已转暖，舌质红苔薄白，脉细。','2','','在糖尿病足的发病中各种类型常同时互见，但多以某一种类型为主。筋疽的发病最常以血管闭塞缺血型同时发病，奚老将其称为混合型，以肌腱变性坏死（筋疽）为主，为混合一型，治疗时按筋疽处理；以血管闭塞为主为混合二型，治疗时按动脉硬化闭塞症治疗，不可冒然切开。','3','2009-2-5 13:51:10','dd0fba5a-4686-4322-9c50-835a603abdfe',0,'0');
