INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','2','','哮喘证之发，无不责之于痰，痰证之辩，有寒、热之别，痰粘不畅属热，痰热久郁，耗伤肺阴，日久虚实夹杂，此乃阴虚痰饮之证也。治拟攻补兼施，以麦冬、沙参养胃阴，太子参、茯苓益气健脾化痰，射干、麻黄开肺气，柴胡、黄芩清肺热，半夏、泽漆、白前化痰饮降逆气，佐以桔梗、枳壳，理气化痰，丹参、郁金调理气血，诸药合之，使痰热得化，肺气得畅，肺津得复而哮喘久延之症得以平复。','3','2009-1-6 16:13:47','b7d5a22b-4519-43db-92c0-bbeb86040f51',0,'0');
