INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','2','','患者近屡用抗菌素未能获效，曾用激素、氨茶碱静滴虽可临时缓解，但仍迁延不愈，饮邪化热。《金匮》曰“咳嗽上气，喉间水鸡声，射干麻黄汤主之。”近崇仲师方意化裁，运用黄氏平喘定哮方加味治疗，2天起效，一周胸闷显减，2周得以缓解，继因痰热伤阴，转入益气养阴佐以化痰热治之。','3','2009-1-7 23:38:27','b7d5a22b-4519-43db-92c0-bbeb86040f51',0,'0');
