INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('53b9596b-4504-47db-8c3e-d9a1700094cd','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200901130002','018907','1','张某某','27','0','0','0','0','','','','','','','0','0','','','','','','发现HBV-M:（+）一年半，肝功能正常。刻下肝区隐痛，恶心无呕吐，胃纳尚可，失眠，大便日1次。BP:115/80mmmHg。','2','观察九月，肝区不适消失。12.9复查：肝功能正常，HBV-DNA转阴','乙型肝炎病毒脱氧核糖核酸（HBV-DNA）阳性的治疗，为当今治疗乙型肝炎的难点与热点，中西医各抒已见，中西药各逞所长。本案药用犀角（水牛角代）地黄汤与解毒调控免疫方加味而成，清热逐邪、凉血解毒为主题，疏肝和胃、养心安神为相辅，从整体出发，审证论治，显示中医治病特色。','1','2009-1-13 10:52:57','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('ee061481-9b16-460d-b3a5-1f3489f4a25f','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200901130003','2002935875—01','1','李某某','49','0','0','0','0','','','','','','','0','0','慢性乙肝二年','','','','慢性乙型病毒性肝炎病史二年','慢性乙肝二年，持续黄疸不退。精神疲倦，时有齿衄，血压时有升高','2','服药56剂。随访两月，肝功能好转，GPT下降，黄疸渐退。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('','乙型病毒性肝炎，系疫毒内伏、肝气郁结、肝络痹阻所致。急性活动期则表现为邪热鸱张，肝经有热，胆失疏泄，血清丙氨酸氨基转移酶、总胆红素升高，舌质红，脉弦等，此时治宜清解疫毒，凉肝利胆为先，挫其邪势，五脏乃安，待邪热平息，再逐步转向调控免疫，提高免疫功能。','1','2009-1-13 14:24:32','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('74b79c6a-69fd-48ac-87b4-3c25b7aa91c0','7eb3964c-9143-48a9-a450-a40d6b8d80b1','170cccdc-d920-4cc3-906b-dd6816a7b8cb','bl200903100011','sq20090310002','1','刘XX','50','0','0','0','4','','','','','310101','','0','0','既往史无特殊。','','','','','3周前劳累后，突感颈肩背部,咽喉部疼痛，颈部活动和遇寒时症状加重，休息和得热后疼痛减轻。二便尚可，夜寐欠安，胃纳尚可。检查：触诊颈肩背部肌肉紧张度略高，颈椎3~6棘突，棘旁压痛(+),霍氏征（-），扣顶试验（-），咽部充血（+++），臂丛牵拉试验（-），颈椎活动度正常。苔白黄腻，质紫，脉弦细。X线和MRI检查示：颈椎生理曲度变直，C3~6','椎体后缘轻度骨质增生，C3~4,C4~5椎间盘膨出，无脊髓压迫征象。','2','','该患者因工作之因，长期低头伏案工作为此病之源，咽喉部发炎为次病之引。为颈型颈椎病，中医辨证为痹症—淤滞痰火。据现代医学临床研究发现，咽喉部炎症为颈椎病发病原因之一，尸体解剖发现咽喉部的动静脉以及淋巴管同颈椎的椎体和韧带相关。咽喉部的炎症可造成颈椎椎体失稳，椎体周围韧带和脊髓的缺血，以至颈椎病。故选用益气和营清咽汤加减而治之。方中以生黄芪，川桂枝，白芍，生姜，大枣益气和营解肌；赤芍，大川芎，全当归，熟地活血化瘀；板蓝根，大玄参，生熟地，赤芍，粉葛根清热凉血，养阴利咽；制南星，春砂仁化湿祛瘀止痛。二诊时，患者症状基本缓解，故“以攻为主”改为“攻补兼施”，');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('将生黄芪改为炙黄芪,加强补气，增加机体免疫功能，并减轻清热利咽的板蓝根，大玄参等剂量。由于痰湿已化，故去制南星，春砂仁等药。加入鸡血藤，威灵仙，老鹳草取活血通络，补益肝肾之效。三诊时患者症状以解，但颈椎病较为缠绵，故继服药一周，预防复发。');
