INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('f602db8c-64d9-4d50-82ac-52ed60ab3728','868ee43d-9114-44ff-a87c-b6beb55a20d6','8bcf4438-fc6e-4966-839d-9b3b204f29ad','bl200902020003','022798','1','章某某','47','0','0','0','0','','','','','','','0','0','','','','','','肝功能正常。刻下神疲乏力,大便日1次,舌质微红，苔薄白,脉弦细。','2','观察3月，7.18随访肝功能正常。','本例为脂肪性肝炎反应期，有肝功能异常，此时痰瘀互结、疫毒犯肝是病机的关键。治疗重在痰瘀并治、清热解毒。ALT升高多为热毒内盛所致，故以盆草、紫花地丁、败酱草、小蓟草、连翘等药清肝解毒，用决明子、泽泻、丹参、郁金、生山楂化痰活血，兼以陈皮和胃。药后肝功能、血脂均降至正常。','1','2009-2-2 14:54:25','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('91b36ccf-f215-46a3-8d30-4c48bc84a2fd','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902030004','01','1','周治人','58','0','0','0','0','无','工人','','','310000','310105','0','0','高血压病史10余年','否认疫水疫区接触史。','无','无','慢性湿疹','糖尿病足2年，双下经常出现水疱，丘疹等损害，反复发作。本次二月前再发，足背皮疹，趾丫湿糜。几经治疗，现皮肤粗糙，痒甚，伴便秘，两手发麻，四肢偏凉。','3','半年未发','糖尿病足慢性湿疹同时并发周围神经病变，现处于慢性阶段。奚老认为：糖尿病慢性皮肤病变多属气阴两虚，血虚风燥所致，神经病变则为阳气不足，风阻脉络造成，同时都有风湿之邪凝滞肌肤脉络。一般以黄芪、首乌、阿胶益气养血，地骨皮、生熟地养阴润燥，白蒺藜、浮萍、荆防风祛风除湿，白术健脾化湿，生军利下给邪出路，并可散出毒邪，甘草调和诸药。糖尿病皮肤病变多与神经变性密切相关，常与鹿角片、桂枝温阳通络之品同用。','','3','2009-2-3 10:10:06','dd0fba5a-4686-4322-9c50-835a603abdfe',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('a31c991a-6514-4a6d-8e82-b4fe1b6cb365','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902030005','02','1','梁红妹','46','0','1','0','0','无','工人','','','210000','310101','3','0','有糖尿病史5年','否认疫水疫区接触史。','无','无','趾疣','右前跖溃疡一年伴趾丫湿糜，无明显痒痛。有糖尿病史5年，现血糖13.6mmol/L。','1','半年未发','','3','2009-2-3 10:33:36','dd0fba5a-4686-4322-9c50-835a603abdfe',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('a4da363f-6815-4fda-aa9a-26af8d2fe990','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902030008','04','1','马妙福','80','0','0','0','1','','退休','','','320000','310109','2','0','有糖尿病史5年','否认疫水疫区接触史，否认烟酒等不良嗜好。','无','','','两下肢有慢性溃疡二年，溃疡呈疣状，患足肿胀时作，无明显疼痛。体检：右小腿静脉曲张，小腿紫暗灼热，两踝弥漫性颗粒粟状溃疡，干燥，伴广泛色素沉着，左小腿轻度色素沉着。两足趾丫白糜。','1','','糖尿病慢性皮肤病变多属气阴两虚，血虚风燥所致，神经病变则为阳气不足，风阻脉络造成，同时都有风湿之邪凝滞肌肤脉络。','3','2009-2-3 14:01:11','dd0fba5a-4686-4322-9c50-835a603abdfe',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('3b0fd6d4-0f30-479b-89cd-160380d6a956','868ee43d-9114-44ff-a87c-b6beb55a20d6','6d6b267e-5dfa-4993-a2a9-c885df2cd3f5','bl200902030010','716','1','柴逸夫','70','0','0','0','4','','','','武夷路373弄31号','','','0','0','患者有慢性乙肝病史30年，肝硬化史20年，伴腹水3年。曾食道静脉破裂出血五次。','','','','','近月来，肝区疼痛，腹胀，尿少加剧。B超示：肝硬化，脾增厚，少量腹水。用多种利尿剂、补充白蛋白等，腹水均未退净。检查：面色晦滞，巩膜微黄，精神尚可，心肺（-），腹部静脉轻度怒张，肝掌，二下肢轻压迹，腹围79厘米。舌质微红，舌苔薄白，脉细弦。','2','','本例肝硬化腹水，经治一月，腹水消退，腹围从79厘米缩小至74厘米。本例逐水利尿药物只用黑白丑、车前子、猪苓，但却可使腹水消退，原因在于退水不能单纯利水，应与补气之黄芪、活血之丹参、软坚化痰之牡蛎、海藻、软坚养阴之炙必甲、清热解毒之白花蛇舌草、半枝莲等合用，同时又能护了脾胃，使祛邪不碍胃，再加上外敷药的使用，使利水不伤正，确是治疗腹水的一种好方法。','1','2009-2-3 14:26:20','8c320c62-0dc5-4c86-a160-cc634a5e1b7f',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('30100c6d-f776-4266-9a00-c11eeaf8e93a','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902040004','09','1','张某','63','0','0','0','2','无','','无','','220000','310101','2','0','患者有高血压史2年，肾功能不全1年。','否认疫水、疫区接触史，嗜好烟酒，已戒。','无','无','糖尿病足感染','患者有明确糖尿病史五年，平时血糖控制不佳，一月前右足无明显诱因右足趾趾体红肿，灼热，趾体渐肿大（巨趾征），并蔓延跖部，肿胀明显，皮温升高，大趾腹部溃破，分泌物多而臭秽，足背动脉搏动良好。','1','住院两月愈合，随访半年未发。','糖尿病筋疽急性活动期，患足呈现为潮红，灼热，肿胀，伴全身发热。血糖持续不下。筋疽之证，以清解湿毒为第一要诣，常用大黄、黄连、黄柏、苦参、茵陈等清解湿毒治疗；同时，必须及早清创，使邪有去路，减少毒素吸收，则全身高热，高血糖等方可得到缓解。');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('糖尿病足清创不宜太过，最好采取蚕食或啄食的方法，分次清除腐腱为佳。其次清创后必须采用中西药外洗，以达到清热解毒、祛腐抗炎作用。一般经治疗1~2周即可使患者病情好转。因此，1~2周也可作为判断病情好转与否，或是否需要截肢治疗的阶段。');
