INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('30bb7d8c-164d-4a57-be48-35235eca2cb6','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902040008','12','1','赵某','66','0','1','0','1','无','退休','无','','310000','310107','0','0','糖尿病史10年','否认疫水、疫区接触史。否认烟酒等不良嗜好。','无','无','糖尿病足感染','患者左4趾因修剪趾甲致甲缘受损，继之甲沟外侧出现紫红，肿胀，向左足前跖蔓延，皮温灼热。现左足肿胀较重，无发热，纳少，便秘2、3日一行。体检：左4趾紫绀肿胀连及左足背，红肿、灼热明显，趾根部溃破，肌腱外露，连及足掌跖部；两下肢动脉博动存在，抬高苍白试验双足不明显；舌尖红，苔黄腻，脉滑数。','1','经内治外用，1月余疮面愈合','奚氏发现糖尿病足坏疽中，截至率最高，并发全身症状最严重的类型为肌腱变性坏死症（筋疽），若能及早鉴别诊断，正确处理，绝大多数能免于高位截肢致残。经国内外有关文献检索，奚氏筋疽病理类型至今尚未见报道。该病症辨证要点为湿郁筋损，属阳证、热证。本病属阳证范畴，因病发筋腱，预后凶险，故以“筋疽”名之。本病需与脱疽鉴别。脱疽系血管病变缺血性坏死，属阴证，患足厥凉，肢端苍白或紫黑，间歇跛行，静息痛剧烈，缺阳，脉消失，呈干性坏死。脱疽与筋疽有着本质的不同，故治疗也有差异。','3','2009-2-4 13:10:02','dd0fba5a-4686-4322-9c50-835a603abdfe',0,'1');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('2d3833a1-b8ca-44e1-9e80-2bc148c1c646','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200902040009','0066','1','刘烨','69','0','1','0','0','','','','','','','0','0','既往有高血压、胆囊炎病史，否认其他内科疾病史。否认传染病史，否认手术史。','已婚已育，哺乳过。已绝经。','否认家族性遗传病史，否认肿瘤家族史。','否认药食过敏史。','右乳癌术后','患者2008年4月3日在肿瘤医院行右乳癌保乳手术，病理示浸润性导管癌。ALN0/11（-），ER++,PR+,neu（-），术后未化疗，放疗30次。口服来曲唑。神疲乏力，胃脘作胀，夜寐安好，大便溏薄，日行2-3次。','2','','','3','2009-2-4 13:20:07','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('0470dbc0-9d48-4990-9462-91a1933e2410','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902040011','13','1','李某','78','0','0','0','1','无','退休','无','','310000','310105','0','0','糖尿病史20年，冠心病史5年。','否认疫水、疫区接触史，嗜好烟酒，目前已戒。','无','无','糖尿病足感染','患者有糖尿病史20年，平时双足麻木，感觉迟钝，近一月来无明显诱因出现右足红肿、疼痛，呈进行性加剧，近一周来出现高热，达39.5℃左右，血糖也高，在外院经抗感染、降血糖、活血化瘀治疗，症状未得到控制。并日渐加重。体检：右足足趾，足背漫肿，呈面包状，皮肤潮红、灼热、变薄，局部溃破，患足臭秽，伴脓性分泌物，足背动脉、胫后动脉搏动（±），舌红苔黄腻、脉滑数。','2','患足经清创处理半月后，右足红肿减退，疼痛缓解，疮面肉芽鲜红，基本无臭秽，','糖尿病筋疽急性活动期，以清解湿毒为第一要诣，常用大黄、黄连、黄柏、苦参、茵陈等清解湿毒治疗；同时配合清创治疗，可使邪去病减，逆转病情。但是，待疾病稳定后，即糖尿病足筋疽稳定期，则以益气养阴，除消养筋治疗为主，多使用黄芪、首乌、菝葜、葛根、白术、麦冬、五味子等药物。在过度阶段，也可养清并用，如同本案，可或佳效。同时必须注重足部保养，避免外伤。与饮食、体疗、保健等结合，长期预防，可降低复发率。','3','2009-2-4 13:49:20','dd0fba5a-4686-4322-9c50-835a603abdfe',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('c622b966-2e0a-46b5-acf9-77abc0400c1b','c826724a-65c8-430f-bbd1-c193ddddea66','a47b7b44-668a-44f9-921f-0579e643f2a5','bl200902040014','15','1','吴某','78','0','1','0','2','','退休','','','330000','310109','1','0','有糖尿病史20年。','否认疫水、疫区接触史，否认烟酒等不良嗜好。','无','无','糖尿病足溃疡','有糖尿病史20年，3月前因穿鞋不慎致有足外侧溃破，久治不愈。前阶段在本院门诊治疗，经服陈兰花颗粒、三黄消炎颗粒后，右外侧红肿痛已退，但疮面夹腐未去，患足麻木。体检：右足溃面直径1.5cm，创面肉芽浅淡，夹腐，触痛不敏感，周缘肿退，舌质红苔薄，脉细。','','1','随访三个月未发。','糖尿病足患者在肌腱变性坏死（筋疽）发病同时常伴有神经损害，一方面表现有筋疽的症状体征，另一方面表现有糖尿病神经损害的改变。如：患足麻木、或觉足部冰冷、或觉肢体灼热、创面多位于足底压迫部位，创面溃疡难愈，创面肉芽浅淡，夹腐，触痛不敏感等。急性阶段，多为湿毒阻络，表现红肿明显，自觉灼热，疼痛较甚，或不明显。治疗常以生地、水牛角、羚羊角粉、生石膏凉血解毒、炙全虫通络止痛、佐以白芍、地骨皮、甘草养阴缓急。慢性迁延阶段，常以气阴两虚，肾气亏损为主，治疗时常温清并用，以附片、炙麻黄、黄芪、鹿角片、桂枝温经通络，以茵陈、山枝、蚤休、垂盆草、黄芩解毒清热。','3','2009-2-4 14:09:13','dd0fba5a-4686-4322-9c50-835a603abdfe',0,'0');
INSERT INTO d_case (CASEid, SPETid, DTMPid, code, outpatientCode, caseKind, name, age, month, genger, nationality, personSort, afflication, job, tel, address, birthplace, liveplace, education, marriage, ohistory, phistory, fhistory, allergy, extraMed, mresult, vresult, illustration, state, createDay, optrid, hasFile, preState) VALUES ('c67c063c-9cb1-450f-920c-3b9ded321771','d91e5826-1cc0-4ceb-9dba-8e1a086f2a69','87c4b64d-d043-4cbf-8303-a5215a885d42','bl200902050018','4742','2','周竞燕','52','0','1','0','0','','','','','','','0','0','','已婚已育，哺乳过。月经正常。','否认家族性肿瘤病史。','否认药食过敏史。','左乳癌术后','患者2008年12月11日在仁济医院作左乳癌改良根治术。病理示浸润性导管癌。ALN0/2(-)，ER-.PR-，neu-。20年前因尿素高作肾移植手术，肿瘤医院会诊建议不作化疗。精神食欲尚可，夜寐不易入睡，口干欲饮。','2','','','3','2009-2-5 20:03:12','48b0a4a4-c9a3-4527-9000-b4ddb162e4b8',0,'0');
