INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'590c2344-1e26-408a-b078-79f35385c3b3', N'01', N'中医妇科', N'-1', 1, 1, N'', N'', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'10e5c924-b9f4-4a94-be90-b480b7049a34', N'0101', N'月经先期', N'01', 2, 1, N'', N'月经先期系由气虚不固或热扰冲任，血海不宁，导致月经周期提前7天以上，甚或半月余一行的月经病。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'df820837-1a76-4fa2-b83c-75ad42d9f24b', N'0102', N'月经后期', N'01', 2, 1, N'', N'月经后期系由营血亏损、阳虚、寒凝、气滞、冲任不畅导致月经延后7天以上而至，甚或40～50天一行的月经病。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fc65c84a-0d1e-4f50-945f-3c967f306498', N'0103', N'月经先后无定期', N'01', 2, 1, N'', N'月经先后无定期系由肝郁肾虚，气血失调导致血海蓄溢失常，出现月经周期提前或延后7天以上而至的月经病。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7f5d1b84-f2c9-4ae7-97f4-69f9f1a8a58f', N'0104', N'月经过多', N'01', 2, 1, N'', N'月经过多系由气虚、血热使冲任不固，或因瘀血内阻，血不归经，致月经量较正常明显增多，而周期基本正常的月经病。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6e99def8-813f-4fc1-bce7-6afb2f9b4884', N'0105', N'月经过少', N'01', 2, 1, N'', N'月经过少系由精血衰少，血海不盈，或痰阻瘀滞，血行不畅，致使经期虽准，但经量较正常明显减少，或经期不足2天经量少的月经病。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', N'0106', N'经期延长', N'01', 2, 1, N'', N'经期延长系阴虚内热、瘀阻冲任、血不归经致使经期虽基本正常，但行经时间超过7天，甚至淋漓半月方净的月经病。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'be06429d-94ad-41c8-83a8-45e1571bcf93', N'0107', N'痛经', N'01', 2, 1, N'', N'痛经系由情志所伤，六淫为害，导致冲任受阻；或因素体不足，胞宫失于濡养，导致经期或经行前后呈周期性小腹疼痛的月经病。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', N'0108', N'闭经', N'01', 2, 1, N'', N'闭经系因血枯精亏或气滞痰阻，导致女子年逾18周岁月经未至，或正常月经周期建立后，又停经3个月以上的月经病。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'764def47-5f2b-4be8-bbb8-df84901861e2', N'0109', N'崩漏', N'01', 2, 1, N'', N'崩漏因血热、脾虚、肾虚、血瘀等导致冲任损伤，不能约制经血，非时而下。量多如注者为崩，量少淋漓不尽者为漏，两者常交替出现。多见于子宫功能性出血。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c857d734-2de2-434a-97db-7b92a7161419', N'0110', N'经行乳房胀满', N'01', 2, 1, N'', N'经行乳房胀痛是由肝郁气滞脉络不畅，或肝肾阴虚，脉络失养，以致经前、经后或经行期间出现乳房胀痛或乳头胀痛作痒，甚至不能触衣的病变。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b8edee1f-1278-43d4-8bd0-555fcddd4c90', N'0111', N'经行发热', N'01', 2, 1, N'', N'经行发热是因素体虚弱，或瘀热内阻等，导致气血营卫失调，每值经期或行经前后，出现以发热（体温一般在37.5°C以上）为主要表现的病变。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0f0e36fd-6418-401b-bddb-469f1650c519', N'0112', N'经行头痛', N'01', 2, 1, N'', N'经行头痛系因素体血虚，血不上荣，或情志内伤，瘀血内阻，阳络不通，导致每于经期或行经前后出现以头痛为主要症状的病变。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a880fa7f-14ba-4f8e-8af2-6d21efa350d9', N'0113', N'经行身痛', N'01', 2, 1, N'', N'经行身痛素体血虚、寒湿留滞，经脉失养，致经期或行经前后出现肢体疼痛周期性发作的病变。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'10434b3e-4787-48ad-8882-e43036a625d6', N'0114', N'经行泄泻', N'01', 2, 1, N'', N'经行泄泻是指素体脾肾虚弱，致每逢经行期间大使溏薄，甚或清稀如水，日解数次的病变。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ec09bbef-a2fa-46cb-9088-f30e9edbba3d', N'0115', N'经行吐衄', N'01', 2, 1, N'', N'经行吐衄主要是由肝火上逆，肺胃燥热，迫血妄行，致每值经期或经行前后，有规律出现吐血或衄血，并伴有经量减少或不行的病变。又称"倒经"或"逆经"。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5bf25f9f-dc81-48d4-891b-247de309b0a9', N'0116', N'经行口糜', N'01', 2, 1, N'', N'经行口糜是指阴虚火旺，心火上炎，或胃热熏蒸，致每值经期或经行前后，口舌糜烂，呈周期性发作的病变。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f7eb6ea6-5ee8-4f89-bd1c-45d4c44964b9', N'0117', N'绝经前后诸症', N'01', 2, 1, N'', N'绝经前后诸症是肾气渐衰，天癸将竭，阴阳失衡而致妇女在绝经前后出现月经紊乱，烘热汗出，潮热面红，情志异常等多种症状。相当于更年期综合症。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'76c17650-3820-4cda-864a-368b4fea7005', N'0118', N'带下病', N'01', 2, 1, N'', N'带下病系由湿邪影响冲任，带脉失约，任脉失固，导致阴道分泌物量多或色、质、气味的异常改变。多见于阴道、宫颈等炎症性疾病。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5562ed3a-fe6d-4937-b099-ea86c696b318', N'0119', N'妊娠恶阻', N'01', 2, 1, N'', N'妊娠恶阻是指妊娠早期冲脉之气上逆，胃失和降，出现呕吐厌食，或食入即吐的疾病。相当于妊娠剧吐。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', N'0120', N'胎漏、胎动不安', N'01', 2, 1, N'', N'由于母体与胎儿两方面因素，导致冲任气血不调，胎元失固。在妊娠期阴道少量出血，时下时止者，称"胎漏"；若妊娠期间仅有腰酸，腹部胀坠作痛，或伴有少量出血者，称"胎动不安"。相当于先兆流产。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ad320557-e18e-4922-ae91-fc4d0b2665c6', N'0121', N'滑胎', N'01', 2, 1, N'', N'滑胎是由于禀质虚弱，肾虚冲任不固，而致怀孕后出现自然堕胎，或小产连续发生3次以上者，称为滑胎。相当于习惯性流产。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8ec1fcde-fa82-4594-82c3-d9e8adb733a5', N'0122', N'子肿', N'01', 2, 1, N'', N'妇女怀孕期间由于素体脾肾阳虚，胎气壅阻，水湿内滞，而出现肢体面目发生肿胀者，称为子肿。相当于妊娠水肿。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'718bb78d-ab3f-45f7-9982-3a844170e5fd', N'0123', N'子淋', N'01', 2, 1, N'', N'妇女怀孕期间，由于肾虚或膀胱湿热，气化失常，出现尿频尿急，并感灼热涩痛等临床表现者，称为子淋。相当于妊娠合并尿路感染。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5d99527d-cc0b-44ec-9426-2eb8b43193fb', N'0124', N'转胞', N'01', 2, 1, N'', N'孕妇素体虚弱导致胎气下坠，迫及膀胱，以致水道不利，而出现小便不通，小腹拘急胀痛，心烦不得卧者，称为转胞。相当于妊娠合并小便不通。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5db29490-3003-4348-90b6-3a6593ea6e58', N'0125', N'产后血崩', N'01', 2, 1, N'', N'产后血崩是由于产妇素体气血虚弱，或瘀阻、血热，导致血不归经，阴道出血量达500毫升以上者。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7b362e26-78c1-40be-88b1-d11d6e723b3a', N'0126', N'产后恶露不绝', N'01', 2, 1, N'', N'产后恶露不绝是由于产时劳伤经脉，导致气血运行失常，而致产后3周以上，仍有阴道出血者。相当于子宫复旧不良，子宫轻度感染，胎盘、胎膜残留。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fdb562c7-9f9c-4c62-a5be-57f5afaca38f', N'0127', N'产后大便难', N'01', 2, 1, N'', N'由于分娩时失血伤津，肠道失于儒润，出现大使艰涩，数日不解，或排便时干燥疼痛，难以排出，称为产后大便难。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cfad8981-d0c3-4189-b920-ddc9e4650047', N'0128', N'产后感染发热', N'01', 2, 1, N'', N'由于产时气血骤虚，阳气浮越，感染邪毒，正邪交争，而出现产后10天内发热，伴有腹痛及阴道分泌物的色、质、量、气味异常变化，称产后感染发热。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'834deeb6-c861-4e0b-a56f-466dc1c697c6', N'0129', N'产后自汗、盗汗', N'01', 2, 1, N'', N'产后气血较虚，胰理不密，卫阳不固，出现涔涔汗出，持续不止，动则益甚者，称"产后自汗"。阴虚内热，浮阳不敛而睡后汗出湿衣，醒来即止者，称"产后盗汗"。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'23082329-5388-4482-b911-56bc771ba054', N'0130', N'产后缺乳', N'01', 2, 1, N'', N'产后缺乳系因气血不足，不能生乳，或肝郁气滞，乳脉垂塞，导致产妇在哺乳期乳汁甚少或全无，亦称产后乳汁不行。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ecbce90b-d6a1-406a-9a45-be51fd4022e0', N'0131', N'产后乳汁自出', N'01', 2, 1, N'', N'产后乳汁自出是由于气虚不能固摄，或肝火内积，迫乳汁外溢，导致产后乳汁未经婴儿吮吸而不断自然流出者。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1ed54efd-c53d-4514-aa8f-7ed7fb06a27b', N'0132', N'阴挺', N'01', 2, 1, N'', N'阴挺系指肾虚气弱，失于固摄，出现子宫位置下垂，甚则脱出阴户之外的病变。相当于子宫脱垂。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'90b9fc26-8181-4b63-ba42-891943413edb', N'0133', N'不孕症', N'01', 2, 1, N'', N'育龄妇女由于肾虚、肝郁、痰湿、血瘀等原因，导致冲任、子宫功能失调，结婚1年以上，或曾孕育后1年以上，夫妇同居，配偶生殖功能正常，而不受孕者，称为不孕症。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'81e92c86-2b25-4f3c-a4a7-384ae71dda05', N'02', N'中医耳鼻喉科', N'-1', 1, 1, N'', N'', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', N'0201', N'耳胀、耳闭', N'02', 2, 1, N'', N'耳胀、耳闭是因邪犯耳窍，气血失畅所致，以耳内胀闷闭塞感为特征的耳病。相当于急、慢性卡他性中耳炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'04fe2d0e-5d9c-4e9e-ba49-740225d9177d', N'0202', N'脓耳', N'02', 2, 1, N'', N'脓耳是因邪热上犯耳窍，血腐化脓所致，以鼓膜穿孔、耳内流脓为特征的疾病。相当于化脓性中耳炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', N'0203', N'暴聋', N'02', 2, 1, N'', N'暴聋是因邪犯耳窍、起病迅速的感音神经性聋，主要指特发性暴聋（突发性聋）。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c5ede233-cf57-4a9b-b16c-861e08ee0ece', N'0204', N'久聋', N'02', 2, 1, N'', N'久聋是因脏腑亏虚，耳窍失养，或经脉气滞血瘀所致，以长期听力下降为特征的感音神经性聋。包括药物中毒性聋、老年性聋等。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', N'0205', N'耳眩晕', N'02', 2, 1, N'', N'耳眩晕是因邪犯内耳，或脏腑虚弱，内耳失养所致，以发作性、旋转性眩晕为特征的疾病。相当于耳源性眩晕（美尼尔氏病）。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'66522337-9b13-473d-a8c9-1c8ea4996f59', N'0206', N'伤风鼻塞', N'02', 2, 1, N'', N'伤风鼻塞是指风邪犯及鼻窍所致，以鼻塞、流涕为特征的急性鼻病。相当于急性鼻炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2609d530-b1ed-47ae-ac12-3846c9a55e8e', N'0207', N'鼻窒', N'02', 2, 1, N'', N'鼻窒是因脏腑虚弱，邪滞鼻窍所致，以长期鼻塞、流涕为特征的慢性鼻病。主要指慢性鼻炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', N'0208', N'鼻槁', N'02', 2, 1, N'', N'鼻槁是因津液不能上濡鼻窍所致，以鼻中干燥、粘膜萎缩为特征的慢性鼻病。主要指萎缩性鼻炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'594eda87-98a4-46f1-8e27-2f1ca1f3b86a', N'0209', N'鼻鼽', N'02', 2, 1, N'', N'鼻鼽是因禀质特异，邪犯鼻窍所致，以阵发性鼻痒、连续喷嚏为特征的疾病。相当于变应性鼻炎（过敏性鼻炎）。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6c56539b-b855-43d5-8ed9-e6197828f2d0', N'0210', N'鼻渊', N'02', 2, 1, N'', N'鼻渊是因邪犯鼻窦，窦内湿热蕴积，酿成痰浊所致，以鼻流浊涕量多为特征的鼻病。主要指急、慢性鼻窦炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e79686d3-6a61-4142-9544-1ab5a7b35d51', N'0211', N'鼻衄', N'02', 2, 1, N'', N'鼻衄是由肺热上蒸，逼血逆行，或燥气外袭所致，以鼻腔出血为主要症状的病证。是耳鼻喉科常见急症。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ac70791e-522b-4363-a46c-a5044568a13e', N'0212', N'急喉痹', N'02', 2, 1, N'', N'急喉痹是国外邪客于咽部所致，以咽痛、咽粘膜肿胀为特征的急性咽病。主要指急性咽炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'523762f2-424f-4c00-87be-ef8e14d657f7', N'0213', N'慢喉痹', N'02', 2, 1, N'', N'慢喉痹是因脏腑虚弱，咽部失养，或邪滞于咽所致，以咽部不适，咽粘膜肿胀或萎缩为特征的慢性咽病。主要指慢性咽炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', N'0214', N'乳蛾', N'02', 2, 1, N'', N'乳蛾是因邪客喉核（扁桃体），核内血肉腐败所致，以咽痛、喉核红肿或化脓为特征的咽部疾病。分急乳蛾和慢乳蛾两类，相当于急、慢性扁桃体炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', N'0215', N'喉关痈', N'02', 2, 1, N'', N'喉关痈是因邪客乳蛾，热毒蔓及周围所致，以咽痛剧烈、喉核（扁桃体）旁红肿隆起为特征的咽病。相当于扁桃体周围脓肿。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'51c6ef80-f206-46bb-bd37-d53242775eda', N'0216', N'急喉喑', N'02', 2, 1, N'', N'急喉喑是因邪犯于喉所致，以声音嘶哑、声带水肿为特征的急性喉病。主要指急性喉炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3b4be459-6d84-42c2-83ae-4303aa5ac1c4', N'0217', N'慢喉喑', N'02', 2, 1, N'', N'慢喉喑是因脏腑虚弱，声门失养，或气血瘀滞，痰浊凝聚于声门所致，以长期声音嘶哑为特征的慢性喉病。主要指慢性喉炎，亦包括声带小结、声带息肉。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', N'0218', N'梅核气', N'02', 2, 1, N'', N'梅核气是因情志波动，气机不畅所致，以咽中似有梅核阻塞感为特征的疾病。相当于咽神经官能症。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b8d2a791-9d7c-4747-a4f1-fc04cb6259b0', N'0219', N'牙宣', N'02', 2, 1, N'', N'牙宣是因邪犯牙床，或脏腑虚损，龈肉失养所致，以龈肉肿胀或萎缩，牙根宣露，龈齿间渗出脓血为特征的疾病。主要指牙周炎，亦包括牙龈炎等其他牙周组织病。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'14a55090-ad4c-422c-ba4f-99e111379560', N'0220', N'口疮', N'02', 2, 1, N'', N'口疮是口腔粘膜受邪热蒸灼，或失于气血荣养所致，以局部出现小溃疡，灼热疼痛为特征的口腔粘膜病。包括复发性口疮和口疮性口炎。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
