INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'75201b21-dd7c-4727-a6b6-789aa63086ad', N'0312', N'肛门直肠狭窄', N'03', 2, 1, N'', N'肛门直肠狭窄指任何原因所致肛门、直肠腔道变窄，并导致排便困难。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'939be377-c1d1-45fa-980c-af038e2dbc5f', N'0313', N'肛门失禁', N'03', 2, 1, N'', N'肛门失禁指肛门完全或不完全失去控制排便的能力。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b2cf1c63-313d-4fe2-aed0-16e5747a38e3', N'0314', N'直肠前突', N'03', 2, 1, N'', N'直肠前突系抬直肠前下段向阴道方向突出，形成一个囊腔，尤在努挣排便时为甚，致粪便滞留，出现排便困难。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'abb6ff75-7a70-46a9-ad1f-baf3ac64143d', N'0315', N'盆底肌痉挛综合征', N'03', 2, 1, N'', N'盆底肌痉挛综合征系由于盆底肌痉挛（反常收缩），出现以排便困难，肛门不适为主要症状的疾病。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e4982182-71b4-4fd5-8b9a-fbf02d947f0d', N'0316', N'肛门湿疡', N'03', 2, 1, N'', N'肛门湿疡是一种常见的非传染性皮肤病，病变多局限于肛门周围皮肤。相当于肛门湿疹。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'22c16f97-f663-46e4-b52f-21713644876f', N'0317', N'锁肛痔', N'03', 2, 1, N'', N'锁肛痔系发生在肛管直肠的恶性肿瘤。相当于肛管直肠癌。', CAST(0x00009A79010A6544 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2602967d-03b5-411a-a97d-e6578288a042', N'04', N'中医皮肤科', N'-1', 1, 1, N'', N'', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0204b76e-0e41-4375-b449-efece3765811', N'0401', N'黄水疮', N'04', 2, 1, N'', N'黄水疮是一种因毒热郁于皮毛，以流黄水、浸淫成片为特征的皮肤传染病。相当于脓疱疮。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e763afd8-7fb2-4275-ab07-ae811ac6aef8', N'0402', N'流皮漏', N'04', 2, 1, N'', N'流皮漏又称鸦啖疮，是一种皮肤损害为深红色浸润斑块，表面有棕红色结节，愈后形成萎缩性瘢痕的皮肤病。相当于寻常狼疮。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7ff56278-7880-4610-ba61-d6245b863efa', N'0403', N'腓踹疽', N'04', 2, 1, N'', N'腓踹疽是发生在小腿肚，以暗红色硬结，可溃烂而久不收口为特征的疮疡，类似于硬红斑。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3b8160c9-f835-400f-b718-67160197e4e6', N'0404', N'肥疮', N'04', 2, 1, N'', N'肥疮是多发生在头部的一种癣，以结黄痂、发秃落为特征。相当于黄癣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1a12d225-9f82-4bf5-a7c2-28a7a120f953', N'0405', N'白秃疮', N'04', 2, 1, N'', N'白秃疮是多发生在头部的一种癣，以脱白屑，久则毛发折断为特征。相当于之白癣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'72435478-e2f2-432c-b315-96f9427ea871', N'0406', N'圆癣', N'04', 2, 1, N'', N'圆癣是发生在平滑皮肤的一种癣，以疹如钱币有匡廓，瘙痒为特征。相当于体癣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8cc7202b-4528-4f00-a915-8509b662022b', N'0407', N'鹅掌风', N'04', 2, 1, N'', N'鹅掌风是一种发生在手掌部的皮肤病，、以皮肤粗糙、变厚、于裂为特征。相当于手癣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'79a8c923-fe03-4815-87b5-fa202cce7477', N'0408', N'脚湿气', N'04', 2, 1, N'', N'脚湿气是发生在足部的皮肤病，以足丫白斑湿烂或足跖、趾间起水疱为特征。相当于足癣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'498f67ed-1f95-482f-83f8-7dd3fed78993', N'0409', N'灰指（趾）甲', N'04', 2, 1, N'', N'灰指（趾）甲多由于鹅掌风或脚湿气日久延及爪甲，以指（趾）甲增厚色灰，失去光泽为特征。相当于甲癣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'19f2c756-1d11-44d1-bf88-0ed2e178e756', N'0410', N'疥疮', N'04', 2, 1, N'', N'疥疮是由疥虫侵袭皮肤所致。以指缝、手腕、脐周、阴股部等皮肤发生水疱、隧道，夜间痒甚为特征的传染件皮肤病。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a0109f7c-f5c5-4f43-b1c1-4c3b1abc8ef1', N'0411', N'蛇串疮', N'04', 2, 1, N'', N'蛇串疮是因肝脾内蕴湿热，兼感邪毒所致。以成簇水疮沿身体一侧呈带状分布，排列宛如蛇行，且疼痛剧烈为特征的皮肤病。相当于带状疱疹。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'30fe8813-318d-440b-b497-2c66861d8e21', N'0412', N'热疮', N'04', 2, 1, N'', N'热疮多因内热炽盛所致。以皮肤粘膜交界处发生成群水疱，痒痛相兼为特征。相当于单纯疱疹。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f8f86121-e8e3-418f-9810-1b4d698fcfb3', N'0413', N'扁瘊', N'04', 2, 1, N'', N'扁瘊是一种多发生于颜面或手背的米粒大小、扁平，稍高起皮面的小疣。相当于扁平疣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'566f6d3b-21c8-47da-b194-ebc4fb7b7921', N'0414', N'疣目', N'04', 2, 1, N'', N'疣目为好发于手、足、头皮，大如黄豆，粗糙而坚硬，表面呈刺状，相当于寻常疣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'727a9939-d242-4406-a12a-5a7d686c4e18', N'0415', N'鼠乳', N'04', 2, 1, N'', N'鼠乳多生在躯于、四肢呈绿豆至黄豆大小，半球形隆起，疹中央有脐窝，形如鼠乳。相当干传染性软疣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'82630884-dfae-422c-af1c-8cd4419071b7', N'0416', N'鸡眼', N'04', 2, 1, N'', N'鸡眼是因足部（亦偶见于手部）长期受挤压或压迫所致，其根陷肉里，顶起硬结，形似鸡眼的皮肤病。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a85fb0dd-d808-480a-b9ed-bae0f84832e6', N'0417', N'胼胝', N'04', 2, 1, N'', N'胼胝是指因手足久受摩擦压迫所致，其特征为皮厚涩而圆短如茧。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3e6f90a6-be93-4726-b98b-d6a39c709c67', N'0418', N'湿疮', N'04', 2, 1, N'', N'湿疮是由禀性不耐，风湿热邪客于肌肤而成。皮疹呈多种形态，发无定位，易于湿烂流津的瘙痒性渗出性皮肤病。相当于湿疹。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ad6dcbf2-d476-4b8c-8c8f-c00bde373aee', N'0419', N'四弯风', N'04', 2, 1, N'', N'四弯风是因禀性不耐或脾虚湿盛所致。好发于四肢弯曲处，以皮肤干燥肥厚，抓破流津，瘙痒无度为特征的一种慢性皮肤病。相当于异位性皮炎。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a32700f2-b85c-49d0-b986-9d356fc642c3', N'0420', N'瘾疹', N'04', 2, 1, N'', N'瘾疹又称bei(倍）lei （蕾）。其特征为身体瘙痒，搔之出现红斑隆起，形如豆瓣，堆累成片，发无定处，忽隐忽现，退后不留痕迹。相当于荨麻疹。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ca50967f-9424-4e08-bda6-47338520d3b0', N'0421', N'土风疮', N'04', 2, 1, N'', N'土风疮指因肌腠虚疏，复感风邪所致，以状如风疹而头破，乍发乍瘥为特征的皮肤病。类似于丘疹性荨麻疹。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'57ba211e-598e-4b2d-99f9-cf2c63271bdc', N'0422', N'药毒', N'04', 2, 1, N'', N'药毒是指因禀赋不耐，药毒内侵所致的皮肤发疹。相当于药物性皮炎。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6688fcdb-4463-4c15-b8f4-069d2cfdea35', N'0423', N'天疱疮', N'04', 2, 1, N'', N'天疱疮是由心火脾湿，兼感风热暑湿之邪，薰蒸肌肤而成。以皮肤起燎浆水疱，小如芡实，大如棋子，皮破流津，缠绵不愈的大疱性皮肤病。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a18c45c7-6a82-4cb6-97d4-e57c29234351', N'0424', N'火赤疮', N'04', 2, 1, N'', N'火赤疮是以水疱成群，多呈环状排列，并伴有红斑，瘙痒难忍为特征的大疱性皮肤病。相似于疱疹样皮炎。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7459fbd6-4a60-4da1-bd51-f532edb3ab40', N'0425', N'登豆疮', N'04', 2, 1, N'', N'登豆疮是因表虚里实，毒热入于营血所致。以皮肤大片潮红，出现群集帽针头至粟粒大小脓疱为特征的皮肤病，相似于疱疹样脓疱病。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ca9673be-10d9-4e0a-8b1d-c08f16ed7af8', N'0426', N'红蝴蝶疮', N'04', 2, 1, N'', N'红蝴蝶疮是一种面部常发生状似蝴蝶形之红斑，并可伴有关节疼痛、脏腑损伤等全身病变的系统性疾病。相当于红斑性狼疮。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ddef2ce8-a9dd-4f36-aadf-37d00cdc9d46', N'0427', N'皮痹', N'04', 2, 1, N'', N'皮痹是以皮肤肿胀、硬化，后期发生萎缩的皮肤病。可局限于某一部位，亦可累及全身。相似硬皮病。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ef9fb9d7-50f7-450e-9d78-e28e30b03d96', N'0428', N'狐惑病', N'04', 2, 1, N'', N'狐惑病是一种以口咽、阴部蚀烂，目赤如鸠眼为特征的综合性皮肤病。类似于白塞氏综合证。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e111efd8-506c-4d38-8d8f-9c0d1f49409b', N'0429', N'猫眼疮', N'04', 2, 1, N'', N'猫眼疮因其疹形如猫之眼而得名。多发于春秋季，好发于手足，可累及口腔及阴部。相当于多形性红斑。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1b49b96f-a69c-4477-b4b9-48ac0576b0e5', N'0430', N'瓜藤缠', N'04', 2, 1, N'', N'瓜藤缠是以小腿起红斑结节，犹如藤系瓜果绕胫而生为特征的皮肤病。类似于结节性红斑。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6ce15cfc-1f03-4ab5-88b8-486ef03f3867', N'0431', N'风热疮', N'04', 2, 1, N'', N'风热疮是以躯干起淡红色斑片，多随皮纹排列，脱屑如糠秕之状为特征的皮肤病。相当于玫瑰糠疹。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fbcc7440-3df6-46e8-8543-172c364fdd6e', N'0432', N'白疕', N'04', 2, 1, N'', N'白疕是以皮肤上起红色斑片，上覆多层白色皮屑，抓去皮屑可见点状出血为特征的皮肤病，相当于银屑病。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e7e4cbf2-07e9-4992-ad53-bfecaf99ae63', N'0433', N'摄领疮', N'04', 2, 1, N'', N'摄领疮是好发在颈部两侧，以皮肤粗糙肥厚，剧烈瘙痒为特征的皮肤病。相当于神经性皮炎。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9be8370a-bc77-4305-a99e-a8e9e1c488e5', N'0434', N'顽湿聚结', N'04', 2, 1, N'', N'顽湿聚结是因体内蕴湿，外感风毒或虫咬所致，以四肢发生灰褐色坚实结节，伴奇痒为特征的皮肤病。相当于结节性痒疹。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6e4e72f1-8318-456c-95a0-2a350071a4dd', N'0435', N'紫癜风', N'04', 2, 1, N'', N'紫癜风是以皮肤出现紫红色扁平皮疹，自觉瘙痒，可发于全身各处，常累及口唇为特征的皮肤病。相当于扁平苔癣。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6b0ffbd1-4bc5-4b60-acc4-ecf20d859849', N'0436', N'白驳风', N'04', 2, 1, N'', N'白驳风是以皮肤变白，形状不一，并不痒痛为特征的皮肤病。相当于白癜风。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ba030faf-6167-491e-9a1b-9cc1d0251b99', N'0437', N'黧黑斑', N'04', 2, 1, N'', N'黧黑斑是发生在面部的黄褐色或灰黑色斑片，不高出皮肤，常见于鼻背两侧。类似于黄褐斑。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1a23a39f-2533-4b70-b25d-834237a8224f', N'0438', N'蟹足肿', N'04', 2, 1, N'', N'蟹足肿是创伤或自发而起。在皮肤上出现肥大而坚实的斑块，形状如蟹足的一种皮肤病。相当于瘢痕疙瘩。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8798cc4d-8c99-4383-92ef-c93ed78f3444', N'0439', N'酒糟鼻', N'04', 2, 1, N'', N'酒糟鼻是发生在鼻准头及鼻两侧，以皮肤潮红、丘疹、脓疱，甚则鼻头增大变厚为特征的一种皮肤病。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6633b88d-5692-4dd3-aa5c-4ce1e8ae1acc', N'0440', N'粉刺', N'04', 2, 1, N'', N'粉刺多发于面部，以丘疹、脓疱、结节有时可挤出白色碎米样粉汁为特征的一种皮肤病。相当于寻常性痤疮。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9e1b6da9-39f5-4d21-b00f-96bdc330b7bb', N'0441', N'面游风', N'04', 2, 1, N'', N'面游风是多发生于面部，以皮肤油腻或干燥，结黄痂或起白屑，痒甚为特征的皮肤病。类似于脂溢性皮炎。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'432f866d-a069-4bf2-bb0e-78efecf1c511', N'0442', N'淋病', N'04', 2, 1, N'', N'淋病多为淋球菌感染而引起，以尿频、尿急、尿道刺痛或尿道溢脓，甚至排尿困难为主要临床表现的性传播疾病。', CAST(0x00009A79011811A8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'23b59ca9-b01d-4cd9-966f-16b460f3fd90', N'05', N'中医儿科', N'-1', 1, 1, N'', N'', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'08ecbb6e-3f5c-4dcb-a5fc-0ad49b845281', N'0501', N'感冒', N'05', 2, 1, N'', N'感冒指由外感风邪，客于肺卫所致。临床以恶寒、发热、头痛、鼻塞、流涕、打嚏、咳嗽为主要症状。一年四季均可发生。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'444a26aa-31fe-435a-b7a7-f109a57952fe', N'0502', N'咳嗽', N'05', 2, 1, N'', N'咳嗽指由外邪袭肺，肺失宣肃而致咳嗽的病症。其长期不愈者为慢性咳嗽。多见于急、慢性支气管炎。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ce76695a-2bc9-4624-9fee-53628bf07a3e', N'0503', N'哮喘', N'05', 2, 1, N'', N'哮喘由感受外邪，或因伏痰夙根复加外感、饮食等因素诱发，以喉间痰鸣有声，呼吸困难为主要临床表现的疾病。常见于哮喘性支气管炎或支气管哮喘。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f640ec4f-9c56-42b7-830f-df869ff382eb', N'0504', N'肺炎喘嗽', N'05', 2, 1, N'', N'肺炎喘嗽由外邪犯肺，邪壅肺气而见发热、咳嗽、气喘、鼻煽为主症的疾病。若正虚邪恋，可迁延难愈。常见于支气管肺炎、大叶性肺炎、迁延性肺炎。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fb8b15c6-1cfe-43c9-9eeb-dc384a7b2854', N'0505', N'鹅口疮', N'05', 2, 1, N'', N'鹅口疮是由感受邪毒，内因心脾积热，上熏口舌，而致口腔出现成片白屑，状如鹅口的疾病。相当于霉菌性口腔炎。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'54c9d129-2b5c-49f3-ac22-60febd35911c', N'0506', N'泄泻', N'05', 2, 1, N'', N'泄泻是由外感时邪，或内伤乳食而致大便次数增多的疾病。如病久不愈，常可导致疳症。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f30decc7-48ed-499f-9b75-9c249186b214', N'0507', N'厌食', N'05', 2, 1, N'', N'厌食指由脾胃失调，不欲进食，甚至拒食的疾病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'446ee709-7376-45c1-83ef-3815c5943a8e', N'0508', N'疳症', N'05', 2, 1, N'', N'疳症指由喂养不当，脾胃受伤，影响生长发育的病症。相当于营养障碍的慢性疾病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6b70500c-8b09-4a9d-8cca-2f8a20439501', N'0509', N'水肿', N'05', 2, 1, N'', N'水肿是由外感风邪或邪毒入侵，导致肺、脾、肾功能失调，水道不利，水湿溢于肌肤而致全身水肿。多见于急、慢性肾炎，肾病综合征。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e1217f88-5139-4781-bb76-dcd40a1570fb', N'0510', N'麻疹', N'05', 2, 1, N'', N'麻疹指由感受麻疹疫毒所致，以发热咳嗽，泪水汪汪，唇内颊"麻疹粘膜斑"及满身布发红疹为特征，好发于冬春季节的乙类传染病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8811e45f-75c8-43d8-b6ea-6872063672dc', N'0511', N'风痧', N'05', 2, 1, N'', N'风痧指外感风痧时毒，蕴于肺脾，发于肌肤而出现的淡红色斑丘疹，症状轻浅。好发于5岁以下小儿，相当于丙类传染病的风疹。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e4bfe5da-a85d-4fa2-8b87-f7e7ddf9c9d2', N'0512', N'丹痧', N'05', 2, 1, N'', N'丹痧由丹痧疫毒感染，以肌肤布有弥漫性鲜红细小皮疹若丹，咽喉肿痛或有腐烂，口唇苍白为特征的传染病。相当于猩红热。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cadd2374-0df7-4a70-856d-2ae065ebc1f3', N'0513', N'水痘', N'05', 2, 1, N'', N'水痘指由感染时行病毒，蕴于肺脾，发于肌肤，皮肤出现红色丘疹，中有水疱的传染病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4726022d-0426-48b0-9796-860cf7917402', N'0514', N'痄腮', N'05', 2, 1, N'', N'痄腮是由风温邪毒引起，以发热、耳下腮部肿痛为主症的急性传染病，相当于流行性腮腺炎。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8a5810e2-7932-4fe2-8237-b7c43dd76b7f', N'0515', N'顿咳', N'05', 2, 1, N'', N'顿咳是由感染时行疫毒，客于肺系所致，以阵发痉挛性咳嗽，咳后有特殊的吸气性吼声为特征。相当于百日咳。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9cb534e1-babe-480a-8c95-737687c91de2', N'0516', N'暑瘟', N'05', 2, 1, N'', N'暑瘟由感受暑疫邪毒所致，以发热、头痛、呕叶、项强为主症。重症可突然高热、神昏抽搐而生内闭外脱危象。相当于流行性乙型脑炎。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ca31a2aa-fc6e-47de-b6c3-9fff05ab59cc', N'0517', N'夏季热', N'05', 2, 1, N'', N'夏季热是由于小儿不耐暑气的熏蒸，蕴于肺胃，以致长期发热，汗闭，口渴，多尿为主症。其特点为体温常随气温的变化而升降的季节性疾病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c00cdad0-0123-4831-9049-e55f82b4a2ea', N'0518', N'疰夏', N'05', 2, 1, N'', N'疰夏是指春夏之交，开始全身倦怠，食欲不振，大便不调，身微热，但体温多正常，秋凉后多可自愈的季节性疾病，有别于夏季热。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2bcfb60a-48b6-40e7-9bc8-ea09b38c39a8', N'0519', N'遗尿', N'05', 2, 1, N'', N'遗尿是指5岁以上幼童，不能自主控制排尿，经常入睡后遗尿者。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'186120d2-8b08-4f10-9309-2af18ef0650a', N'0520', N'胎黄', N'05', 2, 1, N'', N'胎黄是由胎儿时期感受湿热，或瘀热内阻，出生后全身皮肤、巩膜发黄为主要症状的疾病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd431a08e-5ecd-4971-8ebb-c4002de099e9', N'0521', N'疫毒痢', N'05', 2, 1, N'', N'疫毒痢是由进食不洁之物，而突然发生高热，昏迷，惊厥，便下脓血（或无）等为主要症状的急性肠道传染病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8e739ce6-1ad6-4048-88d3-de54e87ee801', N'0522', N'奶癣', N'05', 2, 1, N'', N'奶癣又名胎疮，是指哺乳期婴儿因风湿热邪浸淫皮肤，而以面部出现的湿性或干性皮疹的疾病。相当于婴儿湿疹。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
