INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'779483ea-0a4a-421b-86a6-06acb215c235', N'0523', N'积滞', N'05', 2, 1, N'', N'积滞是由乳食内积，脾胃受损而引起的肠胃疾病，临床以腹泻或便秘、呕吐、腹胀为主要症状。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b4caa70c-e42f-4de2-9fad-572051696b01', N'0524', N'佝搂病', N'05', 2, 1, N'', N'佝偻病是由先天不足或营养缺乏而致的发育迟缓，骨软变形的疾病。相当于中医学五迟、五软、鸡胸、肾疳之类的疾病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'02a9f1e8-6008-48d8-8d71-bc82ff28260b', N'0525', N'营养性贫血', N'05', 2, 1, N'', N'营养性贫血是由后天失调，气血生化乏源而致的气血虚弱性疾病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8e275bc4-8e1f-480c-b814-ef957f35cf50', N'0526', N'多动症', N'05', 2, 1, N'', N'多动症是指学龄儿童由于肝气偏旺，阴阳失凋，心肾不交而致的好动不安，冒失无礼貌，脾气倔强的病症。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'af10bb4e-95a0-4cbe-a7fd-1b98d222791e', N'0527', N'解颅', N'05', 2, 1, N'', N'解颅是由先天不足，颅内受损，或因热毒壅滞，水停于脑，致以头颅增大，前囟和颅缝开解为特征的疾病。相当于脑积水。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a639fbd7-10da-4494-b69b-97e4040b0c73', N'0528', N'夜啼', N'05', 2, 1, N'', N'夜啼是指1岁以内的哺乳婴儿，因寒、热、受惊等而致的夜间定时啼哭，甚则可通宵达旦的疾病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'077c39c0-7551-4976-b477-7ca39aa1b379', N'0529', N'奶麻', N'05', 2, 1, N'', N'奶麻指哺乳期婴儿因感受时行疫毒，发病急，体温高，热退后肌肤出现玫瑰色细散皮疹的疾病。相当于幼儿急疹。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'377ee666-d3e3-4596-9c87-161f888f7bbf', N'0530', N'新生儿肺炎', N'05', 2, 1, N'', N'新生儿肺炎是指出生后感受外邪，以致肺气郁闭或心血瘀阻而出现以不乳喘憋为主症的疾病。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5a7d6446-12da-4562-b450-133afb6c1819', N'0531', N'蛔虫病', N'05', 2, 1, N'', N'蛔虫病为肠道寄生虫病，儿童发病率颇高。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'dc2aab39-00f9-4699-85df-8d02553b8b3a', N'0532', N'蛲虫病', N'05', 2, 1, N'', N'蛲虫病为肠寄生虫病，多见于1～5岁小儿，以肛门或外阴部作痒，夜间为甚，见有虫形细小如线头，俗称为"线虫"。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'736b5522-df34-468e-b66b-13d535369e37', N'0533', N'姜片虫病', N'05', 2, 1, N'', N'姜片虫病为肠道寄生虫病，多见于5岁以上儿童。吐出物或大便排出成虫，其色赤如生肉，亦称"赤虫"。', CAST(0x00009A7A00014CD0 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4726022d-0426-48b0-9796-860cf7917406', N'051002', N'逆证', N'0510', 3, 1, N' ', N'热毒炽盛者，可出现下列严重证候', CAST(0x00009A7A00014CD0 AS DateTime), N' ', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'30ffd830-ae1f-4551-b0b5-93861c9964f9', N'07', N'中医内科', N'-1', 1, 1, N'', N'', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'88b89644-866f-4f3b-b7bb-fbb65f6ed925', N'0701', N'感冒', N'07', 2, 1, N'', N'感冒病系外感风邪，客于肺卫，以鼻塞、流涕、咳嗽、恶寒、发热、头身疼痛为主要临床表现。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'61bdfcd4-438d-4b49-9b17-ef338f395d67', N'0702', N'咳嗽', N'07', 2, 1, N'', N'咳嗽是因邪客肺系，肺失宣肃，肺气不清所致，以咳嗽、咯痰为主要症状的病症。多见于急、慢性支气管炎。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'305694af-7bb8-45de-aef9-0cabd48a6525', N'0703', N'风温肺热病', N'07', 2, 1, N'', N'风温肺热病是由风热病邪犯肺，热壅肺气，肺失清肃所致，以发热，咳嗽，胸痛等为主要临床表现。相当于急性肺部炎性病变。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fd2be8fe-8064-4c6a-9784-b965e581366c', N'0704', N'肺痈', N'07', 2, 1, N'', N'肺痈是由风热邪毒蕴滞于肺，热壅血瘀，血腐化脓而成。以发热，胸痛，咳吐腥臭脓血痰为主要症状的肺化脓症。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e0266cae-83ae-4168-9572-fbd03f9eaa62', N'0705', N'肺痨', N'07', 2, 1, N'', N'肺痨系由感染“瘵虫”所致的肺部慢性消耗性传染性疾患。可见咳嗽、咯血、潮热、盗汗、消瘦等主症。相当于肺结核。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a39ff4b3-cd9d-4df9-9711-eb15d1a39fe7', N'0706', N'咯血', N'07', 2, 1, N'', N'咯血是肺络受伤，血溢脉外，以咳嗽，咯血，或痰中带血等为主要表现。多见于支气管扩张。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'79f76a66-ee21-4ae9-b0e2-f612c16bc1ff', N'0707', N'悬饮', N'07', 2, 1, N'', N'悬饮是指肺气不足，外邪乘虚侵袭，肺失宣通，胸络郁滞，气不布津，以致饮停胸胁，出现咳唾胸胁引痛，或见胁肋饱满。多见于渗出性胸膜炎。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'497f4299-c6fc-48ea-9420-ee4f5d47b841', N'0708', N'喘病', N'07', 2, 1, N'', N'喘病是因久患肺系疾病或他脏病变影响，致肺气上逆，肃降无权，出现气短喘促，呼吸困难，甚则张口抬肩，不能平卧等症。多见于阻塞性肺气肿、肺原性心脏病、心肺功能不全等。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ac6cd916-545e-441e-a85a-d38b612e511d', N'0709', N'哮病', N'07', 2, 1, N'', N'哮病系宿痰伏肺，因外邪、饮食、情志、劳倦等因素，致气滞痰阻，气道挛急、狭窄而发病。以发作性喉中哮鸣有声，呼吸困难，甚则喘息不得平卧为主要表现。相当于支气管哮喘、喘息性支气管炎。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'dc721271-afd0-4be7-b9c1-08ff2a847d95', N'0710', N'胃脘痛', N'07', 2, 1, N'', N'胃脘痛系因胃气郁滞，气血不畅所致。临床以上腹部近心窝处经常发生疼痛为主症。多见于胃、十二指肠炎症、溃疡、痉挛等疾病。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9b6fb7f8-5285-46cf-a39b-fb4aa9f76ca5', N'0711', N'噎膈', N'07', 2, 1, N'', N'噎膈系因痰气交阻，或痰瘀阻滞，致胃失和降，出现饮食梗噎难下，或食入即吐为主要临床表现的病症。多见于食道、贲门部疾病。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd162865b-92be-4d8d-a0a0-e1fb08b926a3', N'0712', N'呕吐', N'07', 2, 1, N'', N'呕吐系因胃失和降，胃气上逆，而出现以胃内容物从口吐出为主要临床表现的病症。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5835b53c-98b1-41d3-a851-5485ca48c0b3', N'0713', N'吐血', N'07', 2, 1, N'', N'吐血系胃络受损，络伤血溢，出现血从口中呕吐而出的病症。指上消化道出血。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd9daa152-8514-4d79-ab96-d36a45ec6ab5', N'0714', N'便血', N'07', 2, 1, N'', N'便血系胃、肠络脉受损，出现血液随大便而下，或大便呈柏油样为主要临床表现的病症。可见于消化道出血。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fdacd65f-4e13-4eff-8a49-3d49415fe8ce', N'0715', N'痢疾', N'07', 2, 1, N'', N'痢疾系因感受湿热病毒，积滞肠腑，脂膜血络受伤，以腹痛、腹泻，里急后重，大便呈赤白粘冻或脓血为主要临床表现。相当于细菌性痢疾或肠阿米巴病。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6ea8328b-0849-4957-81bf-16b9ea0b6b54', N'0716', N'泄泻', N'07', 2, 1, N'', N'泄泻系因感受外邪，或饮食内伤，致脾失健运，传导失司，以大便次数增多，质稀溏或如水样为主要表现的病症。相当于急、慢性肠炎或肠功能紊乱等疾病。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'83886669-4da0-48aa-9dde-c1169e25be3c', N'0717', N'黄疸', N'07', 2, 1, N'', N'黄疸系感受湿热病邪，阻滞肝胆，气机受阻，疏泄失常，胆汁外溢所致。以目黄、身黄、溲黄为主要临床表现的病症。多见于肝胆系统疾病。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'bc25108c-8066-417d-bfac-30872064b1ab', N'0718', N'水臌', N'07', 2, 1, N'', N'水臌系因肝脾受伤，疏运失常，气血交阻，致水气内停，出现腹满胀大为主要临床表现的病症。相当于肝硬化腹水。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c8b12adb-edd9-418e-a943-b52fa6095876', N'0719', N'便秘', N'07', 2, 1, N'', N'便秘系因气阴不足，或燥热内结，腑气不畅所致，以排便间隔时间延长，大便干结难解为主要临床表现的病症。常指习惯性便秘。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7868745d-8e39-464d-ab60-caa4f0df62b2', N'0720', N'紫癜', N'07', 2, 1, N'', N'紫癜是由血络受伤，血渗于肌肤之间，皮肤表现点状或片状青紫斑块的病症。相当于原发性血小板减少或血管性、过敏性紫癜。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'54db04ee-9f22-4375-bd65-9e62353edaa3', N'0721', N'湿温', N'07', 2, 1, N'', N'湿温是由湿热病邪，阻遏脾胃，壅滞肠道，致身热不扬，头重身困，胸痞腹胀，苔腻脉缓等为主症的肠道传染病。多见于伤寒、副伤寒。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b4810674-76ab-4ead-8567-e7bf9ad76ddc', N'0722', N'霍乱', N'07', 2, 1, N'', N'霍乱系由饮食不洁，感受疫毒，致突然剧烈泄泻，继则呕吐，呈挥霍撩乱之势的烈性传染病。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'47543a10-4b00-4745-953e-5ed307906d68', N'0723', N'中暑', N'07', 2, 1, N'', N'中暑是夏季在烈日或高温环境下劳动，因暑热侵袭，致邪热内郁，体温调节功能失常，所发生的急性病变。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4815e524-7678-4b6a-be28-f7cd71c066bd', N'0724', N'疟疾', N'07', 2, 1, N'', N'疟疾是由感受疟邪所致的传染病。以寒战壮热，汗后热退，休作有时为主症。据休作时间分每日疟、间日疟、三阴疟；据证候分温疟、瘅疟、牝疟、疫疟等。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8219b66f-4567-4647-a700-704b45439f28', N'0725', N'血吸虫病', N'07', 2, 1, N'', N'血吸虫病是因日本裂殖体血吸虫寄生在人体门静脉系统所引起的地方性疾病。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7c9d8faa-5bf6-43af-9ae3-7c424f053d7c', N'0726', N'绦虫病、囊虫病', N'07', 2, 1, N'', N'绦虫病是由猪、牛绦虫寄生于人体小肠所致的疾病。囊虫病是由绦虫囊尾蚴寄生于人体各部组织引起的疾病。二者均属肠寄生虫病。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4c1dee7e-6020-4356-b270-3c5ab090d9fe', N'0727', N'钩虫病', N'07', 2, 1, N'', N'由于钩虫寄生于人体肠道所致的寄生虫病。别名“桑叶黄”、“黄胖病”、“懒黄病”。', CAST(0x00009A7A010329B4 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6c7021d3-ecca-4be4-934a-c44bdc790036', N'08', N'中医外科', N'-1', 1, 1, N'', N'', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'91884d9e-aaa2-4434-98e7-21ff5faaf66e', N'0801', N'疖', N'08', 2, 1, N'', N'疖是指肌肤浅表部位感受火毒，致局部红肿热痛为主要表现的急性化脓性疾病。包括有头疖、无头疖、蝼蛄疖、疖病。相当于疖、皮肤脓肿、头皮穿凿性脓肿及疖病。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'47002e12-49d8-4984-9697-bf81a3d4e3de', N'0802', N'痰毒', N'08', 2, 1, N'', N'痰毒是感受风热湿毒，气血被毒邪壅塞于皮肉之间，继而炼液成痰，痰毒互阻，结块而肿的急性化脓性疾病。本病包括颈痈、腋痈、胯腹痈。相当于急性化脓性淋巴结炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a88cc32f-cb01-4027-8150-ed12a2e479a3', N'0803', N'有头疽', N'08', 2, 1, N'', N'有头疽是因外感风热、湿热、火毒之邪，气血瘀滞，结聚于肌肤间的急性化脓性疾病。以局部红肿热痛，有多个脓栓堆积为特征。多发于中老年尤其以消渴病多见，易出现“陷证”。相当于痈。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'46759b5b-96b9-4a30-9ecb-b0f982f0ee5c', N'0804', N'颜面疔疮', N'08', 2, 1, N'', N'颜面疔疮是感受火热之毒，或因昆虫咬伤，皮肤破损染毒，蕴蒸肌肤，气血凝滞而发生的病变迅速、危险性较大的急性化脓性疾病。相当于颜面部疖和痈。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f21ec997-aa76-4744-8d90-97c362d003af', N'0805', N'手部疔疮', N'08', 2, 1, N'', N'手部疗疮是因外伤后感染毒邪，火毒阻于皮肉，患处出现红肿剧烈疼痛，容易损骨的急性化脓性疾病。包括甲沟炎、化脓性指头炎、手指化脓性健鞘炎、掌中间隙感染等。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'81fdb486-7c56-426d-b90a-6ac673829446', N'0806', N'红丝疔', N'08', 2, 1, N'', N'红丝疔多因手足皮肤损伤，感染邪热火毒，走注经络，呈红丝显露，迅速上攻手膊或小腿的急性感染性疾病。邪毒重者可内攻脏腑，发生“走黄”。本病指急性淋巴管炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c1b52342-59a6-4171-ba7e-2790fa286794', N'0807', N'烂疔', N'08', 2, 1, N'', N'烂疔多由皮肉破损，接触污泥、脏物，感染毒邪，以致毒聚肌肤，皮肉迅速腐烂，流出臭秽污血的危急重症。毒邪走散可并发“走黄”。相当于气性坏疽。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'69ad1f1f-1be7-411f-91c2-1494bff5b93b', N'0808', N'发颐', N'08', 2, 1, N'', N'发颐多由外感或手术后，汗出不畅，余邪热毒未能外达，结聚于颐颌之间的急性化脓性疾病。以颐颌部肿胀疼痛，张口受限，伴高热为特征。相当于急性化脓性腮腺炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd1defda5-27db-4535-b374-dd10662aadda', N'0809', N'丹毒', N'08', 2, 1, N'', N'丹毒多先由皮肤、粘膜破损，外受火毒与血热搏结，蕴阻肌肤，不得外泄，致患部鲜红灼热，有如涂丹为特征的急性感染疾病。生于下肢者称“流火”；生于头面的称“抱头火丹”；新生儿多生于臀部，称“赤游丹”。相当于急性网状淋巴管炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', N'0810', N'流注', N'08', 2, 1, N'', N'流注是因感染邪毒，流窜血络，阻于膜理肌肉之间，出现一处或数处漫肿、微热疼痛的脓肿。有暑湿流注，湿痰流注，余毒流注，瘀血流注，骼窝流注之分。相当于脓血症、肌肉深部脓肿和骼窝脓肿。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4f952459-5dc6-48bc-9b12-dec0d53c5295', N'0811', N'瘿瘤', N'08', 2, 1, N'', N'瘿瘤是内有郁火，外感风热邪毒，结于瘿囊而成。证见喉结两旁结块肿硬疼痛。相当于急性甲状腺炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'130c5f08-714f-4cb5-96cf-f52fdb21daa9', N'0812', N'肉瘿', N'08', 2, 1, N'', N'肉瘿多因情志内伤，痰浊凝结，聚而成块，发于结喉两旁，随吞咽而上下移动。相当于甲状腺腺瘤。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3f80a5bc-36cd-43d7-a1dc-7780dbec4fd1', N'0813', N'瘰疬', N'08', 2, 1, N'', N'瘰疬因肝气郁结，气郁化火，灼津为痰，结于颈项而发病。以结核累累成串，溃后脓出清稀，疮口经久不愈为特征。相当于颈部淋巴结结核。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ffdd213d-59bf-4677-9b95-9fc12354b47b', N'0814', N'乳痈', N'08', 2, 1, N'', N'乳痈多因乳头破碎，风邪外袭，或乳汁淤积，乳络阻滞，郁久化热而成。以乳房部结块肿胀疼痛，溃后脓出稠厚为特征。相当于急性乳房炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2f7c7b5f-7e7d-46fb-a677-a1d8787901fb', N'0815', N'乳痨', N'08', 2, 1, N'', N'乳痨多由素体肺肾阴虚，或先患肺痨、瘰疬而后继发乳痨。乳房结块不痛，数月后肿块化脓，脓出稀薄，疮口不易收敛，又名乳痰。相当于乳房结核。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2fcc4ba9-58f7-46f3-a976-95763a11c476', N'0816', N'乳疠', N'08', 2, 1, N'', N'乳疠多因肝肾不足，痰瘀凝结而成。乳房结块发生在乳晕部一侧或二侧，呈半球形，触之疼痛。相当于乳房异常发育症。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4df24a9c-9a2a-4157-ae47-1f483416816b', N'0817', N'乳岩', N'08', 2, 1, N'', N'乳岩由情志内伤，冲任失调，气滞痰瘀互结而成。乳房部结块，质地坚硬，高低不平，病久肿块溃烂，脓血污秽恶臭，疼痛日增，后期可病灶转移。相当于乳癌。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'bd1985d7-eaab-41bb-945e-40041c0b5e09', N'0818', N'乳癖', N'08', 2, 1, N'', N'乳癖是由情志内伤，冲任失调，痰瘀凝结而成。乳房肿块和疼痛与月经周期相关。相当于乳腺增生病。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f3e5f2d9-31be-408c-ad93-151f022f679f', N'0819', N'粉刺性乳痛', N'08', 2, 1, N'', N'粉刺性乳痈由肝郁化热，肉腐化脓而成。多有先天性乳头凹陷畸形，乳头常有粉渣样物排出。乳晕或乳房部出现肿块，溃后形成瘘管，与乳头相通，经久反复不愈。相当于浆细胞性乳腺炎或导管扩张症。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'06da98e8-b46f-416b-bf1c-d67b2b30d5eb', N'0820', N'乳衄', N'08', 2, 1, N'', N'乳衄是因肝脾两伤，肝经火郁、脾失统血而发。证见乳窍溢出血性液体，在乳头或乳晕部可摸及肿块。相当于乳腺导管乳头状瘤及乳腺导管乳头状癌。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'74cb80ea-1bff-4bcc-aeba-841110d6a14d', N'0821', N'乳核', N'08', 2, 1, N'', N'乳核由冲任失调，情志所伤，血瘀痰凝互结于乳房而成。肿块表面光滑，质地坚硬，边界清楚，推之活动不痛，与月经周期无关。相当于乳腺纤维腺瘤。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e29108bf-0c6c-4a67-b5f3-37424cb70411', N'0822', N'肠痈', N'08', 2, 1, N'', N'肠痈是因饮食不节，湿热内阻，致败血浊气壅遏于阑门而成。以转移性右下腹痛为本病主要症状。相当于急性阑尾炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'98d1c7df-baef-4b8c-aef2-57a457e13b1e', N'0823', N'水疝', N'08', 2, 1, N'', N'水疝是由先天肾气不足，脾失健运及外伤等，使水湿内停于肾子（睾丸）及子系（精索）所致。以阴囊下垂肿大或子系处有痰包为特征。相当于睾丸鞘膜积液及精索囊肿。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5a4dc62d-052f-4cf0-973c-36ad3e4648c9', N'0824', N'子痈', N'08', 2, 1, N'', N'子痈是由湿热下注厥阴之络，致气血凝滞而成。以阴囊胀痛下垂，睾丸肿胀疼痛为特征。相当于急性附睾炎，慢性附睾炎与急性睾丸炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'03fb439b-d2a9-44c7-9e3a-8c19fe3b7aab', N'0825', N'子痰', N'08', 2, 1, N'', N'子痰系由肝肾阴亏，痰湿乘虚入络，凝于肾子而生。以附睾肿大，硬结触痛，溃后流脓清稀，形成瘘管为特征。相当于附睾结核。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'32abd504-6f65-4530-89d7-af814f529ded', N'0826', N'精癃', N'08', 2, 1, N'', N'精癃是由肾元亏虚等多种原因，导致精室肿大，膀胱气化失司，以排尿困难和尿潴留为主要临床表现的疾病。相当于前列腺肥大、增生症。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7934a3b3-43ff-4be4-9b63-b80f5789c3ae', N'0827', N'精浊', N'08', 2, 1, N'', N'精浊是由肾虚或湿热瘀滞等病邪，下注于精室，导致会阴坠胀疼痛，尿液常有精液溢出为主要临床表现的疾病。相当于前列腺炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1bc5a4af-6342-431f-8b7d-00876f54693d', N'0828', N'臁疮', N'08', 2, 1, N'', N'臁疮多由久站或过度负重，而致小腿筋脉横解，青筋显露，瘀停脉络，久而化热，或小腿皮肤破损染毒，湿热下注而成，疮口经久不愈。相当于下肢慢性溃疡。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5ff2f19d-720d-4331-8870-8a76088b9c1c', N'0829', N'褥疮', N'08', 2, 1, N'', N'褥疮又名“席疮”。多因久病卧床，气血运行失畅，肌肤失养，每因摩擦皮破，染毒而成。多发于尾骶、肘踝、背脊等容易受压部位，以皮肤破溃，疮口经久不愈为特征。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'33c518c4-703f-4052-8ebf-bdb9ae3e6d79', N'0830', N'附骨疽', N'08', 2, 1, N'', N'附骨疽是因毒邪深袭，阻于筋骨，经络壅塞，气血凝滞所成。相当于急、慢性化脓性骨髓炎。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4c9d8db8-1197-4c6c-9587-531ba0aed57e', N'0831', N'流痰', N'08', 2, 1, N'', N'流痰是因先天肝肾不足，后天失调，骨骼空虚，风寒湿痰之邪，乘隙而入，流注于筋骨关节而成。本病以脓肿旁流和溃后脓液中伴败絮状痰样物为特征。相当于骨与关节结核。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c947406e-6e96-44b9-bc7b-3205d076ca8c', N'0832', N'脱疽', N'08', 2, 1, N'', N'脱疽是因先天不足，正气衰弱，寒湿之邪侵袭，致瘀阻脉络，气血不畅，甚或痹阻不通而发病。初起肢冷麻木，后期趾节坏死脱落，黑腐溃烂，疮口经久不愈。相当于血栓闭塞性脉管炎和闭塞性动脉硬化症。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
