INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'892a6cc0-313f-4118-81ae-e090b8353c17', N'0901', N'针眼', N'09', 2, 1, N'', N'针眼是因感受外邪，胞睑边缘生小硬结，红肿疼痛，形如麦粒的眼病。相当于麦粒肿。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ea9a7039-1070-4a80-a378-b49dd4bba0a4', N'0902', N'胞生痰核', N'09', 2, 1, N'', N'胞生痰核是因痰湿凝集胞睑皮下生核状硬结，不红不痛，皮色如常，推之能移的眼病。相当于霰粒肿。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'21cf4e75-14b9-43fc-a20a-50dec18034da', N'0903', N'睑弦赤烂', N'09', 2, 1, N'', N'睑弦赤烂是由风湿热邪蕴结引起，以胞睑边缘赤肿溃烂，刺痒灼痛为主要表现的眼病。相当于睑缘炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'98763218-44e3-4b13-9ac3-5a169195684b', N'0904', N'眼丹', N'09', 2, 1, N'', N'眼丹是因热毒内蕴，引起胞睑红肿高起，质硬，边界清楚，皮色光滑，鲜红如徐丹砂的眼病。相当于眼睑丹毒。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'29383d83-84c4-4711-a0c0-f97190cf6cbf', N'0905', N'风赤疮痍', N'09', 2, 1, N'', N'风赤疮痍是因脾胃湿热，复感风邪，客於胞睑，以致胞睑皮肤红肿疼痛，起丘疹，小泡，继则引起脓疱，溃烂的眼病。相当于某些眼睑皮肤炎症。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'31952cfa-f7ad-40b9-b74e-776b49d404e3', N'0906', N'沙眼', N'09', 2, 1, N'', N'沙眼是由沙眼衣原体感染引起的传染性结膜角膜炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'312748ce-f6c8-417e-911e-0681f4eb32ed', N'0907', N'目刽', N'09', 2, 1, N'', N'目刽是风邪侵目或精血不足，目失濡养引起的以胞睑频频眨动，不能自主控制的状态，常见于沙眼、浅层点状角膜炎、角膜软化症等。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4ceee186-4179-4c18-9416-014d5542a7b3', N'0908', N'胞肿如桃', N'09', 2, 1, N'', N'胞肿如桃是因实热雍盛，脉络阻滞，以胞睑高度红肿。垂肿如桃，疼痛等为主要表现的眼病。相当于重度眼睑炎性水肿。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'db617603-dc0b-4c00-bae9-b7ef5205f2a3', N'0909', N'胞虚如球', N'09', 2, 1, N'', N'胞虚如球是因脾虚气弱，水湿上泛，以胞睑肿胀，皮色正常，虚软如球，按之不痛为主要症状的眼病。相当于眼睑非炎性水肿。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd6508d74-d165-4a0b-bff2-103009c6bb09', N'0910', N'上胞下垂', N'09', 2, 1, N'', N'上胞下垂是因气虚不能上提，血虚不能养筋，或风邪中络而致，以上睑提举无力或不能抬起以致睑裂变窄，甚至遮盖部分或全部瞳神，影响视力的一种眼病。即上睑下垂。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'62fe2cef-99fc-4ed1-89eb-f5dfdd4cef14', N'0911', N'胞轮振跳', N'09', 2, 1, N'', N'胞轮振跳是因气血不和致眼睑不自主牵拽跳动的疾病。即眼睑轮匝肌痉挛。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4c697fcc-f600-491a-b8aa-b5a1a2970831', N'0912', N'冷泪症', N'09', 2, 1, N'', N'冷泪症是因肝虚泪窍约束无力，或风邪引起泪液频频外溢，但无热感目无赤痛的眼病。又分“无时冷泪”，“迎风冷泪”。相当于泪道不通或不畅，及泪囊功能不全引起的溢泪。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fcd6e54e-d8d3-47be-a8da-be359bcb4f5d', N'0913', N'漏睛疮', N'09', 2, 1, N'', N'漏睛疮是因热毒蕴结内眦部近睛明穴下方，突发红肿高起，继而破溃出脓的一种眼病。即急性泪囊炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'369228ec-2111-405c-85c0-ba0eec0a4fa5', N'0914', N'暴风客热', N'09', 2, 1, N'', N'暴风客热是因风热之邪突然侵目，白睛粹然红赤，生眵流泪的眼病。相当于某些急性卡他性结膜炎和过敏性结膜炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'dd2c564f-72cc-4b3f-bac5-1294f2189a48', N'0915', N'天行赤眼', N'09', 2, 1, N'', N'天行赤眼是因外染天行疫疠之气，白睛红赤，相互传染易引起流行的眼病。相当于流行性角膜结膜炎，流行性出血性结膜炎等。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'929c5145-f8d0-4149-bd40-dce1eaba167c', N'0916', N'时复症', N'09', 2, 1, N'', N'时复症是因时邪与湿热交阻，发病时白睛红赤，奇痒难忍，每年至期而发，过期乃愈，呈周期性反复发作的眼病。相当于春季卡他性结膜炎等病。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'149d25ba-5d32-44e5-8680-89bf2800f409', N'0917', N'神水将枯', N'09', 2, 1, N'', N'神水将枯是气郁化火，津液亏损，泪液减少以致目珠干燥失泽的眼病，相当于泪腺分泌降低引起的结膜干燥症。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'be0a474a-416b-497f-9dd5-d1fedf5a4944', N'0918', N'金疳', N'09', 2, 1, N'', N'金疳是因燥热伤阴，肺失肃降，白睛表层生圆形小泡如玉粒，周围绕以赤脉的眼病。相当于泡性结膜炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9dabaf71-6284-40aa-ac0e-38eab8646f61', N'0919', N'火疳', N'09', 2, 1, N'', N'火疳是因火邪郁结，白睛深层有结节隆起，色紫红，疼痛拒按的眼病。相当于前巩膜炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'14d06bc4-ecf7-4611-a870-68970cf4ae25', N'0920', N'聚星障', N'09', 2, 1, N'', N'聚星障是因外感风邪，挟热化火，致黑睛上生细小星翳伴有涩疼、畏光、流泪的眼病。相当于单疱病毒性角膜炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2c8bcd98-4f64-4a57-82fa-b6f7cc7ece10', N'0921', N'凝脂翳', N'09', 2, 1, N'', N'凝脂翳是由风热邪毒入侵，致风轮黑睛生翳，表面色白或黄，状如凝脂，发病迅速，或伴黄液上冲的眼病。相当于匐行性角膜溃疡和绿脓杆菌性角膜溃疡。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'99711dac-0e1c-4368-bd01-c9c6d3fda7a8', N'0922', N'花翳白陷', N'09', 2, 1, N'', N'花翳白陷是因肝经结热，或感外邪，致黑睛生翳、混浊灰暗，四周高起，中央低陷，形如花瓣的眼病。相当于某些角膜溃疡。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f3d039ff-e70f-44ef-8544-e43ba4e89b84', N'0923', N'混睛障', N'09', 2, 1, N'', N'混睛障是因邪伏风轮，气血瘀滞，黑睛深层漫珠一片翳障混浊，以致黑睛呈镜面哈气状的眼病。相当于基质性角膜炎等病。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'efc2c926-83b2-4fc1-9d4a-46f1ea93c79e', N'0924', N'风轮赤豆', N'09', 2, 1, N'', N'风轮赤豆是因脾虚挟痰，肝经积热，黑睛生翳，有赤脉从白睛追随环绕状如赤豆的眼病。相当于束状角膜炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'13d310f2-1fa7-4cae-954c-1d6b3dbc4783', N'0925', N'疳积上目', N'09', 2, 1, N'', N'疳积上目是由小儿疳积引起的，初为于涩、夜盲，日久黑睛生翳，甚则溃破穿孔的眼病。相当于维生素A缺乏引起的角膜软化症。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
