INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c042c8e6-50dd-406e-988e-6c30dfad7e0f', N'0926', N'血翳包睛', N'09', 2, 1, N'', N'血翳包睛是由风热壅盛，气血瘀滞，以致赤脉从黑睛四周侵入，密集形成翳膜，漫散黑睛的一种眼病。相当于全角膜血管翳。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'04d5aa76-ca6f-4ac5-831e-6bf5f3d2ea59', N'0927', N'宿翳', N'09', 2, 1, N'', N'宿翳是在黑睛疾患痊愈后，结成搬痕，表面光滑，边缘清楚，无红赤疼痛的眼病。古代根据瘢痕的厚薄、形状，有"冰暇翳"、"云翳"、"厚翳"、"斑脂翳"等命名。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6bc75893-48a0-4f2c-ae0c-503aacb9615f', N'0928', N'瞳回神紧小', N'09', 2, 1, N'', N'瞳神紧小是因邪热灼伤黄仁以致瞳神缩小，展缩失灵，伴抱轮红赤，黑睛后壁有沉着物，神水混浊，视力下降的眼病。相当于前部色素膜炎。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7904528d-72e3-4dd9-82f7-735bc02d1588', N'0929', N'黄液上冲', N'09', 2, 1, N'', N'黄液上冲是因黄仁为邪热所伤，灼津为脓、黑睛与黄仁之间，积有黄色脓液的眼病。即前房积脓。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'21b6439b-b931-4549-bf41-14308f2eb3da', N'0930', N'血灌瞳神', N'09', 2, 1, N'', N'血灌瞳神是因目内之血不循经而行，溢于瞳神前后的眼病。即前房积血或玻璃体积血。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'afcea8f7-c9eb-4b5f-8aba-ce286e13968f', N'0931', N'绿风内障', N'09', 2, 1, N'', N'绿风内障是因暴怒忿郁，气郁化火，风火痰浊上攻，或脾胃虚寒，饮邪上泛，致黑睛气色混浊，瞳神散大不清呈隐隐绿色，伴有头痛眼胀，恶心呕吐，视力急剧下降的眼病。相当于闭角型青光眼急性发作期。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a5843a38-f85d-4cb0-8d7a-8a4aad937501', N'0932', N'圆翳内障', N'09', 2, 1, N'', N'圆翳内障是因高年体弱，精气日衰，目失涵养所致晶珠混浊，视力渐降，最终瞳神内呈圆形银白色翳障，视力障碍的眼病。相当于老年性白内障。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'52afacdb-1061-40ce-b1f5-c03a6c0bae53', N'0933', N'云雾移睛', N'09', 2, 1, N'', N'云雾移睛是因神膏为邪所乘，混浊不清所致，眼外观端好，自觉眼前有蚊蝇或云雾样黑影飞舞飘移，甚者视物昏蒙的眼病。即玻璃体混浊。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'40dc233d-1c6d-450d-8c12-6afcc3bc0388', N'0934', N'视瞻昏渺', N'09', 2, 1, N'', N'视瞻昏渺是因气血失调，精气不能上荣於目所致，自觉视力下降，视物昏蒙而外眼无异的眼病。相当于慢性球后视神经炎、中心性浆液性脉络膜视网膜病变等。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0315f6db-af0d-4d96-9d79-51b7d1c27cc2', N'0935', N'视直如曲', N'09', 2, 1, N'', N'视直如曲是因视衣津液输布失调或血溢络外，导致患眼视正直之物如弯曲之状的眼病。相当于中心性浆液性脉络膜视网膜病变等黄斑部疾病。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3dedca7d-a754-4fc5-8911-6fcf6e57c271', N'0936', N'暴盲', N'09', 2, 1, N'', N'暴盲是由视衣目系脉络阻滞，气机郁闭，导致神光离散，而出现视力急骤下降失明的内障眼病。多见于西医之许多急性视力障碍眼底病，主要有视网膜中央动脉阻塞，眼内出血和急性视神经炎等，兹分述如下：', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
