INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'11351975-3c25-4e51-b56d-b09127347f8f', N'0941', N'青盲', N'09', 2, 1, N'', N'青盲是指眼外观端好，而视力逐渐下降，以致失明的眼病。多由视瞻昏渺，高风雀目等瞳神疾病日久失治演变而来。相当于视神经萎缩。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f24ca2b3-80b0-471a-beb8-7ddd871c75e2', N'0942', N'高风雀目', N'09', 2, 1, N'', N'高风雀目是由先天禀赋不足，脉络细涩，神光衰微所致。眼外观端好，是以夜盲和视野缩小为主要症状的眼病。相当于原发性视网膜色素变性。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'34e4a6b8-ab92-455b-acbf-c8dcf7cfe183', N'0943', N'能近怯远', N'09', 2, 1, N'', N'能近怯远是由神光不足，表现为视近物清晰，视远物模糊的眼病。即屈光不正的近视眼。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ce016986-f424-4405-a064-9716811fb397', N'0944', N'目偏视', N'09', 2, 1, N'', N'目偏视系邪中经络，气血不和，筋脉失养，弛张不收，在双眼注视目标时，呈现一眼眼位偏斜的眼病。相当于显斜，主要指麻痹性斜视。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'17ab75e2-a57b-47b2-a1b7-fccc2cd26041', N'0945', N'辘轳转关', N'09', 2, 1, N'', N'辘轳转关是由先天禀赋不足或风邪扰动，致筋脉振惕，出现两眼不自主地颤动的眼病。即眼球震颤。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b0d299c7-3050-4456-a5a4-7272afd4d490', N'0946', N'眉棱骨痛', N'09', 2, 1, N'', N'眉棱骨痛是因经气不通致眉棱骨部或兼眼眶深部胀痛的眼病。相当于眶上神经痛。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2b1ce67c-586e-44ae-a5e0-02f9632a6d1b', N'0947', N'鹘眼凝肋', N'09', 2, 1, N'', N'鹘眼凝睛是由痰湿凝滞，气血瘀阻，或热毒内攻，双眼突出，凝视，白睛瘀滞红赤的眼病。主要见于血栓性海绵窦炎和重症内分泌突眼。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'901b0967-208f-4639-aeb0-c3c8aae2fc83', N'0948', N'突起睛高', N'09', 2, 1, N'', N'突起睛高是由风热火毒攻目，发病较急，以单侧患眼突起、疼痛，白睛红赤、肿胀的急性眼病。相当于急性眶内炎症如眼眶蜂窝织炎、眶骨膜炎和眼筋膜炎或全眼球炎等。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8b804aa1-ab5c-4db9-88db-cb340d8aa1ff', N'0949', N'振胞瘀痛', N'09', 2, 1, N'', N'振胞瘀痛是指眼部受钝器撞伤后，胞睑肿胀，瘀血内停，色呈青紫，疼涌难睁的眼病。相当于眼睑挫伤。', CAST(0x00009A7A01532838 AS DateTime), N'', 3)
