INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6061c6c2-d7c4-427d-a723-f244e618037f', N'0763', N'胸痹心痛', N'07', 2, 1, N'', N'胸痹心痛是由邪痹心络，气血不畅而致胸闷心痛，甚则心痛彻背，短气喘息不得卧等为主症的心脉疾病.多见于冠状动脉硬化性心脏病.', CAST(0x00009BD10147A7B3 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6a9a8db9-7234-49bd-8b70-87dc36c50261', N'0764', N'心悸', N'07', 2, 1, N'', N'心悸是由心失所养或邪扰心神，致心跳异常，自觉心慌悸动不安的病症，多见于心神经官能症及心律失常.', CAST(0x00009BD10147AB5C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'312f8471-2c4f-499d-bc06-6a68534ecdf5', N'0766', N'自汗、盗汗', N'07', 2, 1, N'', N'自汗、盗汗是指人体阴阳失调，营卫不和，腠理失密而引起的汗液外泄.时时汗出，动则益甚者为自汗；睡眠中汗出，醒后汗止者为盗汗.常由植物神经功能紊乱所致.', CAST(0x00009BD10147B1D8 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6cf24bbf-75a3-4985-8dee-e4030f0be8d2', N'0767', N'郁病', N'07', 2, 1, N'', N'郁病因情志不舒，气机郁滞而致病.以抑郁善忧，情绪不宁，或易怒善哭为主症.多见于神经官能症、癔病.', CAST(0x00009BD10147B471 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4108b40f-2393-451d-862f-afff4d47ce2f', N'0768', N'癫病', N'07', 2, 1, N'', N'癫病因情志内伤、脏腑功能失调，致痰气郁结，蒙蔽心窍所发.以精神抑郁，表情淡漠，沉默痴呆，语无伦次，静而少动为特征.主要指精神分裂症、忧郁症、强迫症等.', CAST(0x00009BD10147B75F AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9c68fc3e-3a8e-4dc5-9b8b-b0691c870daf', N'0769', N'狂病', N'07', 2, 1, N'', N'狂病因七情化火，煎熬津液为痰，痰热壅盛，迷塞心窍所致.以精神亢奋，躁扰喧狂不宁，毁物打骂，动而多怒为特征.主要见于精神分裂症、躁狂症.', CAST(0x00009BD10147B989 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'18b4011d-2121-4954-887b-fd6b9c6c26d5', N'0770', N'痫病', N'07', 2, 1, N'', N'痫病由痰、火、瘀，以及先天因素等，致气血逆乱，清窍蒙蔽而发病，以卒然昏仆，强直抽搐，移时自醒，醒后如常人为特征的发作性疾病.主要指癫痫，包括原发性及继发性癫痫.', CAST(0x00009BD10147BB4B AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0db72e0e-0935-4516-9e43-373807ee607e', N'0771', N'头风', N'07', 2, 1, N'', N'头风由肝阳上亢、痰瘀互结而致清阳不升，或浊邪上犯，清窍失养，以头部疼痛为主要表现的病症.主要指血管神经性头痛，及高血压病、脑动脉硬化等.', CAST(0x00009BD10147BE88 AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'da4a516a-931b-4093-aba7-7a6d1dfe3193', N'0772', N'眩晕', N'07', 2, 1, N'', N'眩晕由风阳上扰，痰瘀内阻等导致脑窍失养，脑髓不充.以头晕目眩，视物运转为主要表现.多见于内耳性眩晕，颈椎病，椎一基底动脉系统血管病，及高血压病、脑动脉硬化、贫血等.', CAST(0x00009BD10147C14C AS DateTime), N'', 3)
