INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6283e203-a395-4c41-a1fb-ae788f8ab3ca', N'0765', N'不寐', N'07', 2, 1, N'', N'不寐是指脏腑机能紊乱，气血亏虚，阴阳失调，导致不能获得正常睡眠.', CAST(0x00009BD10147AEEF AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b85c22a5-be85-4755-beb5-013d8cf57adb', N'0777', N'热淋', N'07', 2, 1, N'', N'热淋是下焦感受湿热病邪，膀胱气化不利所致.以小便频急，解时滴沥涩痛为主要表现.常指急、慢性泌尿系感染.', CAST(0x00009BD10147D17C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3d312130-3ed3-4c57-a8a4-5034ebd1e613', N'0779', N'乳糜尿', N'07', 2, 1, N'', N'乳糜尿由感受病邪后湿热未清，脾肾两伤，清浊不分所致.以小便混浊，乳白如泔浆，尿时无疼痛为主要临床表现.', CAST(0x00009BD10147D6BD AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'404dda19-e601-4e8b-ae31-ea0c9ce47993', N'0780', N'癃闭', N'07', 2, 1, N'', N'癃闭由于膀胱气化不利，尿液排出困难，小便不利，点滴而出为“癃”；小便不通，欲解不得为“闭”，一般合称癃闭.相当于尿潴留.', CAST(0x00009BD10147D97C AS DateTime), N'', 3)
INSERT [dbo].[cDisease] ([CDISid], [code], [name], [parentcode], [level], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1af20dec-edd3-4e03-a0fa-99c2431a424a', N'0785', N'骨痹', N'07', 2, 1, N'', N'骨痹由于年老体衰，骨失滋养，气血失凋，所致局部或全身骨关节退化改变.临床表现以大关节疼痛，活动受限为主症.多见于退行性骨关节病，肥大性改变等.', CAST(0x00009BD10147E71C AS DateTime), N'', 3)
