INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'740cb29f-0910-4634-b12f-fdf5f4af2ceb', N'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', N'020101', N'0201', N'风邪犯耳', 1, N'', N'耳中胀闷，耳鸣，听力下降，鼻塞流涕，或有咳嗽咯痰、头痛等症。舌苔薄白，脉浮。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7a0394fb-ae4f-4caf-91c3-a220bf23945a', N'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', N'020102', N'0201', N'痰浊积聚', 1, N'', N'耳胀不适，听力不聪，头晕头重，或有咳嗽咯痰，胸脘痞闷。检查见鼓室积液，量多难消。舌苔白腻，脉溺或滑。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6cecdb85-77e2-497b-9b25-620e0c913cdf', N'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', N'020103', N'0201', N'气滞血瘀', 1, N'', N'耳胀、耳中闭气，或有刺痛感，耳鸣不聪。检查见鼓膜混浊、内陷，或增厚、粘连，或有鼓室积液。舌质紫暗或有瘀点，脉涩。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'11612f8d-d6c5-4f06-af2d-7a09f883a98d', N'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', N'020104', N'0201', N'脾气虚弱', 1, N'', N'耳闭时轻时重，面色无华，食少腹胀，或有便溏。检查见鼓膜内陷，或有鼓室积液。舌淡，苔白，脉弱。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'20811f9b-5eab-4a16-b17b-d04f334d376f', N'f8f0c6ee-c8f3-443b-ba72-3ecf426bbd34', N'020105', N'0201', N'肝肾阴虚', 1, N'', N'耳闭、听力下降，头晕眼花，腰膝酸软，手足心热。舌红，苔少，脉细数。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1b522595-97d3-4844-b520-59fdf46b4589', N'04fe2d0e-5d9c-4e9e-ba49-740225d9177d', N'020201', N'0202', N'肝胆火热', 1, N'', N'急性发作，耳深部痛，头痛，听力下降。发热，面红目赤，小便黄赤。检查见鼓膜充血、穿孔，流脓较多。舌红，苔黄，脉弦数。 。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'750b740b-4251-476f-b8c3-57c21d6f820b', N'04fe2d0e-5d9c-4e9e-ba49-740225d9177d', N'020202', N'0202', N'脾虚湿困', 1, N'', N'耳内流脓，量较多，日久不愈。倦怠乏力，食少，便溏。舌质淡红，苔白腻，脉细无力。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9d3fca70-0c47-4b62-9647-8b672a5f9ef6', N'04fe2d0e-5d9c-4e9e-ba49-740225d9177d', N'020203', N'0202', N'肾阴亏虚', 1, N'', N'耳内流脓，时多时少，混有豆渣样物，带秽臭味。听力检查呈传导性耳聋或混合性耳聋。头晕头痛，腰酸乏力。X线乳突摄片见骨质破坏。舌质红，苔薄，脉细数。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fb2c672c-b194-499c-8ba5-da093b6524bc', N'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', N'020301', N'0203', N'风邪外犯', 1, N'', N'突发耳聋，伴鼻塞、流涕，或有头痛、耳胀闷，或有恶寒、发热、身疼。舌苔薄白，脉浮。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'13edf7d4-7033-4b17-b4f6-bcd172d01f07', N'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', N'020302', N'0203', N'肝火上炎', 1, N'', N'情志抑郁或恼怒之后，突发耳聋，伴口苦口干，便秘尿黄，面红，目赤。舌红，苔黄，脉弦数。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5dbcaa55-2e8f-4cf7-b5ac-20d4579ed711', N'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', N'020303', N'0203', N'肝阳上亢', 1, N'', N'突聋发于恼怒之后，头晕、头痛、口苦，烦躁易怒。舌苔薄白，脉弦。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'073613c3-dc1f-461e-aa66-bc67daf8fee1', N'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', N'020304', N'0203', N'气滞血瘀', 1, N'', N'耳聋伴耳胀闷感，耳鸣不休，或耳聋因强大声音震击而成。舌质暗红，脉涩。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f1158c53-4937-4593-84a3-9b211fe6f2bb', N'b7575517-c8b1-4652-a3e7-9e4a765e1ea8', N'020305', N'0203', N'气血亏虚', 1, N'', N'素体虚弱，面色无华，突发耳聋；或暴聋数日后头痛、耳胀闷等症消除，而面色无华、头晕眼花、语声无力、四肢倦怠等症仍在。舌淡，苔薄，脉细弱。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'024be939-16b2-48b3-9a2a-c477b9f8cc0e', N'c5ede233-cf57-4a9b-b16c-861e08ee0ece', N'020401', N'0204', N'肾精亏损', 1, N'', N'耳聋日久，耳鸣如闻蝉声，头晕眼花，腰膝酸软。舌红，苔少，脉细数。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6a2aa490-4924-49fb-aaaa-60d098c4928e', N'c5ede233-cf57-4a9b-b16c-861e08ee0ece', N'020402', N'0204', N'气血亏虚', 1, N'', N'耳聋耳鸣，时轻时重，劳累后加重，面色无华，食欲不振。舌苔薄白，脉细弱。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8eae9eec-0ef7-4f10-a916-67f60f3518ee', N'c5ede233-cf57-4a9b-b16c-861e08ee0ece', N'020403', N'0204', N'痰火郁结', 1, N'', N'耳聋，头重而晕，耳中胀闷，脘腹痞满。舌苔黄腻，脉滑数。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'90527357-1f4d-48f5-a951-de64c640bc98', N'c5ede233-cf57-4a9b-b16c-861e08ee0ece', N'020404', N'0204', N'气滞血瘀', 1, N'', N'耳聋或重或轻，或伴耳鸣，鼓膜增厚，或有粘连、钙质沉着。舌质紫暗，脉涩。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1772a305-a48e-4872-9156-c18f8d48a1ab', N'4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', N'020501', N'0205', N'肝阳上亢', 1, N'', N'眩晕因情绪波动而发作或加重。平时性情急躁，胸胁胀闷。舌红，苔黄，脉弦。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c2f81af9-2bbc-44ad-a210-a4cd901d0e30', N'4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', N'020502', N'0205', N'痰浊中阻', 1, N'', N'眩晕而胸脘痞闷，泛泛欲吐，咽喉痰多而粘。舌苔白腻，脉滑。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ce8c6932-8165-43ca-89d1-5c3ea797b3ea', N'4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', N'020503', N'0205', N'脾气虚弱', 1, N'', N'眩晕因疲劳而发，或发作后头晕乏力，食欲不振，大便溏薄。舌淡，苔白，脉弱。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'be9152fb-f8b7-4424-9e47-1107c49e544c', N'4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', N'020504', N'0205', N'肾阴亏虚', 1, N'', N'眩晕、耳鸣常有发作，腰膝酸软，记忆力差，手足心热。舌红，苔薄黄，脉细数。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a190d0f9-85c8-43af-8552-5853b06c9f1f', N'4fd6674a-8bc9-4e4c-8ad3-730feb7a0e73', N'020505', N'0205', N'肾阳亏虚', 1, N'', N'眩晕、耳鸣，听力下降，精神萎靡，腰痛背冷，面色恍白。舌淡胖，苔白，脉沉细。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c6b2a6bc-34a9-4496-8565-948f7e958e2b', N'66522337-9b13-473d-a8c9-1c8ea4996f59', N'020601', N'0206', N'风寒外袭', 1, N'', N'鼻塞遇寒加重，鼻涕清稀、量多。伴恶寒，发热，头痛，身痛。鼻粘膜充血轻，但肿胀较甚。舌苔薄白，豚浮紧。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'60a854fc-8024-448f-a396-7a36ee19b75f', N'66522337-9b13-473d-a8c9-1c8ea4996f59', N'020602', N'0206', N'风热外犯', 1, N'', N'鼻塞，流涕粘稠，色白或黄，发热，微恶寒，口微渴。鼻粘膜充血。舌质偏红，脉浮数。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1895eab4-da46-4c31-89f2-88e1ebd53b70', N'2609d530-b1ed-47ae-ac12-3846c9a55e8e', N'020701', N'0207', N'肺虚邪滞', 1, N'', N'鼻塞多为间歇性，时轻时重，鼻涕白粘、量多，遇寒加重。或伴气短乏力、大便溏薄等症。鼻粘膜肿胀，色淡红。舌苔薄白，脉细弱。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c7795194-18f2-4556-a8e8-27b4c4f98768', N'2609d530-b1ed-47ae-ac12-3846c9a55e8e', N'020702', N'0207', N'气滞血瘀', 1, N'', N'鼻塞多为持续性，鼻涕粘稠，不易擤出，嗅觉迟钝。伴头昏、耳鸣、记忆力减退等症。鼻粘膜充血，呈暗红或深红色。鼻甲肿大，表面不平滑，如桑椹样，触之较硬，缺乏弹性，对一般滴鼻剂收缩反应较差。舌质紫暗或有瘀点，脉涩。', CAST(0x00009A7901058D6C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fea12fd0-08d7-4fc3-ab73-8464caa4bff2', N'404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', N'020801', N'0208', N'燥热外犯', 1, N'', N'鼻干无涕，鼻腔痂皮积聚，时作鼻衄。伴口渴喜饮，小便短黄，大便干燥。鼻腔粘膜干燥充血。舌红，苔黄燥，脉数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4dd1aa50-cfb7-499b-87cb-e05a82305801', N'404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', N'020802', N'0208', N'肺阴亏虚', 1, N'', N'鼻中干燥，痂皮积聚，鼻塞失嗅，口干不欲多饮，或有午后潮热、盗汗、头晕、手足：心热。鼻甲萎缩，粘膜暗红。舌红，苔薄，脉细数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b695c489-110b-4165-a3cb-6437e7bda727', N'404bfb0a-44c6-4a5a-acf1-57c0bf8bf8b7', N'020803', N'0208', N'肺脾气虚', 1, N'', N'鼻燥少涕，头晕，神疲乏力，语声低弱，食少，便溏。鼻粘膜干燥萎缩，色淡红。舌淡，苔白润，脉细弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a3edff0b-018f-434e-a6bb-3e7436f85d87', N'594eda87-98a4-46f1-8e27-2f1ca1f3b86a', N'020901', N'0209', N'肺虚感寒', 1, N'', N'常因感受风冷异气发病，恶风寒，面白，气短，咳嗽，咯痰色白。舌苔薄白，脉浮。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a778a1bd-b0e6-4492-9a15-3f3e262c5b4b', N'594eda87-98a4-46f1-8e27-2f1ca1f3b86a', N'020902', N'0209', N'脾气虚弱', 1, N'', N'鼻痒而喷嚏连作，清涕量多，四肢乏力，大便溏薄。鼻粘膜色淡红。舌淡，苔白，脉细弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3356825e-c9a4-4bd0-84c5-ef7d6b0fb599', N'594eda87-98a4-46f1-8e27-2f1ca1f3b86a', N'020903', N'0209', N'肾阳亏虚', 1, N'', N'鼻痒，鼻塞，喷嚏较多，遇风冷则易发作。畏寒肢冷，小便清长，大便溏薄。鼻粘膜淡白，鼻甲水肿。舌淡，苔白，脉沉细。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'553c31cb-6104-4e3b-af4c-59575dffcd5f', N'6c56539b-b855-43d5-8ed9-e6197828f2d0', N'021001', N'0210', N'肺经风热', 1, N'', N'多见于发病初期，或慢性鼻渊因外感而急性发作。鼻塞，涕多色白或微黄，头痛，咳嗽，咯痰。鼻粘膜充血，鼻甲肿大。舌苔薄白，脉浮数。。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b30df52b-01d2-47b1-958f-99b1fb3754b4', N'6c56539b-b855-43d5-8ed9-e6197828f2d0', N'021002', N'0210', N'胆经郁热', 1, N'', N'多见于急性鼻渊，或慢性鼻渊急性发作。鼻塞、头痛较甚，涕多色黄而浊。身热，口渴，大便干燥。鼻粘膜充血明显，且肿胀，鼻腔内可见较多脓性分泌物。舌红，苔黄腻，脉弦数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6f2f180b-cd28-4b4d-b3c4-7101bb2d732b', N'6c56539b-b855-43d5-8ed9-e6197828f2d0', N'021003', N'0210', N'脾胃湿热', 1, N'', N'多见于急性鼻渊后期。鼻塞，流涕缠绵不愈。伴头昏，食欲不振，大便溏薄。鼻粘膜充血肿胀，鼻腔内有较多黄浊分泌物。舌苔黄腻，脉濡数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7c2adc11-d561-46a4-8f85-90dbc32c9e59', N'6c56539b-b855-43d5-8ed9-e6197828f2d0', N'021004', N'0210', N'肺脾气虚', 1, N'', N'多见于慢性鼻渊。鼻塞，头昏，记忆力减退，鼻涕混浊，时多时少。面色萎黄或白，少气乏力，大便溏薄。鼻腔粘膜不充血，但肿胀，并有粘性或脓性分泌物。舌淡，苔白，脉细弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'51135a47-1749-438c-b22e-c5ec4c9d5c41', N'e79686d3-6a61-4142-9544-1ab5a7b35d51', N'021101', N'0211', N'肺经热盛', 1, N'', N'鼻衄点滴渗出，血色鲜红，伴鼻塞，咳嗽，或有发热。舌质偏红，脉数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0f1bc748-3dbf-49f7-b5fa-336fe31c8e7e', N'e79686d3-6a61-4142-9544-1ab5a7b35d51', N'021102', N'0211', N'胃火炽盛', 1, N'', N'鼻中出血量多，血色深红。身热，口渴，便秘。鼻腔粘膜充血。舌红，苔黄，脉洪数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2cd30e08-7cf5-4bd4-8e90-5ea2db197042', N'e79686d3-6a61-4142-9544-1ab5a7b35d51', N'021103', N'0211', N'肝火上炎', 1, N'', N'鼻衄起于恼怒之后，头痛头晕，口苦咽干，胸胁苦满。舌红，苔黄，脉弦数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'46f5c0cb-a351-4179-9e5c-676fa8a2fa58', N'e79686d3-6a61-4142-9544-1ab5a7b35d51', N'021104', N'0211', N'阴虚火旺', 1, N'', N'鼻衄量少，口干咽燥，头晕眼花，手足心热。舌红，苔少，脉细数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8d0cb477-1f24-42c0-b88d-5900f41c1487', N'e79686d3-6a61-4142-9544-1ab5a7b35d51', N'021105', N'0211', N'脾不统血', 1, N'', N'鼻衄量少，面白肢冷，大便溏薄，鼻粘膜色淡。舌淡，苔白，脉细弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5642f1da-3196-4c82-bd67-312fc593a173', N'ac70791e-522b-4363-a46c-a5044568a13e', N'021201', N'0212', N'风寒外袭', 1, N'', N'咽痛，口不渴，恶寒，不发热或微发热，咽粘膜水肿，不充血或轻度充血。舌质淡红，苔薄白，脉浮紧。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3cb52112-6b88-4334-84d0-66e81c5c4ee4', N'ac70791e-522b-4363-a46c-a5044568a13e', N'021202', N'0212', N'风热外侵', 1, N'', N'咽痛而口微渴，发热，微恶寒，咽部轻度充血，水肿。舌边尖红，苔薄白，脉浮数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9f48bccd-c582-4cf5-be2f-04720cfec64e', N'ac70791e-522b-4363-a46c-a5044568a13e', N'021203', N'0212', N'肺胃实热', 1, N'', N'咽痛较剧，口渴多饮，咳嗽，痰粘稠，发热，大便偏干，小便短黄。咽部充血较甚。舌红，苔黄，脉数有力。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9dbeab9f-d2e9-4b4d-8130-cd6eec6bd0ad', N'523762f2-424f-4c00-87be-ef8e14d657f7', N'021301', N'0213', N'阴虚肺燥', 1, N'', N'咽喉干疼、灼热，多言之后症状加重，呛咳无痰，频频求饮，而饮量不多，午后及黄昏时症状明显。咽部充血呈暗红色，粘膜干燥、或有萎缩，或有淋巴滤泡增生。舌红，苔薄，脉细数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e04f492b-cc50-4b92-845f-b8201918b11f', N'523762f2-424f-4c00-87be-ef8e14d657f7', N'021302', N'0213', N'肺脾气虚', 1, N'', N'咽喉干燥，但不欲饮，咳嗽，有痰易咯，平时畏寒，易感冒，神倦乏力，语声低微，大便溏薄。咽部充血较轻。舌苔白润，脉细弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5e02cb91-9c47-4f97-9469-aad1aaa4a4e6', N'523762f2-424f-4c00-87be-ef8e14d657f7', N'021303', N'0213', N'痰热蕴结', 1, N'', N'咽喉不适，因受凉、疲劳、多言之后症状较重。咳嗽、咯痰粘稠，口渴喜饮。咽粘膜充血呈深红色，肥厚，有黄白色分泌物附着。舌红，苔黄腻，脉滑数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1e3e2877-2ad7-446a-be6c-e5a54ed80bf6', N'8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', N'021401', N'0214', N'风热外侵', 1, N'', N'急乳蛾初起，咽痛，轻度吞咽困难。伴发热、恶寒、咳嗽、咯痰等症。咽粘膜及扁桃体充血，未成脓。舌苔薄白，脉浮数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0908c9c4-94cf-4807-bec3-ee1b5fd7cf83', N'8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', N'021402', N'0214', N'胃火炽盛', 1, N'', N'咽痛较甚，吞咽困难。身热，口渴，大便秘结。咽部及扁桃体充血红肿，上有脓点或小脓肿。舌红，苔黄，脉滑数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd10ec532-38b1-44b8-b03b-ab39af4726b7', N'8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', N'021403', N'0214', N'肺肾阴虚', 1, N'', N'咽部干燥、灼热，微痛不适。干咳少痰，手足心热，精神疲乏，或午后低热，颧赤。扁桃体暗红、肿大，或有少许脓液附于表面。舌红，苔薄，脉细数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a3946062-2d42-4cf3-a270-50c94d27e1a3', N'8eb4acd1-ce6f-45ed-8931-a95ea45bf5ca', N'021404', N'0214', N'脾气虚弱', 1, N'', N'咽部不适，微痒或干燥，或有异物感，咯痰色白，面色少华，声音低怯，神疲乏力，食少，便溏。扁桃体肿大，充血较轻或不充血，挤压时有少许脓液。舌质淡胖，苔白润，脉细弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'85131675-fb94-455e-b835-1f7b6f79fe6d', N'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', N'021501', N'0215', N'胃火炽盛', 1, N'', N'咽痛，吞咽困难，发热。扁桃体旁充血，隆起，悬雍垂被推向对侧。舌红，苔黄，脉数有力。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9ed9540f-1ccf-43f3-942b-0d1149141568', N'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', N'021502', N'0215', N'阳明腑实', 1, N'', N'咽痛剧烈，汤水难下，口涎外流，语言含混，发热，口渴，小便短黄，腹胀便秘，咽部充血，扁桃体周围隆起明显，触之软。舌红，苔黄而厚，脉数有力。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'746bbf56-29b1-406d-9d23-e13a1c5a8cde', N'fc6fd5c1-3c88-4e3c-ab7e-ce3c0dd0245a', N'021503', N'0215', N'阴虚邪恋', 1, N'', N'脓液排出，发热渐退，咽痛渐减，口渴，尿黄，便结。舌红，苔薄少，脉细数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
