INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'68c4fa94-eca2-45cd-a4f0-a702edd9e537', N'51c6ef80-f206-46bb-bd37-d53242775eda', N'021602', N'0216', N'风热犯肺', 1, N'', N'声音粗糙，嘶哑，咽喉干燥，疼痛，咳嗽，咯痰粘白或微黄，咽喉粘膜充血，肿胀。舌边尖红，苔薄白，脉浮数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1c4e8b98-2978-4023-a22c-469529173bae', N'51c6ef80-f206-46bb-bd37-d53242775eda', N'021603', N'0216', N'肺热壅盛', 1, N'', N'声嘶，咽痛，口渴，咳嗽，咯痰色黄，身热，便秘。咽喉粘膜充血深红、肿胀，有黄白色分泌物粘附于表面。舌红，苔黄，脉数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'37f2d7ea-cfd6-4746-ba17-2021fbde586d', N'3b4be459-6d84-42c2-83ae-4303aa5ac1c4', N'021701', N'0217', N'肺肾阴虚', 1, N'', N'声嘶日久，咽喉干燥、锨热微痛，口干，干咳无痰，或痰少而粘。声带微红。舌红，少苔，脉细数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'664ac158-d400-4481-a53a-af052331851a', N'3b4be459-6d84-42c2-83ae-4303aa5ac1c4', N'021702', N'0217', N'肺脾气虚', 1, N'', N'语声低沉，气短懒言，咳嗽咯痰，色白略稀，体倦乏力，纳少便溏。声带肿而不红，声门关闭不密。舌淡，苔白，脉细弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'773b3a64-9e4d-41db-8ccd-259aa29ac950', N'3b4be459-6d84-42c2-83ae-4303aa5ac1c4', N'021703', N'0217', N'气滞血瘀', 1, N'', N'声音嘶哑，咳嗽痰少，多言后喉中觉痛，痛处不移，胸胁胀闷。声带暗红、增厚，或有声带小结、声带息肉，或室带肥厚、超越。舌质紫暗或有瘀点，脉涩。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'916345db-4d0e-4f86-8b2d-cb7460dd5ce8', N'3b4be459-6d84-42c2-83ae-4303aa5ac1c4', N'021704', N'0217', N'痰浊凝聚', 1, N'', N'声音粗浊，喉中痰多，痰白而粘，声带水肿，或有声带小结、声带息肉，色灰白。舌苔白腻，脉滑。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'46f30086-45f1-41b7-98e3-7dd85c3bb25e', N'01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', N'021801', N'0218', N'痰气互结', 1, N'', N'咽中如有炙脔或其他异物感，咽之不下、吐之不出。时作嗳气、呃逆、恶心、泛泛欲吐，胸脘胀满。舌苔白腻，脉弦滑。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ef1e2b4e-bf0c-475d-997c-6f1cf6e5896d', N'01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', N'021802', N'0218', N'肝郁气滞', 1, N'', N'咽中梗阻感，嗳气频频，或作呃逆，胁下胀闷，嗳气后稍舒。舌苔薄白，脉弦。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'43b7f4f3-607a-425d-85cd-f755e96b9e27', N'01b4f15f-f64e-4ba0-8ec4-db062cb2b03f', N'021803', N'0218', N'心脾气虚', 1, N'', N'咽中异物感，不思饮食，口中无味，面白神疲，少气懒言，或时时悲伤欲哭，夜寐不实，易惊醒或惶恐不安，小便清长，大便溏薄。舌淡，苔白，脉弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'6a6f27b8-0e73-4884-b502-feff01329e07', N'b8d2a791-9d7c-4747-a4f1-fc04cb6259b0', N'021901', N'0219', N'胃火炽盛', 1, N'', N'牙龈作痛、出血，口气热臭，渴喜冷饮，大便干结。牙龈红肿疼痛，溢出脓血。舌红，苔黄，脉数。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
