INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'31e32bcd-9f67-4e1c-824a-b4818f248984', N'14a55090-ad4c-422c-ba4f-99e111379560', N'022003', N'0220', N'气血亏虚', 1, N'', N'口不渴，或伴畏寒，便溏。口疮数量不多，周围粘膜不充血。舌淡，苔薄白，脉细弱。', CAST(0x00009A7901058E98 AS DateTime), N'', 3)
