INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c9d7b5f4-8b86-4013-9b52-6794ecb9e54f', N'a88cc32f-cb01-4027-8150-ed12a2e479a3', N'080303', N'0803', N'阴虚火炽', 1, N'', N'肿势平塌，根脚散漫，皮色紫滞，疼痛剧烈，脓腐难化，脓水稀少或带血水。全身发热烦躁，口渴多饮，大便燥结，小便短赤。舌红，苔黄燥，脉细弦数。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e8cc4153-c8b7-4a57-9915-4a3f0d7cf73a', N'a88cc32f-cb01-4027-8150-ed12a2e479a3', N'080304', N'0803', N'气虚毒滞', 1, N'', N'肿势平塌，根脚散漫，皮色灰暗不泽，胀重木痛，腐肉不化，脓液稀少，易成空腔。全身畏寒高热或身热不扬，小便频数，口渴喜热饮，精神萎靡，面色少华。舌质淡红，苔白或微黄，脉数无力。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
