INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'13e207b0-2574-401f-b221-9f41a4ef1bdd', N'81fdb486-7c56-426d-b90a-6ac673829446', N'080602', N'0806', N'火毒入营', 1, N'', N'患肢红丝粗肿明显，迅速向近端蔓延。全身寒战高热，烦躁，头痛，口渴。苔黄腻，脉洪数。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3bcff740-e447-4757-9093-aa6e5ed2e4b5', N'c1b52342-59a6-4171-ba7e-2790fa286794', N'080701', N'0807', N'湿火炽盛', 1, N'', N'初起患肢有沉重和紧束感，以后逐渐出现胀裂样疼痛，创口周围皮肤呈红色、肿胀发亮，按之陷下，迅速蔓延成片。1～2日后肿胀剧烈，可出现水疱，皮肉腐烂，高热持续。舌质红，苔薄白或黄，脉弦数。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd27c5f89-b805-49bd-a5ce-e1654ed40d2b', N'c1b52342-59a6-4171-ba7e-2790fa286794', N'080702', N'0807', N'毒入营血', 1, N'', N'壮热头痛，神昏谵语，气促，烦躁不安，呃逆呕吐。局部胀痛，伤口周围高度水肿发亮，迅速成暗紫色，有血疱，肌肉腐烂，脓液稀薄，混有气泡溢出，恶臭。舌质红绛，苔薄黄，脉洪滑数。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fb90e016-7da4-4bdf-b61f-8b6be6a2ce4f', N'69ad1f1f-1be7-411f-91c2-1494bff5b93b', N'080801', N'0808', N'热毒蕴结', 1, N'', N'颐颌之间结块疼痛，张口不利，继则肿痛渐增，检视腮腺管口常现红肿，压迫局部有粘稠的分泌物溢出，身热。苔薄腻，脉浮数。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'e22d6b2d-8b79-49f8-b924-d65085b24fa6', N'69ad1f1f-1be7-411f-91c2-1494bff5b93b', N'080802', N'0808', N'毒盛酿脓', 1, N'', N'颐颌间结肿疼痛日增，甚至肿势延及面颊和颈项，掀红灼热，张口困难，继之酿脓应指，腮腺管口能挤出脓性分泌物，高热口渴。苔黄腻，脉弦数。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'62a61987-4e63-4b9b-9c15-639463886472', N'69ad1f1f-1be7-411f-91c2-1494bff5b93b', N'080803', N'0808', N'热毒内陷', 1, N'', N'颐颌间肿块多平塌散漫，肿势可延及脸面和颈项，焮红灼热，疼痛剧烈，汤水难咽，壮热口渴，痰涌气粗，烦躁不安，甚至神昏谵语。舌质红绛，苔少而干，脉细数。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3afc7c7e-00d5-4cb2-a0cd-89b87636f90b', N'69ad1f1f-1be7-411f-91c2-1494bff5b93b', N'080804', N'0808', N'余毒未清', 1, N'', N'病程日久，经常反复发作，发作时颐颌部肿痛，腮腺管口能挤出脓性分泌物，每在进食时颐颌部感到胀痛，口内常有恶臭。苔薄黄或腻，脉滑。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'04dbdd4d-2b45-47e9-8d49-8cfcd05726da', N'd1defda5-27db-4535-b374-dd10662aadda', N'080901', N'0809', N'风热毒蕴', 1, N'', N'发于头面部，恶寒发热，皮肤掀红灼热，肿胀疼痛，甚则发生水疮，眼胞肿胀难睁。舌质红，苔薄黄，脉浮数。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f3c5cb19-f8ac-400d-81a8-81adc0b0f50e', N'd1defda5-27db-4535-b374-dd10662aadda', N'080902', N'0809', N'湿热毒蕴', 1, N'', N'发于下肢。除发热等症状外，局部以红赤肿胀，灼热疼痛为主，亦可发生水疱、紫斑，甚至结毒化脓或皮肤坏死。苔黄腻，脉洪数。反复发作，可形成大脚疯（象皮腿）。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'40d4b86c-22e5-4df2-9356-b0ead056aae1', N'd1defda5-27db-4535-b374-dd10662aadda', N'080903', N'0809', N'胎火蕴毒', 1, N'', N'发于新生儿。多见于臀部，局部红肿灼热，可呈游走性，并有壮热烦躁。', CAST(0x00009A7A014E518C AS DateTime), N'', 3)
