INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd83c8974-591b-453a-9819-79318e9fcb5b', N'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', N'081002', N'0810', N'暑湿交阻', 1, N'', N'多发于夏秋之间。初起恶寒发热，头胀，胸闷呕恶，周身骨节酸痛，胸部可布白pei(陪）。苔白腻，脉滑数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f0e6ecf5-c104-4251-868e-637cc719c0e6', N'ec1a54c5-fe91-4853-a571-2eb03ef7c9db', N'081003', N'0810', N'瘀血凝滞', 1, N'', N'劳伤筋脉诱发者，多发于四肢内侧。跌打损伤诱发者，多发于伤处。局部漫肿疼痛，皮色微红，或呈青紫，溃后脓液中央有瘀血块。妇女产后恶露停滞而成者，多发于小腹及大腿等处。发病较缓，初起一般无全身症状或全身症状较轻，化脓时出现高热。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
