INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c2e9dd14-34a9-4a91-9e82-68dc9a7f25a0', N'3f80a5bc-36cd-43d7-a1dc-7780dbec4fd1', N'081301', N'0813', N'气滞痰凝', 1, N'', N'多见于瘰疬初期，肿块坚实，无明显全身症状。苔薄腻，脉弦滑。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
