INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ebf76a8f-3783-42d6-bd3d-7ca7a6aee965', N'3f80a5bc-36cd-43d7-a1dc-7780dbec4fd1', N'081303', N'0813', N'气血两虚', 1, N'', N'疮口脓出清稀，夹有败絮样物，形体消瘦，精神倦怠，面色无华。舌淡质嫩，苔薄，脉细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a1ad8226-c939-4edb-b8b9-eb7e31c0929c', N'ffdd213d-59bf-4677-9b95-9fc12354b47b', N'081401', N'0814', N'气滞热壅', 1, N'', N'乳汁淤积结块，皮色不变或微红，肿胀疼痛。伴有恶寒发热，头痛，周身酸楚，口渴，便秘。苔黄，脉数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b1d5cebf-ba68-401c-afdb-aa13f5828172', N'ffdd213d-59bf-4677-9b95-9fc12354b47b', N'081402', N'0814', N'热毒炽盛', 1, N'', N'壮热，乳房肿痛，皮肤掀红灼热，肿块变软，有应指感。或切开排脓后引流不畅，红肿热痛不消，有“传囊”现象。舌质红，苔黄腻，脉洪数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd676a101-1b99-4daa-9ae8-8daf8b876315', N'ffdd213d-59bf-4677-9b95-9fc12354b47b', N'081403', N'0814', N'正虚毒恋', 1, N'', N'溃脓后乳房肿痛虽轻，但疮口脓水不断，脓汁清稀，愈合缓慢或形成乳漏。全身乏力，面色少华，或低热不退，饮食减少。舌质淡，苔薄，脉弱无力。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'59af24d1-b9d9-429b-acf8-c966ba0fca13', N'2f7c7b5f-7e7d-46fb-a677-a1d8787901fb', N'081501', N'0815', N'气滞痰凝', 1, N'', N'多见于初起阶段。肿块不红、不热、不痛，伴心情不畅，胸闷胁胀。苔薄腻，脉弦滑。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd056f2c3-9d4a-4d25-8fba-b0f866654961', N'2f7c7b5f-7e7d-46fb-a677-a1d8787901fb', N'081502', N'0815', N'阴虚痰热', 1, N'', N'多见于化脓或溃后阶段，皮色暗红，肿块变软，溃后脓出稀薄，有败絮状物。伴潮热盗汗，神疲乏力，颧红，形瘦，食欲减退。舌质红，苔黄，脉细数。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0e2c817b-d199-4239-b697-e6efd9e34bbe', N'2fcc4ba9-58f7-46f3-a976-95763a11c476', N'081601', N'0816', N'肝气郁结', 1, N'', N'性情急躁，遇事易怒，乳房肿块胀痛，触痛明显，胸胁牵痛。舌质红，苔白，脉弦。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7aae383f-fc73-46c3-bd64-cfa967ad2c7e', N'2fcc4ba9-58f7-46f3-a976-95763a11c476', N'081602', N'0816', N'肾气亏虚', 1, N'', N'多见于中老年。轻者多无全身症状。重者，偏于肾阳虚，面色淡白，腰腿酸软，容易倦怠，舌质淡，苔白，脉沉弱；偏于肾阴虚，头目眩晕，五心烦热，眠少梦多，舌红，苔少，脉弦细。', CAST(0x00009A7A014E52B8 AS DateTime), N'', 3)
