INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3e656687-fc18-420d-9016-39949b9f7678', N'fc65c84a-0d1e-4f50-945f-3c967f306498', N'010301', N'0103', N'肝气郁滞', 1, N'', N'月经周期不定，经量或多或少，色紫红有块，经行不畅，胸胁、乳房以及小腹胀痛，脘闷不舒，时叹息。苔薄白或薄黄，脉弦。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'1dd2c262-b451-4edb-935f-c560df38e0a9', N'fc65c84a-0d1e-4f50-945f-3c967f306498', N'010302', N'0103', N'肾气不足', 1, N'', N'月经周期不定，量少，色淡黯，质稀，神疲乏力，腰骶酸痛，头晕耳鸣。舌淡苔少，脉细尺弱。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
