INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'02bcde44-47c1-4a0f-9fdf-4ce90cf84ad7', N'7f5d1b84-f2c9-4ae7-97f4-69f9f1a8a58f', N'010402', N'0104', N'血热内扰', 1, N'', N'经来量多，色鲜红或深红，质稠粘，或有小血块。常伴心烦口渴，尿黄便秘。舌质红，苔黄，脉滑数。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c07bc77c-f314-41cc-a144-bdacd09b53fc', N'7f5d1b84-f2c9-4ae7-97f4-69f9f1a8a58f', N'010403', N'0104', N'瘀滞胞宫', 1, N'', N'经来量多，或多时不净，色紫黑，有血块或伴小腹疼痛拒按。舌质紫暗或有瘀点,脉细涩。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'495b1e2f-7148-4803-a934-ec9713bc9adc', N'6e99def8-813f-4fc1-bce7-6afb2f9b4884', N'010501', N'0105', N'肝血亏虚', 1, N'', N'月经量少或点滴即净，色淡无块，或伴头晕眼花，心悸怔忡，面色萎黄,小腹空坠，舌质淡红，脉细。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'daa38ed3-6a27-48e6-b0d0-9174493296fa', N'6e99def8-813f-4fc1-bce7-6afb2f9b4884', N'010502', N'0105', N'肾阳亏虚', 1, N'', N'月经量少，色淡红或黯红，质稀，腰脊酸软，头晕耳鸣，或小腹冷，夜尿多。舌质淡，脉弱或沉迟。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7fa8c358-cd54-43e3-aab1-2f9ed8439e2c', N'6e99def8-813f-4fc1-bce7-6afb2f9b4884', N'010503', N'0105', N'瘀滞胞宫', 1, N'', N'月经量少，色紫黑，有血块，小腹胀痛，拒按，血块排出后胀痛减轻。舌正常或紫黯，或有瘀点，脉细弦涩。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9d81feae-8f55-435a-886f-4a1dacdc1777', N'6e99def8-813f-4fc1-bce7-6afb2f9b4884', N'010504', N'0105', N'痰湿阻滞', 1, N'', N'月经量少，色淡红，质粘腻，形体肥胖，胸闷呕恶，或带多粘稠。苔白腻，脉滑。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5d5ffbdd-050c-4333-b72e-82d9cd89bdac', N'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', N'010601', N'0106', N'瘀滞胞宫', 1, N'', N'经行淋漓8－9日或10余日始净，量少，色黯有块，小腹疼痛拒按。舌质紫黯或有瘀点，脉弦涩。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3d7e2552-c6a1-4323-8bab-17e3766cc89f', N'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', N'010602', N'0106', N'阴虚血热', 1, N'', N'月经持续8－10天，量少色红质稠，咽干口燥，或有颧红，潮热，或见手心灼热。舌质红少津，苔少或无苔，脉细数。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0e1b8032-c29d-4b50-b63f-2719bd08c281', N'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', N'010603', N'0106', N'气不摄血', 1, N'', N'月经持续8－l0天始净，量少，色淡，质清稀。伴神疲乏力，或头晕眼花，心悸少寐，或纳少便溏。舌质淡，苔薄白，脉弱或虚细。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'68ca8f5d-5bca-402d-a369-b0975f3a2a6d', N'c2c9bc82-0132-48d2-b4fa-4ffb52afb1bf', N'010604', N'0106', N'湿热下注', 1, N'', N'经行8～10天始净，量少，色黯如酱，混杂粘液，气味秽臭，腰腹胀痛。平素带多色黄、有臭味。舌正常或偏红，苔黄腻，脉濡数。', CAST(0x00009A7900F4B294 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'06263374-b11e-4984-a540-e1c919584a0e', N'be06429d-94ad-41c8-83a8-45e1571bcf93', N'010701', N'0107', N'气血瘀滞', 1, N'', N'经前或经期小腹胀痛拒按，或伴乳胁胀痛。经行量少不畅，色紫黑有块，块下痛减。舌质紫黯或有瘀点，脉沉弦或涩。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f5c5f4bd-6270-4914-a199-948d222245c0', N'be06429d-94ad-41c8-83a8-45e1571bcf93', N'010702', N'0107', N'寒湿凝滞', 1, N'', N'经行小腹冷痛，得热则舒，经量少，色紫黯有块。伴形寒肢冷，小便清长。苔白，脉细或沉紧。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'67e04691-ba6b-4c94-8b33-0d15168d70f0', N'be06429d-94ad-41c8-83a8-45e1571bcf93', N'010703', N'0107', N'肝郁湿热', 1, N'', N'经前或经期小腹疼痛，或痛及腰骶，或感腹内灼热。经行量多质稠，色鲜或紫，有小血块。时伴乳胁胀痛，大便干结，小便短赤，平素带下黄稠。舌质红，苔黄腻，脉弦数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fbb60c3f-7674-493d-a90b-bceca7ef4c7c', N'be06429d-94ad-41c8-83a8-45e1571bcf93', N'010704', N'0107', N'气血亏虚', 1, N'', N'经期或经后小腹隐痛喜按，经行量少质稀。形寒肢疲，头晕目花，心悸气短.舌质淡，苔薄，脉细弦。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'd35a7c8a-94fa-4bdb-b0d5-ff8b5163189e', N'be06429d-94ad-41c8-83a8-45e1571bcf93', N'010705', N'0107', N'肝肾亏损', 1, N'', N'经期或经后小腹绵绵作痛，经行量少，色红无块。腰膝酸软，头晕耳鸣。舌淡红，苔薄，脉细弦。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'972eb5ef-74e2-4b6a-9511-2cf0bc9d57ca', N'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', N'010801', N'0108', N'肾气不足', 1, N'', N'年逾18周岁，月经未至或来潮后复闭。素体虚弱，头晕耳鸣，第二性征不足，腰腿酸软，腹无胀痛，小便频数。舌淡红，脉沉细。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'fd9a6748-b1d2-4356-8092-128891101455', N'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', N'010802', N'0108', N'气血亏虚', 1, N'', N'月经周期后延，经量偏少，继而闭经。面色不荣，头晕目眩，心悸气短，神疲乏力。舌淡边有齿印，苔薄，脉细无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'f3a4db26-301e-4b3e-9bd2-480c0176388a', N'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', N'010803', N'0108', N'痰湿阻滞', 1, N'', N'月经停闭，形体肥胖，神疲嗜睡，头晕目眩，胸闷泛恶多痰，带下量多。苔白腻，脉濡或滑。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'54fa28da-19be-4de6-95a7-f7a27829e18d', N'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', N'010804', N'0108', N'阴虚内热', 1, N'', N'月经先多后少，渐致闭经。五心烦热，颧红升火，潮热盗汗，口干舌燥。舌质红或有裂纹，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3c691396-b8eb-43c3-9848-9e71f1f66a5e', N'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', N'010805', N'0108', N'血寒凝滞', 1, N'', N'经闭不行，小腹冷痛，得热痛减，四肢欠温，大便不实。苔白，脉沉紧。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7d7692ed-c7d9-43a4-ac02-5f51dd41048a', N'b9fad751-93d9-4c11-90c4-fa6ed23e1f4e', N'010806', N'0108', N'血瘀气滞', 1, N'', N'月经闭止，胸胁胀满，小腹胀痛，精神抑郁。舌质紫黯，边有瘀点，苔薄，脉沉涩或沉弦。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c423d247-f753-490f-a7c3-9ce182fdef38', N'764def47-5f2b-4be8-bbb8-df84901861e2', N'010901', N'0109', N'血热内扰', 1, N'', N'经血量多，或淋漓不净，色深红或紫红，质粘稠，夹有少量血块。面赤头晕，烦躁易怒，口干喜饮，便秘尿赤。舌质红，苔黄，脉弦数或滑数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'52e0d567-8547-4074-8a1c-2485bcc4fc98', N'764def47-5f2b-4be8-bbb8-df84901861e2', N'010902', N'0109', N'气不摄血', 1, N'', N'经血量多，或淋漓不净，色淡质稀。神疲懒言，面色萎黄，动则气促，头晕心悸，纳呆便溏。舌质淡胖或边有齿印，舌苔薄润，脉芤或细无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'816bf6fb-1948-4d29-9d44-28f5db6b0c96', N'764def47-5f2b-4be8-bbb8-df84901861e2', N'010903', N'0109', N'肾阳亏虚', 1, N'', N'经血量多，或淋漓不净，色淡质稀。精神不振，面色晦暗，肢冷畏寒，腰膝酸软，小便清长。舌质淡，苔薄润，脉沉细无力，尺部尤弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'4e110aef-79d4-439c-8b19-015d4eedd52f', N'764def47-5f2b-4be8-bbb8-df84901861e2', N'010904', N'0109', N'肾阴亏虚', 1, N'', N'经血时多时少，色鲜红。头晕耳鸣，五心烦热，夜寐不安。舌质红或有裂纹，苔少或无苔，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'84ec306e-692b-4eb6-9ce5-826740814c33', N'764def47-5f2b-4be8-bbb8-df84901861e2', N'010905', N'0109', N'瘀滞胞宫', 1, N'', N'经漏淋漓不绝，或骤然暴下，色暗或黑，夹有瘀块，小腹疼痛，块下痛减。舌质紫暗或边有瘀斑，脉沉涩或弦紧。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'87b0a196-3834-4971-b020-d79ef326b066', N'c857d734-2de2-434a-97db-7b92a7161419', N'011001', N'0110', N'肝气郁滞', 1, N'', N'经前或行经时，乳房胀硬作痛，或乳头胀痛作痒，郁怒不欢。苔薄白，脉弦。痛', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ab8489cd-2291-4465-971b-e070a2f0fcae', N'c857d734-2de2-434a-97db-7b92a7161419', N'011002', N'0110', N'肝郁肾虚', 1, N'', N'经前或经行时乳胀，触之无块，经后消失。腰酸耳鸣，五心烦热。舌红少苔，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'10159807-bc5d-45d8-b226-71c94c548b89', N'b8edee1f-1278-43d4-8bd0-555fcddd4c90', N'011101', N'0111', N'阴虚内热', 1, N'', N'经行前后见有发热，午后潮热，烦躁少寐，口于便艰。舌红而于，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'bbefff27-74f6-4231-9051-34e0e9a919fb', N'b8edee1f-1278-43d4-8bd0-555fcddd4c90', N'011102', N'0111', N'气血亏虚', 1, N'', N'经期或经后发热，经行量多色淡，神疲畏寒，气短懒言，面目虚浮。舌淡嫩，苔白润，脉虚或细略数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'867d5d5b-37a5-4096-9e75-f1fc3da55c9d', N'b8edee1f-1278-43d4-8bd0-555fcddd4c90', N'011103', N'0111', N'营卫不和', 1, N'', N'经行发热，经血量少不畅，小腹作胀，恶风自汗。舌质淡红，苔薄白，脉浮缓。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5fc03d7c-6241-478e-8875-acdb5e081bce', N'b8edee1f-1278-43d4-8bd0-555fcddd4c90', N'011104', N'0111', N'瘀热内郁', 1, N'', N'经前或经期发热，经色紫黯，间有血块，腹痛。舌黯或边尖有瘀点，脉沉弦数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ffda6580-5368-4323-a22b-376e6a33f22a', N'0f0e36fd-6418-401b-bddb-469f1650c519', N'011201', N'0112', N'气血瘀滞', 1, N'', N'经前或经期头痛剧烈，痛如锥刺。经血量少，行而不畅，色紫有块，或伴小腹疼痛，拒按。舌暗边有瘀点，脉细涩或弦涩。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a9006bd5-d543-4df3-8b03-0f4c88313b1b', N'0f0e36fd-6418-401b-bddb-469f1650c519', N'011202', N'0112', N'肝血亏虚', 1, N'', N'经期', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'646a2490-16f1-4c5c-9282-6f34fe3abbc2', N'0f0e36fd-6418-401b-bddb-469f1650c519', N'011203', N'0112', N'肝火旺盛', 1, N'', N'经前头痛目胀，甚或巅顶掣痛。烦躁易怒，口苦咽干，经行量多色鲜。舌质红，苔薄白，脉弦带数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'42c57d87-d704-416b-b6a7-51c2519d11be', N'a880fa7f-14ba-4f8e-8af2-6d21efa350d9', N'011301', N'0113', N'气血亏虚', 1, N'', N'经期或行经前后，肢体酸痛麻木，神疲乏力，经行量少色淡。舌质淡，苔薄，脉细。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0d2863d6-a7d9-4a34-be85-0f8a0ed751b1', N'a880fa7f-14ba-4f8e-8af2-6d21efa350d9', N'011302', N'0113', N'寒湿凝滞', 1, N'', N'经期或行经前后，关节疼痛酸楚重着，腰膝尤甚，得热则舒。月经后期，量少不畅，色暗有块。苔薄白，脉沉紧。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'af0c040c-a8ab-464a-9ebb-d8f88eadec40', N'10434b3e-4787-48ad-8882-e43036a625d6', N'011401', N'0114', N'肝郁脾虚', 1, N'', N'经行期间大便溏泻。神疲肢重，面色不华，纳差口淡，或伴腹胀隐痛。舌质淡，边有齿印，苔薄，脉濡。若兼肝旺者，则有小腹胀痛，胸胁痞闷等，脉兼弦象。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3998a702-1779-46ef-b404-e26ef4e27407', N'10434b3e-4787-48ad-8882-e43036a625d6', N'011402', N'0114', N'肾阳亏虚', 1, N'', N'经行期间大便泄泻或完谷不化，面色晦暗，腰腿酸软，形寒肢冷，小便清长。舌质淡，苔白滑，脉沉细。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'304a96da-b932-468c-9317-8fb5e139118b', N'ec09bbef-a2fa-46cb-9088-f30e9edbba3d', N'011501', N'0115', N'肝郁化火', 1, N'', N'经期或行经前，发生吐血、衄血，量较多色鲜红。月经先期，量少色红质稠，或月经不行，面赤烦热，口苦咽干，乳胁胀痛，烦躁易怒。舌红苔黄，脉弦数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'13066707-d96c-43a8-85db-57c5a6fef748', N'ec09bbef-a2fa-46cb-9088-f30e9edbba3d', N'011502', N'0115', N'阴虚肺燥', 1, N'', N'行经前后或月经期间，出现鼻衄或咳血。经行量少色红，甚或不行。咽干鼻燥，咳呛气逆，音哑不扬，潮热颧红，腰膝酸软。舌红少津，苦薄，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2fed8fa2-a70b-40eb-8b5d-1bb429d7ff55', N'ec09bbef-a2fa-46cb-9088-f30e9edbba3d', N'011503', N'0115', N'胃热炽盛', 1, N'', N'经行前后或经期发生吐血，色紫红或兼夹食物。经行先期，量少色鲜，甚则闭而不行，唇红口干，牙龈肿痛，口臭便秘。舌红，苔黄燥，脉滑数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'51ceb114-c38d-425a-b77e-43b7ab400711', N'5bf25f9f-dc81-48d4-891b-247de309b0a9', N'011601', N'0116', N'阴虚火旺', 1, N'', N'经期见有口舌糜烂，口燥咽干，五心烦热，形体消瘦，头晕腰酸，夜寐不安、尿少色黄。舌尖红，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7f2259e3-879a-484c-b1ef-e80967b03ac8', N'5bf25f9f-dc81-48d4-891b-247de309b0a9', N'011602', N'0116', N'胃热炽盛', 1, N'', N'经行见有口舌糜烂，口臭，口干喜饮，胸闷纳呆，尿黄，便结。舌苔黄厚，脉滑数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cb0b1e96-c782-4787-989c-d6e2d7588299', N'f7eb6ea6-5ee8-4f89-bd1c-45d4c44964b9', N'011701', N'0117', N'肝肾阴虚', 1, N'', N'经行先期，量多色红或淋漓不绝。烘热汗出，五心烦热，口干便艰，腰膝酸软，头晕耳鸣。舌红少苔，脉细数。兼肝旺者多见烦躁易怒；心火旺者可见心悸失眠。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'a78ed6bd-3c58-4fee-bcb9-0fcbbba833c3', N'f7eb6ea6-5ee8-4f89-bd1c-45d4c44964b9', N'011702', N'0117', N'肾阳亏虚', 1, N'', N'月经后愆或闭阻不行，行则量多，色淡质稀或淋漓不止。神萎肢冷，面色晦暗，头目晕眩，腰酸尿频。舌淡，苔薄，脉沉细无力。兼脾阳虚者可见纳少便溏，面浮肢肿；兼心脾两虚者，可见心悸善忘，少寐多梦。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'8c734fb7-7ce6-435b-9fe2-5ac29a033279', N'76c17650-3820-4cda-864a-368b4fea7005', N'011801', N'0118', N'脾虚湿困', 1, N'', N'分泌物色白或淡黄，量多如涕，无臭，绵绵不断。恶心纳少，腰酸神倦。舌淡胖，苔白腻，脉缓弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'92725e16-f253-486a-b597-8df21000a68e', N'76c17650-3820-4cda-864a-368b4fea7005', N'011802', N'0118', N'肾阴亏虚', 1, N'', N'分泌物色黄或兼赤，质粘无臭。阴户灼热，五心烦热，腰酸耳鸣，头晕心悸。舌红，苔少，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'0c99bff9-c00b-43d8-ae6c-249b5a529208', N'76c17650-3820-4cda-864a-368b4fea7005', N'011803', N'0118', N'肾阳亏虚', 1, N'', N'分泌物量多，清稀如水，或透明如鸡子清，绵绵不绝，腰酸腹冷，小便频数清长，夜间尤甚。舌质淡，苔薄白，脉沉迟。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'595faae9-8e1a-4563-a6fc-3473dcaca6db', N'76c17650-3820-4cda-864a-368b4fea7005', N'011804', N'0118', N'湿热下注', 1, N'', N'分泌物量多，色黄或兼绿，质粘稠，或如豆渣，或似泡沫，气秽或臭，阴户灼热瘙痒，小便短赤，或伴有腹部掣痛。舌质红，苔黄腻，脉儒数。兼肝胆湿热者，出现乳胁胀痛，头痛口苦，烦躁易怒，大便干结。舌红，苔黄，脉弦数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'c885c8a2-4b0e-43aa-8e6f-b28ee726a786', N'5562ed3a-fe6d-4937-b099-ea86c696b318', N'011901', N'0119', N'肝胃不和', 1, N'', N'妊娠初期呕吐酸水或苦水，恶闻油腥。胸满胁痛，心烦口苦，暖气叹息，头胀而晕。舌淡红，苔微黄，脉滑。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ca991a20-659d-45f9-8cdd-419a9123ef2a', N'5562ed3a-fe6d-4937-b099-ea86c696b318', N'011902', N'0119', N'脾胃虚弱', 1, N'', N'妊娠初起，呕吐不食，或吐清水，头晕体倦，脘痞腹胀。舌淡，苔自，脉缓滑。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9beb1176-28d7-4b1c-bd73-d748d9ceedee', N'5562ed3a-fe6d-4937-b099-ea86c696b318', N'011903', N'0119', N'痰湿阻滞', 1, N'', N'妊娠早期，呕吐痰涎，口淡而腻，不思饮食，胸腹满闷。舌淡，苔白腻，脉滑。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'54ee64af-eb10-4efa-9305-855428b2118c', N'5562ed3a-fe6d-4937-b099-ea86c696b318', N'011904', N'0119', N'气阴两虚', 1, N'', N'妊娠剧吐，甚至吐苦黄水或兼血水，频频发作。持续日久，以致精神萎靡，嗜睡消瘦，双目无神，眼眶下陷，肌肤干皱失泽，低热口于，尿少便艰。舌红少津，苔薄黄或光剥，脉细滑数无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7effa4e9-5fc6-4217-9851-4e749528cf82', N'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', N'012001', N'0120', N'肾气不足', 1, N'', N'妊娠期，阴道漏红，量少色淡。腰酸腹坠，或伴头晕耳鸣，小便频数，或有流产史。舌淡，苔白，脉沉滑尺弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ea0c9cdd-aeb3-45b7-ad06-f0059d41c2f6', N'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', N'012002', N'0120', N'气血亏虚', 1, N'', N'妊娠期，阴道漏红，量少，色淡质薄。腰酸腹坠，神疲肢软，心悸气短，面色少华。舌质淡，苔薄白，脉细滑。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5d21abff-cb3a-4afd-b47e-2a17ab0ce8ed', N'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', N'012003', N'0120', N'血热内扰', 1, N'', N'妊娠期，阴道漏红，色鲜。或腹痛下坠，心烦不安，手心灼热，口干咽燥，大使秘结。舌红，苔黄而干，脉弦滑或滑数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'3226e963-9d5e-48ec-9572-c4a75a238602', N'b1bb4d46-61d4-49c4-9c72-1afc9b9796de', N'012004', N'0120', N'外伤损络', 1, N'', N'妊娠外伤后腰腹胀坠作痛，阴道漏红，色紫红，或有小血块。舌淡红，脉细滑无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'86ea35ca-72cd-4f2d-871e-a0939c7576ca', N'ad320557-e18e-4922-ae91-fc4d0b2665c6', N'012101', N'0121', N'脾肾两虚', 1, N'', N'头晕耳鸣，腰酸膝软，神疲肢倦，气短懒言，纳少便溏，夜尿频多，眼眶黯黑或面有黯斑。舌质淡，苔薄，脉沉弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'daea2a8d-5df8-4268-b1ca-67a2c5a1d424', N'ad320557-e18e-4922-ae91-fc4d0b2665c6', N'012102', N'0121', N'气血亏虚', 1, N'', N'面色苍自或萎黄，心悸气短，头晕肢软，神疲乏力。舌淡，苔薄，脉沉细弱无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'5f378c9f-0c98-4e0c-9990-f34189e8a658', N'ad320557-e18e-4922-ae91-fc4d0b2665c6', N'012103', N'0121', N'阴虚内热', 1, N'', N'两颧潮红，口干咽燥，手足心热，烦躁不宁，或形体消瘦。舌红，少苔，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2b78e7be-5ffa-44fb-ab4b-8cf437dbe9bb', N'8ec1fcde-fa82-4594-82c3-d9e8adb733a5', N'012201', N'0122', N'脾虚湿困', 1, N'', N'妊娠数月，面目四肢浮肿或遍及全身，肤色淡黄，皮薄而光亮，胸闷气短，懒于言语，口淡无味，食欲不振，大便溏薄。舌质淡胖，脉缓滑无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'33746089-54b6-4440-8755-1b9429d3a543', N'8ec1fcde-fa82-4594-82c3-d9e8adb733a5', N'012202', N'0122', N'肾阳亏虚', 1, N'', N'孕后数月或晚期，面目浮肿，下肢尤甚，按之没指，心悸气短，肢冷畏寒，小便短少。舌质淡嫩或边尖有齿痕，苔白润，脉沉细无力或沉迟而弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'ffc748d9-43c5-4425-8fe3-d6c667d64a12', N'8ec1fcde-fa82-4594-82c3-d9e8adb733a5', N'012203', N'0122', N'肝气郁滞', 1, N'', N'妊娠4～5月后，先由脚肿，渐及于腿，皮色不变，随按随起，头晕胀痛，胸闷胁胀，食少。苔薄腻，脉弦滑。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9f2b625e-bfa0-42f5-b741-42897e8bd289', N'718bb78d-ab3f-45f7-9982-3a844170e5fd', N'012301', N'0123', N'膀胱湿热', 1, N'', N'小便频数涩痛，淋沥不爽，尿少色黄而赤，小腹拘急。舌尖红，苔黄而干，脉数有力或弦滑。湿重者，尿色黄浊，头身困重，胸闷纳呆，苔黄腻，脉滑数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'df4a079e-df86-4070-8246-7f02ccb0477e', N'718bb78d-ab3f-45f7-9982-3a844170e5fd', N'012302', N'0123', N'阴虚内热', 1, N'', N'小便频数短黄，欲出未尽，腰腹酸坠，五心烦热，心悸颧赤。舌红，少苔，脉细滑数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'739be0ef-09b9-4cbb-ad82-a8b205702688', N'5d99527d-cc0b-44ec-9426-2eb8b43193fb', N'012401', N'0124', N'脾虚气陷', 1, N'', N'妊娠期间尿急频作点滴量少，甚或不通，小腹拘急，胀坠难忍，烦躁不安。面色淡白，神疲肢软。舌淡，苔薄白，脉细无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'9834e676-68a0-4aa4-9470-adc0bbb4b55b', N'5d99527d-cc0b-44ec-9426-2eb8b43193fb', N'012402', N'0124', N'肾阳亏虚', 1, N'', N'妊娠期间尿频尿涩，点滴不畅或闭而不通，小腹拘急，胀坠疼痛，烦躁不安。腰膝酸软，形寒畏冷。舌质淡嫩，苔薄润，脉沉细无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b57feb62-6ea0-40f6-a8ea-52466063f20d', N'5db29490-3003-4348-90b6-3a6593ea6e58', N'012501', N'0125', N'气不摄血', 1, N'', N'产后下血如崩，色红或淡红，质稀。面色苍白，腰膝酸软，头晕目眩，精神疲乏，心悸气短，甚或出现冷汗淋漓，四肢厥冷，神志昏迷等症。舌淡，苔薄，脉微或虚大。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'00aa270f-06eb-4ccf-9c6f-13a4a89b359f', N'5db29490-3003-4348-90b6-3a6593ea6e58', N'012502', N'0125', N'气血瘀滞', 1, N'', N'产后出血不止，量多如崩，色紫黯，夹有血块，小腹胀痛。舌质紫或有瘀斑，脉弦或涩。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b739d34d-fc10-4557-8f56-bb8269017a9a', N'5db29490-3003-4348-90b6-3a6593ea6e58', N'012503', N'0125', N'血热内扰', 1, N'', N'产后下血量多，血色鲜红或紫，势急如崩。头胀眩晕，胸胁胀痛，烦躁易怒。舌质偏红，苔黄，脉弦数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'cd97c087-124e-46e2-965f-27a716cf0ba4', N'7b362e26-78c1-40be-88b1-d11d6e723b3a', N'012601', N'0126', N'脾虚气陷', 1, N'', N'产后恶露过期不止，量多或淋漓不断，色淡红，质稀薄，无臭味。小腹空坠，神倦懒言，面色淡白。舌淡，脉缓弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'b25c417a-ecfa-405c-9e38-7330b40eceb2', N'7b362e26-78c1-40be-88b1-d11d6e723b3a', N'012602', N'0126', N'血热内扰', 1, N'', N'产后恶露过期不止，量较多，色深红，质惆粘，有臭秽气。面色潮红，口燥咽干。舌质红，脉虚细而数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2bfb4503-e1da-43d3-8352-699b71a237ed', N'7b362e26-78c1-40be-88b1-d11d6e723b3a', N'012603', N'0126', N'气血瘀滞', 1, N'', N'产后恶露淋漓涩滞不爽，量少，色紫黯有块，小腹疼痛拒按。舌紫黯或边有紫点，脉弦涩或沉而有力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'2655fdfd-dce5-460d-9b20-0748ff6473c7', N'fdb562c7-9f9c-4c62-a5be-57f5afaca38f', N'012701', N'0127', N'血虚津亏', 1, N'', N'产后大便干燥，数日不解，或解时艰涩难下，但腹无胀痛，饮食如常。面色萎黄，皮肤不润。舌淡，苔薄，脉虚涩。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'747fa79c-ae7d-4f61-94de-1b69c9503adc', N'fdb562c7-9f9c-4c62-a5be-57f5afaca38f', N'012702', N'0127', N'阴虚内热', 1, N'', N'产后大便燥结，数日不解，伴口干咽燥，手心灼热，胸满腹胀。舌红少津，苔薄黄，脉细数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'7c661881-4830-444d-a20c-060cd93a15de', N'fdb562c7-9f9c-4c62-a5be-57f5afaca38f', N'012703', N'0127', N'脾虚气陷', 1, N'', N'产后虽有便意，临厕努责乏力，气短汗出，倦怠无力，便出并不干硬，或头晕目眩。舌淡，苔薄，脉大而虚。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'33e6d8f2-a3ac-4a48-92ba-5f71a998f02b', N'cfad8981-d0c3-4189-b920-ddc9e4650047', N'012801', N'0128', N'热毒炽盛', 1, N'', N'恶寒，高热，恶露量多或少，色紫黯如败酱，有秽臭，小腹疼痛拒按，烦躁口渴，尿少色黄，大便燥结。舌红，苔黄，脉数有力。若邪入营血，则高热不退，斑疹隐隐，舌红绛，苔黄燥，脉细弦而数。若热传心包，则神昏谵语，甚至昏厥，面色苍白，四肢厥逆，脉微细而数。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'091d5d6e-a524-4df9-9b85-b2709c0871f7', N'cfad8981-d0c3-4189-b920-ddc9e4650047', N'012802', N'0128', N'气血瘀滞', 1, N'', N'寒热时作，恶露不下或甚少，气秽臭，色紫黯有块，小腹疼痛拒按。舌紫黯有瘀点，脉细涩。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'340b98b4-74ce-42ae-b1bd-a9abaa47bd65', N'834deeb6-c861-4e0b-a56f-466dc1c697c6', N'012901', N'0129', N'肺卫气虚', 1, N'', N'产后汗出较多，不能自止，动则益甚。时或恶风，面色恍白，气短懒言，语声低怯，倦怠乏力。舌质淡，苔薄，脉虚弱。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'21e81a51-957b-46d9-8e77-81f46fd209a8', N'834deeb6-c861-4e0b-a56f-466dc1c697c6', N'012902', N'0129', N'阴虚内热', 1, N'', N'产后睡中汗出，甚则湿透衣衫，醒来即止。面色潮红，头晕耳鸣，口燥咽干，渴不思饮，或五心烦热，腰膝酸软。舌红嫩或绛，少苔或无苔，脉细数无力。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
INSERT [dbo].[semiotic] ([SEMCid], [CDISid], [code], [groupCode], [name], [isClassical], [SPETid], [illustration], [createDay], [optrid], [state]) VALUES (N'73d4a342-74d6-4531-be8f-9fae50a220d0', N'23082329-5388-4482-b911-56bc771ba054', N'013001', N'0130', N'气血亏虚', 1, N'', N'产后乳少，甚或全无，乳汁清稀，乳房柔软，无胀感。伴面色少华，神疲食少。舌淡，少苔，脉虚细。', CAST(0x00009A7900F4B3C0 AS DateTime), N'', 3)
